import{_ as a,o as n,c as s,a as t,b as r}from"./chunks/framework.bd8d98b6.js";const k=JSON.parse('{"title":"Панчакшара мантра","description":"Пятислоговая мантра","frontmatter":{"title":"Панчакшара мантра","description":"Пятислоговая мантра","sans":"पञ्चक्षरमन्त्र","trans":"pañcakṣaramantra","tags":"mantra","date":"2021-04-05T00:00:00.000Z"},"headers":[],"relativePath":"mantra/panchakshara/index.md","filePath":"mantra/panchakshara/index.md"}'),o={name:"mantra/panchakshara/index.md"},e=t("hr",null,null,-1),i=t("p",{sans:"",big:""},"ॐ नमः शिवाय",-1),_=t("p",{trans:""},"om namah śivāya",-1),c=r('<p>Поклонение Благому</p><div class="stanza"><h3 id="основные-толкования" tabindex="-1">Основные толкования: <a class="header-anchor" href="#основные-толкования" aria-label="Permalink to &quot;Основные толкования:&quot;">​</a></h3><ul><li><strong>Толкование джняни</strong>. Слово «<strong>намах</strong>» (नमः) обозначает ограниченную человеческую душу (дживу, जिव). Слово «<strong>шива</strong>» (शिव) обозначает Мировой Дух (Параматман). Окончание «<strong>йа</strong>» (य) указывает на идентичность Дживы и Параматмана. Слог <strong>Ом</strong> (ॐ) символизирует разрушение <strong>Майи</strong> — иллюзии.</li><li><strong>Толкование бхакти</strong>. Слог «<strong>ом</strong>» (ॐ) означает «весь мир, живой и неживой». Слово «намах» (नमः) — это сокращение от «<strong>намама</strong>» (न मम) — «не мой», «не для меня». Слово «<strong>шивайа</strong>» (शिवाय) означает «для Шивы». Мантра в целом переводится так: весь этот мир, живой и неживой, принадлежит не мне, существует не для меня, а для Шивы.</li></ul><p>Хотя она и имеет прямой перевод — <strong>Поклонение Благому</strong>, — её основное значение заключается не в словах, а в звуках, её составляющих, которые, в свою очередь, связываются с пятью перво-элементами (землёй, водой, огнём, воздухом и акашей) и ипостасью Шивы — <strong>Панчамукха</strong> или <strong>Панчанана</strong> ([Обладающий] Пятью ликами).</p></div><hr>',3),g=[e,i,_,c];function d(l,h,p,m,u,f){return n(),s("div",null,g)}const T=a(o,[["render",d]]);export{k as __pageData,T as default};
