import{_ as a,o as t,c as d,b as s}from"./chunks/framework.bd8d98b6.js";const i=JSON.parse('{"title":"Таблица 36 таттв","description":"","frontmatter":{"title":"Таблица 36 таттв","tags":"scheme"},"headers":[],"relativePath":"schemes/tattvas/index.md","filePath":"schemes/tattvas/index.md"}'),c={name:"schemes/tattvas/index.md"},v=s('<table id="tattvas" class="names" data-v-750bdc14><tbody data-v-750bdc14><tr data-v-750bdc14><td colspan="4" data-v-750bdc14><p class="sans" data-v-750bdc14>परमशिव</p><p class="trans" data-v-750bdc14>PARAMAŚIVA</p><p class="title" data-v-750bdc14>Высший Шива или Источник Всего</p></td></tr><tr data-v-750bdc14><td class="num" style="width:80px;" data-v-750bdc14>1.</td><td style="width:80px;" data-v-750bdc14><p class="sans" data-v-750bdc14>शिव</p><p class="trans" data-v-750bdc14>Śiva</p></td><td style="width:80%;" colspan="2" data-v-750bdc14><span class="title" data-v-750bdc14>Шива</span> выше описаний, однако можно сказать, что он есть истинное &quot;Я&quot;. Шива полон Cicchakti (Cit + śakti) или Силы Сознания. И Шива и Шакти возникают из Парамашивы или Высшего Шивы. В Парамашиве нет даже тонкой двойственности Шива—Шакти. Все таттвы возникают из этой Высшей Реальности.</td></tr><tr data-v-750bdc14><td class="num" data-v-750bdc14>2.</td><td data-v-750bdc14><p class="sans" data-v-750bdc14>शक्ति</p><p class="trans" data-v-750bdc14>Śakti</p></td><td colspan="2" data-v-750bdc14><span class="title" data-v-750bdc14>Шакти</span> также вне описаний. И всё же, можно сказать, что она — истинное Бытие. Шива и Шакти являют истинное &quot;Я есть&quot;. Шакти делает Шиву сознающим Себя, но при этом нельзя найти никакого различия между ними. Шакти — очень тонкое &quot;напряжение&quot; на бесконечном и неподвижном теле Шивы. Из неё все остальные таттвы возникают, по сути, они — её форма. Между ней и всеми остальными таттвами присутствует совершенное единство. Она полна Ānandaśakti или Силы Блаженства.</td></tr><tr data-v-750bdc14><td colspan="4" data-v-750bdc14><p class="sans" data-v-750bdc14>अनाश्रितशिव</p><p class="trans" data-v-750bdc14>ANĀŚRITAŚIVA</p><p class="translation" data-v-750bdc14>Шива без категорий</p></td></tr><tr data-v-750bdc14><td class="num" data-v-750bdc14>3.</td><td data-v-750bdc14><p class="sans" data-v-750bdc14>सदाशिव</p><p class="trans" data-v-750bdc14>Sadāśiva</p> (सादाख्य Sādākhya)</td><td colspan="2" data-v-750bdc14><span class="title" data-v-750bdc14>Садашива</span> — обиталище Icchāśakti или Силы Воли. Здесь появляется туманная Вселенная. Сознание принимает форму &quot;Я есть Это&quot; (&quot;Я — эта туманная Вселенная&quot;). Вселенная только появляется и потому предстаёт размытой. Так как Вселенная ещё не сформировалась, &quot;Я&quot; преобладает.</td></tr><tr data-v-750bdc14><td class="num" data-v-750bdc14>4.</td><td data-v-750bdc14><p class="sans" data-v-750bdc14>ईश्वर</p><p class="trans" data-v-750bdc14>Īśvara</p></td><td colspan="2" data-v-750bdc14><span class="title" data-v-750bdc14>Ишвара</span> — обиталище Jñānaśakti или Силы Знания. Здесь появляется ясная отчётливая Вселенная. Сознание принимает форму &quot;Это есть Я&quot; (&quot;Отчётливая Вселенная есть Я&quot;). Вселенная теперь полностью проявлена и потому отчётлива. Так как Вселенная полностью сформировалась, &quot;Это&quot; преобладает. &quot;Я&quot; тоже здесь, но оно потрясено божественным чудом раскрытия Себя перед Самим Собой.</td></tr><tr data-v-750bdc14><td class="num" data-v-750bdc14>5.</td><td data-v-750bdc14><p class="sans" data-v-750bdc14>सद्विद्या</p><p class="trans" data-v-750bdc14>Sadvidyā</p> (<span lang="sa" data-v-750bdc14>शुद्धविद्या</span> Śuddhavidyā)</td><td colspan="2" data-v-750bdc14><span class="title" data-v-750bdc14>Садвидья</span> — обиталище Kriyāśakti или Силы Действия. Здесь сознание полностью сбалансировано между &quot;Я&quot; и &quot;Это&quot;. &quot;Это&quot; — Вселенная. Осознание таково: &quot;Я есть Я, Это есть Это&quot;. &quot;Я&quot; сбалансировано &quot;Этим&quot; и они каким-то образом преобладают оба. Это единство в различиях, ведь здесь есть вселенная, отличная от Шивы (&quot;Я&quot;), но в то же время присутствует лежащее в их основе единство.</td></tr><tr data-v-750bdc14><td colspan="4" data-v-750bdc14><p class="sans" data-v-750bdc14>आणवमल</p><p class="trans" data-v-750bdc14>ĀṆAVAMALA</p> Анавамала или Первичное Загрязнение, предвосхищающее все остальные таттвы, в которых различия и двойственность преобладают. После Анавамалы возникает ещё две малы или загрязнения: Māyīyamala <span lang="sa" data-v-750bdc14>मायीयमल</span> и Kārmamala <span lang="sa" data-v-750bdc14>कार्ममल</span>, которые привносят различия и привязанности в деятельность соответственно.</td></tr><tr data-v-750bdc14><td class="num" data-v-750bdc14>6.</td><td data-v-750bdc14><p class="sans" data-v-750bdc14>माया</p><p class="trans" data-v-750bdc14>Māyā</p></td><td colspan="2" data-v-750bdc14><span class="title" data-v-750bdc14>Майя</span> — это Неведение. Майя Трики не та же, что и в Веданте. В Трике Майя — это таттва, или &quot;реальный&quot; уровень Творения. Майя отвечает за сокрытие сущностной природы Шивы вуалью заблуждения. Майя превращает бесконечное Я в кого-то полного ограничений. Майия-мала и Карма-мала возникают в результате действия Майя-таттвы. Эти две нечистоты наполняют деятельность различиями и привязанностями. Иногда Майя рассматривается как &quot;шестая&quot; канчука или покров, окутывающий сознание тонкой, но прочной вуалью неведения.</td></tr><tr data-v-750bdc14><td class="num" data-v-750bdc14>7.</td><td data-v-750bdc14><p class="sans" data-v-750bdc14>कला</p><p class="trans" data-v-750bdc14>Kalā</p></td><td width="80px" data-v-750bdc14><span class="title" data-v-750bdc14>КалА</span> формирует представление об &quot;ограниченной активности&quot; покрытого вуалью Майи сознания. Это Крия-шакти (Всемогущество), претерпевшая сжатие. Она вносит ошибочные суждения: &quot;Я ограничен в действиях&quot;, &quot;Я не могу сделать это&quot;, &quot;Я не могу сделать то&quot; и т.д.</td><td style="width:10%;" rowspan="5" data-v-750bdc14><p class="sans" data-v-750bdc14>कञ्चुक</p><span class="trans" data-v-750bdc14>KAÑCUKA</span> Канчуки (покровы) </td></tr><tr data-v-750bdc14><td class="num" data-v-750bdc14>8.</td><td data-v-750bdc14><p class="sans" data-v-750bdc14>विद्या</p><p class="trans" data-v-750bdc14>Vidyā</p></td><td data-v-750bdc14><span class="title" data-v-750bdc14>Видья</span> формирует представление об &quot;ограниченном знании&quot; покрытого вуалью Майи сознания. Это Джняна-шакти (Всезнание), претерпевшая сжатие. Она вносит ошибочные суждения: &quot;Я ограничен в познании&quot;, &quot;Я не знаю этого&quot;, &quot;Я не знаю того&quot;, и т.д.</td></tr><tr data-v-750bdc14><td class="num" data-v-750bdc14>9.</td><td data-v-750bdc14><p class="sans" data-v-750bdc14>राग</p><p class="trans" data-v-750bdc14>Rāga</p></td><td data-v-750bdc14><span class="title" data-v-750bdc14>Рага</span> формирует представление об &quot;ограниченной воле&quot;, ведущее покрытое вуалью Майи сознание к привязанности. Это Иччха-шакти (Совершенная Воля) претерпевшая сжатие. Она привносит ошибочные суждения: &quot;Мне чего-то не хватает&quot;, &quot;Я в чем-то нуждаюсь&quot; и, закономерно, возникает и результат такой нужды в форме &quot;Я привязан к чему-то&quot;.</td></tr><tr data-v-750bdc14><td class="num" data-v-750bdc14>10.</td><td data-v-750bdc14><p class="sans" data-v-750bdc14>काल</p><p class="trans" data-v-750bdc14>Kāla</p></td><td data-v-750bdc14><span class="title" data-v-750bdc14>КАла</span> формирует представление о &quot;частях&quot;, ведущее сознание, покрытое вуалью Майи к представлению о времени. Это Ананда-шакти (Высшее блаженство), претерпевшая сжатие. Она привносит ошибочные суждения вроде: &quot;Я не вечен, и не непреходящ&quot;, &quot;Я живу лишь несколько лет&quot;, &quot;Я стар&quot;, &quot;Я молод&quot; и т.д.</td></tr><tr data-v-750bdc14><td class="num" data-v-750bdc14>11.</td><td data-v-750bdc14><p class="sans" data-v-750bdc14>नियति</p><p class="trans" data-v-750bdc14>Niyati</p></td><td data-v-750bdc14><span class="title" data-v-750bdc14>Нияти</span> формирует представление о связи причины и следствия, ведущее к представлению об обусловленном судьбой порядке вещей. Это Cicchakti (Сила знания) претерпевшая сжатие. Она вносит ошибочные суждения: &quot;Если Я сделаю это, я получу соотвествующий результат&quot;, &quot;Я достоин&quot;, &quot;Я грешен&quot; и т.д.</td></tr><tr data-v-750bdc14><td class="num" data-v-750bdc14>12.</td><td data-v-750bdc14><p class="sans" data-v-750bdc14>पुरुष</p><p class="trans" data-v-750bdc14>Puruṣa</p></td><td colspan="2" data-v-750bdc14><span class="title" data-v-750bdc14>Пуруша</span> — это Сам Шива, подвергнувшийся всем перечисленным ограничениям (Kañcuka). Несмотря на добровольное самоограничение, Шива при этом остаётся неизменным. Этот Пуруша — это внутреннее &quot;Я&quot; всех существ. В то время как оно является конечной целью философии Санкхья, Трика считет это только этапом инволюции/эволюции Шакти.</td></tr><tr data-v-750bdc14><td class="num" data-v-750bdc14>13.</td><td data-v-750bdc14><p class="sans" data-v-750bdc14>प्रकृति</p><p class="trans" data-v-750bdc14>Prakṛti</p></td><td colspan="2" data-v-750bdc14><span class="title" data-v-750bdc14>Пракрити</span> — это просто состояние, в котором все три качества (Guṇa) остаются полностью сбалансированными. Три гуны — это Sattva, Rajas и Tamas. Они известны также как Джняна-шакти, Иччха-шакти и Крия-шакти соответственно, претерпевшие еще одно сжатие. Когда Высший свидетель (Шива) покрыт вуалью Майи и её канчуками, Он &quot;будто бы&quot; превращается в Пурушу. Этот Пуруша созерцает Шакти и она предстаёт теперь сформированной из трёх сил, известных как гуны или качества. Ни Шива, ни Шакти в действительности не изменяются, но Шива, через добровольное самоограничение, меняет Свою точку зрения и начинает считать Себя ограниченой душой.</td></tr><tr data-v-750bdc14><td class="num" data-v-750bdc14>14.</td><td data-v-750bdc14> $1 <p class="trans" data-v-750bdc14>Buddhi</p> (महत् Mahat)</td><td data-v-750bdc14><span class="title" data-v-750bdc14>Буддхи</span> — первая ступень эволюции Природы. Он формирует определяющую способность, с помощью которой вы выбираете направление действий в вашей жизни. По простому, Буддхи — это интеллект. Это преимущественно саттвичная таттва, что означает, что Джняна (знание) преобладает в нём. Буддхи — также тот принцип, который позволяет вам абстрактно каталогизировать конкретные объекты, животных или личностей по чётким категориям. Например, &quot;Это — собака&quot;, &quot;Это — шляпа&quot;.</td><td rowspan="3" data-v-750bdc14><p class="sans" data-v-750bdc14>अन्तःकरण</p><p class="trans" data-v-750bdc14>ANTAḤKARAṆA</p> Внутренний (психический) орган</td></tr><tr data-v-750bdc14><td class="num" data-v-750bdc14>15.</td><td data-v-750bdc14><p class="sans" data-v-750bdc14>अहङ्कार</p><p class="trans" data-v-750bdc14>Ahaṅkāra</p> (अस्मिता Asmitā)</td><td data-v-750bdc14><span class="title" data-v-750bdc14>Аханкара</span> — вторая ступень эволюции Природы. Она формирует осознание ограниченного &quot;Я&quot;. По простому, Аханкара — это эго. Манас, индрии и танматры не только возникают из него, но все они постоянно поддерживаются асмитой. Главная её характеристика — &quot;самоприсвоение&quot;. Оно делает чистую Самость деятелем, &quot;Ahaṅkāra&quot; значит &quot;Я-делатель&quot;. Она связывает чистое Я, которое есть лишь свидетель, с конкретным действием или концепцией. Например, &quot;Я делаю свою работу&quot;, &quot;Я строю здания&quot;, &quot;Я люблю тебя&quot;, &quot;Я беден&quot; и т.д. Давайте уберем аханкару и предложения станут выглядеть так: &quot;Работа делается&quot;, &quot;Здания строятся&quot;, &quot;Есть любовь для тебя&quot;, &quot;Существует бедность&quot; и т.д. Аханкара также придаёт всему объем. Это причина этой трёхмерной Вселенной. Эго преимущественно раджасично.</td></tr><tr data-v-750bdc14><td class="num" data-v-750bdc14>16.</td><td data-v-750bdc14><p class="sans" data-v-750bdc14>मनस्</p><p class="trans" data-v-750bdc14>Manas</p></td><td data-v-750bdc14><span class="title" data-v-750bdc14>Манас</span> — третья ступень эволюции Природы. По простому, манас — это ум. Ум формирует сеть мыслей. Ум — источник и управитель Джянендрий (Сил восприятия) и Кармендрий (Сил действия). Одна из его функций — выпрямлять и раскрашивать примитивную картинку, формируемую сетчаткой глаза. Ум работает в двухмерном, а не трёхмерном пространстве. Задать третье измерение — это работа аханкары. Ум преимущественно тамасичен.</td></tr><tr data-v-750bdc14><td class="num" data-v-750bdc14>17.</td><td data-v-750bdc14><p class="sans" data-v-750bdc14>श्रोत्र</p><p class="trans" data-v-750bdc14>Śrotra</p> (श्रवण Śravaṇa)</td><td data-v-750bdc14>Сила слуха, обычно циркулирующая в ушах.</td><td rowspan="5" data-v-750bdc14><p class="sans" data-v-750bdc14>ज्ञानेन्द्रिय</p><p class="trans" data-v-750bdc14>JÑĀNENDRIYA</p> Силы восприятия (преимущественно саттвичные)</td></tr><tr data-v-750bdc14><td class="num" data-v-750bdc14>18.</td><td data-v-750bdc14><p class="sans" data-v-750bdc14>त्वक्</p><p class="trans" data-v-750bdc14>Tvak</p></td><td data-v-750bdc14>Сила осязания, обычно циркулирующая в коже.</td></tr><tr data-v-750bdc14><td class="num" data-v-750bdc14>19.</td><td data-v-750bdc14><p class="sans" data-v-750bdc14>चक्षुस्</p><p class="trans" data-v-750bdc14>Cakṣus</p></td><td data-v-750bdc14>Сила зрения, обычно циркулирующая в глазах.</td></tr><tr data-v-750bdc14><td class="num" data-v-750bdc14>20.</td><td data-v-750bdc14><p class="sans" data-v-750bdc14>जिह्वा</p><p class="trans" data-v-750bdc14>Jihvā</p> (रसना Rasanā)</td><td data-v-750bdc14>Сила вкуса, обычно циркулирующая в языке.</td></tr><tr data-v-750bdc14><td class="num" data-v-750bdc14>21.</td><td data-v-750bdc14><p class="sans" data-v-750bdc14>घ्राण</p><p class="trans" data-v-750bdc14>Ghrāṇa</p></td><td data-v-750bdc14>Сила обоняния, обычно циркулирующая в носу.</td></tr><tr data-v-750bdc14><td class="num" data-v-750bdc14>22.</td><td data-v-750bdc14><p class="sans" data-v-750bdc14>वाक्</p><p class="trans" data-v-750bdc14>Vāk</p></td><td data-v-750bdc14>Сила речи, обычно циркулирующая во рту.</td><td rowspan="5" data-v-750bdc14><p class="sans" data-v-750bdc14>कर्मेन्द्रिय</p><span class="trans" data-v-750bdc14>KARMENDRIYA</span> Силы действия (преимущественно раджасичные) </td></tr><tr data-v-750bdc14><td class="num" data-v-750bdc14>23.</td><td data-v-750bdc14><p class="sans" data-v-750bdc14>पाणि</p><p class="trans" data-v-750bdc14>Pāṇi</p></td><td data-v-750bdc14>Сила манипуляции, обычно циркулирующая в руках.</td></tr><tr data-v-750bdc14><td class="num" data-v-750bdc14>24.</td><td data-v-750bdc14><p class="sans" data-v-750bdc14>पाद</p><p class="trans" data-v-750bdc14>Pāda</p></td><td data-v-750bdc14>Сила передвижения, обычно циркулирующая в ногах и стопах.</td></tr><tr data-v-750bdc14><td class="num" data-v-750bdc14>25.</td><td data-v-750bdc14><p class="sans" data-v-750bdc14>उपस्थ</p><p class="trans" data-v-750bdc14>Upastha</p></td><td data-v-750bdc14>Сила сексуальной активности и удовлетворения, обычно циркулирующая в гениталиях.</td></tr><tr data-v-750bdc14><td class="num" data-v-750bdc14>26.</td><td data-v-750bdc14><p class="sans" data-v-750bdc14>पायु</p><p class="trans" data-v-750bdc14>Pāyu</p></td><td data-v-750bdc14>Сила выделения, обычно циркулирующая в анусе.</td></tr><tr data-v-750bdc14><td class="num" data-v-750bdc14>27.</td><td data-v-750bdc14><p class="sans" data-v-750bdc14>शब्द</p><p class="trans" data-v-750bdc14>Śabda</p></td><td data-v-750bdc14>Звук как таковой. Звук тишины.</td><td rowspan="5" data-v-750bdc14><p class="sans" data-v-750bdc14>तन्मात्र</p><span class="trans" data-v-750bdc14>TANMĀTRA</span> Тонкие элементы (преимущественно тамасичные) </td></tr><tr data-v-750bdc14><td class="num" data-v-750bdc14>28.</td><td data-v-750bdc14><p class="sans" data-v-750bdc14>स्पर्श</p><p class="trans" data-v-750bdc14>Sparśa</p></td><td data-v-750bdc14>Соприкосновение как таковое. Прикосновение воздуха.</td></tr><tr data-v-750bdc14><td class="num" data-v-750bdc14>29.</td><td data-v-750bdc14><p class="sans" data-v-750bdc14>रूप</p><p class="trans" data-v-750bdc14>Rūpa</p></td><td data-v-750bdc14>Цвет как таковой. Цвет ясного неба.</td></tr><tr data-v-750bdc14><td class="num" data-v-750bdc14>30.</td><td data-v-750bdc14><p class="sans" data-v-750bdc14>रस</p><p class="trans" data-v-750bdc14>Rasa</p></td><td data-v-750bdc14>Вкус как таковой. Вкус воды.</td></tr><tr data-v-750bdc14><td class="num" data-v-750bdc14>31.</td><td data-v-750bdc14><p class="sans" data-v-750bdc14>गन्ध</p><p class="trans" data-v-750bdc14>Gandha</p></td><td data-v-750bdc14>Запах как таковой. Запах чистоты.</td></tr><tr data-v-750bdc14><td class="num" data-v-750bdc14>32.</td><td data-v-750bdc14><p class="sans" data-v-750bdc14>आकाश</p><p class="trans" data-v-750bdc14>Ākāśa</p></td><td data-v-750bdc14>Трёхмерный каркас, в котором существует весь физический мир — Пространство.</td><td rowspan="5" data-v-750bdc14><p class="sans" data-v-750bdc14>महाभूत</p><span class="trans" data-v-750bdc14>MAHĀBHŪTA</span> Грубые элементы (преимущественно тамасичные) </td></tr><tr data-v-750bdc14><td class="num" data-v-750bdc14>33.</td><td data-v-750bdc14><p class="sans" data-v-750bdc14>वायु</p><p class="trans" data-v-750bdc14>Vāyu</p></td><td data-v-750bdc14>Всё газообразное — Воздух.</td></tr><tr data-v-750bdc14><td class="num" data-v-750bdc14>34.</td><td data-v-750bdc14><p class="sans" data-v-750bdc14>अग्नि</p><p class="trans" data-v-750bdc14>Agni</p> (तेजस् Tejas)</td><td data-v-750bdc14>Всё горячее, светящееся и обладающее цветом — Огонь.</td></tr><tr data-v-750bdc14><td class="num" data-v-750bdc14>35.</td><td data-v-750bdc14><p class="sans" data-v-750bdc14>आपस्</p><p class="trans" data-v-750bdc14>Āpas</p></td><td data-v-750bdc14>Всё жидкое — Вода.</td></tr><tr data-v-750bdc14><td class="num" data-v-750bdc14>36.</td><td data-v-750bdc14><p class="sans" data-v-750bdc14>पृथिवी</p><p class="trans" data-v-750bdc14>Pṛthivī</p></td><td data-v-750bdc14>Всё твердое — Земля.</td></tr></tbody></table>',1),b=[v];function p(n,l,r,u,o,q){return t(),d("div",null,b)}const m=a(c,[["render",p],["__scopeId","data-v-750bdc14"]]);export{i as __pageData,m as default};
