import{_ as t,c as f,d,o as s}from"./chunks/framework.B7ZEGzqH.js";const u=JSON.parse('{"title":"Таблица 36 таттв","description":"","frontmatter":{"title":"Таблица 36 таттв","tags":"scheme"},"headers":[],"relativePath":"schemes/tattvas/index.md","filePath":"schemes/tattvas/index.md"}'),e={name:"schemes/tattvas/index.md"};function v(b,a,p,n,l,c){return s(),f("div",null,a[0]||(a[0]=[d('<table id="tattvas" class="names" data-v-2b96fe8f><tbody data-v-2b96fe8f><tr data-v-2b96fe8f><td colspan="4" data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>परमशिव</p><p class="trans" data-v-2b96fe8f>PARAMAŚIVA</p><p class="title" data-v-2b96fe8f>Высший Шива или Источник Всего</p></td></tr><tr data-v-2b96fe8f><td class="num" style="width:80px;" data-v-2b96fe8f>1.</td><td style="width:80px;" data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>शिव</p><p class="trans" data-v-2b96fe8f>Śiva</p></td><td style="width:80%;" colspan="2" data-v-2b96fe8f><span class="title" data-v-2b96fe8f>Шива</span> выше описаний, однако можно сказать, что он есть истинное &quot;Я&quot;. Шива полон Cicchakti (Cit + śakti) или Силы Сознания. И Шива и Шакти возникают из Парамашивы или Высшего Шивы. В Парамашиве нет даже тонкой двойственности Шива—Шакти. Все таттвы возникают из этой Высшей Реальности.</td></tr><tr data-v-2b96fe8f><td class="num" data-v-2b96fe8f>2.</td><td data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>शक्ति</p><p class="trans" data-v-2b96fe8f>Śakti</p></td><td colspan="2" data-v-2b96fe8f><span class="title" data-v-2b96fe8f>Шакти</span> также вне описаний. И всё же, можно сказать, что она — истинное Бытие. Шива и Шакти являют истинное &quot;Я есть&quot;. Шакти делает Шиву сознающим Себя, но при этом нельзя найти никакого различия между ними. Шакти — очень тонкое &quot;напряжение&quot; на бесконечном и неподвижном теле Шивы. Из неё все остальные таттвы возникают, по сути, они — её форма. Между ней и всеми остальными таттвами присутствует совершенное единство. Она полна Ānandaśakti или Силы Блаженства.</td></tr><tr data-v-2b96fe8f><td colspan="4" data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>अनाश्रितशिव</p><p class="trans" data-v-2b96fe8f>ANĀŚRITAŚIVA</p><p class="translation" data-v-2b96fe8f>Шива без категорий</p></td></tr><tr data-v-2b96fe8f><td class="num" data-v-2b96fe8f>3.</td><td data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>सदाशिव</p><p class="trans" data-v-2b96fe8f>Sadāśiva</p> (सादाख्य Sādākhya)</td><td colspan="2" data-v-2b96fe8f><span class="title" data-v-2b96fe8f>Садашива</span> — обиталище Icchāśakti или Силы Воли. Здесь появляется туманная Вселенная. Сознание принимает форму &quot;Я есть Это&quot; (&quot;Я — эта туманная Вселенная&quot;). Вселенная только появляется и потому предстаёт размытой. Так как Вселенная ещё не сформировалась, &quot;Я&quot; преобладает.</td></tr><tr data-v-2b96fe8f><td class="num" data-v-2b96fe8f>4.</td><td data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>ईश्वर</p><p class="trans" data-v-2b96fe8f>Īśvara</p></td><td colspan="2" data-v-2b96fe8f><span class="title" data-v-2b96fe8f>Ишвара</span> — обиталище Jñānaśakti или Силы Знания. Здесь появляется ясная отчётливая Вселенная. Сознание принимает форму &quot;Это есть Я&quot; (&quot;Отчётливая Вселенная есть Я&quot;). Вселенная теперь полностью проявлена и потому отчётлива. Так как Вселенная полностью сформировалась, &quot;Это&quot; преобладает. &quot;Я&quot; тоже здесь, но оно потрясено божественным чудом раскрытия Себя перед Самим Собой.</td></tr><tr data-v-2b96fe8f><td class="num" data-v-2b96fe8f>5.</td><td data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>सद्विद्या</p><p class="trans" data-v-2b96fe8f>Sadvidyā</p> (<span lang="sa" data-v-2b96fe8f>शुद्धविद्या</span> Śuddhavidyā)</td><td colspan="2" data-v-2b96fe8f><span class="title" data-v-2b96fe8f>Садвидья</span> — обиталище Kriyāśakti или Силы Действия. Здесь сознание полностью сбалансировано между &quot;Я&quot; и &quot;Это&quot;. &quot;Это&quot; — Вселенная. Осознание таково: &quot;Я есть Я, Это есть Это&quot;. &quot;Я&quot; сбалансировано &quot;Этим&quot; и они каким-то образом преобладают оба. Это единство в различиях, ведь здесь есть вселенная, отличная от Шивы (&quot;Я&quot;), но в то же время присутствует лежащее в их основе единство.</td></tr><tr data-v-2b96fe8f><td colspan="4" data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>आणवमल</p><p class="trans" data-v-2b96fe8f>ĀṆAVAMALA</p> Анавамала или Первичное Загрязнение, предвосхищающее все остальные таттвы, в которых различия и двойственность преобладают. После Анавамалы возникает ещё две малы или загрязнения: Māyīyamala <span lang="sa" data-v-2b96fe8f>मायीयमल</span> и Kārmamala <span lang="sa" data-v-2b96fe8f>कार्ममल</span>, которые привносят различия и привязанности в деятельность соответственно.</td></tr><tr data-v-2b96fe8f><td class="num" data-v-2b96fe8f>6.</td><td data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>माया</p><p class="trans" data-v-2b96fe8f>Māyā</p></td><td colspan="2" data-v-2b96fe8f><span class="title" data-v-2b96fe8f>Майя</span> — это Неведение. Майя Трики не та же, что и в Веданте. В Трике Майя — это таттва, или &quot;реальный&quot; уровень Творения. Майя отвечает за сокрытие сущностной природы Шивы вуалью заблуждения. Майя превращает бесконечное Я в кого-то полного ограничений. Майия-мала и Карма-мала возникают в результате действия Майя-таттвы. Эти две нечистоты наполняют деятельность различиями и привязанностями. Иногда Майя рассматривается как &quot;шестая&quot; канчука или покров, окутывающий сознание тонкой, но прочной вуалью неведения.</td></tr><tr data-v-2b96fe8f><td class="num" data-v-2b96fe8f>7.</td><td data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>कला</p><p class="trans" data-v-2b96fe8f>Kalā</p></td><td width="80px" data-v-2b96fe8f><span class="title" data-v-2b96fe8f>КалА</span> формирует представление об &quot;ограниченной активности&quot; покрытого вуалью Майи сознания. Это Крия-шакти (Всемогущество), претерпевшая сжатие. Она вносит ошибочные суждения: &quot;Я ограничен в действиях&quot;, &quot;Я не могу сделать это&quot;, &quot;Я не могу сделать то&quot; и т.д.</td><td style="width:10%;" rowspan="5" data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>कञ्चुक</p><span class="trans" data-v-2b96fe8f>KAÑCUKA</span> Канчуки (покровы) </td></tr><tr data-v-2b96fe8f><td class="num" data-v-2b96fe8f>8.</td><td data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>विद्या</p><p class="trans" data-v-2b96fe8f>Vidyā</p></td><td data-v-2b96fe8f><span class="title" data-v-2b96fe8f>Видья</span> формирует представление об &quot;ограниченном знании&quot; покрытого вуалью Майи сознания. Это Джняна-шакти (Всезнание), претерпевшая сжатие. Она вносит ошибочные суждения: &quot;Я ограничен в познании&quot;, &quot;Я не знаю этого&quot;, &quot;Я не знаю того&quot;, и т.д.</td></tr><tr data-v-2b96fe8f><td class="num" data-v-2b96fe8f>9.</td><td data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>राग</p><p class="trans" data-v-2b96fe8f>Rāga</p></td><td data-v-2b96fe8f><span class="title" data-v-2b96fe8f>Рага</span> формирует представление об &quot;ограниченной воле&quot;, ведущее покрытое вуалью Майи сознание к привязанности. Это Иччха-шакти (Совершенная Воля) претерпевшая сжатие. Она привносит ошибочные суждения: &quot;Мне чего-то не хватает&quot;, &quot;Я в чем-то нуждаюсь&quot; и, закономерно, возникает и результат такой нужды в форме &quot;Я привязан к чему-то&quot;.</td></tr><tr data-v-2b96fe8f><td class="num" data-v-2b96fe8f>10.</td><td data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>काल</p><p class="trans" data-v-2b96fe8f>Kāla</p></td><td data-v-2b96fe8f><span class="title" data-v-2b96fe8f>КАла</span> формирует представление о &quot;частях&quot;, ведущее сознание, покрытое вуалью Майи к представлению о времени. Это Ананда-шакти (Высшее блаженство), претерпевшая сжатие. Она привносит ошибочные суждения вроде: &quot;Я не вечен, и не непреходящ&quot;, &quot;Я живу лишь несколько лет&quot;, &quot;Я стар&quot;, &quot;Я молод&quot; и т.д.</td></tr><tr data-v-2b96fe8f><td class="num" data-v-2b96fe8f>11.</td><td data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>नियति</p><p class="trans" data-v-2b96fe8f>Niyati</p></td><td data-v-2b96fe8f><span class="title" data-v-2b96fe8f>Нияти</span> формирует представление о связи причины и следствия, ведущее к представлению об обусловленном судьбой порядке вещей. Это Cicchakti (Сила знания) претерпевшая сжатие. Она вносит ошибочные суждения: &quot;Если Я сделаю это, я получу соотвествующий результат&quot;, &quot;Я достоин&quot;, &quot;Я грешен&quot; и т.д.</td></tr><tr data-v-2b96fe8f><td class="num" data-v-2b96fe8f>12.</td><td data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>पुरुष</p><p class="trans" data-v-2b96fe8f>Puruṣa</p></td><td colspan="2" data-v-2b96fe8f><span class="title" data-v-2b96fe8f>Пуруша</span> — это Сам Шива, подвергнувшийся всем перечисленным ограничениям (Kañcuka). Несмотря на добровольное самоограничение, Шива при этом остаётся неизменным. Этот Пуруша — это внутреннее &quot;Я&quot; всех существ. В то время как оно является конечной целью философии Санкхья, Трика считет это только этапом инволюции/эволюции Шакти.</td></tr><tr data-v-2b96fe8f><td class="num" data-v-2b96fe8f>13.</td><td data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>प्रकृति</p><p class="trans" data-v-2b96fe8f>Prakṛti</p></td><td colspan="2" data-v-2b96fe8f><span class="title" data-v-2b96fe8f>Пракрити</span> — это просто состояние, в котором все три качества (Guṇa) остаются полностью сбалансированными. Три гуны — это Sattva, Rajas и Tamas. Они известны также как Джняна-шакти, Иччха-шакти и Крия-шакти соответственно, претерпевшие еще одно сжатие. Когда Высший свидетель (Шива) покрыт вуалью Майи и её канчуками, Он &quot;будто бы&quot; превращается в Пурушу. Этот Пуруша созерцает Шакти и она предстаёт теперь сформированной из трёх сил, известных как гуны или качества. Ни Шива, ни Шакти в действительности не изменяются, но Шива, через добровольное самоограничение, меняет Свою точку зрения и начинает считать Себя ограниченой душой.</td></tr><tr data-v-2b96fe8f><td class="num" data-v-2b96fe8f>14.</td><td data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>बुद्धि</p><p class="trans" data-v-2b96fe8f>Buddhi</p> (महत् Mahat)</td><td data-v-2b96fe8f><span class="title" data-v-2b96fe8f>Буддхи</span> — первая ступень эволюции Природы. Он формирует определяющую способность, с помощью которой вы выбираете направление действий в вашей жизни. По простому, Буддхи — это интеллект. Это преимущественно саттвичная таттва, что означает, что Джняна (знание) преобладает в нём. Буддхи — также тот принцип, который позволяет вам абстрактно каталогизировать конкретные объекты, животных или личностей по чётким категориям. Например, &quot;Это — собака&quot;, &quot;Это — шляпа&quot;.</td><td rowspan="3" data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>अन्तःकरण</p><p class="trans" data-v-2b96fe8f>ANTAḤKARAṆA</p> Внутренний (психический) орган</td></tr><tr data-v-2b96fe8f><td class="num" data-v-2b96fe8f>15.</td><td data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>अहङ्कार</p><p class="trans" data-v-2b96fe8f>Ahaṅkāra</p> (अस्मिता Asmitā)</td><td data-v-2b96fe8f><span class="title" data-v-2b96fe8f>Аханкара</span> — вторая ступень эволюции Природы. Она формирует осознание ограниченного &quot;Я&quot;. По простому, Аханкара — это эго. Манас, индрии и танматры не только возникают из него, но все они постоянно поддерживаются асмитой. Главная её характеристика — &quot;самоприсвоение&quot;. Оно делает чистую Самость деятелем, &quot;Ahaṅkāra&quot; значит &quot;Я-делатель&quot;. Она связывает чистое Я, которое есть лишь свидетель, с конкретным действием или концепцией. Например, &quot;Я делаю свою работу&quot;, &quot;Я строю здания&quot;, &quot;Я люблю тебя&quot;, &quot;Я беден&quot; и т.д. Давайте уберем аханкару и предложения станут выглядеть так: &quot;Работа делается&quot;, &quot;Здания строятся&quot;, &quot;Есть любовь для тебя&quot;, &quot;Существует бедность&quot; и т.д. Аханкара также придаёт всему объем. Это причина этой трёхмерной Вселенной. Эго преимущественно раджасично.</td></tr><tr data-v-2b96fe8f><td class="num" data-v-2b96fe8f>16.</td><td data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>मनस्</p><p class="trans" data-v-2b96fe8f>Manas</p></td><td data-v-2b96fe8f><span class="title" data-v-2b96fe8f>Манас</span> — третья ступень эволюции Природы. По простому, манас — это ум. Ум формирует сеть мыслей. Ум — источник и управитель Джянендрий (Сил восприятия) и Кармендрий (Сил действия). Одна из его функций — выпрямлять и раскрашивать примитивную картинку, формируемую сетчаткой глаза. Ум работает в двухмерном, а не трёхмерном пространстве. Задать третье измерение — это работа аханкары. Ум преимущественно тамасичен.</td></tr><tr data-v-2b96fe8f><td class="num" data-v-2b96fe8f>17.</td><td data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>श्रोत्र</p><p class="trans" data-v-2b96fe8f>Śrotra</p> (श्रवण Śravaṇa)</td><td data-v-2b96fe8f>Сила слуха, обычно циркулирующая в ушах.</td><td rowspan="5" data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>ज्ञानेन्द्रिय</p><p class="trans" data-v-2b96fe8f>JÑĀNENDRIYA</p> Силы восприятия (преимущественно саттвичные)</td></tr><tr data-v-2b96fe8f><td class="num" data-v-2b96fe8f>18.</td><td data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>त्वक्</p><p class="trans" data-v-2b96fe8f>Tvak</p></td><td data-v-2b96fe8f>Сила осязания, обычно циркулирующая в коже.</td></tr><tr data-v-2b96fe8f><td class="num" data-v-2b96fe8f>19.</td><td data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>चक्षुस्</p><p class="trans" data-v-2b96fe8f>Cakṣus</p></td><td data-v-2b96fe8f>Сила зрения, обычно циркулирующая в глазах.</td></tr><tr data-v-2b96fe8f><td class="num" data-v-2b96fe8f>20.</td><td data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>जिह्वा</p><p class="trans" data-v-2b96fe8f>Jihvā</p> (रसना Rasanā)</td><td data-v-2b96fe8f>Сила вкуса, обычно циркулирующая в языке.</td></tr><tr data-v-2b96fe8f><td class="num" data-v-2b96fe8f>21.</td><td data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>घ्राण</p><p class="trans" data-v-2b96fe8f>Ghrāṇa</p></td><td data-v-2b96fe8f>Сила обоняния, обычно циркулирующая в носу.</td></tr><tr data-v-2b96fe8f><td class="num" data-v-2b96fe8f>22.</td><td data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>वाक्</p><p class="trans" data-v-2b96fe8f>Vāk</p></td><td data-v-2b96fe8f>Сила речи, обычно циркулирующая во рту.</td><td rowspan="5" data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>कर्मेन्द्रिय</p><span class="trans" data-v-2b96fe8f>KARMENDRIYA</span> Силы действия (преимущественно раджасичные) </td></tr><tr data-v-2b96fe8f><td class="num" data-v-2b96fe8f>23.</td><td data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>पाणि</p><p class="trans" data-v-2b96fe8f>Pāṇi</p></td><td data-v-2b96fe8f>Сила манипуляции, обычно циркулирующая в руках.</td></tr><tr data-v-2b96fe8f><td class="num" data-v-2b96fe8f>24.</td><td data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>पाद</p><p class="trans" data-v-2b96fe8f>Pāda</p></td><td data-v-2b96fe8f>Сила передвижения, обычно циркулирующая в ногах и стопах.</td></tr><tr data-v-2b96fe8f><td class="num" data-v-2b96fe8f>25.</td><td data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>उपस्थ</p><p class="trans" data-v-2b96fe8f>Upastha</p></td><td data-v-2b96fe8f>Сила сексуальной активности и удовлетворения, обычно циркулирующая в гениталиях.</td></tr><tr data-v-2b96fe8f><td class="num" data-v-2b96fe8f>26.</td><td data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>पायु</p><p class="trans" data-v-2b96fe8f>Pāyu</p></td><td data-v-2b96fe8f>Сила выделения, обычно циркулирующая в анусе.</td></tr><tr data-v-2b96fe8f><td class="num" data-v-2b96fe8f>27.</td><td data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>शब्द</p><p class="trans" data-v-2b96fe8f>Śabda</p></td><td data-v-2b96fe8f>Звук как таковой. Звук тишины.</td><td rowspan="5" data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>तन्मात्र</p><span class="trans" data-v-2b96fe8f>TANMĀTRA</span> Тонкие элементы (преимущественно тамасичные) </td></tr><tr data-v-2b96fe8f><td class="num" data-v-2b96fe8f>28.</td><td data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>स्पर्श</p><p class="trans" data-v-2b96fe8f>Sparśa</p></td><td data-v-2b96fe8f>Соприкосновение как таковое. Прикосновение воздуха.</td></tr><tr data-v-2b96fe8f><td class="num" data-v-2b96fe8f>29.</td><td data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>रूप</p><p class="trans" data-v-2b96fe8f>Rūpa</p></td><td data-v-2b96fe8f>Цвет как таковой. Цвет ясного неба.</td></tr><tr data-v-2b96fe8f><td class="num" data-v-2b96fe8f>30.</td><td data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>रस</p><p class="trans" data-v-2b96fe8f>Rasa</p></td><td data-v-2b96fe8f>Вкус как таковой. Вкус воды.</td></tr><tr data-v-2b96fe8f><td class="num" data-v-2b96fe8f>31.</td><td data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>गन्ध</p><p class="trans" data-v-2b96fe8f>Gandha</p></td><td data-v-2b96fe8f>Запах как таковой. Запах чистоты.</td></tr><tr data-v-2b96fe8f><td class="num" data-v-2b96fe8f>32.</td><td data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>आकाश</p><p class="trans" data-v-2b96fe8f>Ākāśa</p></td><td data-v-2b96fe8f>Трёхмерный каркас, в котором существует весь физический мир — Пространство.</td><td rowspan="5" data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>महाभूत</p><span class="trans" data-v-2b96fe8f>MAHĀBHŪTA</span> Грубые элементы (преимущественно тамасичные) </td></tr><tr data-v-2b96fe8f><td class="num" data-v-2b96fe8f>33.</td><td data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>वायु</p><p class="trans" data-v-2b96fe8f>Vāyu</p></td><td data-v-2b96fe8f>Всё газообразное — Воздух.</td></tr><tr data-v-2b96fe8f><td class="num" data-v-2b96fe8f>34.</td><td data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>अग्नि</p><p class="trans" data-v-2b96fe8f>Agni</p> (तेजस् Tejas)</td><td data-v-2b96fe8f>Всё горячее, светящееся и обладающее цветом — Огонь.</td></tr><tr data-v-2b96fe8f><td class="num" data-v-2b96fe8f>35.</td><td data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>आपस्</p><p class="trans" data-v-2b96fe8f>Āpas</p></td><td data-v-2b96fe8f>Всё жидкое — Вода.</td></tr><tr data-v-2b96fe8f><td class="num" data-v-2b96fe8f>36.</td><td data-v-2b96fe8f><p class="sans" data-v-2b96fe8f>पृथिवी</p><p class="trans" data-v-2b96fe8f>Pṛthivī</p></td><td data-v-2b96fe8f>Всё твердое — Земля.</td></tr></tbody></table>',1)]))}const o=t(e,[["render",v],["__scopeId","data-v-2b96fe8f"]]);export{u as __pageData,o as default};
