import{_ as s}from"./chunks/books.10d3b658.js";import{_ as i,o as r,c as o,a,d as n,b as p,e as d}from"./chunks/framework.bd8d98b6.js";const c="/assets/Jagad_Guru_Swamy_Satyananda_Saraswathy.a1562804.jpg",_="/assets/sarasvati.73e879a3.jpg",l="/assets/svami_sarasvati.dfc4db72.jpg",h="/assets/Swami-Satyananda.64c34cca.jpg",m="/assets/Swami-Satyananda-Saraswati.5f02a31e.jpg",w=JSON.parse('{"title":"Свами Сатьянанда Сарасвати","description":"24 декабря 1923, Алмора — 5 декабря 2009, Деогхар, Сантал-Паргана, Джаркханд, Индия","frontmatter":{"title":"Свами Сатьянанда Сарасвати","description":"24 декабря 1923, Алмора — 5 декабря 2009, Деогхар, Сантал-Паргана, Джаркханд, Индия","sans":"स्वामी सत्यानन्द सरस्वती","trans":"svāmī satyānanda sarasvatī","tags":"advaita-teachers","cover":"/img/teachers/Swami-Satyananda-H7,5cm_Q1.jpg","date":"2022-06-30T00:00:00.000Z","books":[{"title":"Древние тантрические техники йоги и крийи","description":"Том 1. Вводный курс","author":"Свами Сатьянанда Сарасвати","book":"/books/drevnie_tantricheskie_vvodniy.pdf","ISBN":"978-5-89533-283-2","year":2013},{"title":"Древние тантрические техники йоги и крийи","description":"Том 2. Продвинутый курс","author":"Свами Сатьянанда Сарасвати","book":"/books/drevnie_tantricheskie_prodvinutiy.pdf","ISBN":"978-5-89533-288-7","year":2013},{"title":"Древние тантрические техники йоги и крийи","description":"Том 3. Мастер-курс","author":"Свами Сатьянанда Сарасвати","book":"/books/drevnie_tantricheskie_master.pdf","ISBN":"978-5-89533-289-4","year":2013},{"title":"Йога нидра","description":"Бихарская школа йоги, Мунгер","author":"Свами Сатьянанда Сарасвати","book":"/books/yoga_nidra.pdf","ISBN":"5-89395-145-X","year":2001}]},"headers":[],"relativePath":"teachers/advaita/sarasvati/index.md","filePath":"teachers/advaita/sarasvati/index.md"}'),v={name:"teachers/advaita/sarasvati/index.md"},k=p('<div class="stanza"><p><a href="https://ru.wikipedia.org/wiki/%D0%A1%D0%B0%D1%82%D1%8C%D1%8F%D0%BD%D0%B0%D0%BD%D0%B4%D0%B0_%D0%A1%D0%B0%D1%80%D0%B0%D1%81%D0%B2%D0%B0%D1%82%D0%B8" class="external-link" target="_blank" rel="noreferrer">Wikipedia</a></p><p><img src="'+c+'" alt=""></p><p>Свами Сатьяна́нда Сара́свати — йогин, мастер йоги и тантры, широко признанный как в Индии так и на Западе, один из учеников Свами Шивананды. Автор более 80 книг. Свами Сатьянанда основал несколько организаций, способствующих развитию и изучению йоги:</p><ul><li>Международное движение йоги в 1963 г.</li><li>Бихарская школа йоги в 1964 г.</li><li>Фонд исследований йоги в 1984 г.</li></ul></div><div class="stanza"><h2 id="биография" tabindex="-1">Биография <a class="header-anchor" href="#биография" aria-label="Permalink to &quot;Биография&quot;">​</a></h2><p>Свами Сатьянанда Сарасвати родился в 1923 году, в Индии, в маленьком городке близ Алморы, у подножья Гималаев. Описывается, что его благословляли многие святые и мудрецы, проходившие мимо его дома по пути к более высокогорным районам Гималаев. В детстве он был необычайно способный ребёнок и пережил свой первый духовный опыт в возрасте шести лет. После этого он стал искать причины подобных переживаний. Так он встретил своего первого духовного наставника — это была женщина тантрик, бхайрави Сукхман Гири, она дала ему базовые знания по тантре, шактипат, велела ему отправляться на поиски гуру. Так в 19 лет он покинул свой дом и больше никогда не возвращался туда.</p><p>Вскоре он прибыл в Ришикеш, где встретил Свами Шивананду, в котором он признал своего гуру. Следующие двенадцать лет он провёл в ашраме Свами Шивананды, где погрузился в карма-йогу до такой степени, что Свами Шивананда говорил, что он работает за четверых. Свами Сатьянанда трудился с рассвета до поздней ночи, занимаясь всем, от уборки до управления ашрамом. В 1947 году он принял посвящение в орден дашанами санньясы от своего учителя. После этого Свами Сатьянанда покинул Ришикеш и в течение восьми лет вёл жизнь странствующего аскета, путешествуя по разным уголкам Индии, Афганистана, Бирмы, Непала и Цейлона. В этот период он встречался со многими великими святыми и йогами, проводил время в уединении, формулируя и совершенствуя техники йоги.</p><p><img src="'+_+'" alt=""></p><p>В 1963 году ему открылась его миссия, и он основал Международное содружество йоги (Interantional Yoga Fellowship). Он поселился в Мунгере, где на берегу Ганги была основана Бихарская школа йоги, чтобы помогать людям на духовном пути. Вскоре ученики уже прибывали из всех уголков Индии и из-за рубежа. Учение Свами Сатьянанды распространялось по всему миру. Начиная с 1968 г. он активно ездил по всему миру, обучая древним практикам йоги людей всех национальностей, социальных групп, убеждений и религиозных верований. С тех пор Свами Сатьянанда приобрёл широкую известность на всех континентах как авторитетный представитель традиций йоги и тантры. Он направлял тысячи духовных искателей и вдохновлял многие ашрамы и духовные центры по всему миру. В течение последующих двадцати лет он делил своё время между поездками по Индии, зарубежным странам и пребыванием в ашраме в Мунгере.</p><p>В 1983 году Свами Сатьянанда передал все полномочия по управлению ашрамом своему преемнику Свами Ниранджананде Сарасвати.</p><p>В 1984 Свами Сатьянанда основал Шивананда-матх (Sivananda Math), благотворительную организацию для помощи слабо развитым и угнетённым слоям общества. В это же время он основал Фонд исследований йоги (Yoga Research Foundation), который сотрудничает с институтами, докторами и учёными, для проведения научных исследований в области йоги и тантры. В 1988 году Свами Сатьянанда покинул Мунгер и больше никогда не возвращался туда снова. Он принял образ жизни кшетра-санньяси и путешествовал инкогнито по святым местам Индии, не принимая помощи от тех организаций, которые он сам основал.</p><p><img src="'+l+'" alt=""></p><h2 id="жизнь-в-рикхии" tabindex="-1">Жизнь в Рикхии <a class="header-anchor" href="#жизнь-в-рикхии" aria-label="Permalink to &quot;Жизнь в Рикхии&quot;">​</a></h2><p>В 1989 году в Трямбакишваре ему открылась его новая миссия и место её осуществления. 23 сентября 1989 года он прибыл в небольшую деревню Рикхия, рядом с городом Деогарх (штат Джаркханд), где основал Шри Панч Дашнам Парамахамса Алак Бара. Следующие 12 лет он провел в уединении, занимаясь сложными духовными практиками.</p><p>Начиная с 1994 года Свами Сатьянанда даёт своим ученикам и последователям новые наставления по бхакти-йоге (йога любви и преданности). Основным принципом данной йоги является любовь к Богу в форме служения человечеству. Шивананда-матх начинает оказывать активную поддержку жителям окружающих деревень в виде образования, медицинской помощи и базовых вещей для выживания, так как этот район считается одним из самых бедных в Индии. В 2001 году Свами Сатьянанада организует крупное жертвоприношение раджасуя, на которую он приглашает всех своих учеников. С этого момента он начинает регулярно проводить различные яджны и сатсанги, но он по-прежнему остаётся большую часть времени в уединении, редко появляясь на публике. Ашрам в Рикхии начинает постепенно разрастаться.</p><p>Свои последние годы Свами Сатьянанда провёл в Рикхии, занимаясь благоустройством и поддержкой жителей окружающих деревень, иногда давая наставления духовным искателям, которые приезжали к нему со всего света. Свами Сатьянанда умер в полночь, в ночь с пятого на шестое декабря 2009 года. Он назначил двух основных преемников своей традиции, это Свами Ниранджанананда Сарасвати и Свами Сатьясангананда Сарасвати (Свами Сатсанги), которые продолжают начатое им дело.</p><p><img src="'+h+'" alt=""></p><p><img src="'+m+'" alt=""></p><p>Свами Сатьянанда родился в Альморе (индийский штат Уттаранчал) в 1923 г.</p><p>Будучи с раннего возраста влекомым к духовной жизни, он оставил дом в восемнадцать лет и в 1943 году поступил на обучение к Свами Шивананде в Риши-кеше, который инициировал его в дашнами-санньясу в 1947. Он служил своему гуру в течение двенадцати лет, совершенствуя каждый аспект духовной жизни.</p><p>После этого он путешествовал по всему Индийскому субконтиненту в качестве странствующего аскета. Осознавая потребность времени в виде научной интерпретации древней системы йоги, он основал Международное Содружество Йоги в 1956 г. В течение следующих двадцати лет Свами Сатьянанда поднял флаг йоги в каждой части света, превратил Бихарскую Школу в передовую школу йоги и написал более восьмидесяти важных книг по йоге, тантре и духовной жизни.</p><p>«Сатьянанда-Йога» стала традицией, сочетающей в себе классические знания с современным мировоззрением. В 1984 он основал Институт Исследования Йоги с целью объединения научных исследований и йоги и Шивананда-Матх для помощи нуждающимся. В 1988 г., находясь на вершине своих достижений, он отрёкся от всего, принял кшетра-санньясу и стал жить как аскет-парамахамса.</p><p>B 1989 он получил откровение о том, чтобы жить в Рикхии и выполнять там высшие ведические ритуалы, живя в уединении. Получив в 1991 г. распоряжение заботиться о соседях, он позволил ашраму помогать бедствующим селениям округи. Начиная с 1995 г. он выполнял двенадцатилетнюю Раджасуя-яджну с установкой на мир, достаток и процветание для всех, а в 2007 объявил об основании Рикхипитха с его девизом «служить, любить, отдавать».</p><p>Свами Сатьянанда достиг махасамадхи, йогического свершения в виде добровольного отбрасывания собственного тела с целью слияния со вселенским сознанием, в 2009 в присутствии своих учеников.</p></div>',2),D={class:"stanza"},b=a("h2",{id:"книги-на-русском-языке",tabindex:"-1"},[d("Книги на русском языке "),a("a",{class:"header-anchor",href:"#книги-на-русском-языке","aria-label":'Permalink to "Книги на русском языке"'},"​")],-1);function f(t,u,g,S,B,y){const e=s;return r(),o("div",null,[k,a("div",D,[b,n(e,{books:t.$frontmatter.books},null,8,["books"])])])}const T=i(v,[["render",f]]);export{w as __pageData,T as default};
