import{_ as n,c as t,a,a0 as s,o as i}from"./chunks/framework.B7ZEGzqH.js";const e=JSON.parse('{"title":"Нираламба упанишада","description":"Наставление о безопорном","frontmatter":{"title":"Нираламба упанишада","description":"Наставление о безопорном","sans":"निरालम्बोपनिषत्","trans":"nirālambopaniṣat","tags":"text","date":"2021-01-01T00:00:00.000Z"},"headers":[],"relativePath":"texts/niralamba/index.md","filePath":"texts/niralamba/index.md"}'),b={name:"texts/niralamba/index.md"};function p(d,r,m,h,v,l){return i(),t("div",null,r[0]||(r[0]=[a("p",null,"Малая упанишада канона Муктика (она идёт 34-ой в списке упанишад); принадлежит к упанишадам Шукла-Яджур-веды и Саманья-упанишадам",-1),a("p",{sans:""},"॥ निरालम्बोपनिषत् ॥",-1),a("p",{trans:""},".. nirālambopaniṣat ॥",-1),a("div",{class:"stanza"},[a("p",{sans:""},[s("यत्रालम्बालम्बिभावो विद्यते न कदाचन ।"),a("br"),s(" ज्ञविज्ञसम्यग्ज्ञालम्बं निरालम्बं हरिं भजे ॥")]),a("p",{trans:""},[s("yatrālambālambibhāvo vidyate na kadācana ।"),a("br"),s(" jñavijñasamyagjñālambaṁ nirālambaṁ hariṁ bhaje ॥")])],-1),a("div",{class:"stanza"},[a("p",{sans:""},[s("ॐ पूर्णमदः पूर्णमिदं पूर्णात्पूर्णमुदच्यते ।"),a("br"),s(" पूर्णस्य पूर्णमादाय पूर्णमेवावशिष्यते ॥"),a("br"),s(" ॐ शान्तिः शान्तिः शान्तिः ॥")]),a("p",{trans:""},[s("oṁ pūrṇamadaḥ pūrṇamidaṁ pūrṇātpūrṇamudacyate ।"),a("br"),s(" pūrṇasya pūrṇamādāya pūrṇamevāvaśiṣyate ॥"),a("br"),s(" oṁ śāntiḥ śāntiḥ śāntiḥ ॥")]),a("p",null,"Ом. То — бесконечность, и это — бесконечность. Бесконечность исходит из бесконечности, Обретая бесконечность бесконечного, она остается лишь бесконечностью. Ом! Покой! Покой! Покой!")],-1),a("div",{class:"stanza"},[a("p",{sans:""},[s("ॐ नमः शिवाय गुरवे सच्चिदानन्द मूर्तये ।"),a("br"),s(" निष्प्रपञ्चाय शान्ताय निरालम्बाय तेजसे ॥")]),a("p",{trans:""},[s("oṁ namaḥ śivāya gurave saccidānanda mūrtaye ।"),a("br"),s(" niṣprapañcāya śāntāya nirālambāya tejase ॥")]),a("p",null,"«Ом. Поклон Благому (Шиве), [высшему] Гуру, образу Бытия, Сознания и Блаженства! Поклон Трансцендентному [Господу], [Который есть блаженный] Покой, Независимому (Нираламбе), Сияющему!»")],-1),a("div",{class:"stanza"},[a("p",{sans:""},[s("निरालम्बं समाश्रित्य सालम्बं विजहाति यः ।"),a("br"),s(" स संन्यासी च योगी च कैवल्यं पदमश्नुते ।"),a("br"),s(" एषमज्ञानजन्तूनां समस्तारिष्टशान्तये ।"),a("br"),s(" यद्यद्बोद्धव्यमखिलं तदाशङ्क्य ब्रवीम्यहम् ॥")]),a("p",{trans:""},[s("nirālambaṁ samāśritya sālambaṁ vijahāti yaḥ ।"),a("br"),s(" sa saṁnyāsī ca yogī ca kaivalyaṁ padamaśnute ।"),a("br"),s(" eṣamajñānajantūnāṁ samastāriṣṭaśāntaye ।"),a("br"),s(" yadyadboddhavyamakhilaṁ tadāśaṅkya bravīmyaham ॥")]),a("p",null,"[Брахма сказал:] «Тот, кто [всецело] предался свободе, победив зависимости, [есть истинный] санньясин и йогин, он достигает освобождения (кайвальи). Я без колебаний поведаю то, что [нужно для] пробуждения мира и для освобождения существ, [живущих в] неведении, от всевозможных страданий».")],-1),a("div",{class:"stanza"},[a("p",{sans:""},[s("किं ब्रह्म ।"),a("br"),s(" क ईश्वरः ।"),a("br"),s(" को जीवः ।"),a("br"),s(" का प्रकृतिः ।"),a("br"),s(" कः परमात्मा ।"),a("br"),s(" को ब्रह्मा ।"),a("br"),s(" को विष्णुः ।"),a("br"),s(" को रुद्रः ।"),a("br"),s(" क इन्द्रः ।"),a("br"),s(" कः शमनः ।"),a("br"),s(" कः सूर्यः ।"),a("br"),s(" कश्चन्द्रः ।"),a("br"),s(" के सुराः ।"),a("br"),s(" के असुराः ।"),a("br"),s(" के पिशाचाः ।"),a("br"),s(" के मनुष्याः ।"),a("br"),s(" काः स्त्रियः ।"),a("br"),s(" के पश्वादयः ।"),a("br"),s(" किं स्थावरम् ।"),a("br"),s(" के ब्राह्मणादयः ।"),a("br"),s(" का जातिः ।"),a("br"),s(" किं कर्म ।"),a("br"),s(" किमकर्म ।"),a("br"),s(" किं ज्ञानम् ।"),a("br"),s(" किमज्ञानम् ।"),a("br"),s(" किं सुखम् ।"),a("br"),s(" किं दुःखम् ।"),a("br"),s(" कः स्वर्गः ।"),a("br"),s(" को नरकः ।"),a("br"),s(" को बन्धः ।"),a("br"),s(" को मोक्शः ।"),a("br"),s(" क उपास्यः ।"),a("br"),s(" कः शिष्यः ।"),a("br"),s(" को विद्वान् ।"),a("br"),s(" को मूढः ।"),a("br"),s(" किमासुरम् ।"),a("br"),s(" किं तपः ।"),a("br"),s(" किं परमं पदम् ।"),a("br"),s(" किं ग्राह्यम् ।"),a("br"),s(" किमग्राह्यम् ।")]),a("p",{trans:""},[s("kiṁ brahma ।"),a("br"),s(" ka īśvaraḥ ।"),a("br"),s(" ko jīvaḥ ।"),a("br"),s(" kā prakṛtiḥ ।"),a("br"),s(" kaḥ paramātmā ।"),a("br"),s(" ko brahmā ।"),a("br"),s(" ko viṣṇuḥ ।"),a("br"),s(" ko rudraḥ ।"),a("br"),s(" ka indraḥ ।"),a("br"),s(" kaḥ śamanaḥ ।"),a("br"),s(" kaḥ sūryaḥ ।"),a("br"),s(" kaścandraḥ ।"),a("br"),s(" ke surāḥ ।"),a("br"),s(" ke asurāḥ ।"),a("br"),s(" ke piśācāḥ ।"),a("br"),s(" ke manuṣyāḥ ।"),a("br"),s(" kāḥ striyaḥ ।"),a("br"),s(" ke paśvādayaḥ ।"),a("br"),s(" kiṁ sthāvaram ।"),a("br"),s(" ke brāhmaṇādayaḥ ।"),a("br"),s(" kā jātiḥ ।"),a("br"),s(" kiṁ karma ।"),a("br"),s(" kimakarma ।"),a("br"),s(" kiṁ jñānam ।"),a("br"),s(" kimajñānam ।"),a("br"),s(" kiṁ sukham ।"),a("br"),s(" kiṁ duḥkham ।"),a("br"),s(" kaḥ svargaḥ ।"),a("br"),s(" ko narakaḥ ।"),a("br"),s(" ko bandhaḥ ।"),a("br"),s(" ko mokśaḥ ।"),a("br"),s(" ka upāsyaḥ ।"),a("br"),s(" kaḥ śiṣyaḥ ।"),a("br"),s(" ko vidvān ।"),a("br"),s(" ko mūḍhaḥ ।"),a("br"),s(" kimāsuram ।"),a("br"),s(" kiṁ tapaḥ ।"),a("br"),s(" kiṁ paramaṁ padam ।"),a("br"),s(" kiṁ grāhyam ।"),a("br"),s(" kimagrāhyam ।"),a("br"),s(" kaḥ saṁnyāsītyāhaśaṅkyāha brahmeti ।"),a("br")]),a("p",null,"[Брахму спросили:] «Кто есть Брахман? Кто есть Господь (Ишвара)? Кто есть живая душа (джива)? Кто есть пракриты? Кто есть Высшая Сущность (Параматман)? Кто есть Брахма? Кто есть Вишну? Кто есть Рудра? Кто есть Индра? Кто есть [бог] смерти (Яма)? Кто есть Сурья? Кто есть Чандра? Кто такие боги? Кто такие демоны асуры? Кто такие пишачи? Кто такие мужчины? Кто такие женщины? Кто такие животные и другие [живые существа]? Кто есть недвижимое (стхавара)? Кто есть брахманы и остальные [три касты]? Что есть каста (джати)? Что есть действие (карма)? Что есть отсутствие действия (акарма)? Что есть знание (джняна)? Что есть незнание? Что есть счастье? Что есть несчастье? Что есть рай (сварга)? Что есть ад (нарака)? Что есть связанность? Что есть освобождение (мокша)? Кого следует почитать? Кто есть ученик? Кто есть познавший? Кто есть глупец? Что демонично? Что есть подвижничество (;тапас)? Что есть высшее состояние? Что следует принимать? Что следует отвергать? Кто есть санньясин?»")],-1),a("div",{class:"stanza"},[a("a",{href:"#s1",id:"s1",class:"num"},"1"),a("p",{sans:""},[s("कः संन्यासीत्याहशङ्क्याह ब्रह्मेति ।"),a("br"),s(" स होवाच महदहङ्कारपृथिव्यप्तेजोवाय्वाकाशत्वेन बृहद्रूपेणाण्डकोशेन कर्मज्ञानार्थरूपतया भासमानमद्वितीयमखिलोपाधिविनिर्मुक्तं तत्सकलशक्त्युपबृंहितमनाद्यनन्तं शुद्धं शिवं शान्तं निर्गुणमित्यादिवाच्यमनिर्वाच्यं चैतन्यं ब्रह्म ॥")]),a("p",{trans:""},"sa hovāca mahadahaṅkārapṛthivyaptejovāyvākāśatvena bṛhadrūpeṇāṇḍakośena karmajñānārtharūpatayā bhāsamānamadvitīyamakhilopādhivinirmuktaṁ tatsakalaśaktyupabṛṁhitamanādyanantaṁ śuddhaṁ śivaṁ śāntaṁ nirguṇamityādivācyamanirvācyaṁ caitanyaṁ brahma ॥"),a("p",null,"Он сказал: «Брахман — это Сознание и Жизнь (чайтанья). Он неописуем и описывается как Бескачественный (ниргуна), Недвижимый, Благой (Шива), Пречистый, Безначальный и Бесконечный. Он — Полнота всех энергий (шакти), Свободный от каких-либо наименований, [Один] без второго. [Он] проявляется в образе [энергий] действия, знания и намерения, [Он проявляется] как бесконечное пространство и как этот ограниченный мир, как махат, аханкара, земля, вода, огонь, воздух и эфир.")],-1),a("div",{class:"stanza"},[a("a",{href:"#s2",id:"s2",class:"num"},"2"),a("p",{sans:""},[s("ईश्वर इति च ॥"),a("br"),s(" ब्रह्मैव स्वशक्तिं प्रकृत्यभिधेयामाश्रित्य लोकान्सृष्ट्वा प्रविश्यान्तर्यामित्वेन ब्रह्मादीनां बुद्धीन्द्रियनियन्तृत्वादीश्वरः ॥")]),a("p",{trans:""},[s("īśvara iti ca ॥"),a("br"),s(" brahmaiva svaśaktiṁ prakṛtyabhidheyāmāśritya lokānsṛṣṭvā praviśyāntaryāmitvena brahmādīnāṁ buddhīndriyaniyantṛtvādīśvaraḥ ॥")]),a("p",null,"Теперь о Владыке (Ишваре). Он един с Брахманом; благодаря своей Шакти, именуемой пракрити, Он творит миры и входит [в них] как внутренний Владыка. Поскольку [Он] контролирует разум и чувства [всех живых существ], начиная с Брахмы, [Он] именуется Владыкой.")],-1),a("div",{class:"stanza"},[a("a",{href:"#s3",id:"s3",class:"num"},"3"),a("p",{sans:""},[s("जीव इति च ब्रह्मविष्ण्वीशानेन्द्रादीनां नामरूपद्वारा स्थूलोऽहमिति मिथ्याध्यासवशाज्जीवः ।"),a("br"),s(" सोऽहमेकोऽपि देहारम्भकभेदवशाद्बहुजीवः ।")]),a("p",{trans:""},[s("jīva iti ca brahmaviṣṇvīśānendrādīnāṁ nāmarūpadvārā sthūlo'hamiti mithyādhyāsavaśājjīvaḥ ।"),a("br"),s(" so'hameko'pi dehārambhakabhedavaśādbahujīvaḥ ।"),a("br")]),a("p",null,"Теперь о живой душе (дживе). Посредством ложного отождествления джива считает: «Я материальна» — по причине имён и форм Брахмы, Вишну, Ишаны, Индры и других. «Он» (т. е. Бог) и «я» (т. е. индивидуальная душа) едины; однако, по причине различия тел, существует множество живых душ.")],-1),a("div",{class:"stanza"},[a("a",{href:"#s4",id:"s4",class:"num"},"4"),a("p",{sans:""},"प्रकृतिरिति च ब्रह्मणः सकाशान्नानाविचित्रजगन्निर्माणसामार्थ्यबुद्धिरूपा ब्रह्मशक्तिरेव प्रकृतिः ।"),a("p",{trans:""},[s("prakṛtiriti ca brahmaṇaḥ sakāśānnānāvicitrajagannirmāṇasāmārthyabuddhirūpā brahmaśaktireva prakṛtiḥ ।"),a("br")]),a("p",null,"Теперь о Природе (пракриты). Пракриты — сила Брахмана, образ разума (буддхи), [которая,] благодаря единению с Брахманом, творит [весь] разнообразный мир.")],-1),a("div",{class:"stanza"},[a("a",{href:"#s5",id:"s5",class:"num"},"5"),a("p",{sans:""},[s("परमात्मेति च देहादेः परतरत्वद्ब्राह्मैव परमात्मा स ब्रह्मा स विष्णुः स इन्द्रः स शमनः स सूर्यः स चन्द्रस्ते सुरास्ते असुरास्ते पिशाचास्ते मनुष्यास्ताः स्त्रियस्ते पश्वादयस्तत्स्थावरं ते ब्राह्मणादयः ।"),a("br"),s(" सर्वं खल्विदं ब्रह्म नेह नानास्ति किञ्चन ।")]),a("p",{trans:""},[s("paramātmeti ca dehādeḥ parataratvadbrāhmaiva paramātmā sa brahmā sa viṣṇuḥ sa indraḥ sa śamanaḥ sa sūryaḥ sa candraste surāste asurāste piśācāste manuṣyāstāḥ striyaste paśvādayastatsthāvaraṁ te brāhmaṇādayaḥ ।"),a("br"),s(" sarvaṁ khalvidaṁ brahma neha nānāsti kiñcana ।"),a("br")]),a("p",null,"Теперь о Высшей Сущности (Параматмане). [Параматман назван так] потому, что [он] трансцендентен телам; [он] же есть Брахман. Он (т. е. Брахман, который есть Высшая Сущность, Дух) есть Брахма, Он есть Вишну, Он есть Индра, Он есть [бог] смерти (Яма), Он есть Сурья, Он есть Чандра; Он — все боги и демоны, вампиры, мужчины и женщины, звери и другие [живые существа], недвижимое (стхавара; т. е. неживая материя), брахманы и остальные [три варны]. Всё это есть Брахман. Здесь нет никаких различий.")],-1),a("div",{class:"stanza"},[a("a",{href:"#s6",id:"s6",class:"num"},"6"),a("p",{sans:""},[s("जातिरिति च ।"),a("br"),s(" न चर्मणो न रक्तस्य न मांसस्य न चास्थिनः ।"),a("br"),s(" न जातिरात्मनो जातिर्व्यवहारप्रकल्पिता ।")]),a("p",{trans:""},[s("jātiriti ca ।"),a("br"),s(" na carmaṇo na raktasya na māṁsasya na cāsthinaḥ ।"),a("br"),s(" na jātirātmano jātirvyavahāraprakalpitā ।"),a("br")]),a("p",null,"Теперь о касте (джати). Нет [касты] у кожи, нет у крови, нет у плоти, нет у костей. Нет касты у Духа (Атмана). Воображаемое [понятие] касты [существует лишь] из-за [различных родов] занятий [человека].")],-1),a("div",{class:"stanza"},[a("a",{href:"#s7",id:"s7",class:"num"},"7"),a("p",{sans:""},"कर्मेति च क्रियमाणेन्द्रियैः कर्मण्यहं करोमीत्यध्यात्मनिष्ठतया कृतं कर्मैव कर्म ।"),a("p",{trans:""},[s("karmeti ca kriyamāṇendriyaiḥ karmaṇyahaṁ karomītyadhyātmaniṣṭhatayā kṛtaṁ karmaiva karma ।"),a("br")]),a("p",null,"Теперь о действии (карма). «Я делаю то, что совершается через органы [чувств и действия]»; те действия, которые совершаются [таким образом,] с утверждённостью в Сущности, [есть истинные] действия.")],-1),a("div",{class:"stanza"},[a("a",{href:"#s8",id:"s8",class:"num"},"8"),a("p",{sans:""},"अकर्मेति च कर्तृत्वभोक्तृत्वाद्यहङ्कारतया बन्धरूपं जन्मादिकारणं नित्यनैमित्तिकयागव्रततपोदानादिषु फलाभिसन्धानं यत्तदकर्म ।"),a("p",{trans:""},[s("akarmeti ca kartṛtvabhoktṛtvādyahaṅkāratayā bandharūpaṁ janmādikāraṇaṁ nityanaimittikayāgavratatapodānādiṣu phalābhisandhānaṁ yattadakarma ।"),a("br")]),a("p",null,"Теперь об отсутствии действия (акарма). То, что делается с эго (аханкара), с мыслью «я деятель, я наслаждающийся и т. д.», связывает [человека] и является причиной рождений. Также ежедневные (нитья) и совершаемые по случаю (наймиттика) [ритуалы, такие, как] жертвоприношение, обеты, аскезы и подаяние, делаемые с желанием награды, есть отсутствие [истинного] действия.")],-1),a("div",{class:"stanza"},[a("a",{href:"#s9",id:"s9",class:"num"},"9"),a("p",{sans:""},"ज्ञानमिति च देहेन्द्रियनिग्रहसद्गुरूपासनश्रवणमनननिदिध्यासनैर्यद्यदृग्दृश्यस्वरूपं सर्वान्तरस्थं सर्वसमं घटपटादिपदार्थमिवाविकारं विकारेषु चैतन्यं विना किञ्चिन्नास्तीति साक्शात्कारानुभवो ज्ञानम् ।"),a("p",{trans:""},[s("jñānamiti ca dehendriyanigrahasadgurūpāsanaśravaṇamanananididhyāsanairyadyadṛgdṛśyasvarūpaṁ sarvāntarasthaṁ sarvasamaṁ ghaṭapaṭādipadārthamivāvikāraṁ vikāreṣu caitanyaṁ vinā kiñcinnāstīti sākśātkārānubhavo jñānam ।"),a("br")]),a("p",null,"Теперь о знании (джняна). [Истинное] знание — это непосредственное переживание Сознания (чайтанья), помимо которого ничего нет; которое во всех видоизменениях [материи], во всех объектах остаётся неизменным. [Это Сознание есть] истинная форма (сварупа) субъекта и объекта, [оно] равно присутствует во всем [сущем]. [Подобное истинное знание достигается] посредством осознания тела и органов [чувств и действия], следования истинному учителю и [джняна-йоги:] слушания (шравана), размышления (манана) и медитации (нидидхьясана).")],-1),a("div",{class:"stanza"},[a("a",{href:"#s10",id:"s10",class:"num"},"10"),a("p",{sans:""},"अज्ञानमिति च रज्जौ सर्पभ्रान्तिरिवाद्वितीये सर्वानुस्यूते सर्वमये ब्रह्मणि एवतिर्यङ्नरस्थावरस्त्रीपुरुषवर्णाश्रमबन्धमोक्शोपाधिनानात्मभेदकल्पित ज्ञानमज्ञानम् ।"),a("p",{trans:""},[s("ajñānamiti ca rajjau sarpabhrāntirivādvitīye sarvānusyūte sarvamaye brahmaṇi evatiryaṅnarasthāvarastrīpuruṣavarṇāśramabandhamokśopādhinānātmabhedakalpita jñānamajñānam ।"),a("br")]),a("p",null,"Теперь о незнании (аджняна). Незнание — это ложное восприятие в вездесущем неделимом Брахмане воображаемых различных сущностей, называемых «боги и люди, неживая материя, женщины и мужчины, касты и стадии жизни (варнашрама), связанность и освобождение». [Это подобно тому,] как верёвку [по ошибке принимают] за змею.")],-1),a("div",{class:"stanza"},[a("a",{href:"#s11",id:"s11",class:"num"},"11"),a("p",{sans:""},"सुखमिति च सच्चिदानन्दस्वरूपं ज्ञात्वानन्दरूपा या स्थितिः सैव सुखम् ।"),a("p",{trans:""},[s("sukhamiti ca saccidānandasvarūpaṁ jñātvānandarūpā yā sthitiḥ saiva sukham ।"),a("br")]),a("p",null,"Теперь о счастье (сукха). Счастье — это состояние блаженства, [возникающее] благодаря познанию истинной природы (сварупа) Бытия, Сознания и Блаженства.")],-1),a("div",{class:"stanza"},[a("a",{href:"#s12",id:"s12",class:"num"},"12"),a("p",{sans:""},"दुःखमिति अनात्मरूपः विषयसङ्कल्प एव दुःखम् ।"),a("p",{trans:""},[s("duḥkhamiti anātmarūpaḥ viṣayasaṅkalpa eva duḥkham ।"),a("br")]),a("p",null,"Теперь о несчастье (дуккха). Форма, лишённая сущности, и желание объектов [вместо сущности] есть страдание.")],-1),a("div",{class:"stanza"},[a("a",{href:"#s13",id:"s13",class:"num"},"13"),a("p",{sans:""},"स्वर्ग इति च सत्संसर्गः स्वर्गः ।"),a("p",{trans:""},[s("svarga iti ca satsaṁsargaḥ svargaḥ ।"),a("br")]),a("p",null,"Теперь о рае. Истинное общение есть рай.")],-1),a("div",{class:"stanza"},[a("a",{href:"#s14",id:"s14",class:"num"},"14"),a("p",{sans:""},"नरक इति च असत्संसारविषयजनसंसर्ग एव नरकः ।"),a("p",{trans:""},[s("naraka iti ca asatsaṁsāraviṣayajanasaṁsarga eva narakaḥ ।"),a("br")]),a("p",null,"Теперь об аде. Общение с людьми, [которые] не [ведают] истины [и интересуются лишь] объектами этого мира, есть ад.")],-1),a("div",{class:"stanza"},[a("a",{href:"#s15",id:"s15",class:"num"},"15"),a("p",{sans:""},[s("बन्ध इति च अनाद्यविद्यावासनया जातोऽहमित्यादिसङ्कल्पो बन्धः ।"),a("br"),s(" पितृमातृसहोदरदारापत्यगृहारामक्शेत्रममता संसारावरणसङ्कल्पो बन्धः ।"),a("br"),s(" कर्तृत्वाद्यहङ्कारसङ्कल्पो बन्धः ।"),a("br"),s(" अणिमाद्यष्टैश्वर्याशासिद्धसङ्कल्पो बन्धः ।"),a("br"),s(" देवमनुष्याद्युपासनाकामसङ्कल्पो बन्धः ।"),a("br"),s(" यमाद्यष्टाङ्गयोगसङ्कल्पो बन्धः ।"),a("br"),s(" वर्णाश्रमधर्मकर्मसङ्कल्पो बन्धः ।"),a("br"),s(" आज्ञाभयसंशयात्मगुणसङ्कल्पो बन्धः ।"),a("br"),s(" यागव्रततपोदानविधिविधानज्ञानसम्भवो बन्धः ।"),a("br"),s(" केवलमोक्शापेक्शासङ्कल्पो बन्धः ।"),a("br"),s(" सङ्कल्पमात्रसंभवो बन्धः ।")]),a("p",{trans:""},[s("bandha iti ca anādyavidyāvāsanayā jāto'hamityādisaṅkalpo bandhaḥ ।"),a("br"),s(" pitṛmātṛsahodaradārāpatyagṛhārāmakśetramamatā saṁsārāvaraṇasaṅkalpo bandhaḥ ।"),a("br"),s(" kartṛtvādyahaṅkārasaṅkalpo bandhaḥ ।"),a("br"),s(" aṇimādyaṣṭaiśvaryāśāsiddhasaṅkalpo bandhaḥ ।"),a("br"),s(" devamanuṣyādyupāsanākāmasaṅkalpo bandhaḥ ।"),a("br"),s(" yamādyaṣṭāṅgayogasaṅkalpo bandhaḥ ।"),a("br"),s(" varṇāśramadharmakarmasaṅkalpo bandhaḥ ।"),a("br"),s(" ājñābhayasaṁśayātmaguṇasaṅkalpo bandhaḥ ।"),a("br"),s(" yāgavratatapodānavidhividhānajñānasambhavo bandhaḥ ।"),a("br"),s(" kevalamokśāpekśāsaṅkalpo bandhaḥ ।"),a("br"),s(" saṅkalpamātrasaṁbhavo bandhaḥ ।"),a("br")]),a("p",null,"Теперь о связанности (бандхе). Связанность есть мысли — «Я рождён» и другие — [возникающие] вследствие [присущей уму] тенденции (васана) – безначальной авидьи (неведения). Воображение ограды [вокруг какого-либо участка] этого мира, [воображение того, что] отец, мать, братья и сёстры, жёны и мужья, дома, сады и поля принадлежат нам (то есть нашему эго), есть связанность. Эгоистические идеи, такие, как [воображение себя] деятелем, есть связанность. Стремление получить восемь видов могущества — анима и остальные — есть связанность. Стремление почитать богов и людей ради [осуществления своих] желаний есть связанность. Желание [практиковать] восьмичленную йогу, начинающуюся с самоконтроля (яма), есть связанность. Стремление действовать в соответствии с предписаниями каст и ступеней жизни (варнашрама) есть связанность. Воображение того, что Дух (Атман) имеет качества, такие, как желание познания, страх и сомнение, есть связанность. [Совершение] жертвоприношений, [принятие религиозных] обетов, аскетизм и раздача [милостыни] — вследствие знания предписаний [шастр и их выполнения] — есть связанность. [Жизнь] с единственной идеей — надеждой на освобождение — есть связанность. Связанность возникает из воображения и стремлений ума (санкальпа).")],-1),a("div",{class:"stanza"},[a("a",{href:"#s16",id:"s16",class:"num"},"16"),a("p",{sans:""},"मोक्श इति च नित्यानित्यवस्तुविचारादनित्यसंसारसुखदुःखविषयसमस्तक्शेत्रममताबन्धक्शयो मोक्शः ।"),a("p",{trans:""},[s("mokśa iti ca nityānityavastuvicārādanityasaṁsārasukhaduḥkhaviṣayasamastakśetramamatābandhakśayo mokśaḥ ।"),a("br")]),a("p",null,"Теперь об освобождении. Уничтожение связанности (бандха) [идеей того, что] всевозможные объекты этого мира, причиняющие удовольствие и страдание, принадлежат [человеку], [происходящее] вследствие различения вечного и преходящего, есть освобождение.")],-1),a("div",{class:"stanza"},[a("a",{href:"#s17",id:"s17",class:"num"},"17"),a("p",{sans:""},"उपास्य इति च सर्वशरीरस्थचैतन्यब्रह्मप्रापको गुरुरुपास्यः ।"),a("p",{trans:""},[s("upāsya iti ca sarvaśarīrasthacaitanyabrahmaprāpako gururupāsyaḥ ।"),a("br")]),a("p",null,"Теперь о том, кого следует почитать. Того учителя (Гуру), который помогает [ученику] постичь Брахман, присутствующий во всех телах как Сознание и Жизнь (чайтанья), надлежит почитать [и ему стоит следовать].")],-1),a("div",{class:"stanza"},[a("a",{href:"#s18",id:"s18",class:"num"},"18"),a("p",{sans:""},"शिष्य इति च विद्याध्वस्तप्रपञ्चावगाहितज्ञानावशिष्टं ब्रह्मैव शिष्यः ।"),a("p",{trans:""},[s("śiṣya iti ca vidyādhvastaprapañcāvagāhitajñānāvaśiṣṭaṁ brahmaiva śiṣyaḥ ।"),a("br")]),a("p",null,"Теперь об ученике. Ученик есть воистину Брахман, погруженный в познание мира как наполненного силой Сознания.")],-1),a("div",{class:"stanza"},[a("a",{href:"#s19",id:"s19",class:"num"},"19"),a("p",{sans:""},"विद्वानिति च सर्वान्तरस्थस्वसंविद्रूपविद्विद्वान् ।"),a("p",{trans:""},[s("vidvāniti ca sarvāntarasthasvasaṁvidrūpavidvidvān ।"),a("br")]),a("p",null,"Теперь о познавшем. Тот, кто постиг форму своего Сознания (свасамвидрупу), присутствующую внутри всего, есть познавший.")],-1),a("div",{class:"stanza"},[a("a",{href:"#s20",id:"s20",class:"num"},"20"),a("p",{sans:""},"मूढ इति च कर्तृत्वाद्यहङ्कारभावारूढो मूढः ।"),a("p",{trans:""},[s("mūḍha iti ca kartṛtvādyahaṅkārabhāvārūḍho mūḍhaḥ ।"),a("br")]),a("p",null,"Теперь о [том, кто есть] глупец. Тот, кто восседает на чувстве [своего] эго (аханкаре), [проявляющемся как идея] «Я [есть истинный] деятель» и т. д., есть глупец.")],-1),a("div",{class:"stanza"},[a("a",{href:"#s21",id:"s21",class:"num"},"21"),a("p",{sans:""},"आसुरमिति च ब्रह्मविष्ण्वीशानेन्द्रादीनामैश्वर्यकामनया निरशनजपाग्निहोत्रादिष्वन्तरात्मानं सन्तापयति चात्युग्ररागद्वेषविहिंसा दम्भाद्यपेक्शितं तप आसुरम् ।"),a("p",{trans:""},[s("āsuramiti ca brahmaviṣṇvīśānendrādīnāmaiśvaryakāmanayā niraśanajapāgnihotrādiṣvantarātmānaṁ santāpayati cātyugrarāgadveṣavihiṁsā dambhādyapekśitaṁ tapa āsuram ।"),a("br")]),a("p",null,"Теперь о том, что демонично. Демонична аскеза, происходящая из насильственной страсти, враждебности, [желания] насилия, лицемерия и т. п.; [демоничны такие] посты, повторение [мантр], огненные жертвоприношения и другие [практики], [совершаемые] со стремлением [получить] власть, [подобную власти богов:] Брахмы, Вишну, Ишаны, Индры и других. [Этим человек] мучает [свою] внутреннюю сущность.")],-1),a("div",{class:"stanza"},[a("a",{href:"#s22",id:"s22",class:"num"},"22"),a("p",{sans:""},"तप इति च ब्रह्म सत्यं जगन्मिथ्येत्यपरोक्शज्ञानाग्निना ब्रह्माद्यैश्वर्याशासिद्धसङ्कल्पबीजसन्तापं तपः ।"),a("p",{trans:""},[s("tapa iti ca brahma satyaṁ jaganmithyetyaparokśajñānāgninā brahmādyaiśvaryāśāsiddhasaṅkalpabījasantāpaṁ tapaḥ ।"),a("br")]),a("p",null,"Теперь о подвижничестве (тапас). Подвижничество есть сжигание [в себе] семени стремления к обретению власти, [подобной власти богов —] Брахмы и других, в огне непосредственного постижения того, что «Брахман истинен, [а восприятие] мира [как отдельного от Брахмана] иллюзорно».")],-1),a("div",{class:"stanza"},[a("a",{href:"#s23",id:"s23",class:"num"},"23"),a("p",{sans:""},"परमं पदमिति च प्राणेन्द्रियाद्यन्तःकरणगुणादेः परतरं सच्चिदानन्दमयं नित्यमुक्तब्रह्मस्थानं परमं पदम् ।"),a("p",{trans:""},[s("paramaṁ padamiti ca prāṇendriyādyantaḥkaraṇaguṇādeḥ parataraṁ saccidānandamayaṁ nityamuktabrahmasthānaṁ paramaṁ padam ।"),a("br")]),a("p",null,"Теперь о высшем состоянии (парамапада). Обитель Брахмана, вечная свобода, исполненная Бытия, Сознания и Блаженства и превосходящая прану, органы [чувств и действия], психику (антахкарана) и качества материи (гуна), есть высшее состояние.")],-1),a("div",{class:"stanza"},[a("a",{href:"#s24",id:"s24",class:"num"},"24"),a("p",{sans:""},"ग्राह्यमिति च देशकालवस्तुपरिच्छेदराहित्यचिन्मात्रस्वरूपं ग्राह्यम् ।"),a("p",{trans:""},[s("grāhyamiti ca deśakālavastuparicchedarāhityacinmātrasvarūpaṁ grāhyam ।"),a("br")]),a("p",null,"Теперь о том, что следует воспринять. Надлежит постичь истинную природу (сварупа), которая есть Сознание, лишённое разделения на место, время и материю.")],-1),a("div",{class:"stanza"},[a("a",{href:"#s25",id:"s25",class:"num"},"25"),a("p",{sans:""},"अग्राह्यमिति च स्वस्वरूपव्यतिरिक्तमायामयबुद्धीन्द्रियगोचरजगत्सत्यत्वचिन्तनमग्राह्यम् ।"),a("p",{trans:""},[s("agrāhyamiti ca svasvarūpavyatiriktamāyāmayabuddhīndriyagocarajagatsatyatvacintanamagrāhyam ।"),a("br")]),a("p",null,"Теперь о том, чего не следует иметь. Не следует иметь мысли о том, что мир, воспринимаемый разумом и органами [чувств], сотворённый творческой силой Брахмана (майей) и отличный от собственной истинной природы [человека], есть [конечная] Истина.")],-1),a("div",{class:"stanza"},[a("a",{href:"#s26",id:"s26",class:"num"},"26"),a("p",{sans:""},"संन्यासीति च सर्वधर्मान्परित्यज्य निर्ममो निरहङ्कारो भूत्वा ब्रह्मेष्टं शरणमुपगम्य तत्त्वमसि अहं ब्रह्मास्मि सर्वं खल्विदं ब्रह्म नेह नानास्ति किञ्चनेत्यादिमहावाक्यार्थानुभवज्ञानाद्ब्रह्मैवाहमस्मीति निश्चित्य निर्विकल्पसमाधिना स्वतन्त्रो यतिश्चरति स संन्यासी स मुक्तः स पूज्यः स योगी स परमहंसः सोऽवधूतः स ब्राह्मण इति ।"),a("p",{trans:""},[s("saṁnyāsīti ca sarvadharmānparityajya nirmamo nirahaṅkāro bhūtvā brahmeṣṭaṁ śaraṇamupagamya tattvamasi ahaṁ brahmāsmi sarvaṁ khalvidaṁ brahma neha nānāsti kiñcanetyādimahāvākyārthānubhavajñānādbrahmaivāhamasmīti niścitya nirvikalpasamādhinā svatantro yatiścarati sa saṁnyāsī sa muktaḥ sa pūjyaḥ sa yogī sa paramahaṁsaḥ so'vadhūtaḥ sa brāhmaṇa iti ।"),a("br")]),a("p",null,"Теперь о том, кто есть санньясин. Тот мудрец, кто освободился от чувства собственности и эго, отрёкся ото всех дхарм, предался всецело Брахману, осознал себя тождественным Брахману благодаря непосредственному постижению смысла великих изречений — «То ты есть», «Я есть Брахман», «Всё это есть Брахман, без каких-либо различий» и других, и живёт свободно, пребывая в лишённом викальп экстазе (нирвикальпа-самадхи), есть санньясин. Он — освобождённый, он достоин почитания, он — йогин, он — парамахамса, он — авадхута, он — брахман.")],-1),a("div",{class:"stanza"},[a("p",{sans:""},"इदं निरालम्बोपनिषदं योऽधीते गुर्वनुग्रहतः सोऽग्निपूतो भवति स वायुपूतो भवति न स पुनरावर्तते न स पुनरावर्तते पुनर्नाभिजायते पुनर्नाभिजायत इत्युपनिषत् ॥"),a("p",{trans:""},"idaṁ nirālambopaniṣadaṁ yo'dhīte gurvanugrahataḥ so'gnipūto bhavati sa vāyupūto bhavati na sa punarāvartate na sa punarāvartate punarnābhijāyate punarnābhijāyata ityupaniṣat ॥"),a("p",null,"Тот, кто изучает эту «Нираламба упанишаду», по милости Гуру (Шивы) очищается огнём [любви] и очищается воздухом [знания]. Воистину, он более не связан перерождениями».")],-1),a("div",{class:"stanza"},[a("p",{sans:""},[s("ॐ पूर्णमदः पूर्णमिदं पूर्णात्पूर्णमुदच्यते ।"),a("br"),s(" पूर्णस्य पूर्णमादाय पूर्णमेवावशिष्यते ॥"),a("br"),s(" ॐ शान्तिः शान्तिः शान्तिः ॥")]),a("p",{trans:""},[s("oṁ pūrṇamadaḥ pūrṇamidaṁ pūrṇātpūrṇamudacyate ।"),a("br"),s(" pūrṇasya pūrṇamādāya pūrṇamevāvaśiṣyate ॥"),a("br"),s(" oṁ śāntiḥ śāntiḥ śāntiḥ ॥")]),a("p",null,"Ом. То — бесконечность, и это — бесконечность. Бесконечность исходит из бесконечности, Обретая бесконечность бесконечного, она остается лишь бесконечностью. Ом! Покой! Покой! Покой!  ")],-1),a("p",{sans:""},"॥ इति निरालम्बोपनिषत्समाप्ता ॥",-1),a("p",{trans:""},".. iti nirālambopaniṣatsamāptā ॥",-1),a("p",null,"Такова «Нираламба упанишада» Шукла-Яджурведы.",-1),a("p",null,[a("a",{href:"/books/niralamba.pdf",target:"_blank"},"Санскритский текст Нираламба Упанишады")],-1)]))}const k=n(b,[["render",p]]);export{e as __pageData,k as default};
