import{_ as e,c as s,a,a0 as n,o as t}from"./chunks/framework.B7ZEGzqH.js";const h=JSON.parse('{"title":"Пратьябхиджняхридаям","description":"Сущность узнавания","frontmatter":{"title":"Пратьябхиджняхридаям","description":"Сущность узнавания","sans":"प्रत्यभिज्ञाहृदयम्","trans":"Pratyabhijñāhṛdayam","tags":"text","date":"2021-06-07T00:00:00.000Z"},"headers":[],"relativePath":"texts/pratyabhijnya/index.md","filePath":"texts/pratyabhijnya/index.md"}'),p={name:"texts/pratyabhijnya/index.md"};function u(m,l,i,r,g,d){return t(),s("div",null,l[0]||(l[0]=[a("p",null,[n("Перевод с санскрита и комментарии С. В. Пахомова. Перевод коренного текста откорректирован и дополнен на основе "),a("a",{href:"https://www.sanskrit-sanscrito.com/en/pratyabhijnahrdayam-only-the-20-sutra-s-trika-scriptures-non-dual-shaivism-of-kashmir/541",class:"external-link",target:"_blank",rel:"noreferrer"},"пословного перевода на английский Гуру Габриэля Прадиипаки"),n(".")],-1),a("div",{class:"stanza"},[a("p",null,"Ом, слава [Шиве], воплощению счастья! Итак, Сущность Узнавания."),a("p",{eng:""},"Слава Шиве, вечно совершающему пять деяний, [Ему], чья природа [состоит] из целокупности сознания и блаженства, Сияющему Высшей реальностью! "),a("p",{eng:""},"Из великого океана самоузнавания – основы тайного учения Шанкары – Извлек Кшемараджа эссенцию ради избавления [существ] от яда сансары. Здесь, [в этом мире,] есть некоторые [люди], которые, [хотя они и] не умеют мыслить строго и не могут разобраться в тонкостях логического познания, наделены глубоким благочестием и желают стать едиными с Верховным Господом через нисхождение Шакти.Для них и излагается вкратце истина Узнавания Бога. Чтобы показать, [почему] сущностная природа Божества [является] универсальной причиной, [каким образом она] легко достигается, [почему она есть] великий плод, говорится: ")],-1),a("div",{class:"stanza"},[a("a",{href:"#s1",id:"s1",class:"num"},"1"),a("p",{sans:""},"चितिः स्वतन्त्रा विश्वसिद्धिहेतुः ॥१॥"),a("p",{trans:""},"Citiḥ svatantrā viśvasiddhihetuḥ ॥1॥"),a("p",null,[n("Самоестественная "),a("em",null,"(svatantrā)"),n(" Сила Абсолютного Сознания "),a("em",null,"(citiḥ)"),n(" является причиной "),a("em",null,"(hetuḥ)"),n(" реализации "),a("em",null,"(siddhi)"),n(" вселенной "),a("em",null,"(viśva)"),n(".")]),a("p",{eng:""},[a("strong",null,"Комментарий"),a("br"),n(" [Слово] «вселенная» означает [совокупность элементов], от Садашивы до земли. «Реализация» – это «исполнение», т. е. проявление [мира], сохранение [его] и уничтожение, успокоение [его] в Высшем Субъекте. «Сила Абсолютного Сознания» – форма Верховной Шакти как Божественной энергии, и состоит [эта Сила] из наивысшей вимарши, [которая] не отделима от Благословенного Шивы и является «причиной», источником [реализации вселенной]. Именно когда Она развертывается, мир возникает и сохраняется, а когда Она сворачивается, он пропадает из виду. Личный зрительный опыт может подтвердить это. Другие [начала] – майя, пракрити и т. д. – не могут являться [главным] источником, поскольку они [предполагаются] отделенными от Света Сознания, [но ведь то, что] отделено от этого Света, не может существовать. Проявиться может только то, что является единым со Светом Сознания.[Таким образом,] только Чити, имеющая форму Света Сознания, и есть [эта] причина, и ничто другое. Поэтому пространство, время и [ограниченная] форма, будучи созданы и оживлены этой [Силой Сознания], не способны разделить [с Ней] Ее сущность, тогда как Она является всепроникающей, вечной, наделенной наивысшей полнотой. Таков смысл cказанного.")]),a("p",{eng:""},"Могут возразить: Если мир не отделен от Сознания, то тогда ничего не существует [помимо Него]. А раз [мир] не отделен, как тогда [возможны] причинно-следственные отношения [между ними]?"),a("p",{eng:""},"[Ответ]: только Сила Божественного Сознания, по своему желанию, совершенно свободно проявляет себя в форме бесчисленных миров. Таков высший смысл причинно-следственных отношений. Поскольку [Сила Сознания] одна является причиной реализации, проявления вселенной, [подразделяющейся на] субъект, источник и объект познания, то Ее, абсолютную, безграничную, самосветящуюся, невозможно ухватить ограниченными средствами познания, с помощью которых [можно разве что] постигать внешние объекты."),a("p",{eng:""},"Так говорится в Трикасаре: «Подобно тому, как при попытке перепрыгнуть тень от собственной головы [невозможно ухватить эту] тень, [которая] все время будет передвигаться дальше, [невозможно ухватить] и Силу Универсального Сознания ». Поскольку Она (Сила Сознания – С. П.) является источником манифестации вселенной, а также [причиной ее] уничтожения, [благодаря которому] достигается соединение с высшей недвойственностью, Ее [называют] самоестественной. Когда Ее узнают как самоестественную, то [видят в ней] причину реализации вселенной в форме наслаждения и освобождения. Так вторично можно истолковать эту сутру. Далее, [слово] «вселенная» [означает внешние объекты, например,] голубой [цвет], [внутренние, например,] удовольствие, [а также] тело, прану и т. п. Реализация вселенной, состоящая в постепенном возвышении от [ступени] источника познания до [ступени] погружения в [Высшего] сознающего субъекта, имеющего форму вимарши – [такая реализация суть] средство [достижения] высшего знания. Имеется в виду «легкое средство»."),a("p",{eng:""},"Как говорит Виджнянабхаттарака, «Все телесные существа наделены знанием субъекта и объекта, но только йогины могут воспринимать их в единстве». [Термин] «Сила Сознания», [употребленный в сутре] в единственном числе, означает не ограниченность пространством, временем и т. д., [и это] демонстрирует ложность всех дуалистических учений."),a("p",{eng:""},"Термин «самоестественная», о котором не знает учение о Брахмане, означает то, что сущностью Великого Господа является Сознание. «Вселенная» и т. д. означает, что [Сознание] наделено безграничной силой, является причиной всего, легким средством [достижения освобождения] и великой наградой."),a("p",{eng:""},"На это возразят: Если Сила Сознания является причиной вселенной, то это предусматривает материальную причину и т. п., что оставляет дуализм в силе. В [ответе] на это возражение говорится:")],-1),a("div",{class:"stanza"},[a("a",{href:"#s2",id:"s2",class:"num"},"2"),a("p",{sans:""},"स्वेच्छया स्वभित्तौ विश्वमुन्मीलयति ॥२॥"),a("p",{trans:""},"Svecchayā svabhittau viśvamunmīlayati ॥2॥"),a("p",null,[n("По своей "),a("em",null,"(sva)"),n(" воле "),a("em",null,"(icchayā)"),n(", на собственной "),a("em",null,"(sva)"),n(" поверхности "),a("em",null,"(bhittau)"),n(", [Сила Сознания] разворачивает "),a("em",null,"(unmīlayati)"),n(" вселенную "),a("em",null,"(viśvam)"),n(".")]),a("p",{eng:""},[a("strong",null,"Комментарий"),a("br"),n(" «По своей воле» означает, что [это происходит] не по воле другого, как полагают сторонники учения о Брахмане и им подобные; означает [также, что Она производит вселенную сама, а] не с помошью материальной причины и т. д. Сознание не может функционировать, если не обладает вышеназванным [аспектом] самоестественности.")]),a("p",{eng:""},"«На собственной поверхности» означает, что это не происходит нигде больше. Она [Чити] «делает видимой» вышеупомянутую «вселенную», как, [например] город [делается видимым] в зеркале, [и отражение этого города], хотя и не отличное [от зеркальной поверхности], все же [выглядит] как отличное [от него]. [Чити как] причина манифестации [вселенной] делает ее видимой и стабильной."),a("p",{eng:""},"Смысл сказанного таков: мир идентичен Свету Сознания. Для того, чтобы прояснить в деталях природу вселенной, [автор] говорит:")],-1),a("div",{class:"stanza"},[a("a",{href:"#s3",id:"s3",class:"num"},"3"),a("p",{sans:""},"तन्नानानुरूपग्राह्यग्राहकभेदात् ॥३॥"),a("p",{trans:""},"Tannānānurūpagrāhyagrāhakabhedāt ॥3॥"),a("p",null,[n("Она "),a("em",null,"(tad)"),n(" множественна "),a("em",null,"(nānā)"),n(" из-за дифференциации "),a("em",null,"(bhedāt)"),n(" на соответствующие "),a("em",null,"(anurūpa)"),n(" субъекты "),a("em",null,"(grāhaka)"),n(" и объекты "),a("em",null,"(grāhya)"),n(" .")]),a("p",{eng:""},[a("strong",null,"Комментарий"),a("br"),n(" «Она» – это вселенная. «Множественна» – [т. е. имеет] природу иного. Почему [множественна]? Потому что есть различие, «дифференциация» между «субъектами и объектами», которые находятся в состоянии «соответствия» [между собой], т. е. взаимно соотнесены. На ступени садашива, где доминирует совершенство Я и объектность в ее полноте [еще] не проявлена, вселенная как объект является и отличной, и не отличной [от Я], и пребывает там (на этой ступени – С. П.) разряд сознающих субъектов, называемых мантрамахешварами, зависящих от Благословенного Вечного Шивы.")]),a("p",{eng:""},"Своим существованием они обязаны воле Верховного Господа. На ступени ишвара вселенная понимается как [содержащая] различие между полнотой Я и не-Я, [хотя Я и не-Я продолжают находиться] в состоянии идентичности [друг с другом]; и пребывает там разряд мантрешвар, покорных воле Бога-Творца. На уровне видья пребывают субъекты созерцания, [называемые] мантрами, ими управляет Благословенный Ананта, и многим видам разных подразделений [этих мантр] соответствует объект знания в форме вселенной, сущность которой [на этой ступени] состоит в разделении единого. Над [ступенью] майи [пребывают субъекты, называемые] виджнянакалы; у них нет [стремления] к деятельности, а есть природа чистого сознавания. Объект [познания] не отличен от них и состоит в опыте переживания [ими] прошлых существований, во время которых они находились на ступенях сакала и пралаякала. На ступени майя находятся субъекты, созерцающие пустоту, или «освободившиеся от пралаи»; им соответствует в качестве объекта то, что не воспринимается чувствами. [Далее], вплоть до [элемента] земли пребывают отделенные от всех вещей, ограниченные сакалы, и им [в опыте] соответствуют такие же [изолированные] объекты. Над всем же этим [располагаются] существа, которых направляет Благословенный Шива и плоть которых суть только чистый свет. Но именно в этом великом Парамашиве, [одновременно] и трансцендентном, и имманентном вселенной, наделенном высшим блаженством, состоящем из световой массы, проявляется, не отделяясь [от Него], «все» [в мире], начиная [от таттвы] Шива и заканчивая [таттвой] земля. В реальности нет никакого различия между схватывающим и схватываемым; тем не менее, Благословенный Шри Парамашива манифестируется в тысячах разнообразных [форм]. Так это должно быть понято.")],-1),a("div",{class:"stanza"},[a("a",{href:"#s4",id:"s4",class:"num"},"4"),a("p",{sans:""},"चितिसङ्कोचात्मा चेतनोऽपि सङ्कुचितविश्वमयः ॥४॥"),a("p",{trans:""},"Citisaṅkocātmā cetano'pi saṅkucitaviśvamayaḥ ॥4॥"),a("p",null,[n("Ограниченная "),a("em",null,"(saṅkucita)"),n(" вселенная становится телом "),a("em",null,"(viśva-mayaḥ)"),n(" Абсолютного Сознания "),a("em",null,"(citi)"),n(", также "),a("em",null,"(api)"),n(" сжатого "),a("em",null,"(saṅkoca-ātmā)"),n(" до Высшего разума "),a("em",null,"(cetanaḥ)"),n(".")]),a("p",{eng:""},[a("strong",null,"Комментарий"),a("br"),n(" Когда Шри Парамашива желает проявить содержащуюся в нем и идентичную ему вселенную – в форме Вечного Шивы и других соответствующих формах – он проявляется в [собственном] Свете сначала как несознающий единство сознания, т. е. как Анашрита-Шива.")]),a("p",{eng:""},"Это [состояние] абсолютной пустоты, [но оно все же] неотделимо от Света Сознания. Затем он раскрывает свою сущность во множестве манифестаций: принципах, мирах, сущностях, [а также] в соответствующих им субъектах. [Все они] – только затвердевшие формы квинтэссенции Сознания. Подобно тому, как телом Бхагавана является вселенная, так и телом «ограниченного субъекта», имеющего «ограниченное сознание», является ограниченная форма вселенной, подобно тому, как [дерево] вата [в сжатом виде] содержится в семени."),a("p",{eng:""},"Так говорится в Сиддханте: «Одно тело, одна форма суть все тела, все формы». Также в Триширомате, [которая начинается так]: «В теле содержатся все божества; послушай об этом, дорогая. Земля называется [так] из-за [сво ей] твердости, вода – текучести…», и заканчивается [следующим]: «Трехголовый Бхайрава присутствует в индивиде как пронизывающий вселенную»."),a("p",{eng:""},"Таким образом, в [данном] тексте говорится о том, что индивид есть по своей форме ограниченная вселенная. К этому есть дополнение: Индивид этот тождественен Шиве, телом которого является вселенная, из-за того, что истинная его природа – Свет Сознания, о чем говорится в упомянутых агамах. Только из-за деятельности Его [Шивы] майя-шакти [индивид] становится ограниченным, ведь его истинная природа не проявлена. Но это ограничение тоже [состоит] из одного сознания, поскольку оно появляется как имеющее природу Сознания – в противном случае оно не было бы ничем."),a("p",{eng:""},"Так что всякий субъект – это и есть Благословенный Шива, чьим телом является вселенная. Мною было сказано [в другом месте]: «Если незнание не [появляется в форме] знания, то остается только знание. Если [незнание] появляется в форме знания, то [опять] остается одно только знание»."),a("p",{eng:""},"Об этом же особо [сказано] в Спанда-шастре, в стихах, начинающихся так: «Джива, [идентичный] вселенной…» и заканчивающихся «ни в слове, ни в объекте, ни в мышлении нет состояния, в котором отсутствовал бы Шива»."),a("p",{eng:""},"Тем самым говорится о неразделенности Шивы и дживы. Одно понимание этой истины [приносит] освобождение; незнание же этой истины [ввергает] во мрак."),a("p",{eng:""},"Об этом будет еще сказано [ниже]. На это могут возразить: ведь субъекту свойственно дифференцировать [объекты], а это свойство происходит от читты, почему тогда [говорится], что он идентичен Шиве? Отвечая на подобное возражение, [автор] говорит, устанавливая [значение] читты:")],-1),a("div",{class:"stanza"},[a("a",{href:"#s5",id:"s5",class:"num"},"5"),a("p",{sans:""},"चितिरेव चेतनपदावरूढा चेत्यसङ्कोचिनी चित्तम् ॥५॥"),a("p",{trans:""},"Citireva cetanapadādavarūḍhā cetyasaṅkocinī cittam ॥5॥"),a("p",null,[n("Только "),a("em",null,"(eva)"),n(" Абсолютное Сознание "),a("em",null,"(citiḥ)"),n(", нисходя "),a("em",null,"(avarūḍhā)"),n(" со ступени "),a("em",null,"(padāt)"),n(" Высшего разума "),a("em",null,"(cetana)"),n(", становится сознанием "),a("em",null,"(cittam)"),n(", ограниченным "),a("em",null,"(saṅkocinī)"),n(" объектами "),a("em",null,"(cetya)"),n(".")]),a("p",{eng:""},[a("strong",null,"Комментарий"),a("br"),n(" То, что называется индивидуальным сознанием, есть не что иное как сама Бхагавати.")]),a("p",{eng:""},"Когда Она, скрывая свою истинную природу, принимает ограничение, тогда Она выступает в двух аспектах. Иногда в Ней преобладает чит, и тогда ограничение, сделавшее Ее проявление, освещено этим чит; иногда преобладает ограничение. Когда чит доминирует в естественном состоянии, [при котором] преобладает Свет Сознания, то субъектом его является виджнянакала."),a("p",{eng:""},"Если Свет Сознания [выступает совместно] с Силой Рефлексии, то субъект [в этом случае] – видья. По мере постепенного убывания ограничения [естественным путем] появляются такие формы [Шивы], как Иша, Садашива и Анашрита. Но когда аспект чит достигается при помощи самадхи, субъекты Чистого пути [обретают] Высшее [состояние] последовательно. Где преобладает ограничение, там появляется субъектность пустоты и т. д."),a("p",{eng:""},"Находясь в таком положении, Высшее Сознание, в форме ограниченного субъекта, «нисходя со ступени четана», обращается к воспринимаемым «объектам», таким как голубому, счастью и т. д. и становится «ограниченным» обоими [видами] ограничений [внешним и внутренним], становится читтой. Как сказано: «То, что в Его членах явлено как джняна, крия и майя, у пашу становится саттвой, раджасом и тамасом»."),a("p",{eng:""},"Таким образом, чити-шакти, имеющая природу ограничения, становится в индивидуальном сознании сущностью, имеющей [качества] саттва, раджас и тамас – так говорится в Пратьябхиджне. Поскольку истинная природа «наделенного таттвами», [т. е. индивида, сохраняется] даже в состоянии викальпы, то с целью прояснить это в Таттва гарбха-стотре говорится: «Поэтому светозарная сущность тех, кто следует высшей истине, никогда не омрачается».")],-1),a("div",{class:"stanza"},[a("a",{href:"#s6",id:"s6",class:"num"},"6"),a("p",{sans:""},"तन्मयो मायाप्रमाता ॥६॥"),a("p",{trans:""},"Tanmayo māyāpramātā ॥6॥"),a("p",null,[n("Из этого "),a("em",null,"(tad)"),n(" состоит "),a("em",null,"(mayaḥ)"),n(" субъект "),a("em",null,"(pramātā)"),n(" майи "),a("em",null,"(māyā)"),n(".")]),a("p",{eng:""},[a("strong",null,"Комментарий"),a("br"),n(" Читта доминирует больше всего в теле и в дыхании. Область пустоты тоже содержит остаточные следы читты – в противном случае не смог бы действовать очнувшийся [от переживания пустоты] индивид.")]),a("p",{eng:""},"Поэтому субъект майи состоит только из читты. С этой целью в Шива-сутре (1. 1), при обсуждении вопроса о реальности, говорится: "),a("p",{eng:""},[a("strong",null,"«Абсолютное Сознание суть Я»"),n(", ")]),a("p",{eng:""},"а далее, обсуждая тему признаков субъекта майи, снова говорится: "),a("p",{eng:""},[a("strong",null,"«Индивидуальное сознание – это Я»"),n(". ")]),a("p",{eng:""},"Одно лишь верное знание собственной сущности приносит освобождение, а неверное [ввергает] в сансару.")],-1),a("div",{class:"stanza"},[a("a",{href:"#s7",id:"s7",class:"num"},"7"),a("p",{sans:""},"स चैको द्विरूपस्त्रिमयश्चतुरात्मा सप्तपञ्चकस्वभावः ॥७॥"),a("p",{trans:""},"Sa caiko dvirūpastrimayaścaturātmā saptapañcakasvabhāvaḥ ॥7॥"),a("p",null,[n("И "),a("em",null,"(ca)"),n(", будучи Одним "),a("em",null,"(ekaḥ)"),n(", Он "),a("em",null,"(saḥ)"),n(" [обретает] две формы "),a("em",null,"(dvirūpaḥ)"),n(", три облика "),a("em",null,"(trimayaḥ)"),n(", четыре самости "),a("em",null,"(catur-ātmā)"),n(", природу "),a("em",null,"(sva-bhāvaḥ)"),n(" семи "),a("em",null,"(sapta)"),n(" пятёрок "),a("em",null,"(pañcaka)"),n(".")]),a("p",{eng:""},[a("strong",null,"Комментарий"),a("br"),n(" На основании [этого утверждения ясно], что только имеющий природу Сознания Благословенный Шива является по своей природе «одним», и никто другой, потому что невозможно разделить Свет Сознания на пространственные, временные и другие рамки, а также потому, что субъектом познания не может быть инертная материя. ")]),a("p",{eng:""},"Когда Свет Сознания благодаря своей самоестественности становится ограничен праной и т. д. и превращается в субъекта ограниченного опыта, то такое [положение, когда существуют] форма Света и ограниченное проявление, [называется] «двумя формами». "),a("p",{eng:""},"По причине сокрытия себя [тремя] нечистыми качествами, а именно анавой, майией и кармой, [Он называется принявшим] «три облика». "),a("p",{eng:""},"Он [называется имеющим] «четыре природы» из-за того, что [проходит этапы] пустоты, праны, тонкого и плотного тела. "),a("p",{eng:""},[n("«Тридцать пять» – это тридцать шесть таттв, «сущностей», от Шивы до стихии земли. Начиная от Шивы и заканчивая сакалой [насчитывается] седьмица [родов] познающих субъектов. Хотя [по своей сути индивид имеет] форму сознания-блаженства-воли-знания-действия, под воздействием омрачающей силы он становится связан пятерицей затмевающих слоев-канчук, а именно: фрагментарностью "),a("em",null,"(kalа)"),n(", ограниченным знанием "),a("em",null,"(vidyа)"),n(", страстью "),a("em",null,"(rаga)"),n(", временем "),a("em",null,"(kаla)"),n(" и упорядоченностью "),a("em",null,"(niyati)"),n(". И только узнавание единой сущности Шивы, которая [развивается] в тридцать пять таттв, семь субъектов познания, пятерицу шакти начиная с шакти Сознания – только оно дарует освобождение, в противном случае она [стала бы] источником сансары. И также: ")])],-1),a("div",{class:"stanza"},[a("a",{href:"#s8",id:"s8",class:"num"},"8"),a("p",{sans:""},"तद्भूमिकाः सर्वदर्शनस्थितयः ॥८॥"),a("p",{trans:""},"Tadbhūmikāḥ sarvadarśanasthitayaḥ ॥8॥"),a("p",null,[n("Воззрения "),a("em",null,"(sthitayaḥ)"),n(" всех "),a("em",null,"(sarva)"),n(" философских систем "),a("em",null,"(darśana)"),n(" [суть лишь различные] обличья "),a("em",null,"(bhūmikāḥ)"),n(" этой "),a("em",null,"(tad)"),n(" [Реальности].")]),a("p",{eng:""},[a("strong",null,"Комментарий"),a("br"),n(" «Воззрения», т. е. основоположения «всех даршан», начиная с чарваки, суть принимаемые этим Я по своей воле искусственные «обличья», подобные обличьям актера. ")]),a("p",{eng:""},"Так, чарваки [заявляют], что отличительным признаком Сознания является его тождественность телу. Найяики и другие считают Я, [когда Я] подвержено трансмиграции, почти тем же самым, что и буддхи, который является основой знания и других качеств; при освобождении же, когда буддхи исчезает, [Я рассматривается ими как] почти идентичное пустоте.Мимансаки тоже связывают Я с буддхи, считая, что то, что известно в представлении о «я», искаженном привнесением [в него] удовольствия, страдания и т. д., и есть Я. "),a("p",{eng:""},"Для сторонников Сугаты истиной является поток знания; таким образом, они тоже [связывают Я] с действиями буддхи. Некоторые последователи веданты [считают], что Я – это прана. Знатоки Брахмана, говоря, что «этот [мир когда-то] не существовал» и [руководствуясь такой идеей] небытия, принимают пустоту за основополагающий принцип. "),a("p",{eng:""},"Мадхьямики также согласны с этой [позицией]. Панчаратрины [полагают], что высшей [материальной] причиной является Господь Васудева, а отдельные души подобны искрам от его [костра] и суть трансформации этой Высшей Причины. Так [считая, панчаратрины] цепляются за непроявленное. Санкхьяики и им подобные останавливаются на уровне, приблизительно соответствующем виджнянакалам. "),a("p",{eng:""},"«Вначале было только Одно» – так привязываются к принципу Ишвары другие ведантисты. Грамматисты думают, что Я – это [принцип] Шабдабрахман в форме пашьянти, [тем самым] ставя его на уровень Садашивы. Так же можно представить и другие системы. "),a("p",{eng:""},"Так говорится в агамах: «Буддисты видят [Я в принципе] буддхи, архаты – в комбинации гун, знатоки вед – в Пуруше, а панчаратрики – в непроявленности». "),a("p",{eng:""},"Тантрики [утверждают], что Я запредельно вселенной. Принадлежащие к традиции кула уверены, что [Я] совпадает со вселенной. Знатоки трики и т. п. [утверждают, что Я одновременно] и превосходит вселенную, и совпадает с ней. "),a("p",{eng:""},"Таким образом, все эти роли разыгрываются относящейся к Бхагавану – сущностью которого является Сознание – его cамоестественностью (сватантрьей), и они различаются [друг от друга] только по разным степеням укрывания или раскрытия сватантрьи. Поэтому один лишь Атман и исполняет все эти [роли]. Однако, наделенные ограниченным видением, все эти предыдущие субъекты, которые, согласно Его воле, [выражают] по-своему разные [Его] уровни, отождествляют [Я] с телом и т. д., цепляются за ошибочные воззрения, и не способны понять без нисхождения Парашакти великого пронизывания [вещей Атманом], о котором говорилось выше. "),a("p",{eng:""},"Как было сказано: «Вайшнавы и другие, опутанные ограниченным знанием и страстью, не ведают Высшего Бога, всезнающего, наделенного истинным знанием». "),a("p",{eng:""},"Говорится и так: «Одна лишь майя заставляет их, желающих освободиться, блуждать во тьме ». Говорится еще, [что] «сторонники [ложно понятого] Атмана не достигают высшей ступени Шивы». Далее, [если понимать] под «всеми даршанами» совокупность знаний о внешнем («голубое») и внутреннем («счастье»), [то они], оказываясь на самом деле «прекращением», становятся для Того, кто имеет сущность сгустка сознания-блаженства, средствами Его самопроявления. Таким образом, всегда, когда внешняя форма [сознания] приходит к своей основе, внешние объекты исчезают. "),a("p",{eng:""},"Успокоение во внутреннем [приносит] стабильность; затем континуальность сознания развертывается заново. Так эта великолепная Турия Сознания, соединяющая воедино творение, сохранение и растворение, вечно сверкает, то разворачивая таттвы в творении, то сворачивая их, и всегда оставаясь и полной, и производящей, и той и другой [сразу], и ни той и не другой [сразу]. "),a("p",{eng:""},"Как говорится в Пратьябхиджня-трике, «когда [Шакти] поглощает объекты, [Она] обретает полноту». И эта благодатная [Шакти] шаг за шагом ведет к своей сущности подражающего ей верного. Если Атман существ имеет [такое] величие, почему, спрашивается, он фрагментарен, стянут узами, облеплен нечистыми таттвами, блуждает в сансаре? На это отвечают: ")],-1),a("div",{class:"stanza"},[a("a",{href:"#s9",id:"s9",class:"num"},"9"),a("p",{sans:""},"चिद्वत्तच्छक्तिसङ्कोचान्मलावृतः संसारी ॥९॥"),a("p",{trans:""},"Cidvattacchaktisaṅkocānmalāvṛtaḥ saṁsārī ॥9॥"),a("p",null,[n("Вследствие этого сужения "),a("em",null,"(saṅkocāt)"),n(" Силы "),a("em",null,"(śakti)"),n(", та "),a("em",null,"(tad)"),n(" Полнота Сознания "),a("em",null,"(cit-vat)"),n(", [превращается в] сансарическое существо "),a("em",null,"(saṁsārī)"),n(", скованное "),a("em",null,"(avṛtaḥ)"),n(" узами "),a("em",null,"(mala)"),n(".")]),a("p",{eng:""},[a("strong",null,"Комментарий"),a("br"),n(" Когда Парамешвара, имеющий «сущность Сознания», по собственной Воле останавливая расширение неразличения, производит различение, тогда «эти его иччха и другие шакти», хотя [по существу] они и неограниченны, становятся ограниченными. Тогда и появляется это «сансарическое существо, отягощенное узами». Тогда иччха-шакти, будучи по природе своей не встречающей помех Абсолютной Волей, становится ограниченной, и это есть анава-мала, [которая состоит] в понимании [себя] как несовершенного. Джняна-шакти, постепенно ограничивая себя в мире обособленности, редуцирует свое всезнание до знания ограниченного. Окончательно суживаясь, она приобретает комплекс внутренних органов, [прежде всего] интеллект, [а также] способность восприятия, и отягощается майия-малой, т. е. видением всех объектов как различных. Крия-шакти постепенно в мире разделений редуцирует [свою способность] неограниченного действия до ограниченного. Начиная сужение с принятия органов действия, в конце концов [она] очень сильно ограничивается, окутываясь карма-малой, которая состоит в совершении чистых и нечистых [деяний]. Таким образом, те шакти, которые ограничивают свои [аспекты] всемогущества, всезнания, совершенства, вечности, вездесущности, принимают соответственно аспекты фрагментарности, ограниченного знания, страстности, времени и упорядоченности. Поэтому и называется этот [человек], лишенный Шакти, сансарным. Однако если его шакти раскроются, тогда [он] – сам Шива. ")]),a("p",{eng:""},"Тут возникает вопрос: можно ли узнать Шиву по какому-нибудь признаку, находясь даже в таком вот сансарическом состоянии? «Можно», [отвечаем], и вот почему – ")],-1),a("div",{class:"stanza"},[a("a",{href:"#s10",id:"s10",class:"num"},"10"),a("p",{sans:""},"तथापि तद्वत्पञ्चकृत्यानि करोति ॥१०॥"),a("p",{trans:""},"Tathāpi tadvatpañcakṛtyāni karoti ॥10॥"),a("p",null,[n("Но даже "),a("em",null,"(api)"),n(" в этом состоянии "),a("em",null,"(tathā)"),n(" [индивид] совершает "),a("em",null,"(karoti)"),n(" те же пять "),a("em",null,"(pañca)"),n(" действий "),a("em",null,"(kṛtyāni)"),n(", что и "),a("em",null,"(vat)"),n(" То [Высшее Я] "),a("em",null,"(tad)"),n(".")]),a("p",{eng:""},[a("strong",null,"Комментарий"),a("br"),n(" Здесь различие системы трика от брахмавады заключается в том, что Бхагаван, природа которого состоит в Cознании, всегда является причиной пяти действий, о чем говорится в Сваччханда-[тантре] и в других священных текстах, а именно: «[Поклоняюсь] Богу, [который] вызывает творение, поглощение, самосокрытие, сохранение, ниспосылание милости и [который] удаляет [грехи тех, кто] поклонился [ему]». И как Бхагаван в процессе «нечистого пути» является причиной творения и других манифестаций Своей сущности, так даже на уровне сансары, когда Его Сила Сознания ограничена, исполняет Он «пять действий». Говорится [в Пратьябхиджня-карике]: «таким образом, даже в эмпирическом мире Господь, входя в тело и т. п., заставляет объекты появиться вовне по Своей воле, хотя они, в сущности, находятся в Нем». ")]),a("p",{eng:""},"В том же произведении сказано, что когда Великий Ишвара, форма которого – Сознание, вступает в сферу тела, праны и т. п. и по причине обращенности ко внешнему проявляет объекты, ограниченные пространством, временем и т. д., типа голубого [цвета] и др., это [называется] актом творения; в отношении к явлениям объектов в другом пространстве, времени и т. д. – это растворение; в отношении к появлению голубого и т. п. [и их сохранения] – это поддержание; "),a("p",{eng:""},"в отношении к [их] появлению как иного – сокрытие; в отношении к [их] идентичности Свету Сознания – милость. А каким образом Бхагаван является причиной пятерицы [божественных] действий, это мною подробно разобрано в Спандасандохе. "),a("p",{eng:""},"Таким образом, если искренний практикующий постоянно фиксирует [свое] внимание на функционирование этой пятеричности в самом себе, Великая Ишвари обязательно откроет [ему] Себя. Поэтому те, кто всегда сосредоточен на этой [божественной пятирице], являются освобожденными при жизни, знающими Сущность, проявляющую Себя во вселенной – так [гласит] сакральная [традиция]. "),a("p",{eng:""},"Те же, кто так не [практикует], кто считает объекты чувств обособленными, всегда остаются во тьме. "),a("p",{eng:""},"Но не только так [функционирует] пятерица [божественных] действий – есть и другой, тайный способ. "),a("p",{eng:""},"О нем [автор] говорит: ")],-1),a("div",{class:"stanza"},[a("a",{href:"#s11",id:"s11",class:"num"},"11"),a("p",{sans:""},"आभासनरक्तिविमर्शनबीजावस्थापनविलापनतस्तानि ॥११॥"),a("p",{trans:""},"Ābhāsanaraktivimarśanabījāvasthāpanavilāpanatastāni ॥11॥"),a("p",null,[n("Они "),a("em",null,"(tāni)"),n(" такие "),a("em",null,"(taḥ)"),n(": проявление "),a("em",null,"(ābhāsana)"),n(", наслаждение "),a("em",null,"(rakti)"),n(", самосознание "),a("em",null,"(vimarśana)"),n(", сеяние семени "),a("em",null,"(bīja-avasthāpana)"),n(", растворение "),a("em",null,"(vilāpana)"),n(".")]),a("p",{eng:""},[a("strong",null,"Комментарий"),a("br"),n(" «Исполняет пять действий» – это связано с предыдущим [изложением]. С точки зрения высшей доктрины, [объекты] проявляются [в мире] благодаря постепенному развертыванию богини зрения и [богинь] других [органов чувств]. Богиня поддержания действует тогда, когда [Я] наслаждается объектами уровня творения, [находящимися] определенное время в поле [его] восприятия. ")]),a("p",{eng:""},"Через вимаршу, или, иначе, всплеск изумления, он [объект] исчезает. Как говорит Рама, «[целую] гору различий, которую не в силах одолеть даже [практикующие] алмазоподобное сосредоточение, способны воспринять как неотделимую от себя и тем самым разрушить те, чья мощь проистекает от преданности Тебе». "),a("p",{eng:""},"Однако, если в период растворения этот [объект опыта, мир] сохраняет внутри различные следы, вроде сомнения и т. п., [это значит, что] сансара, остающаяся в зародыше, проявится вновь, что приведет к сокрытию [истинного Я]. "),a("p",{eng:""},"В то же время, когда этот [объект] воспринимается как нечто внутреннее, как иное, чем [таким образом] переживаемое, то в процессе хатха-паки и приведения к тождеству с [Я] [этот объект] приобретает сущность огня Сознания, и тогда он [йогин], достигая совершенства, обретает божественную милость. "),a("p",{eng:""},"Но эта пятерица божественных действий, хотя и всегда возможна для любого [существа], не проявится без помощи святого наставника. Поэтому нужно почтительно служить святому наставнику, дабы эта [пятерица проявилась]. Тот же, у кого из-за отсутствия помощи наставника не появляется истинное знание, становится сбит с толку своими же шакти, ибо подлинная их суть закрыта [от него]. Поэтому сказано: ")],-1),a("div",{class:"stanza"},[a("a",{href:"#s12",id:"s12",class:"num"},"12"),a("p",{sans:""},"तदपरिज्ञाने स्वशक्तिभिर्व्यामोहितता संसारित्वम् ॥१२॥"),a("p",{trans:""},"Tadaparijñāne svaśaktibhirvyāmohitatā saṁsāritvam ॥12॥"),a("p",null,[n("Cостояние сансары "),a("em",null,"(saṁsāritvam)"),n(" – омрачение "),a("em",null,"(vyāmohitatā)"),n(" собственными "),a("em",null,"(sva)"),n(" энергиями "),a("em",null,"(śaktibhiḥ)"),n(" из-за незнания "),a("em",null,"(aparijñāne)"),n(" этих "),a("em",null,"(tad)"),n(" [пяти действий].")]),a("p",{eng:""},[a("strong",null,"Комментарий"),a("br"),n(" «Этих» – т. е. всегда происходящих пяти божественных действий; «незнание» означает отсутствие «света», из-за нераскрытия Его силы, источника нисхождения Шакти; «омрачение собственными энергиями» – это когда из-за сомнений, возникающих от различных мирских представлений и [содержания] ученых трактатов, появляется «состояние сансары». В Сарвавирабхаттараке сказано: ")]),a("p",{eng:""},"«Из-за незнания у людей [появляются] сомнения, а от них – рождение и смерть». "),a("p",{eng:""},"И еще: «Все мантры состоят из фонем, все же фонемы имеют природу Шивы». "),a("p",{eng:""},"Таким образом, неотделенная от Света Сознания, в виде вечно звучащей Великой мантры, в форме Сознавания совершенного «Я», эта Сила Высшей Речи, содержащая в себе все собрание шакти от а- до кша-, манифестирует в той же мере ступень [ограниченного] субъекта посредством последовательности таких этапов речи, как Видящая, Срединная и т. п. "),a("p",{eng:""},"При этом Ее сущность, Ее высшая форма скрывается в субъекте, объятом майей, производя [в нем] то и дело, вновь и вновь, различающую деятельность, [т. е.] выделение объектов неопределенных и обособленных. Будучи по своей сути чистой и превосходящей различения, Она [все-таки] скрыта этой [различающей деятельностью]. А потому омраченный человек, еще и ослепленный различными шакти в форме ка- и т. д., которыми правят Брахми и другие богини, принимает за Я [такие] ограниченные и нестойкие [сущности], как тело, прана и т. д. "),a("p",{eng:""},"Брахми и другие богини на уровне Раба в отношении к различающимся объектам производят творение и сохранение, а к неразличающимся – уничтожение, на уровне же Господина – уничтожение различия и порождение-сохранение неразличения. Постепенно уменьшая различение, [они] открывают ступень Великого неразличения, дают войти в благословенную бхайрава-мудру. "),a("p",{eng:""},"«Всякий, [постигающий] мое величие в этом [мире, обретает такое же] состояние; [видя] вселенную как [проявление] Я, [он обретает] состояние Махеши, даже если различения продолжают [в нем возникать]». Таким образом, [они] проявляют шакти чистого различения, погруженную в Сознание, Блаженство и Свободу. Состояние же сансарности, как отмечалось выше, состоит в омрачении, которое причиняют собственные шакти [индивида]. Далее. Эта Бхагавати, Шакти-Высшее Сознание, известна и как Вамешвари, потому что Она «извлекает» вселенную, а также потому, что [Она следует] курсом сансары, противоположным [Абсолюту]. "),a("p",{eng:""},"[Принимая] на стадии Раба формы «небесную», «внутреннюю», «пространственную» и «существующую», Она раскрывает себя [соответственно] как ограниченный субъект, как внутренний орган, как органы внешнего действия, как живые существа. Покоясь в состоянии шуньи, скрыв свою высшую сущность «небесного сознания», [Она] светится в форме группы «небесной», состоящей из шакти фрагментарности и т. д., т. е. имеющей природу ограниченной деятельности и т. д. Скрывая свою высшую сущность, состоящую в обретении неразличения и т. д., [Она] является в виде «внутренней» группы, т. е. в форме богини, [управляющей] психическим субъективным комплексом, который состоит, [во-первых] из установки на различение, [во-вторых] из отождествления с различным, [в-третьих] из видении вещей как различных. "),a("p",{eng:""},"Как божество внешних чувств, основанных на различении, [Она] становится группой «пространственности», скрывая свою глубинную суть, состоящую в манифестации неразличения и т. п. Скрывая сущность Всего и омрачая сердца зависимых существ, [Она] появляется и через группу «сущего» в форме объектов познания, которые повсеместно выступают как обособленные феномены. "),a("p",{eng:""},"На стадии же Господина Она вибрирует как «небесное сознание», сущностью которого является всеобъемлющая деятельность и т. д.; как «внутреннее движение», сущность ее – установка на неразличение и т. п.; как «пространственное движение», ее сущность – восприятие неразличения и т. д.; как «сущее», ее сущность – объекты, идентичные [субъекту, подобно] собственным членам. [Всем этим Она] открывает сердце Господина. "),a("p",{eng:""},"Бхатта Дамодара, заповедуя неподдельное уважение, благодаря своему врожденному восторгу, в своих Вимуктаках говорит: «Вамешвари и другие [богини, пребывая в сферах] постигающего субъекта, его внешних и внутренних органов, в объективном мире, [делают индивида] совершенным, освобождая [его] посредством истинного знания, [либо делают его] зависимым, опутывая узами неведения». Таким образом, состояние сансарности состоит в омраченности своими собственными энергиями. Далее, Высший Господь, чья сущность – Сознание, наделен постоянной, целостной, деятельностной по своей природе Творящей Силой, имеющую сущность пульсации Божественного Света. "),a("p",{eng:""},"Когда Она, скрывая свою истинную суть на стадии Раба, причиняет омрачение через циклы прана-, апана- и самана-шакти; через ступени бодрствования, легкого и глубокого сна; через фазы плотного тела, дыхания и тонкого тела, тогда эта омрачающая деятельность [приводит] к состоянию сансары. "),a("p",{eng:""},"Когда же Она заставляет появиться в срединном канале удана-шакти, имеющую характер состояния турья; вьяна-шакти, имеющую сущность пронизывания вселенной и совпадающую с состоянием турьятита, — [обе они наделены природой] эссенции Сознания и Блаженства – тогда, даже обладая телом и т. д., индивид становится существом, достигшим освобождения при жизни, Господином по сути. Таким образом, состояние омраченности собственными энергиями было объяснено трояким образом. "),a("p",{eng:""},"В девятой сутре, o «Полнотe Сознания», говорилось в таком смысле, что Свет Сознания, по своей воле сужаясь, [делается] сансарином; здесь же сказано, с другой точки зрения, что это состояние сансарности вызывается омрачением [индивида] своими собственными энергиями. Кроме того, [если только индивид], даже наделенный ограниченными силами, даже имея праническое тело и другие [помехи], не омрачен собственными энергиями, то, согласно священной традиции, он воспринимается как «Высший Ишвара во плоти», как сам Благословенный Шива. Как [говорится] в агаме: «В человеческом теле скрытно пребывают Высшие Божества». Или также в комментарии на Пратьябхиджню: «Те, кто физическое тело или даже кувшин и т. п. видит как форму Шивы, состоящую из тридцати шести таттв, достигают совершенства». Для того, чтобы показать истинное понимание, смысл вышеупомянутой сутры был перевернут: ")],-1),a("div",{class:"stanza"},[a("a",{href:"#s13",id:"s13",class:"num"},"13"),a("p",{sans:""},"तत्परिज्ञाने चित्तमेवान्तर्मुखीभावेन चेतनापदाध्यारोहाच्चितिः ॥१३॥"),a("p",{trans:""},"Tatparijñāne cittamevāntarmukhībhāvena cetanāpadādhyārohāccitiḥ ॥13॥"),a("p",null,[n("[Обретая] полное знание "),a("em",null,"(parijñāne)"),n(" об этом "),a("em",null,"(tad)"),n(", само "),a("em",null,"(eva)"),n(" индивидуальное сознание "),a("em",null,"(cittam)"),n(", через "),a("em",null,"(bhāvena)"),n(" обращение вовнутрь "),a("em",null,"(antarmukhī)"),n(", восходя "),a("em",null,"(adhyārohāt)"),n(" до ступени "),a("em",null,"(pada)"),n(" чистого Сознания "),a("em",null,"(cetana)"),n(", [превращается в] универсальное сознание "),a("em",null,"(citiḥ)"),n(".")]),a("p",{eng:""},[a("strong",null,"Комментарий"),a("br"),n(" [Содержание] данной сутры уже было подробно освещено в предыдущей сутре, с точки зрения познаваемых объектов. Теперь [то же самое] объясняется с точки зрения слов. «Об этом» – т. е. об исполнении самим Я пятерицы [божественных] действий; [обретение] «полного знания» – это удаление причин, вызывающих недостаточное знание, прекращение омраченности собственными энергиями, обретение Спонтанной Воли. «Индивидуальное сознание», о котором речь шла прежде, отбрасывает [ведущее] к ограничению внешнее становление, и «через внутреннее восстановление восходит до ступени четана», т. е. [оно], шаг за шагом растворяя ограничения на пути к состоянию Субъекта, достигая своей сущностной природы, становится «универсальным сознанием». Оно вступает [здесь] на высшую ступень Сознания – таков смысл. ")]),a("p",{eng:""},"Могут спросить: если у Чит-шакти в [ее] аспекте высшей реальности отсутствует всяческая разделенность, то не должна ли она сохранять эту форму и на ступени майя, подобно тому, как солнце, даже затененное облаками, сохраняет состояние светимости? На этот вопрос [автор] отвечает: ")],-1),a("div",{class:"stanza"},[a("a",{href:"#s14",id:"s14",class:"num"},"14"),a("p",{sans:""},"चितिवह्निरवरोहपदे छन्नोऽपि मात्रया मेयेन्धनं प्लुष्यति ॥१४॥"),a("p",{trans:""},"Citivahniravarohapade channo'pi mātrayā meyendhanaṁ pluṣyati ॥14॥"),a("p",null,[n("Огонь "),a("em",null,"(vahniḥ)"),n(" Высшего Сознания "),a("em",null,"(citi)"),n(", даже "),a("em",null,"(api)"),n(" скованный "),a("em",null,"(channaḥ)"),n(" нисхождением [на низжие] "),a("em",null,"(avaroha)"),n(" ступени "),a("em",null,"(pade)"),n(", частично "),a("em",null,"(mātrayā)"),n(" сжигает "),a("em",null,"(pluṣyati)"),n(" топливо "),a("em",null,"(indhanam)"),n(" познаваемых [объектов] "),a("em",null,"(meya)"),n(".")]),a("p",{eng:""},[a("strong",null,"Комментарий"),a("br"),n(" «Высшее Сознание» [названо] «Огнем» потому, что Оно поглощает вселенную. Хотя и «нисходя» до уровня субъекта майи и будучи «покрыто» [майей], Оно, в силу своей Спонтанной Воли, «частично» «зажигает» «топливо познаваемых [объектов]», [таких, как] голубое, желтое и т. д., подобно тому, как огонь [может зажечь топливо], даже если он покрыт густым слоем пепла. Смысл слова «частично» таков: сжигая [познаваемое], Оно поглощает его не целиком, но частично, [поэтому Оно вновь] вызывает его появление через впечатление о нем. ")]),a("p",{eng:""},"В том, что способность поглощения есть у всех субъектов, можно удостовериться на собственном опыте. Как было сказано достопочтенным Утпаладевой в его стотрах, «[Все] существа, даже [такие] запредельные, как Брахма, Индра и Вишну, существуют через поглощение [объектов]; а потому, о Боже, да почту я тебя, формой которого выступает вселенная». "),a("p",{eng:""},"Далее, когда [практикующий], используя способы (практики) «эманации», осуществляет раскрытие божественных органов чувств, а [используя способы (практики)] «растворения», [их] сокрытие, тогда: ")],-1),a("div",{class:"stanza"},[a("a",{href:"#s15",id:"s15",class:"num"},"15"),a("p",{sans:""},"बललाभे विश्वमात्मसात्करोति ॥१५॥"),a("p",{trans:""},"Balalābhe viśvamātmasātkaroti ॥15॥"),a("p",null,[n("Обретая "),a("em",null,"(lābhe)"),n(" Силу "),a("em",null,"(bala)"),n(", [Он] сводит "),a("em",null,"(karoti)"),n(" вселенную "),a("em",null,"(viśvam)"),n(" к Себе "),a("em",null,"(ātmasāt)"),n(".")]),a("p",{eng:""},[a("strong",null,"Комментарий"),a("br"),n(" Сознание [становится] Силой, [когда Оно] удаляет покровы телесной, пранической и других [оболочек, и когда Оно] Свою исконную сущность приводит к полному раскрытию. Как было сказано, «Тогда, стремясь к [этой] Силе, мантры …» Таким образом, «обретение Силы» [означает] обращение [практикующего] к раскрытию собственной сущности: [он] «сводит вселенную к себе», от стихии земля до [ступени] Садашива, делая [ее] неотличимой от самого себя. ")]),a("p",{eng:""},"Об этом в характерном стиле говорили в Крама-сутрах древние учителя: «Подобно тому, как разгоревшийся огонь сжигает топливо, [практикующий] должен сжечь оковы в виде объектов чувств». В Пратьябхиджне сказано: «Неверно было бы утверждать: ‘[Когда Чити] сводит к себе вселенную, [ее] всеохватность временна. Как можно [в таком случае] говорить [о всеохватности]?’. Только благодаря возникновению и исчезновению тела и т. д. она, [всеохватность], кажется временной. В реальности же это временность появления [всеохватности Чити] зависит от тела и т. д., которые реализуются благодаря спонтанности самой Чити. "),a("p",{eng:""},"Однако эта [всеохватность Чити] всегда находится в проявленности: в противном случае даже тело и т. д. не [смогли бы стать] проявленными. Поэтому практика рекомендуется не ради достижения состояния субъектности, которая по своей природе всегда светозарна, но для того, чтобы удалить ложную идентификацию субъекта с телом и т. д.». "),a("p",null,"И вот:")],-1),a("div",{class:"stanza"},[a("a",{href:"#s16",id:"s16",class:"num"},"16"),a("p",{sans:""},"चिदानन्दलाभे देहादिषु चेत्यमानेष्वपि चिदैकात्म्यप्रतिपत्तिदार्ढ्यं जीवन्मुक्तिः ॥१६॥"),a("p",{trans:""},"Cidānandalābhe dehādiṣu cetyamāneṣvapi cidaikātmyapratipattidārḍhyaṁ jīvanmuktiḥ ॥16॥"),a("p",null,[n("[Состояние] освобожденности при жизни "),a("em",null,"(jīvanmuktiḥ)"),n(" [возникает] при достижении "),a("em",null,"(lābhe)"),n(" блаженства "),a("em",null,"(ānanda)"),n(" Сознания "),a("em",null,"(cit)"),n(" даже "),a("em",null,"(api)"),n(" при переживании "),a("em",null,"(cetyamāneṣu)"),n(" тела "),a("em",null,"(deha)"),n(" и т. д. "),a("em",null,"(ādiṣu)"),n(", если внимание "),a("em",null,"(pratipatti)"),n(" твердо установилось "),a("em",null,"(dārḍhyam)"),n(" на тождестве "),a("em",null,"(aikātmya)"),n(" с Сознанием "),a("em",null,"(cit)"),n(".")]),a("p",{eng:""},[a("strong",null,"Комментарий"),a("br"),n(" Когда вселенная переживается как идентичная с Я в опыте самавеши, т. е. «при достижении блаженства Сознания», то даже в бодрствующем состоянии, в котором плотное и тонкое тела, внешняя и внутренняя объективность и т. д. становятся ограничивающими условиями, [возможна] «твердая установка внимания на тождестве с Сознанием», т. е. есть устойчивое, длительное переживание единства с Сознанием. ")]),a("p",{eng:""},"Это [cостояние идентичности с Сознанием называется] «освобожденностью при жизни», т. е. освобождением того, кто наделен жизнью и дыханием: "),a("p",{eng:""},"узнавая собственную сущность, [индивид] полностью разрушает оковы ограниченности. "),a("p",{eng:""},"Как сказано в Спандашастре, «Тот, кто обладает знанием этого, кто видит мир как игру [Бога], кто всегда сосредоточен [на Высшем], является освобожденным при жизни, в этом нет сомнения». Каким же образом возможно достижение блаженства Сознания? На это следует ответ: ")],-1),a("div",{class:"stanza"},[a("a",{href:"#s17",id:"s17",class:"num"},"17"),a("p",{sans:""},"मध्यविकासाच्चिदानन्दलाभः ॥१७॥"),a("p",{trans:""},"Madhyavikāsāccidānandalābhaḥ ॥17॥"),a("p",null,[n("Достижение "),a("em",null,"(lābhaḥ)"),n(" блаженства "),a("em",null,"(ānanda)"),n(" Абсолютного Сознания "),a("em",null,"(cit)"),n(" [возможно] через развитие "),a("em",null,"(vikāsāt)"),n(" центра "),a("em",null,"(madhya)"),n(".")]),a("p",{eng:""},[a("strong",null,"Комментарий"),a("br"),n(" «Центром» [называется] Богиня Сознания, [когда Она понимается как] глубинная реальность всего, [когда очевидно, что] никакая вещь не сможет существовать, не будучи привязана к Ней как к своей основе. И хотя это так, Она на ступени майя утаивает свою сущность, в соответствии с идеей, что «на уровне праны Сознание обращено вовне». ")]),a("p",{eng:""},"Таким образом, Она становится прана-шакти, спускаясь постепенно на уровни ума, тела и т. д., и рассеивается в потоки тысяч нади. Но даже здесь Она выражена прежде всего как срединный канал, имея местопребыванием Брахмана в форме прана-шакти, [которая тянется] от «дыры Брахмы» вплоть до ануса, подобно главной жилке листа палаши. "),a("p",{eng:""},"Поэтому именно [из Нее] возникают все психические состояния и [в Ней] они исчезают. Тем не менее Ее сущностная природа остается сокрытой для зависимого субъекта. Когда же Богиня-Сознание, будучи внутренним центром всех вещей, развивается путем вышеописанных практик соединения или если развивается в виде внутреннего центра, как предписывается, канал Брахмы, тогда «при развитии центра» происходит «достижение», т. е. обретение «блаженства Сознания»."),a("p",{eng:""},"И это, как говорится выше, ведет к освобождению. Имея в виду способы развития центра, [автор] говорит: ")],-1),a("div",{class:"stanza"},[a("a",{href:"#s18",id:"s18",class:"num"},"18"),a("p",{sans:""},"विकल्पक्षयशक्तिसख़ोचविकासवाहच्छेदद्यन्तकोटिनिभालनादय इहोपायाः ॥१८॥"),a("p",{trans:""},"Vikalpakṣayaśaktisaṅkocavikāsavāhacchedadyantakoṭinibhālanādaya ihopāyāḥ ॥18॥"),a("p",null,[n("Вот "),a("em",null,"(iha)"),n(" средства "),a("em",null,"(upāyāḥ)"),n(": растворение "),a("em",null,"(kṣaya)"),n(" мыслей "),a("em",null,"(vikalpa)"),n("; сжатие "),a("em",null,"(saṅkoca)"),n(" и разворачивание "),a("em",null,"(vikāsa)"),n(" шакти "),a("em",null,"(śakti)"),n("; уничтожение ветров "),a("em",null,"(vāha-cheda)"),n(" и т.д. "),a("em",null,"(ādi)"),n("; созерцание "),a("em",null,"(nibhālana)"),n(" начала и конца "),a("em",null,"(antakoṭi)"),n(" и т. д. "),a("em",null,"(ādayaḥ)")]),a("p",{eng:""},[a("strong",null,"Комментарий"),a("br"),n(" При развитии центральной шакти «средства здесь [таковы]: растворение различения и т. п.». ")]),a("p",{eng:""},"Ранее говорилось, что раскрытие Сознания, которое суть центр всего, достигается через следование вышеупомянутому пятеричному комплексу божественных функций. Однако [здесь] говорится о другом средстве. Есть легкое средство, с помощью которого рассеиваются тяготы, [возникающие от] трудоемких духовных дисциплин, например, пранаямы, мудры, бандхи и др. [Если] сознание [практикующего] сосредоточивается на Сердце, рассеивая, посредством вышеуказанного метода, различающие мысли, которые препятствуют пребыванию в Самости, [если он] ни о чем не рефлексирует, выходя на уровень неразличения, то он имеет цельное восприятие Чит как истинного субъекта, независимого от тела и т. п., и поэтому уже за короткое время он поднимается на уровни турья и турьятита, ступени раскрытия [Я]. "),a("p",{eng:""},"Как говорится в Шри Пратьябхиджне, «благодаря сосредоточенности сознания, уничтожающем различения, достигается состояние Ишвары». Также и в Шри Спанде: «Когда [в сознании] исчезнет волнение, тогда будет обретено высшее состояние». "),a("p",{eng:""},"И в Шри Джнянагарбхе: «О Мать, стойкие люди, которые перестают [цепляться] за все то, что порождено различающей мыслью и освобождаются от чувственных влечений, приходят к чистому состоянию. Это высшее состояние обретается ими сразу, по твоей милости, которая [по своей природе есть] постоянное истечение несравненной [божественной] амриты». "),a("p",{eng:""},"Об этом средстве, излагаемом в Пратьябхиджне, говорится как о высшем. Но мы также описываем сжатие и т. п. Шакти, несмотря на то, что об этом не говорилось в Пратьябхиджне, [описываем], поскольку это находится в духе священной традиции. Когда описывается многообразие [средств], каждый [может] выбрать из них какое-нибудь [для себя]. «Сжатие Шакти»— [означает] процесс отвлечения от того, что проходит через врата чувств, и обращение к Я. "),a("p",{eng:""},"Как было сказано в первой мантре четвертой главы Катхавалли-упанишады, принадлежащей к Атхарванике: «Самосущий проделал внешние отверстия [для чувств], и поэтому [обычный человек] смотрит вовне, а не внутрь себя. Но мудрый, желая вкусить бессмертия, созерцает внутреннее Я с закрытыми глазами». Или же [сжатие Шакти может обозначать]— стягивание в сердцевину всех [Ее] внешних проявлений, подобно тому как черепаха в случае опасности [быстро] прячется в панцире. "),a("p",{eng:""},"Как сказано: «когда [происходит] возвращение, [то возникает] состояние постоянства». "),a("p",{eng:""},"«Развитие Шакти», сокрытой внутри, бывает благодаря внезапному отверзанию всех органов чувств. "),a("p",{eng:""},"«Цель следует [видеть] внутри; ведь внешнее поле зрения [удерживается без труда], независимо от того, закрыты или открыты [глаза]». "),a("p",{eng:""},"При вхождении в бхайрави-мудру [не исчезает] чувственное восприятие. "),a("p",{eng:""},"Как говорится в Какшья-стотре, «Посредством воли одновременно и повсюду направляя все силы чувств, таких как зрение и т. д., на соответствующие объекты и закрепляясь в сердцевине [чувств] словно золотой столб, ты, [Шива], один являешь себя как основание вселенной». "),a("p",{eng:""},"Также и почтеннейший Каллата утверждал: «Осуществление этого [происходит] благодаря трансформации [сознания, направленного на] формы и т. п.». "),a("p",{eng:""},[n("Что касается сжатия "),a("em",null,"(saЃkoca)"),n(" и развития "),a("em",null,"(vikаsa)"),n(" Шакти, [то викаса означает] практику расширения и остановки [Шакти] на стадии постепенного восхождения кундалини через последовательную регуляцию потоков тонкой прана-шакти в ноздрях, [а также] через [сосредоточение] на пространстве между бровей. На стадии нисходящей кундалини, [стремящейся к] шестому отверстию, при распрямлении [прана-] шакти открывается вход в его корень, вершину и середину. В Виджнянабхайраве (68) говорится: «Следует направлять сознание, полное счастья, в середину между «огнем» и «ядом», [и тогда испытаешь] либо отрешенность от всего, либо «наполненность ветром» через блаженство сексуального соединения ». ")]),a("p",{eng:""},"Здесь «огонь» означает ступень сжатия, достигаемую через процесс вхождения [праны] в [основу] индивидуальности. Состояние «яда» – это ступень развития, посредством техники прасара, согласно этимологии слова «яд», которое означает «пронизывание». Оба «потока» – это прана и апана, [связанные соответственно с] левым и правым каналами. «Разделение» [означает] прекращение, т. е. остановку во внутреннем, в сердце, посредством произнесения согласных звуков ка, ха и т. д. "),a("p",{eng:""},"Как говорится в Джнянагарбхе, «Когда ум находится под контролем, когда оба нади, простирающиеся по обеим сторонам [тела, успокаиваются] под воздействием согласного звука ка, когда ты, [о Мать], рассеиваешь густую тьму, в сердце-лотосе появляется этот росток знания, который способен вызвать состояние Парамеши даже у пашу». "),a("p",{eng:""},"«Первый предел» – это сердце, «последний предел» – область величиной в двенадцать пальцев. «Нибхалана» означает практику сосредоточения ума на увеличивающейся и исчезающей пране [в пространстве между] ними. Как говорится в Виджнянабхайраве (49), «О дивная, тот, кто погрузился в глубину сердца, кто вошел в лотос-сердце и отвлек [свои] мысли от [всего] остального, достигает высшего блаженства». И еще: «[Если] постоянно и при любых условиях удерживать сознание на области двадашанты, колебания ума постепенно будут убывать, так что за несколько дней [индивид] достигнет неописуемого».Слово «и так далее» относится к практике в состоянии унмеша. Как говорится в Спанде, «то, что постигается как унмеша, можно наблюдать на своем собственном [опыте]». Это же относится и к вкушению и т. д. приятных объектов. Как сказано в достопочтенной Виджнянабхайраве (72 – 74), «Когда [практикующий] ощущает удовольствие, возникающее от еды и напитков, ему следует сосредоточиться на совершенстве [этого] состояния, [и тогда] он может преисполниться великого блаженства. Когда сознание йогина сосредоточено на ни с чем не сравнимом удовольствии от песни и других [подобных] объектов, тогда это его сознание становится одним целым с ними. От чего бы манас не испытывал наслаждение, следует концентрироваться на этом, и тогда проявится высшее блаженство». "),a("p",{eng:""},"Таким образом, любое другое созерцание своего «я» может привести к состоянию высшего блаженства. Здесь [выражение] «и так далее» [указывает] на методы развития среднего центра. При развитии среднего центра достигается состояние сознания-блаженства. Это высшее йогическое [состояние] есть [состояние] самадхи, [иначе называемое] самавешей, самапатти и другими терминами. "),a("p",{eng:""},"О методе, [который позволяет] сделать его устойчивым, говорится [так]: ")],-1),a("div",{class:"stanza"},[a("a",{href:"#s19",id:"s19",class:"num"},"19"),a("p",{sans:""},"समाधिसंस्कारवति व्युत्थाने भूयो भूयश्चिदैक्यामर्शान्नित्योदितसमाधिलाभः ॥१९॥"),a("p",{trans:""},"Samādhisaṁskāravati vyutthāne bhūyo bhūyaścidaikyāmarśānnityoditasamādhilābhaḥ ॥19॥"),a("p",null,[n("В состоянии вьюттхана "),a("em",null,"(vyutthāne)"),n(", при сохранении остаточных следов "),a("em",null,"(saṁskāravati)"),n(" самадхи "),a("em",null,"(samādhi)"),n(", возможно достижение "),a("em",null,"(lābhaḥ)"),n(" непрерывного "),a("em",null,"(nitya-udita)"),n(" самадхи "),a("em",null,"(samādhi)"),n(" благодаря все более и более "),a("em",null,"(bhūyas bhūyas)"),n(" глубокой концентрации "),a("em",null,"(āmarśāt)"),n(" на единстве "),a("em",null,"(aikya)"),n(" с Абсолютным Сознанием "),a("em",null,"(cit)"),n(".")]),a("p",{eng:""},[a("strong",null,"Комментарий"),a("br"),n(" Опытный йогин, достигший самавеши, из-за остаточных следов сладостного состояния самадхи испытывает и в состоянии вьюттханы радостное головокружение, как у опьяненного. Раз за разом обращаясь к глубинам самого себя и посредством нимилана-самадхи концентрируясь на своем единстве с Чит, он видит, как мир объективных сущностей испаряется в небе-сознании, словно груда облаков в осеннем [небе]. [Таким образом], даже во вьюттхане он [не перестает быть] единым с полнотой самадхи. ")]),a("p",{eng:""},"Как говорится в Крамасутрах, «Посредством крамамудры, имеющей сущностью погруженность в себя, садхака остается в сосредоточенности [даже] при внешнем восприятии. [Впрочем], в этой [практике], благодаря силе сосредоточенности, сначала происходит «вхождение» [сознания] из внешнего во внутреннее, [а затем] из внутреннего во внешнее. Таким образом, эта мудракрама [сочетает] и внешнее, и внутреннее». Таков здесь смысл. Запредельная энергии Чити-шакти делает своим собственным последовательный цикл творения, сохранения, растворения, который [в свернутом виде] содержится в высшем Я. «Садхака» – это высший тип йогина, который «посредством крамамудры», по своей сути являющейся совершенным Я, «остается в сосредоточенности» на полноте Парашакти даже «при внешнем восприятии», т. е. [восприятии] чувственных объектов. Здесь через уподобление «внешнего», т. е. многообразия объектов чувств, «внутреннему», т. е. уровню высшего Сознания, достигается «вхождение» посредством самого процесса уподобления. "),a("p",{eng:""},"«Из внутреннего», т. е. из сущности Чити-шакти, которая аналогична внутреннему созерцанию, или «силе сосредоточенности», происходит сосредоточенное «вхождение» «во внешнее», в то, что имеет природу «этого», т. е. в мир объектов, через процесс овнешнения. [Иначе говоря], это расширение сосредоточенности благодаря оплотнению Сознания. "),a("p",{eng:""},"«[Одновременность] внешнего и внутреннего» означает вечную природу самавеши. "),a("p",{eng:""},"«Мудрой» [называется] благодать радости, имеющей сущность высшего блаженства, [а также] разрушение уз, [а также] запечатывание и удержание всего мира внутри запредельного бытия. "),a("p",{eng:""},"«Крамой» же называется причина последовательной манифестации творения и т. д., а также сама природа этой последовательности. "),a("p",{eng:""},"Теперь [автор] рассказывает о плодах достижения этого самадхи: ")],-1),a("div",{class:"stanza"},[a("a",{href:"#s20",id:"s20",class:"num"},"20"),a("p",{sans:""},"तदा प्रकाशानन्दसारमहामन्त्रवीर्यात्मकपूर्णाहन्तावेशात्सदा सर्वसर्गसंहारकारिनिजसंविद्देवताचक्रेश्वरताप्राप्तिर्भवतीति शिवम् ॥२०॥"),a("p",{trans:""},"Tadā prakāśānandasāramahāmantravīryātmakapūrṇāhantāveśātsadā sarvasargasaṁhārakārinijasaṁviddevatācakreśvaratāprāptirbhavatīti śivam ॥20॥"),a("p",null,[n("Тогда "),a("em",null,"(tadā)"),n(", при вступлении "),a("em",null,"(veśāt)"),n(" в совершенное "),a("em",null,"(pūrṇa)"),n(" Я "),a("em",null,"(ahantā)"),n(", сущностью "),a("em",null,"(sāra)"),n(" [которого] является блаженство "),a("em",null,"(ānanda)"),n(" Света Сознания "),a("em",null,"(prakāśa)"),n(", а природой "),a("em",null,"(ātmaka)"),n(" – великая "),a("em",null,"(mahā)"),n(" энергия "),a("em",null,"(vīrya)"),n(" мантры "),a("em",null,"(mantra)"),n(", всегда "),a("em",null,"(sadā)"),n(" происходит "),a("em",null,"(bhavati)"),n(" обретение "),a("em",null,"(prāptiḥ)"),n(" владычества "),a("em",null,"(īśvaratā)"),n(" над кругом "),a("em",null,"(cakra)"),n(" божеств "),a("em",null,"(devatā)"),n(" собственного "),a("em",null,"(nija)"),n(" Сознания "),a("em",null,"(saṁvid)"),n(", вызывающих "),a("em",null,"(kāri)"),n(" эманирование "),a("em",null,"(sarga)"),n(" и разрушение "),a("em",null,"(saṁhāra)"),n(" всего "),a("em",null,"(sarva)"),n(", и это Шива "),a("em",null,"(iti śivam)"),n(".")]),a("p",{eng:""},[a("strong",null,"Комментарий"),a("br"),n(" «Сущность высшего блаженства Света Сознания» означает сгусток Сознания и Наслаждения; ")]),a("p",{eng:""},"«энергия великой мантры» – одушевляющее начало всех мантр. «Совершенство» – это высшая вимарша, которая есть «Я», или естественное блаженство самосознания. Тем самым «при вступлении» – т. е. от Калагни до последней ступени вселенной достигается стабильное самадхи. "),a("p",{eng:""},"«Эманирование и разрушение» – различные [циклы] творения и растворения. Возобладание над «вызывающими это», а именно «обретение владычества над кругом божеств вечного Сознания» происходит у высших йогинов, [о которых здесь и] идет речь. Таков смысл сказанного. «Это» означает все это, имеющее сущность Шивы, такой [здесь можно сделать] вывод. "),a("p",{eng:""},"Здесь сущностью того, что познается, является познание. Сущностью же познания – субъекты познавательного опыта, направленные внутрь [себя]. "),a("p",{eng:""},"Далее, сущностью познавательных субъектов является Садашива-Ишвара, в котором исчезают заблуждения о телесном и других [элементах] и тело которого бесконечно. "),a("p",{eng:""},"Наконец, наивысшей сущностью последнего становится Сам достохвальный Господь, преисполненный блаженства сознания того, что весь проявленный мир представляет собой единое целое со Светом Сознания. Без вхождения в Свет Сознания, который есть высшая реальность, нет никакой манифестации [в этом мире]. "),a("p",{eng:""},"И Высший Господь полон потока блаженства, – из-за того, что Его сущность – сила самоестественности, из-за того, что Он абсолютно совершенен, из-за того, что Он лишен всех желаний, и из-за того, что Он пребывает в состоянии бесконечной джагадананды, в которую погрузил весь мир, состоящий из обозначающего и обозначаемого, [погрузил] посредством Силы Рефлексии, [направленной на] совокупность не относящихся к царству майи фонем от А до Кша. И поэтому вся эта громадная вселенная, начиная от буквы А, природу которой составляет непревзойденная akula, и заканчивая буквой Ха, обозначающей развертывание Шакти (буква Кша обозначает форму прекращения этого развертывания) – вся она покоится в Непревзойденном в форме бинду, который означает недифференцированное сознание. "),a("p",{eng:""},"Таким образом, эта самоестественная Сила Рефлексии наделена природой совокупности слов. "),a("p",{eng:""},"Как сказано, «успокоение [мирового движения] в глубине Света Сознания является бытием Я. Это упомянутое успокоение есть разрушение всех отношений, и поэтому оно также есть Самоестественность, Источник деятельности, Изначальное и Владычество». "),a("p",{eng:""},"Это высшее Я – ступень великого могущества: из нее возникают и в ней успокаиваются все мантры; с помощью ее силы возможно осуществление любых действий. Об этом говорится в Шри Спанде, со слов «мантры, приобретая эту силу…» и до слов «… эти [мантры] наделены признаками Шивы». А также в Шива-сутрах : «От погружения в Великое озеро – обретение силы мантр». "),a("p",{eng:""},"Здесь «вхождение» в высшее Я, имеющее сущность силы великой мантры, означает соединение с ним посредством растворения [в нем] тела, праны и т. д., посредством устойчивости в достижении этой ступени, а также благодаря погружению в саму сущность [Я ощущений от своего] тела, внешнего чувственного опыта и т. п. "),a("p",{eng:""},"Поэтому во всех случаях, [когда] у кого-то возникает [ощущение] тела, [переживания] внешнего или внутреннего и т. д., [когда] он [что-то] представляет, [о чем-то] вспоминает или [нечто] анализирует, – [всегда] действует вибрирующая божественная Чити-шакти, основа ограниченного [опыта]. "),a("p",{eng:""},"И как [справедливо] говорят, «без ее вибрирующего движения вообще ничего не движется». "),a("p",{eng:""},"Только тогда, когда она движется таким образом, она посредством этой майя-шакти как бы присваивает ту или иную форму, по собственному желанию рассматривая себя как тело, как внешние объекты и т. п. "),a("p",{eng:""},"А субъекты майи принимают ее [движение] за [процесс] познания, представление, умозаключение, и т. п. "),a("p",{eng:""},"В реальности же эта Чити-шакти [всегда] одна и та же. "),a("p",{eng:""},"Как сказано, «то самое сознание, которое становится одним целым с чередой различных объектов, является Великим Господом, Высшим Субъектом познания, имеющим сущность вечного, бесконечного Сознания». "),a("p",{eng:""},"А также [было сказано, что] «благодаря майя-шакти Бога оно (сознание – С. П.) объединяется с различными познаваемыми объектами, [и этот процесс] называется по-разному: познанием, представлением, умозаключением и т. д.». "),a("p",{eng:""},"Итак, во всех ситуациях обнаруживается одна и та же Чити-шакти. Если она обретается через следование за ней и твердое сосредоточение на нее, тогда, через вхождение в нее, посредством вышеописанного способа, т. е. последовательным открыванием и закрыванием органов чувств, из-за того, что все состоит из всего, даже тогда, когда происходит поглощение и т. п. реальности, высший йогин обретает состояние Парабхайравы и «владычество» над «кругом божеств вечного Сознания», которые всегда «вызывают эманирование и разрушение вселенной», в том числе над [группой] немайических [божеств] внутренних и внешних чувств. "),a("p",{eng:""},"[В Спандакарике (III. 19)] говорится: «Когда [индивид] сосредоточен в одном месте, тогда, контролируя поглощение и возникновение (его), он достигает состояния [подлинной] субъективности и тем самым становится господином чакры ». Здесь «одно место» объясняется [в Спандакарике, III. 12]: «Все должно быть сведено к одному месту». [Этот термин ] следует понимать как [обозначение] состояния общей вибрации Сознания, состояние, имеющее форму унмеши. Тогда вышеупомянутое слово «его» следует интерпретировать как «тонкое тело», – поскольку и другой пассаж [Спандакарики] начинается со слов «удержанный посредством тонкого тела…», – а не следовать истолкованию, предложенному [автором комментария] Виварана: «»в одном месте» – т. е. в тонком или плотном теле ». "),a("p",{eng:""},"И как я писал в гимне: «Того, кто стал независимым властелином Чити-чакры и великим Господом, овладевшим группой божеств чувств, никто не может превзойти». "),a("p",{eng:""},"Слово «ити» [в сутре означает] вывод. Все тело вышеприведенной [сутры] есть «Шива», потому что оно – источник достижения [состояния] Шивы; потому что оно — происходит от Шивы; потому что оно — не отличается от истинной сущности Шивы; потому что оно — действительно имеет форму Шивы. "),a("p",{eng:""},"Человек, испытывающий постоянные ограничения из-за тела, жизненности, удовольствий и т. д., не узнает собственную Госпожу Чити, исполненную высшего блаженства. Только того, кто благодаря [моему] наставлению видит везде в океане нектара Сознания вселенную как массу [океанской] пены, можно назвать Шивой. Это наставление, поистине, [предназначено] для тех, которые, хотя они и ощутили ниспосланный на них Шивой поток Шакти, по причине слабых навыков к логической аргументации неспособны постичь [учение] об узнавании Господа. ")],-1),a("p",null,"На этом [трактат] «Сущность Узнавания» завершен.",-1),a("p",null,"Написал почтенный учитель Раджанака Кшемараджа, припавший перед лотосовыми стопами величайшего среди всех, кто учит о Великом Господе, – достопочтенного Абхинавагупты.",-1),a("p",null,"Да будет благо!",-1)]))}const v=e(p,[["render",u]]);export{h as __pageData,v as default};
