import{_ as a,d as e,c as m,g as t}from"./chunks/framework.DF5n-Kzr.js";const y=JSON.parse('{"title":"Афоризм 11","description":"Шива сутра вимаршини. Глава 1.","frontmatter":{"title":"Афоризм 11","description":"Шива сутра вимаршини. Глава 1.","tags":"vim-1","sans":"त्रितयभोक्ता वीरेशः","trans":"Tritayabhoktā vīreśaḥ","date":"2021-02-16T00:00:00.000Z"},"headers":[],"relativePath":"texts/shiva-sutra-vimarshini/1/11/index.md","filePath":"texts/shiva-sutra-vimarshini/1/11/index.md"}'),r={name:"texts/shiva-sutra-vimarshini/1/11/index.md"},i=t('<div class="stanza"><a href="#s1.11" id="s1.11" class="num">1.11</a><p>;;; sans एवं लोकयोग्यनुसारेण व्याख्याते जागरादित्रये शक्तिचक्रसन्धानाद्विश्वसंहारेण यस्य तुर्याभोगमयत्वमभेदव्याप्त्यात्मकं स्फुरति स तद्धाराधिरोहेण तुर्यातीतं पूर्वोक्तं चैतन्यमाविशन्—</p><blockquote><p><strong>त्रितयभोक्ता वीरेशः॥११॥</strong></p></blockquote><p>एतज्जागरादित्रयं शक्तिचक्रानुसन्धानयुक्त्या तुर्यानन्दाच्छुरितं यस्तत्परामर्शानुप्रवेशप्रकर्षाद्विगलितभेदसंस्कारमानन्दरसप्रवाहमयमेव पश्यति स त्रितयस्यास्य भोक्ता चमत्कर्ता। तत एव</p><blockquote><p>त्रिषु धामसु यद्भोग्यं भोक्ता यश्च प्रकीर्तितः वेदैतदुभयं यस्तु स भुञ्जानो न लिप्यते॥</p></blockquote><p>इति नीत्या निःसपत्नस्वात्मसाम्राज्योऽयं परमानन्दपरिपूर्णो भवभेदग्रसनप्रवणानां वीराणामिन्द्रियाणामीश्वरः स्वामी श्रीमन्थानभैरवसत्तानुप्रविष्टो महाम्नायेषूच्यते। यस्त्वेवंविधो न भवति स जागराद्यवस्थाभिर्भुज्यमानो लौकिकः पशुरेव। योग्यपीमां धारामनधिरूढो न वीरेश्वरोऽपि तु मूढ एवेत्युक्तं भवति। एतच्च</p><blockquote><p>योगी स्वच्छन्दयोगेन स्वच्छन्दगतिचारिणा स स्वच्छन्दपदे युक्तः स्वच्छन्दसमतां व्रजेत्॥</p></blockquote><p>इत्यादिना श्रीस्वच्छन्दादिशास्त्रेषु वितत्य दर्शितम्। स्पन्देऽपि</p><blockquote><p>तस्योपलब्धिः सततं त्रिपदाव्यभिचारिणी।</p></blockquote><p>इति कारिकया सङ्गृहीतमेतत्॥११॥</p><h2 id="" tabindex="-1">;;; <a class="header-anchor" href="#" aria-label="Permalink to &quot;;;;&quot;">​</a></h2><p>;;; trans</p><p>Evaṁ lokayogyanusāreṇa vyākhyāte jāgarāditraye śakticakrasandhānādviśvasaṁhāreṇa yasya turyābhogamayatvamabhedavyāptyātmakaṁ sphurati sa taddhārādhiroheṇa turyātītaṁ pūrvoktaṁ caitanyamāviśan—</p><blockquote><p><strong>Tritayabhoktā vīreśaḥ||11||</strong></p></blockquote><p>Etajjāgarāditrayaṁ śakticakrānusandhānayuktyā turyānandācchuritaṁ yastatparāmarśānupraveśaprakarṣādvigalitabhedasaṁskāramānandarasapravāhamayameva paśyati sa tritayasyāsya bhoktā camatkartā| Tata eva</p><blockquote><p>Triṣu dhāmasu yadbhogyaṁ bhoktā yaśca prakīrtitaḥ Vedaitadubhayaṁ yastu sa bhuñjāno na lipyate||</p></blockquote><p>iti nītyā niḥsapatnasvātmasāmrājyo&#39;yaṁ paramānandaparipūrṇo bhavabhedagrasanapravaṇānāṁ vīrāṇāmindriyāṇāmīśvaraḥ svāmī śrīmanthānabhairavasattānupraviṣṭo mahāmnāyeṣūcyate| Yastvevaṁvidho na bhavati sa jāgarādyavasthābhirbhujyamāno laukikaḥ paśureva| Yogyapīmāṁ dhārāmanadhirūḍho na vīreśvaro&#39;pi tu mūḍha evetyuktaṁ bhavati| Etacca</p><blockquote><p>Yogī svacchandayogena svacchandagaticāriṇā Sa svacchandapade yuktaḥ svacchandasamatāṁ vrajet||</p></blockquote><p>ityādinā śrīsvacchandādiśāstreṣu vitatya darśitam| Spande&#39;pi</p><blockquote><p>Tasyopalabdhiḥ satataṁ tripadāvyabhicāriṇī|</p></blockquote><p>iti kārikayā saṅgṛhītametat||11||</p><p>;;;</p><hr><p>Так <em>(evam)</em>, он -- Yogī-- <em>(saḥ)</em>, в ком <em>(yasya)</em> восторг и наслаждение <em>(ābhoga)</em> Четвертым Состоянием <em>(turya)</em>, состоящим <em>(ātmakam)</em> из проникновения <em>(vyāpti)</em> в недвойственность --т.е. отсутствие различий-- <em>(abheda)</em>, сияет <em>(sphurati)</em> благодаря исчезновению <em>(saṁhāreṇa)</em> вселенной <em>(viśva)</em> посредством единения <em>(sandhānāt)</em> с группой <em>(cakra)</em> сил <em>(śakti)</em> в трех <em>(traye)</em> [состояниях] бодрствования <em>(jāgarā)</em>, и т.д. <em>(ādi)</em>, что было подробно разъяснено <em>(vyākhyāte)</em> [также] в соответствии <em>(anusāreṇa)</em> с [опытом] Yogī <em>(yogi)</em> [и] обычных людей <em>(loka)</em><sup>1</sup>, входит <em>(āviśan)</em> в Турьятиту (Turyātīta) --т.е. состояние за пределами Турьи (Turya), или Turīya (Четвертого Состояния)-- <em>(turya-atītam)</em>, которая предварительно была <em>(pūrva)</em> описана <em>(uktam)</em> как Сознание, наделенное Абсолютной Свободой знать и делать все <em>(caitanyam)</em>. [Он въезжает в Turyātīta] верхом, [как говорится] <em>(adhiroheṇa)</em>, на потоке <em>(dhārā)</em> этого [восторга и наслаждения] <em>(tad)</em>—</p><blockquote><p><strong>Тот хозяин <em>(īśaḥ)</em> [своих] чувств <em>(vīra)</em>, кто является наслаждающимся [вышеупомянутым &quot;ābhoga&quot;, или божественным восторгом] <em>(bhoktā)</em> в триаде [бодрствования, сна со сновидениями и глубокого сна] <em>(tritaya)</em>||11||</strong></p></blockquote><p>Он <em>(saḥ)</em>, кто <em>(yaḥ)</em> видит <em>(paśyati)</em> эту <em>(etad)</em> триаду <em>(trayam)</em> бодрствования <em>(jāgarā)</em>, и т.д. <em>(ādi)</em> как изобилующую <em>(ācchuritam)</em> Блаженством <em>(ānanda)</em> Четвертого Состояния <em>(turya)</em> посредством <em>(yuktyā)</em> единения <em>(anusandhāna)</em> с группой <em>(cakra)</em> сил <em>(śakti)</em>, как непрерывный поток <em>(pravāha-mayam eva)</em> сока <em>(rasa)</em> Блаженства <em>(ānanda)</em>, в котором все остаточные впечатления <em>(saṁskāram)</em> различий --двойственности-- <em>(bheda)</em> были растворены <em>(vigalita)</em> через интенсивность <em>(prakarṣāt)</em> вхождения <em>(anupraveśa)</em> в переживание и узнавание <em>(parāmarśa)</em> этого [Четвертого Состояния] <em>(tad)</em>... [такой человек является] наслаждающимся <em>(bhoktā)</em> этой <em>(asya)</em> триадой <em>(tritayasya)</em>, [а также] Camatkāra --Восторгом Высшего Я-Сознания-- <em>(camatkartā)</em>. [Другими словами, он наслаждается Блаженством Я-Сознания во всей триаде состояний]|</p><p>По этой причине <em>(tatas eva)</em>:</p><blockquote><p>&quot;Он <em>(saḥ)</em>, кто <em>(yaḥ)</em> поистине <em>(tu)</em> знает <em>(veda)</em> этот <em>(etad)</em> дуэт <em>(ubhayam)</em>, т.е. [про] который <em>(yad... yaḥ)</em> говорится [как] <em>(prakīrtitaḥ)</em> про объект наслаждения --познаваемое-- <em>(bhogyam)</em> и <em>(ca)</em> наслаждающегося --познающего-- <em>(bhoktā)</em>, в трех <em>(triṣu)</em> состояниях [сознания] <em>(dhāmasu)</em>, не <em>(na)</em> загрязняется [ими] <em>(lipyate)</em> [даже если он может быть] наслаждающимся --переживающим-- <em>(bhuñjānaḥ... iti)</em> [эти состояния]&quot;||</p></blockquote><p>Согласно [вышеизложенному] наставлению <em>(nītyā)</em>, это [великое существо] <em>(ayam)</em>, наполненное <em>(paripūrṇaḥ)</em> Высшим <em>(parama)</em> Блаженством <em>(ānanda)</em>, [получает] непревзойденное <em>(niḥsapatna)</em> владычество над собой <em>(sva-ātma-sāmrājyaḥ)</em> [и становится ] повелителем <em>(īśvaraḥ)</em>[и] господином <em>(svāmī)</em> [своих] чувств <em>(indriyāṇām)</em>, которые [теперь] являются vīra-s, или героями <em>(vīrāṇām)</em><sup>2</sup> , нацеленными на <em>(pravaṇānām)</em> поглощение <em>(grasana)</em> различий <em>(bheda)</em>, [имеющих место] в мирском существовании <em>(bhava)</em>. В великих <em>(mahā)</em> священных текстах --āmnāya-s-- <em>(āmnāyeṣu)</em><sup>3</sup> [о таком человеке] говорится <em>(ucyate)</em>, что он вошел <em>(anupraviṣṭaḥ)</em> в существование <em>(sattā)</em> почтенного <em>(śrī)</em> Manthānabhairava <em>(manthāna-bhairava)</em><sup>4</sup>|</p><p>Однако <em>(tu)</em>, тот <em>(saḥ)</em>, кто <em>(yaḥ)</em> не относится <em>(bhavati)</em> к этому типу --т.е. тот, кто не является освобожденным-- <em>(evaṁ-vidhaḥ)</em>, [есть] просто <em>(eva)</em> обычный ограниченный индивидум <em>(laukikaḥ paśuḥ)</em>, который наслаждается <em>(bhujyamānaḥ)</em> состояниями <em>(avasthābhiḥ)</em> бодрствования <em>(jāgarā)</em>, и т.д. <em>(ādi)</em>|</p><p>Тот же <em>(api)</em> Yogī <em>(yogī)</em>, который не въезжает, [так сказать,] <em>(anadhirūḍhaḥ)</em> на этом <em>(imām)</em> потоке [восторга и наслаждения --ābhoga--] <em>(dhārām)</em>, [не может стать] повелителем <em>(īśvaraḥ)</em> [своих] чувств <em>(vīra)</em>, а <em>(api tu)</em> [является] всего лишь <em>(eva)</em> смущенным и сбитым с толку существом <em>(mūḍhaḥ)</em>. Вот, что было сказано [о нем] <em>(iti uktam bhavati)</em>|</p><p>И <em>(ca)</em> это <em>(etad)</em> [же самое] было показано <em>(darśitam)</em> в Священных Писаниях <em>(śāstreṣu)</em>, [таких как] освященной веками <em>(śrī)</em> Svacchandatantra <em>(svacchanda)</em>, и т.д. <em>(ādi)</em>, после подробного объяснения [этого] <em>(vitatya)</em> [в следующих и других строфах]:</p><blockquote><p>&quot;Тот <em>(saḥ)</em> Yogī <em>(yogī)</em>, кто живет <em>(cāriṇā)</em> в условиях <em>(gati)</em> независимого существования --освобожденное существо-- <em>(svacchanda)</em> посредством Svacchandayoga --тип Йоги, преподаваемой в Svacchandatantra-- <em>(svacchanda-yogena)</em>, будучи единым <em>(yuktaḥ)</em> с состоянием <em>(pade)</em> Svacchanda <em>(svacchanda)</em><sup>5</sup>, достигает <em>(vrajet)</em> тождества <em>(samatā)</em> с Svacchanda <em>(svacchanda... iti ādinā)</em>&quot;| [См. VII, 260 в Svacchandatantra]</p></blockquote><p>В Spandakārikā-s _(spande)_также <em>(api)</em>, это [же самое учение] <em>(etad)</em> было обобщено <em>(saṅgṛhītam)</em> [следующей частью] kārikā --афоризма-- <em>(kārikayā)</em>:</p><blockquote><p>&quot;[Полностью пробужденный всегда и] постоянно <em>(satatam)</em> воспринимает <em>(upalabdhiḥ)</em> Это --т.е. Высшее Я-- <em>(tasya)</em>, [и это восприятие Высшего Я] присутствует <em>(avyabhicāriṇī)</em> [во всех] трех <em>(tri)</em> состояниях [сознания] <em>(pada... iti)</em>...&quot; [См. Spandakārikā-s I, 17]</p></blockquote><p>||11||</p><hr><ol><li><p>Читайте комментарии на афоризмы 8, 9 и 10 этого же раздела.</p></li><li><p>Подобно подлинным героям, эти божественные чувства просветленного человека всегда преследуют высшую цель жизни и благополучия всех. Они уже не просто &quot;indriya-s&quot;, или &quot;чувства&quot;, которые все время мучают ограниченную личность, а высшие силы, служащие этому высоко духовному человеку.</p></li><li><p>Насчитывается 64 великих священных текста, авторство которых не является человеческим. Эта группа явленных Писаний также известна как Bhairavaśāstra-s (Писания Bhairava). Bhairava - это, конечно, Высшее Я.</p></li><li><p>Это эпитет Высшего Я, буквально означает &quot;Bhairava - это тот, кто взбивает, сотрясает и перемешивает (вселенную)&quot;, в том смысле, что Он проявляет, поддерживает и растворяет вселенную в Своем собственном Я снова и снова.</p></li><li><p>Термин &quot;Svacchanda&quot; буквально означает и &quot;собственная свободная воля&quot;, и &quot;независимый&quot;. В этом контексте, Svacchanda - это освобожденное существо, т.е. Сам Śiva, но в то же времяэто также Его Абсолютная Свободная Воля знать и делать все. Применяя соответствующий аффикс (См. Аффиксы), в результате появляется слово &quot;Svācchandya&quot;. Это означает &quot;свобода&quot;. Короче говоря, это состояние Svacchanda, или освобожденного существа. И да, Svācchandya - это синоним Сватантрии (Svātantrya) (Абсолютной Свободы Высшего Я).</p></li></ol></div>',1),p=[i];function o(s,n,h,u,d,c){return m(),e("div",null,p)}const k=a(r,[["render",o]]);export{y as __pageData,k as default};
