import{_ as m,c as e,d as t,o as i}from"./chunks/framework.B7ZEGzqH.js";const k=JSON.parse('{"title":"Афоризм 12","description":"Шива сутра вимаршини. Глава 1.","frontmatter":{"title":"Афоризм 12","description":"Шива сутра вимаршини. Глава 1.","sans":"विस्मयो योगभूमिकाः","trans":"Vismayo yogabhūmikāḥ","tags":"vim-1","date":"2021-02-15T00:00:00.000Z"},"headers":[],"relativePath":"texts/shiva-sutra-vimarshini/1/12/index.md","filePath":"texts/shiva-sutra-vimarshini/1/12/index.md"}'),s={name:"texts/shiva-sutra-vimarshini/1/12/index.md"};function n(o,a,r,y,p,v){return i(),e("div",null,a[0]||(a[0]=[t('<div class="stanza"><a href="#s1.12" id="s1.12" class="num">1.12</a><p>;;; sans</p><p>किमस्य महायोगिनः काश्चित् तत्त्वाधिरोहप्रत्यासन्ना भूमिकाः सन्ति याभिस्तत्त्वोर्ध्ववर्तिनी भूमिर्लक्ष्यते। सन्तीत्याह—</p><blockquote><p><strong>विस्मयो योगभूमिकाः॥१२॥</strong></p></blockquote><p>यथा सातिशयवस्तुदर्शने कस्यचिद्विस्मयो भवति तथास्य महायोगिनो नित्यं तत्तद्वेद्यावभासामर्शाभोगेषु निःसामान्यातिशयनवनवचमत्कारचिद्घनस्वात्मावेशवशात्स्मेरस्मेरस्तिमितविकसितसमस्तकरणचक्रस्य यो विस्मयोऽनवच्छिन्नानन्दे स्वात्मन्यपरितृप्तत्वेन मुहुर्मुहुराश्चर्यायमाणता ता एव योगस्य परतत्त्वैक्यस्य सम्बन्धिन्यो भूमिकास्तदध्यारोहविश्रान्तिसूचिकाः परिमिता भूमयो न तु कन्दबिन्द्वाद्यनुभववृत्तयः। तदुक्तं श्रीकुलयुक्तौ</p><blockquote><p>आत्मा चैवात्मना ज्ञातो यदा भवति साधकैः तदा विस्मयमात्मा वै आत्मन्येव प्रपश्यति॥</p></blockquote><p>इति। एतच्च</p><blockquote><p>तमधिष्ठातृभावेन स्वभावमवलोकयन् स्मयमान इवास्ते यस्तस्येयं कुसृतिः कुतः॥</p></blockquote><p>इति कारिकया सङ्गृहीतम्॥१२॥</p><p>;;;</p><hr><p>;;; trans</p><p>Kimasya mahāyoginaḥ kāścit tattvādhirohapratyāsannā bhūmikāḥ santi yābhistattvordhvavartinī bhūmirlakṣyate| Santītyāha—</p><blockquote><p><strong>Vismayo yogabhūmikāḥ||12||</strong></p></blockquote><p>Yathā sātiśayavastudarśane kasyacidvismayo bhavati tathāsya mahāyogino nityaṁ tattadvedyāvabhāsāmarśābhogeṣu niḥsāmānyātiśayanavanavacamatkāracidghanasvātmāveśavaśātsmerasmerastimitavikasitasamastakaraṇacakrasya yo vismayo&#39;navacchinnānande svātmanyaparitṛptatvena muhurmuhurāścaryāyamāṇatā tā eva yogasya paratattvaikyasya sambandhinyo bhūmikāstadadhyārohaviśrāntisūcikāḥ parimitā bhūmayo na tu kandabindvādyanubhavavṛttayaḥ| Taduktaṁ śrīkulayuktau</p><blockquote><p>Ātmā caivātmanā jñāto yadā bhavati sādhakaiḥ Tadā vismayamātmā vai ātmanyeva prapaśyati||</p></blockquote><p>iti| Etacca</p><blockquote><p>Tamadhiṣṭhātṛbhāvena svabhāvamavalokayan Smayamāna ivāste yastasyeyaṁ kusṛtiḥ kutaḥ||</p></blockquote><p>iti kārikayā saṅgṛhītam||12||</p><p>;;;</p><hr><p>Имеются ли какие-либо стадии <em>(kim... kāścid... bhūmikāḥ santi)</em>, тесно связанные <em>(pratyāsannāḥ)</em> с восхождением <em>(adhiroha)</em> этого <em>(asya)</em> великого <em>(mahā)</em> Yogī <em>(yoginaḥ)</em> к Высшему Принципу <em>(tattva)</em>, посредством которых --т.е. стадий-- _(yābhiḥ)_возвышенный <em>(ūrdhvavartinī)</em> [духовный] уровень <em>(bhūmiḥ)</em> может быть охарактеризован <em>(lakṣyate)</em>?|</p><p>[Господь Śiva, автор афоризмов,] сказал <em>(āha)</em>: &quot;Эти <em>(santi iti)</em>&quot;—</p><blockquote><p><strong>Стадии <em>(bhūmikāḥ)</em> Йоги <em>(yoga)</em> [являют собой] восхитительное изумление <em>(vismayaḥ)</em>||12||</strong></p></blockquote><p>Так же как <em>(yathā)</em> имеет место <em>(bhavati)</em> [переживание] восхитительного изумления <em>(vismayaḥ)</em>, когда кто-то видит <em>(darśane kasyacid)</em> высшую <em>(sātiśaya)</em> реальность <em>(vastu)</em>, точно также <em>(tathā)</em> [имеется] всегда <em>(nityam)</em> [переживание восхитительного изумления, когда] этот великий Yogī <em>(asya mahā-yoginaḥ)</em>, который остается спокойным <em>(stimita)</em>, улыбающимся <em>(smera)</em> [и] расцветшим [как цветок] <em>(smera)</em>, [поскольку] вся его <em>(samasta)</em> группа <em>(cakrasya)</em> чувств <em>(karaṇa)</em> [теперь] расширена <em>(vikasita)</em>, [испытывает] восторг --ābhoga-- <em>(ābhogeṣu)</em> , [связанный с] опытом <em>(āmarśa)</em> проявления <em>(avabhāsa)</em> различных <em>(tad tad)</em> познаваемых --т.е. объектов-- <em>(vedya)</em>. [Такая группа чувств была расширена] посредством <em>(vaśāt)</em> проникновения <em>(āveśa)</em> в его собственное <em>(sva)</em> Я <em>(ātma)</em>, [то есть] массу <em>(ghana)</em> Сознания <em>(cit)</em>, [полного] всегда нового <em>(nava-nava)</em>, превосходящего все другое <em>(atiśaya)</em> [и] невероятного <em>(niḥsāmānya)</em> Восторга Я-Сознания <em>(camatkāra)</em>. Это <em>(yaḥ)</em> восхитительное изумление <em>(vismayaḥ)</em> [является] состоянием, которое становится необыкновенным <em>(āścaryāyamāṇatā)</em> снова и снова <em>(muhurmuhur)</em>. [Это состояние испытывается Yogī] в его собственном <em>(sva)</em> Я <em>(ātmani)</em>, которое состоит из непрерывного <em>(anavacchinna)</em> Блаженства <em>(ānande)</em>, и характеризующееся отсутствием полного удовлетворения <em>(aparitṛptatvena)</em> -- Yogī никогда не удовлетворен полностью этим состоянием изумления, т.е. он желает его больше и больше--. Эти <em>(tāḥ eva)</em> стадии <em>(bhūmikāḥ)</em> связаны <em>(sambandhinyaḥ)</em> с Йогой <em>(yogasya)</em>, [или] единством <em>(aikyasya)</em> с Высшим <em>(para)</em> Принципом <em>(tattva)</em> --т.е. с Высшим Я--. [Такие] определенные <em>(parimitāḥ)</em> этапы, или стадии <em>(bhūmayaḥ)</em> показывают <em>(sūcikāḥ)</em> покой [этого Yogī] <em>(viśrānti)</em> в [его] восхождении <em>(adhyāroha)</em> к Этому--Высшему Принципу-- <em>(tad)</em>, а не <em>(na tu)</em> условия <em>(vṛttayaḥ)</em> опыта <em>(anubhava)</em>, [относящегося к] Kanda <em>(kanda)</em>, Bindu <em>(bindu)</em>, и т.д. <em>(ādi)</em><sup>1</sup>|</p><p>Так <em>(tad)</em> было [также] сказано <em>(uktam)</em> в досточтимой <em>(śrī)</em> Kulayukti <em>(kulayuktau)</em>:</p><blockquote><p>&quot;И <em>(ca eva)</em> когда <em>(yadā)</em> &#39;Я&#39; <em>(ātmā)</em> является <em>(bhavati)</em> познанным <em>(jñātaḥ)</em> садхаками <em>(sādhakaiḥ)</em> посредством [этого самого] &#39;Я&#39; <em>(ātmanā)</em>, тогда <em>(tadā)</em>&#39;Я&#39; <em>(ātmā)</em> созерцает <em>(prapaśyati)</em> восхитительное изумление <em>(vismayam)</em> в Нем Самом <em>(ātmani)</em> действительно <em>(vai... eva... iti)</em>&quot;||</p></blockquote><p>Эта [же самая истина] <em>(etad ca)</em> была резюмирована <em>(saṅgṛhītam)</em> [следующим] афоризмом <em>(kārikayā)</em> [Spandakārikā-s]:</p><blockquote><p>&quot;Как <em>(kutas)</em> [может] этот <em>(iyam)</em> отвратительный <em>(ku)</em> путь трансмиграции <em>(sṛtiḥ)</em> [испытываться] тем <em>(tasya)</em>, кто <em>(yaḥ)</em> пребывает, или остается <em>(āste)</em> изумленным <em>(smayamānaḥ)</em>, так сказать <em>(iva)</em>, созерцая <em>(avalokayan)</em> эту <em>(tam)</em> сущностную природу, [или Spanda] <em>(sva-bhāvam)</em>, как <em>(bhāvena)</em> управителя [всей вселенной] <em>(adhiṣṭhātṛ... iti)</em>?&quot;| [См. Spandakārikā-s I, 11]</p></blockquote><p>||12||</p><hr><ol><li>Хотя термины Kanda и Bindu обычно означают &quot;яйцеподобный источник всех nāḍī-s, или каналов в тонком теле&quot; и &quot;точку, символизирующую Высшего Познающего --т.е. Śiva--&quot; соответственно в этом контексте эти два термина означают Mūlādhāra и Ājñā (хорошо известные чакры (cakra-s), расположенные в тонком теле).</li></ol></div>',1)]))}const u=m(s,[["render",n]]);export{k as __pageData,u as default};
