import{_ as a,o as m,c as e,b as t}from"./chunks/framework.08bce705.js";const q=JSON.parse('{"title":"Афоризм 13","description":"Шива сутра вимаршини. Глава 1.","frontmatter":{"title":"Афоризм 13","description":"Шива сутра вимаршини. Глава 1.","sans":"इच्छाशक्तिरुमा कुमारी","trans":"Icchāśaktirumā kumārī","tags":"vim-1","date":"2021-02-14T00:00:00.000Z"},"headers":[],"relativePath":"texts/shiva-sutra-vimarshini/1/13/index.md","filePath":"texts/shiva-sutra-vimarshini/1/13/index.md"}'),r={name:"texts/shiva-sutra-vimarshini/1/13/index.md"},u=t('<div class="stanza"><a href="#s1.13" id="s1.13" class="num">1.13</a><p>;;; sans</p><p>ईदृग्योगभूमिकासमापन्नस्यास्य योगिनः—</p><blockquote><p><strong>इच्छाशक्तिरुमा कुमारी॥१३॥</strong></p></blockquote><p>योगिनः परभैरवतां समापन्नस्य या इच्छा सा शक्तिरुमा परैव पारमेश्वरी स्वातन्त्र्यरूपा सा च कुमारी विश्वसर्गसंहारक्रीडापरा कुमार क्रीडायामिति पाठात्।</p><p>अथ च कुं भेदोत्थापिकां मायाभूमिं मारयत्यनुद्भिन्नप्रसरां करोति तच्छीला। कुमारी च परानुपभोग्या भोक्त्रैकात्म्येन स्फुरन्ती।</p><p>अथवा यथा उमा कुमारी परिहृतसर्वासङ्गा महेश्वरैकात्म्यसाधनाराधनाय नित्योद्युक्ता तथैवास्येच्छेत्यस्मद्गुरुभिरित्थमेव पाठो दृष्टो व्याख्यातश्च।</p><p>अन्यैस्तु शक्तितमेति पठित्वा ज्ञानक्रियापेक्षोऽस्याः प्रकर्षो व्याख्यातः।</p><p>एवं न लौकिकवदस्य योगिनः स्थूलेच्छापि तु परा शक्तिरूपैव सर्वत्राप्रतिहता। तदुक्तं श्रीमत्स्वच्छन्दे</p><blockquote><p>सा देवी सर्वदेवीनां नामरूपैश्च तिष्ठति योगमायाप्रतिच्छन्ना कुमारी लोकभाविनी</p></blockquote><p>इति। श्रीमृत्युञ्जयभट्टारकेऽपि</p><blockquote><p>सा ममेच्छा परा शक्तिरवियुक्ता स्वभावजा वह्नेरूष्मेव विज्ञेया रश्मिरूपा रवेरिव॥<br> सर्वस्य जगतो वापि सा शक्तिः कारणात्मिका।</p></blockquote><p>इति। तदेतत्</p><blockquote><p>न हीच्छानोदनस्यायं प्रेरकत्वेन वर्तते अपि त्वात्मब्लस्पर्शात्पुरुषस्तत्समो भवेत्</p></blockquote><p>इति कारिकया भङ्ग्या प्रतिपादितम्॥१३॥ ;;;</p><hr><p>;;; trans Īdṛgyogabhūmikāsamāpannasyāsya yoginaḥ—</p><blockquote><p><strong>Icchāśaktirumā kumārī||13||</strong></p></blockquote><p>Yoginaḥ parabhairavatāṁ samāpannasya yā icchā sā śaktirumā paraiva pārameśvarī svātantryarūpā sā ca kumārī viśvasargasaṁhārakrīḍāparā kumāra krīḍāyāmiti pāṭhāt|</p><p>Atha ca kuṁ bhedotthāpikāṁ māyābhūmiṁ mārayatyanudbhinnaprasarāṁ karoti tacchīlā| Kumārī ca parānupabhogyā bhoktraikātmyena sphurantī|</p><p>Athavā yathā umā kumārī parihṛtasarvāsaṅgā maheśvaraikātmyasādhanārādhanāya nityodyuktā tathaivāsyecchetyasmadgurubhiritthameva pāṭho dṛṣṭo vyākhyātaśca|</p><p>Anyaistu śaktitameti paṭhitvā jñānakriyāpekṣo&#39;syāḥ prakarṣo vyākhyātaḥ|</p><p>Evaṁ na laukikavadasya yoginaḥ sthūlecchāpi tu parā śaktirūpaiva sarvatrāpratihatā| Taduktaṁ śrīmatsvacchande</p><blockquote><p>Sā devī sarvadevīnāṁ nāmarūpaiśca tiṣṭhati Yogamāyāpraticchannā kumārī lokabhāvinī||</p></blockquote><p>iti| Śrīmṛtyuñjayabhaṭṭārake&#39;pi</p><blockquote><p>Sā mamecchā parā śaktiraviyuktā svabhāvajā Vahnerūṣmeva vijñeyā raśmirūpā raveriva| Sarvasya jagato vāpi sā śaktiḥ kāraṇātmikā|</p></blockquote><p>iti| Tadetat</p><blockquote><p>Na hīcchānodanasyāyaṁ prerakatvena vartate Api tvātmablasparśātpuruṣastatsamo bhavet||</p></blockquote><p>iti kārikayā bhaṅgyā pratipāditam||13||</p><p>;;;</p><hr><p>У того _(asya)_Yogī <em>(yoginaḥ)</em>, который достиг <em>(samāpannasya)</em> таких <em>(īdṛk)</em> стадий <em>(bhūmikā)</em> в йоге <em>(yoga)</em>—</p><blockquote><p>Сила Воли <em>(icchā śaktiḥ)</em> [просветленного Yogī - это] &quot;Великолепие Śiva&quot; <em>(umā)</em> [то есть] Kumārī <em>(kumārī)</em>||13||</p></blockquote><p>Такая <em>(yā... sā)</em> Сила Воли <em>(icchā śaktiḥ)</em> Yogī <em>(yoginaḥ)</em>, который достиг <em>(samāpannasya)</em> соостояния Высшего Бхайравы (Bhairava) <em>(para-bhairavatāṁ)</em> [есть) _Umā --Великолепие Шивы (Śiva)-- <em>(umā)</em>. [Эта Umā является] только <em>(eva)</em> высшей <em>(parā)</em> Госпожой Всевышнего Господа <em>(pārama-īśvarī)</em>, чья --ее-- природа <em>(rūpā)</em> - Svātantrya, или Абсолютная Свобода <em>(svātantrya)</em>. И <em>(ca)</em>, согласно прочтению --т.е. интерпретации-- <em>(pāṭhāt)</em> &quot;kumāra krīḍāyām&quot; <em>(kumāra krīḍāyām iti)</em><sup>1</sup>, Она <em>(sā)</em> [названа] Kumārī <em>(kumārī)</em>, [поскольку] Она намерена <em>(parā)</em> играть <em>(krīḍā)</em> в проявление <em>(sarga)</em> [и] поглощение <em>(saṁhāra)</em> вселенной <em>(viśva)</em> --Kṣemarāja объяснил значение афоризма с точки зрения &quot;abheda&quot;, т.е. не дуалистически--|</p><p>И аналогично <em>(atha ca)</em>, [Kumārī является той, кто] вынуждает стадию Māyā, [или] Ku умереть <em>(kum... māyā-bhūmiṁ mārayati)</em>. [Māyā] приводит к появлению <em>(utthāpikām)</em> bheda, или двойственности <em>(bheda)</em>. [Утверждение &quot;та, кто вынуждает стадию Māyā, или Ku умереть&quot;, означает, что] Она предотвращает <em>(an... karoti)</em> распространение [Māyā] <em>(udbhinna-prasarām)</em>. [Поэтому, Kumārī является той, кто имеет] такую <em>(tad)</em> природу, или предрасположенность <em>(śīlā)</em><sup>2</sup>. Также <em>(ca)</em>, Kumārī <em>(kumārī)</em> [- это такая Śakti, или Сила, которая] сияет во все стороны <em>(sphurantī)</em> в единстве <em>(eka-ātmyena)</em> с Bhoktā, или Наслаждающимся --т.е. с Śiva-- <em>(bhoktrā)</em> [и] никогда не является объектом наслаждения <em>(anupabhogyā)</em> для других <em>(para)</em> --Kṣemarāja объяснил значение афоризма с точки зрения &quot;bheda-abheda&quot;, т.е. в сочетании двойственности и недвойственности--|</p><p>Или <em>(athavā)</em> также как <em>(yathā)</em> девственница <em>(kumārī)</em> Umā <em>(umā)</em>, отказавшись от <em>(parihṛta)</em> всех <em>(sarva)</em> привязанностей <em>(āsaṅgā)</em>, всегда <em>(nitya)</em> занята <em>(udyuktā)</em> поклонением <em>(ārādhanāya)</em>, которое ведет непосредственно <em>(sādhana)</em> к единству <em>(eka-ātmya)</em> с великим <em>(mahā)</em> Господом <em>(īśvara)</em>, поэтому <em>(tathā)</em> [— это], конечно <em>(eva)</em>, Воля <em>(icchā)</em> этого [Yogī] <em>(asya)</em><sup>3</sup> . Это толкование было таким образом <em>(iti... ittham eva pāṭhaḥ)</em> увидено <em>(dṛṣṭaḥ)</em><sup>4</sup> и <em>(ca)</em> подробно объяснено <em>(vyākhyātaḥ)</em> нашими <em>(asmad)</em> Гуру --духовными наставниками-- <em>(gurubhiḥ)</em> --Kṣemarāja объяснил значение афоризма с точки зрения &quot;bheda&quot; , т.е. с двойственной точки зрения--|</p><p>Однако <em>(tu)</em>, [этот афоризм] был прочитан <em>(paṭhitvā)</em> [как] &quot;śaktitamā&quot; --высшая Сила-- <em>(śaktitamā iti)</em> другими [авторами] <em>(anyaiḥ)</em>, [по их мнению] главенство, или превосходство <em>(prakarṣaḥ)</em> этой [Силы Воли] <em>(asyāḥ)</em> над <em>(apekṣaḥ)</em> знанием <em>(jñāna)</em> [и] действием <em>(kriyā)</em> детально объясняется <em>(vyākhyātaḥ)</em> [в данной сутре (sūtra)]<sup>5</sup>|</p><p>Таким образом <em>(evam)</em>, Сила Воли <em>(icchā)</em> этого <em>(asya)</em> Yogī <em>(yoginaḥ)</em> не грубая <em>(sthūlā)</em>, как это у обычных людей <em>(laukika-vat)</em>, а <em>(api tu)</em> [она подобна] Ее Высшей Силе <em>(parā śakti-rūpā eva)</em>, т.е. беспрепятственной <em>(apratihatā)</em> повсеместно <em>(sarvatra)</em>|</p><p>Так <em>(tad)</em> было провозглашено <em>(uktam)</em> в досточтимой <em>(śrīmad)</em> Svacchandatantra <em>(svacchande)</em>:</p><blockquote><p>&quot;Богиня <em>(sā devī)</em> пребывает <em>(tiṣṭhati)</em> как имена и формы <em>(nāmarūpaiḥ ca)</em> всех <em>(sarva)</em> богинь <em>(devīnāṁ)</em>, остается <em>(tiṣṭhati)</em> сокрытой <em>(praticchannā)</em> посредством Yogamāyā --завуалированной силой, проявляющейся из Йоги, или отождествления с Господом-- <em>(yoga-māyā)</em>, [является] девственницей <em>(kumārī)</em> [и] способствует благоденствию <em>(bhāvinī)</em> людей <em>(loka... iti)</em>&quot;| [См. Svacchandatantra X, 727]</p></blockquote><p>[И] даже <em>(api)</em> в самой досточтимой <em>(śrī... bhaṭṭārake)</em> Mṛtyuñjayatantra <em>(mṛtyuñjaya)</em>:</p><blockquote><p>&quot;Высшая <em>(sā.. parā)</em> Śakti <em>(śaktiḥ)</em>, которая неразделенная --т.е. не отделена от Господа-- <em>(aviyuktā)</em> [и] естественная <em>(sva-bhāva-jā)</em>, [является] моей <em>(mama)</em> [силой] Воли <em>(icchā)</em>. Она должна быть известна <em>(vijñeyā)</em> [в том же отношении ко Мне] как <em>(iva... iva)</em> жар <em>(ūṣmā)</em> к огню <em>(vahneḥ)</em> [и] лучи света <em>(raśmi-rūpā)</em> к солнцу <em>(raveḥ)</em>. Эта _(sā)_Śakti <em>(śaktiḥ)</em> является причиной <em>(kāraṇa-ātmikā)</em> всего мира <em>(sarvasya jagataḥ)</em> поистине <em>(vā api)</em>!&quot; [См. I, 25-26 в Mṛtyuñjayatantra]</p></blockquote><p>Эта же самая [истина] <em>(tad etad)</em> [была] косвенно <em>(bhaṅgyā)</em> высказана <em>(pratipāditam)</em> афоризмом <em>(kārikayā)</em> [Spandakārikā-s]:</p><blockquote><p>&quot;Эта [ограниченная личность] <em>(ayam)</em> не <em>(na hi)</em> руководит, или управляет <em>(prerakatvena vartate)</em> побуждениями <em>(nodanasya)</em> желаний <em>(icchā)</em>. Но все же <em>(api tu)</em>, вступая в контакт <em>(sparśāt)</em> с Силой, или Энергией <em>(bala)</em> Высшего Я <em>(ātma)</em>, [этот] человек <em>(puruṣaḥ)</em> становится <em>(bhavet)</em> подобным <em>(samaḥ)</em> Этому <em>(tad... iti)</em>&quot;| [См. Spandakārikā-s I, 8]</p></blockquote><p>||13||</p><hr><ol><li><p>Первая интерпретация Кшемараджи связана с Абхедой (Abheda), или Недвойственностью. Фраза &quot;kumāra krīḍāyām&quot; подразумевает, кратко, что слово &quot;kumārī&quot; происходит из корня &quot;kumār&quot;, который означает &quot;играть как ребенок&quot;. Термин &quot;krīḍāyām&quot; (любящий игру) означает, что Она любит играть (как дитя), и Ее игра состоит из манифестации вселенной и ее поглощения в Себе снова и снова. Ну, только короткое объяснение на сейчас, поскольку углубление в эту тему привело бы меня к разъяснению как формируется корень &quot;kumār&quot; и как он спрягается... а это включает продвинутый уровень знания Санскрита, о котором большинство читателей могут только мечтать. Достаточно об этом тогда, чтобы ваши умы не запутались еще больше!</p></li><li><p>Святой интерпретирует слово &quot;kumārī&quot; акростически: ku + mārī. По его мнению, &quot;ku&quot; символизирует Māyā, сила которой порождает дуальность (различия в форме множественных субъектов и объектов), в то время как &quot;mārī&quot; означает &quot;mārayati&quot; - &quot;Она заставляет умирать&quot;. Следовательно, высшая сила Воли Yogī есть Kumārī, поскольку Она разрушает Māyā. Другими словами, она не позволяет Māyā распространяться. Автор прояснил это, добавив &quot;anudbhinnaprasarāṁ karoti&quot; - &quot;Она препятствует [Māyā] распространению&quot;, потому что здесь невозможно разрушение. Поскольку вселенная по существу одна с Шакти, то говорить о &quot;разрушении&quot; чего-либо в ней, это только простой способ выразить то, что на самом деле является &quot;растворением&quot; в этом Источнике, т.е. внутри Нее.</p></li><li><p>Автор интерпретирует слово &quot;kumārī&quot;, как &quot;девственница&quot;. В этом случае, Umā - это хорошо известная жена Шивы в пуранических историях, т.е. Pārvatī. Сила Воли Yogī соотносится с Pārvatī (девственной молодой девушкой, постоянно поклоняющейся Своему мужу), потому что у нее нет детей. Другими словами: &quot;Она никогда не рождала ни одной вселенной!&quot; Проявление, поддержание и растворение вселенной являются всего лишь простыми способами, позволяющими говорить о процессах в Реальности, которые, как это ни парадоксально, являются неизменными. Таким образом, ни одна вселенная никогда не возникала из Нее, следовательно не может быть здесь и поддержания/растворения. Эта тайна может быть понята только теми, кто благословлен Милостью Господа. Я могу говорить с двойственной точки зрения, поскольку в настоящем абзаце комментатор интерпретирует афоризм с точки зрения &quot;bheda&quot;, т.е. дуалистически.</p></li><li><p>Не только &quot;прочтение/толкование&quot;, но целые Писания появляются перед возвышенным Yogī, и он способен &quot;видеть&quot; их, говоря буквально. Поэтому, хотя это может показаться странным для большинства людей, но новые толкования, писания, мантры и т.п. не &quot;услышаны&quot;, а &quot;увидены&quot; великим Yogī.</p></li><li><p>Kṣemarāja упоминает толкование этого 13<sup>ого</sup> афоризма согласно Bhāskara, автора знаменитой Śivasūtravārttika (другого важного комментария на Śivasūtra-s): &quot;इच्छा शक्तितमा कुमारी&quot; - &quot;Icchā śaktitamā kumārī&quot; (кратко переведенного как &quot;Воля, которая является наивысшей Силой, [- это] Kumārī&quot;. В общем, в Śivasūtravārttika вы обнаружите, что текущий афоризм появляется как &quot;Icchā śaktitamā kumārī&quot; вместо &quot;Icchāśaktirumā kumārī&quot;. Так или иначе, так как комментарий Кшемараджи (т.е. Śivasūtravimarśinī) считается наиболее авторитетным, я перевел версию <a href="/texts/shiva-sutra">Śivasūtra-s</a>,</p></li><li><p>как это дано Кшемараджей (Kṣemarāja), (а не Bhāskara и другими авторами).</p></li></ol></div>',1),i=[u];function o(p,s,k,h,n,c){return m(),e("div",null,i)}const y=a(r,[["render",o]]);export{q as __pageData,y as default};
