import{_ as e,c as m,d as t,o as r}from"./chunks/framework.B7ZEGzqH.js";const u=JSON.parse('{"title":"Афоризм 14","description":"Шива сутра вимаршини. Глава 1.","frontmatter":{"title":"Афоризм 14","description":"Шива сутра вимаршини. Глава 1.","sans":"दृश्यं शरीरम्","trans":"Dṛśyaṁ śarīram","tags":"vim-1","date":"2021-02-13T00:00:00.000Z"},"headers":[],"relativePath":"texts/shiva-sutra-vimarshini/1/14/index.md","filePath":"texts/shiva-sutra-vimarshini/1/14/index.md"}'),p={name:"texts/shiva-sutra-vimarshini/1/14/index.md"};function i(o,a,s,v,h,d){return r(),m("div",null,a[0]||(a[0]=[t('<div class="stanza"><a href="#s1.14" id="s1.14" class="num">1.14</a><p>;;; sans</p><p>ईदृशस्य महेच्छस्य—</p><blockquote><p><strong>दृश्यं शरीरम्॥१४॥</strong></p></blockquote><p>यद्यद्दृश्यं बाह्यमाभ्यन्तरं वा तत्तत्सर्वम् अहमिदमितिसदाशिववन्महासमापत्त्या स्वाङ्गकल्पमस्य स्फुरति न भेदेन।</p><p>शरीरं च देहधीप्राणशून्यरूपं नीलादिवद्दृश्यं न तु पशुवद्द्रष्टृतया भाति। एवं देहे बाह्ये च सर्वत्रास्य मयूराण्डरसवदविभक्तैव प्रतिपत्तिर्भवति। यथोक्तं श्रीविज्ञानभैरवे</p><blockquote><p>जलस्येवोर्मयो वह्नेर्ज्वालाभङ्ग्यः प्रभा रवेः ममैव भैरवस्यैता विश्वभङ्ग्यो विनिर्गताः॥</p></blockquote><p>इति। एतच्च</p><blockquote><p>भोक्तैव भोग्यभावेन सदा सर्वत्र संस्थितः।</p></blockquote><p>इत्यनेन सङ्गृहीतम्॥१४॥</p><p>;;;</p><hr><p>;;; trans</p><p>Īdṛśasya mahecchasya—</p><blockquote><p><strong>Dṛśyaṁ śarīram||14||</strong></p></blockquote><p>Yadyaddṛśyaṁ bāhyamābhyantaraṁ vā tattatsarvam ahamidamitisadāśivavanmahāsamāpattyā svāṅgakalpamasya sphurati na bhedena|</p><p>Śarīraṁ ca dehadhīprāṇaśūnyarūpaṁ nīlādivaddṛśyaṁ na tu paśuvaddraṣṭṛtayā bhāti| Evaṁ dehe bāhye ca sarvatrāsya mayūrāṇḍarasavadavibhaktaiva pratipattirbhavati| Yathoktaṁ śrīvijñānabhairave</p><blockquote><p>Jalasyevormayo vahnerjvālābhaṅgyaḥ prabhā raveḥ Mamaiva bhairavasyaitā viśvabhaṅgyo vinirgatāḥ||</p></blockquote><p>iti| Etacca</p><blockquote><p>Bhoktaiva bhogyabhāvena sadā sarvatra saṁsthitaḥ|</p></blockquote><p>ityanena saṅgṛhītam||14||</p><p>;;;</p><hr><p>Для такого типа <em>(īdṛśasya)</em> [Yogī], достигшего высшей силы Воли <em>(mahā-icchasya)</em>—</p><blockquote><p><strong>Все феномены [внешние и внутренние] <em>(dṛśyam)</em> [являются] телом <em>(śarīram)</em> [просветленного Yogī]||14||</strong></p></blockquote><p>Что бы ни <em>(yad yad)</em> было воспринято <em>(dṛśyam)</em>, [будь то] внешне <em>(bāhyam)</em> или <em>(vā)</em> внутренне <em>(ābhyantaram)</em> [Йогином,] для него <em>(asya)</em> все <em>(sarvam)</em>, что <em>(tad tad)</em> сияет <em>(sphurati)</em>, подобно <em>(kalpam)</em> его собственному <em>(sva)</em> телу <em>(aṅga)</em>, а не как что-то, отличное [от него] <em>(na bhedena)</em>, благодаря [его] великому достижению <em>(mahā-samāpattyā)</em>, [проявляющемуся в виде ощущения] &quot;Я <em>(aham)</em> [есть] Это <em>(idam iti)</em>&quot; --т.е. Я есть вселенная-- подобно <em>(vat)</em> Sadāśiva <em>(sadāśiva)</em><sup>1</sup> --Kṣemarāja комментирует этот афоризм в его обычном прочтении, т.е. &quot;Dṛśyaṁ śarīram&quot;; но его комментарии следующего абзаца будут базироваться на прочтении &quot;Śarīraṁ dṛśyaṁ&quot;--|</p><p>Также <em>(ca)</em>, [для такого типа Yogī], śarīra, или тело <em>(śarīram)</em>, [в любой ] форме <em>(rūpam)</em> физического ли тела <em>(deha)</em>, мыслей <em>(dhī)</em>, жизненной энергии <em>(prāṇa)</em> [или] пустоты <em>(śūnya)</em> [в бодрствовании, сне со сновидениями и т.д.], проявляется <em>(bhāti)</em> как феномен --т.е. что-то объективное-- <em>(dṛśyam)</em> подобно <em>(vat)</em> синему [цвету] <em>(nīla)</em>, и т.п. <em>(ādi)</em>, а не <em>(na tu)</em> подобно воспринимающему <em>(draṣṭṛtayā)</em>, как в [случае] <em>(vat)</em> обусловленного существа (пашу) <em>(paśu)</em><sup>2</sup>|</p><p>Таким образом <em>(evam)</em>, в теле <em>(dehe)</em>, вовне <em>(bāhye)</em> и <em>(ca)</em> везде <em>(sarvatra)</em>, его <em>(asya)</em> восприятие <em>(pratipattiḥ)</em> является <em>(bhavati)</em>, безусловно <em>(eva)</em>, неразделенным, или недифференцированным <em>(avibhaktā)</em>, как <em>(vat)</em> плазма <em>(rasa)</em> яйца павлина <em>(mayūra-aṇḍa)</em> --т.е. его восприятие полностью однородно, как та плазма--|</p><p>Как <em>(yathā)</em> было заявлено <em>(uktam)</em> в досточтимой <em>(śrī)</em> Vijñānabhairava <em>(vijñānabhairave)</em>:</p><blockquote><p>&quot;[Также как] волны <em>(ūrmayaḥ)</em> [являются аспектом проявления] воды <em>(jalasya)</em>, языки пламени <em>(jvālā-bhaṅgyaḥ)</em> [— аспектом проявления] огня <em>(vahneḥ)</em> [и] свет <em>(prabhā)</em> [— аспектом проявления] солнца <em>(raveḥ)</em>, [точно также] эти <em>(etāḥ)</em> аспекты --как волны-- <em>(bhaṅgyaḥ)</em> вселенной <em>(viśva)</em> вышли <em>(vinirgatāḥ)</em> из Меня <em>(mama eva)</em>, т.е. из Bhairava <em>(bhairavasya... iti)</em>&quot;||</p></blockquote><p>Это <em>(etad ca)</em> было обобщено <em>(saṅgṛhītam)</em> этой [частью афоризма] <em>(anena)</em> [из Spandakārikā-s]:</p><blockquote><p>&quot;... Сам Экспериментатор <em>(bhoktā eva)</em>, всегда <em>(sadā)</em> [и] везде <em>(sarvatra)</em>, остается <em>(saṁsthitaḥ)</em> в виде <em>(bhāvena)</em> испытуемого <em>(bhogya... iti)</em>&quot;| [См. Spandakārikā-s II, 4]</p></blockquote><p>||14||</p><hr><ol><li><p>Sadāśiva — это третья таттва, или категория в процессе проявления вселенной. Когда Всевышняя Сила Господа экстрагирует из Себя вселенную (Idam, или Это), Idam появляется как нечеткий (туманный). Поскольку первые две таттвы, или категории являют собой Господа и Его Силу (Śiva и Śakti), Sadāśiva действительно обозначает появление вселенной. До Sadāśiva, Господь чувствует &quot;Я есть&quot;, но как только Sadāśiva проявился, Господь ощущает &quot;Я есть Это&quot;, потому что Idam, или Это (вселенная) начала проявляться Его божественной Силой. Таким образом, благодаря его идентификации с Высшим Я, Yogī переживает тот же опыт, что и Высшее Я в отношении вселенной.</p></li><li><p>Kṣemarāja указывает здесь, что значение слова śarīra — &quot;тело&quot;, но не просто &quot;физическое тело&quot;. Он показывает, что это &quot;тело&quot; может принимать форму мысли (во сне), жизненной энергии (в глубоком сне) или даже пустоты (в Māyā) также. Кроме того, мудрец ясно указывает на тот факт, что ограниченные существа рассматривают śarīra, или тело как воспринимающего, в то время как просветленное существо так не считает. Возвышенный человек воспринимает śarīra как еще один объект, который никогда не является субъектом, или воспринимающим. А почему обусловленные существа рассматривают śarīra как воспринимающего? Потому что они отождествляются с śarīra, т.е. с физическим телом, мыслями, жизненной энергией или пустотой. Отождествление с śarīra является серьезной ошибкой, тем не менее тяжелое бремя их обусловленности заставляет их делать эту ошибку. И какой выход для них?: &quot;Отождествление с их собственной сущностной природой, т.е. с Господом&quot;. Однако, это происходит только благодаря благословению этого самого Господа, который, парадоксально, является ими самими.</p></li></ol></div>',1)]))}const y=e(p,[["render",i]]);export{u as __pageData,y as default};
