import{_ as e,l as m,v as t,k as p}from"./chunks/framework.Dx1fS_nX.js";const v=JSON.parse('{"title":"Афоризм 15","description":"Шива сутра вимаршини. Глава 1.","frontmatter":{"title":"Афоризм 15","description":"Шива сутра вимаршини. Глава 1.","sans":"हृदये चित्तसङ्घट्टाद्दृश्यस्वापदर्शनम्","trans":"Hṛdaye cittasaṅghaṭṭāddṛśyasvāpadarśanam","tags":"vim-1","date":"2021-02-12T00:00:00.000Z"},"headers":[],"relativePath":"texts/shiva-sutra-vimarshini/1/15/index.md","filePath":"texts/shiva-sutra-vimarshini/1/15/index.md"}'),i={name:"texts/shiva-sutra-vimarshini/1/15/index.md"};function s(o,a,u,r,n,h){return p(),m("div",null,a[0]||(a[0]=[t('<div class="stanza"><a href="#s1.15" id="s1.15" class="num">1.15</a><p>;;; sans</p><p>यच्चेदं सर्वस्य दृश्यस्य शरीरतया शून्यान्तस्य च दृश्यतयैकरूपं प्रकाशनमुक्तं नैतद्दुर्घटमपि तु—</p><blockquote><p><strong>हृदये चित्तसङ्घट्टाद्दृश्यस्वापदर्शनम्॥१५॥</strong></p></blockquote><p>विश्वप्रतिष्ठास्थानत्वाच्चित्प्रकाशो हृदयं तत्र चित्तसङ्घट्टाच्चलतश्चलतस्तदेकाग्रभावताद्दृश्यस्य नीलदेहप्राणबुद्ध्यात्मनः स्वापस्य चैतदभावरूपस्य शून्यस्य दर्शनं त्यक्तग्राह्यग्राहकविभेदेन यथावस्तु स्वाङ्गकल्पतया प्रकाशनं भवति। चित्प्रकाशतामभिनिविशमानं हि चित्तं तदाच्छुरितमेव विश्वं पश्यति। तदुक्तं श्रीविज्ञानभैरवे</p><blockquote><p>हृद्याकाशे निलीनाक्षः पद्मसम्पुटमध्यगः अनन्यचेताः सुभगे परं सौभाग्यमाप्नुयात्॥</p></blockquote><p>इति। परं ह्यत्र सौभाग्यं विश्वेश्वरतापत्तिः। तत्त्ववृत्तिसमापन्नं महायोगिनमुद्दिश्य श्रीमत्स्वच्छन्देऽपि</p><blockquote><p>स च सर्वेषु भूतेषु भावतत्त्वेन्द्रियेषु च स्थावरं जङ्गमं चैव चेतनाचेतनस्थितम्॥<br> आध्वानं व्याप्य सर्वं तु सामरस्येन संस्थितः।</p></blockquote><p>इति। स्पन्दे तु</p><blockquote><p>तथा स्वात्मन्यधिष्ठानात्सर्वत्रैवं भविष्यति</p></blockquote><p>इत्यनेनैवैतत्सङ्गृहीतम्॥१५॥</p><p>;;;</p><hr><p>;;; trans</p><p>Yaccedaṁ sarvasya dṛśyasya śarīratayā śūnyāntasya ca dṛśyatayaikarūpaṁ prakāśanamuktaṁ naitaddurghaṭamapi tu—</p><blockquote><p><strong>Hṛdaye cittasaṅghaṭṭāddṛśyasvāpadarśanam||15||</strong></p></blockquote><p>Viśvapratiṣṭhāsthānatvāccitprakāśo hṛdayaṁ tatra cittasaṅghaṭṭāccalataścalatastadekāgrabhāvatāddṛśyasya nīladehaprāṇabuddhyātmanaḥ svāpasya caitadabhāvarūpasya śūnyasya darśanaṁ tyaktagrāhyagrāhakavibhedena yathāvastu svāṅgakalpatayā prakāśanaṁ bhavati| Citprakāśatāmabhiniviśamānaṁ hi cittaṁ tadācchuritameva viśvaṁ paśyati| Taduktaṁ śrīvijñānabhairave</p><blockquote><p>Hṛdyākāśe nilīnākṣaḥ padmasampuṭamadhyagaḥ Ananyacetāḥ subhage paraṁ saubhāgyamāpnuyāt||</p></blockquote><p>iti| Paraṁ hyatra saubhāgyaṁ viśveśvaratāpattiḥ| Tattvavṛttisamāpannaṁ mahāyoginamuddiśya śrīmatsvacchande&#39;pi</p><blockquote><p>Sa ca sarveṣu bhūteṣu bhāvatattvendriyeṣu ca Sthāvaraṁ jaṅgamaṁ caiva cetanācetanasthitam| Ādhvānaṁ vyāpya sarvaṁ tu sāmarasyena saṁsthitaḥ|</p></blockquote><p>iti| Spande tu</p><blockquote><p>Tathā svātmanyadhiṣṭhānātsarvatraivaṁ bhaviṣyati||</p></blockquote><p>ityanenaivaitatsaṅgṛhītam||15||</p><p>;;;</p><hr><p>Это <em>(idam)</em>, о котором <em>(yad ca)</em> было сказано <em>(uktam)</em> [касательно великого Yogī], т.е. [что] (восприятие) появления <em>(prakāśanam)</em> всех <em>(sarvasya)</em> феноменов [внешних или внутренних] <em>(dṛśyasya)</em> —также <em>(ca)</em> [рассматриваемое] как феномен <em>(dṛśyatayā)</em>, [так сказать,] даже то, что достигает <em>(antasya)</em> [состояния] пустоты <em>(śūnya)</em>— как [его собственного] тела <em>(śarīratayā)</em> единообразно --т.е. как единственную форму-- <em>(eka-rūpam)</em>, не является --т.е. это не является-- <em>(na etad)</em> трудно выполнимым <em>(durghaṭam)</em>, а скорее <em>(api tu)</em><sup>1</sup>—</p><blockquote><p><strong>Через единение <em>(saṅghaṭṭāt)</em> ума <em>(citta)</em> в сердце Сознания <em>(hṛdaye)</em> имеет место проявление <em>(darśanam)</em> [всех] феноменов [внешних или внутренних] <em>(dṛśya)</em> [и даже] пустоты <em>(svāpa)</em> [как они есть в их сущностной природе]||15||</strong></p></blockquote><p>[Здесь] hṛdaya --ядро, или сердце-- <em>(yā... sā)</em> [является] Светом <em>(prakāśaḥ)</em> Сознания <em>(cit)</em> поскольку он является основой <em>(pratiṣṭhā-sthānatvāt)</em> вселенной <em>(viśva)</em>. В этом [hṛdaya] <em>(tatra)</em>, посредством единения <em>(saṅghaṭṭāt)</em> ума <em>(citta)</em> —т.е путем выполнения <em>(bhāvatāt)</em> концентрации <em>(ekāgra)</em> беспокойного [ума] <em>(calataḥ calataḥ)</em> на Том --на Свете Сознания-- <em>(tad)</em>—, происходит <em>(bhavati)</em> darśana <em>(darśanam)</em>, [или] появление <em>(prakāśanam)</em> всех феноменов [внешних или внутренних] <em>(dṛśyasya)</em> —т.е. синего <em>(nīla)</em>, физического тела <em>(deha)</em>, жизненной энергии <em>(prāṇa)</em> [и] интеллекта <em>(buddhi-ātmanaḥ)</em>—, и <em>(ca)</em> [даже] of svāpa <em>(svāpasya)</em>, [или] пустоты <em>(śūnyasya)</em> —т.е. того, чьей природой <em>(rūpasya)</em> [является] отсутствие <em>(abhāva)</em> этого --а именно, феноменов внешнего или внутреннего-- <em>(etad)</em>—, как <em>(kalpatayā)</em> его собственного <em>(sva)</em> тела <em>(aṅga)</em> —т.е. [происходит появление всех феноменов и пустоты] такими, какими они являются в реальности <em>(yathā-vastu)</em>, лишенными различий <em>(tyakta... vibhedena)</em> между субъектом <em>(grāhaka)</em> [и] объектом <em>(grāhya)</em>—<sup>2</sup>|</p><p>Безусловно <em>(hi)</em>, ум <em>(cittam)</em>, проникающий <em>(abhiniviśamānam)</em> в Свет <em>(prakāśatām)</em> Сознания <em>(cit)</em>, видит <em>(paśyati)</em> вселенную <em>(viśvam)</em> как пропитанную <em>(ācchuritam eva)</em> этим [Светом] <em>(tad)</em>|</p><p>Это <em>(tad)</em> было высказано <em>(uktam)</em> в досточтимой <em>(śrī)</em> Vijñānabhairava <em>(vijñānabhairave)</em> [также]:</p><blockquote><p>&quot;Он, чьи ощущения <em>(akṣaḥ)</em> установлены <em>(nilīna)</em> на эфире, или пространстве <em>(ākāśe)</em> внутри сердца --т.е. внутри лотоса сердца-- <em>(hṛdi)</em>, кто пребывает <em>(gaḥ)</em> посередине <em>(madhya)</em> [двух] полусферических чаш <em>(sampuṭa)</em> [этого] лотоса <em>(padma)</em> --т.е. там, где находится Высшее Я--, и чей ум <em>(cetāḥ)</em> [является] неразделенным --т.е. чей ум сконцентрирован и видит вселенную как пронизанную Светом Сознания-- <em>(ananya)</em>, получает <em>(āpnuyāt)</em> наивысшую <em>(param)</em> удачу, или благословение <em>(saubhāgyam)</em>, О Прекрасная <em>(subhage... iti)</em>&quot;||</p></blockquote><p>Здесь <em>(atra)</em> высшая <em>(param)</em> удача <em>(saubhāgyam)</em> — [это] несомненно <em>(hi)</em> приобретение <em>(āpattiḥ)</em> господства <em>(īśvaratā)</em> по отношению к вселенной <em>(viśva)</em>|</p><p>Что касается <em>(uddiśya)</em> великого <em>(mahā)</em> Yogī <em>(yoginam)</em>, который достиг <em>(samāpannam)</em> состояния <em>(vṛtti)</em> Высшего Принципа <em>(tattva)</em>, в досточтимой <em>(śrīmad)</em> Svacchandatantra <em>(svacchande)</em> [было заявлено] также <em>(api)</em>:</p><blockquote><p>&quot;Он <em>(saḥ ca)</em>, [кто достиг тождества с Bhairava --т.е. с Господом--], пронизывая <em>(vyāpya)</em> все <em>(tu sarvam)</em> [шесть] путей <em>(ādhvānam)</em>, присутствующих <em>(sthitam)</em> [в] бесчувственных объектах <em>(acetanam)</em>, [или] неодушевленных <em>(sthāvaram)</em> и <em>(ca eva)</em> [в] чувствующих объектах <em>(cetana)</em>, [или] одушевленных <em>(jaṅgamam)</em> --т.е во всем, что движется и остается неподвижным во вселенной--, остается <em>(saṁsthitaḥ)</em> тождественным с Bhairava <em>(sāmarasyena)</em> во всех <em>(sarveṣu)</em> существах <em>(bhūteṣu)</em>, объектах <em>(bhāva)</em>, категориях проявления вселенной <em>(tattva)</em> и <em>(ca)</em> indriya-s --десяти Силах Восприятия и Действия-- <em>(indriyeṣu... iti)</em><sup>4</sup>&quot; [См. IV, 310 в Svacchandatantra]</p></blockquote><p>В Spandakārikā-s <em>(spande tu)</em> это [же учение] <em>(etad)</em> было резюмировано <em>(saṅgṛhītam)</em> в этой [части афоризма] <em>(anena eva)</em>:</p><blockquote><p>&quot;... поэтому [также] <em>(tathā)</em>, посредством установления <em>(adhiṣṭhānāt)</em> в его собственном <em>(sva)</em> Высшем Я <em>(ātmani)</em>, будет <em>(bhaviṣyati)</em> достигнуто <em>(evam)</em> [всеведение] повсеместно <em>(sarvatra... iti)</em>&quot;| [См. Spandakārikā-s III, 7]</p></blockquote><p>||15||</p><hr><ol><li><p>Хотя кажется, что Кшемараджа шутит, на самом деле он говорит всерьез. Высшего Состояния не трудно достичь, поскольку &quot;каждый — это всегда Господь&quot;, и нет никаких сомнений в этом. Однако, с точки зрения ограниченного существа, задача кажется невыполнимой из-за его тяжелого бремени ограничений. Конечно, в действительности, это способ выражаться, поскольку ограниченное существо - это просто Сам Господь, верящий в то, что Он является &quot;связанным Māyā&quot; и том подобное. Поскольку Господь абсолютен, он может стать &quot;абсолютно&quot; невежественным также. Поэтому, нет противоречия или преувеличения в том, что высказал мудрец. Есть только то, что Господь обманывает Себя, что задача восприятия всех феноменов как свое собственное тело, выглядит как невыполнимая. Поэтому перстань обманывать себя, дорогой Повелитель, и проснись! В общем, если читатель все еще &quot;думает&quot;, что все, что мудрец пишет, связано с Господом, с просветленным Yogī и т.д., отличных от него/нее, следовательно читатель еще ничего не понял об этом писании. В этом случае следует отметить, что читатель не находится пока еще в подходящем состоянии! Ну, это просто напоминание читателю, поскольку довольно-таки обычно забывать его/ее природу, читая такого типа писания, и тогда он начинает думать, что Господь есть Кто-то, существующий &quot;где-то еще&quot; вовне или &quot;глубоко&quot; внутри (как глубоко, когда тело - такое тонкое?), и все это сумасшествие.</p></li><li><p>Если вы чувствуете, что с вами может случиться сердечный приступ, не волнуйтесь, я спешу на помощь! Во-первых, позвольте объяснить, что я сделал: Поскольку этот сайт также обучает санскриту, я выбрал &quot;буквальный&quot; перевод комментария для того, чтобы ученики узнали, как реальный текст написан на санскрите. Другим вариантом мог бы быть перевод отрывка за отрывком [например, [в афоризме,] фраза &quot;citta-saṅghaṭṭāt&quot; [означает] &quot;путем выполнения сосредоточения беспокойного [ума] на Этом--на Свете Сознания--&quot;]. Будьте уверены, что это было бы действительно легче читать. Тем не менее, &quot;это не совсем то, что написал автор&quot;. Kṣemarāja описал каждую часть афоризма непрерывно, а не отрывок за отрывком. Поэтому, поскольку я учу санскриту кроме перевода текстов, я всегда буду выбирать буквальный перевод настолько, насколько это возможно.</p></li></ol><p>Так или иначе, я чувствую сострадание сейчас и поэтому я извлеку абзац из массы дополнительных объяснений, которые имеются между длинными дефисами (—...—):</p><p>&quot;[Здесь] hṛdaya --ядро, или сердце-- [является] Светом Сознания, поскольку он является основой вселенной. В этом [hṛdaya], посредством единения ума, происходит darśana, [или] появление всех феноменов [внешних или внутренних], и [даже] svāpa, [или] пустоты, как его собственного тела&quot;.</p><p>Ну, сейчас абзац выглядит понятней, так ведь? Окончательный смысл заключается в том, что когда человек концентрирует свой ум на Свете Сознания, там (в ядре, или сердце Реальности) все феномены, включая пустоту, предстают не отличными (т.е. как его собственное тело) от него. Другими словами, все воспринимается в единстве со Светом Сознания. Мудрец будет выражать эту истину в следующем абзаце. Используйте то, что находится между длинными дефисами, в качестве дополнительной информации.</p><p>Тем не менее, помимо того факта, что я не могу все время делать такого рода извлечения, помните, что я также обучаю санскриту на этом веб-сайте и &quot;необходимо&quot;, чтобы ученики испытали это &quot;как например, в этом средневековом тексте&quot;, т.е. &quot;чрезвычайно сложно!&quot;. Одно дело, когда вы изучаете санскрит из грамматики, и совсем другое дело, когда вы читаете реальные Писания, где авторы &quot;в основном&quot;, не будут писать, как ожидается. Даже более продвитутые учебники грамматики не могут включать &quot;все&quot; способы выражения для одной и той же вещи в санскрите для простого вопроса о размере (для этого потребовались бы тома), поскольку стили меняются в зависимости от времени и контекста. Неотъемлемые трудности в переводе средневекового текста, относящегося к Трике, отличаются от тех, которые вы найдете при переводе, например, более древних Писаний, посвященных другой философской системе. Поэтому, перевод &quot;реального&quot; санскритского текста, подобно настоящему, это задача, связанная с &quot;реальными&quot; трудностями, с которыми рано или поздно столкнется любой серьезный ученик, изучающий священный язык. Продолжайте читать, пожалуйста.</p><ol start="4"><li>Я не могу подробно комментировать этот сложный предмет, или эта страница будет чрезвычайно длинной, но мудрец Kṣemarāja также написал обширный комментарий в Svacchandatantra, в котором он полностью описывает секретный смысл этого афоризма. Он заканчивает этот комментарий к этой 310<sup>ой</sup> 5. строфе, которая имеется в 4<sup>ом</sup> paṭala (разделе), утверждая следующее: &quot;एषैव च निर्व्युत्थानसमाध्यात्मा महारहस्यभूता&quot; - &quot;Eṣaiva ca nirvyutthānasamādhyātmā mahārahasyabhūtā||&quot;, или &quot;Это [нерушимое поглощение в высшем Bhairava] является великой тайной, состоящей из Nirvyutthānasamādhi, или Полной Концентрации, лишенной vyutthāna --любого состояния, кроме Samādhi (Полной Концентрации)--&quot;. Подводя итоги: Только великий Yogī достигает полного поглощения в Господе, и&quot;никогда&quot; не возвращается к обычному состоянию сознания!</li></ol></div>',1)]))}const c=e(i,[["render",s]]);export{v as __pageData,c as default};
