import{_ as m,d as e,g as t,c as i}from"./chunks/framework.DL7zH_-w.js";const h=JSON.parse('{"title":"Афоризм 3","description":"Шива сутра вимаршини. Глава 1.","frontmatter":{"title":"Афоризм 3","description":"Шива сутра вимаршини. Глава 1.","toc":true,"tags":"vim-1","sans":"योनिवर्गः कलाशरीरम्","trans":"Yonivargaḥ kalāśarīram","date":"2021-02-22T00:00:00.000Z"},"headers":[],"relativePath":"texts/shiva-sutra-vimarshini/1/3/index.md","filePath":"texts/shiva-sutra-vimarshini/1/3/index.md"}'),r={name:"texts/shiva-sutra-vimarshini/1/3/index.md"};function n(p,a,k,l,o,d){return i(),e("div",null,a[0]||(a[0]=[t('<div class="stanza"><a href="#s1.3" id="s1.3" class="num">1.3</a><p>;;; sans</p><p>किम् ईदृगाणवमलात्मैव बन्धो न इत्याह—</p><blockquote><p><strong>योनिवर्गः कलाशरीरम्॥३॥</strong></p></blockquote><p>बन्ध इत्यनुवर्तते योऽयं योनेर्विश्वकारणस्य मायायाः सम्बन्धी वर्गः साक्षात्पारम्पर्येण च तद्धेतुको देहभुवनाद्यारम्भी किञ्चित्कर्तृताद्यात्मककलादिक्षित्यन्तस्तत्त्वसमूहस्तद्रूपं मायीयं तथा कलयति स्वस्वरूपावेशेन तत्तद्वस्तु परिच्छिनत्तीति कला व्यापारः शरीरं स्वरूपं यस्य तत्कलाशरीरं कार्मं मलमपि बन्ध इत्यर्थः। एतदपि</p><blockquote><p>निजाशुद्ध्यासमर्थस्य कर्तव्येष्वभिलाषिणः।</p></blockquote><p>इत्यनेनैव सङ्गृहीतम्। यथा चैतत्तथास्मदीयात्स्पन्दनिर्णयादवबोद्धव्यम्। एषां च कलादीनां किञ्चित्कर्तृत्वादिलक्षणं स्वरूपमाणवमलभित्तिलग्नं पुंसामावरकतया मलत्वेन सिद्धमेव। यदुक्तं श्रीमत्स्वच्छन्दे</p><blockquote><p>मलप्रध्वस्तचैतन्यं कलाविद्यासमाश्रितम्|<br> रागेण रञ्जितात्मानं कालेन कलितं तथा॥<br> नियत्या यमितं भूयः पुम्भावेनोपबृंहितम्|<br> प्रधानाशयसम्पन्नं गुणत्रयसमन्वितम्॥<br> बुद्धितत्त्वसमासीनमहङ्कारसमावृतम्|<br> मनसा बुद्धिकर्माक्षैस्तन्मात्रैः स्थूलभूतकैः॥</p></blockquote><p>इति। कार्ममलस्याप्यावरकत्वं श्रीमालिनीविजये प्रदर्शितम्</p><blockquote><p>धर्माधर्मात्मकं कर्म सुखदुःखादिलक्षणम्।</p></blockquote><p>इति। तदेतन्मायीयं कार्मं च मलम्</p><blockquote><p>भिन्नवेद्यप्रथात्रैव मायाख्यं जन्मभोगदम्|<br> कर्तर्यबोधे कार्मं तु मायाशक्त्याव तत्त्रयम्॥</p></blockquote><p>इति श्रीप्रत्यभिज्ञायामाणवमलभित्तिकं सङ्कुचितविशिष्टज्ञानतयैवोक्तम्॥३॥</p><p>;;;</p><p>;;; trans</p><p>Kim īdṛgāṇavamalātmaiva bandho na ityāha—</p><blockquote><h3 id="yonivargah-kalasariram-3" tabindex="-1">Yonivargaḥ kalāśarīram||3|| <a class="header-anchor" href="#yonivargah-kalasariram-3" aria-label="Permalink to &quot;Yonivargaḥ kalāśarīram||3||&quot;">​</a></h3></blockquote><p>Bandha ityanuvartate yo&#39;yaṁ yonerviśvakāraṇasya māyāyāḥ sambandhī vargaḥ sākṣātpāramparyeṇa ca taddhetuko dehabhuvanādyārambhī kiñcitkartṛtādyātmakakalādikṣityantastattvasamūhastadrūpaṁ māyīyaṁ tathā kalayati svasvarūpāveśena tattadvastu paricchinattīti kalā vyāpāraḥ śarīraṁ svarūpaṁ yasya tatkalāśarīraṁ kārmaṁ malamapi bandha ityarthaḥ| Etadapi</p><blockquote><p>Nijāśuddhyāsamarthasya kartavyeṣvabhilāṣiṇaḥ|</p></blockquote><p>ityanenaiva saṅgṛhītam| Yathā caitattathāsmadīyātspandanirṇayādavaboddhavyam| Eṣāṁ ca kalādīnāṁ kiñcitkartṛtvādilakṣaṇaṁ svarūpamāṇavamalabhittilagnaṁ puṁsāmāvarakatayā malatvena siddhameva| Yaduktaṁ śrīmatsvacchande</p><blockquote><p>Malapradhvastacaitanyaṁ kalāvidyāsamāśritam|<br> Rāgeṇa rañjitātmānaṁ kālena kalitaṁ tathā||<br> Niyatyā yamitaṁ bhūyaḥ pumbhāvenopabṛṁhitam|<br> Pradhānāśayasampannaṁ guṇatrayasamanvitam||<br> Buddhitattvasamāsīnamahaṅkārasamāvṛtam|<br> Manasā buddhikarmākṣaistanmātraiḥ sthūlabhūtakaiḥ||</p></blockquote><p>iti| Kārmamalasyāpyāvarakatvaṁ śrīmālinīvijaye pradarśitam</p><blockquote><p>Dharmādharmātmakaṁ karma sukhaduḥkhādilakṣaṇam|</p></blockquote><p>iti| Tadetanmāyīyaṁ kārmaṁ ca malam</p><blockquote><p>Bhinnavedyaprathātraiva māyākhyaṁ janmabhogadam|<br> Kartaryabodhe kārmaṁ tu māyāśaktyāiva tattrayam||</p></blockquote><p>iti śrīpratyabhijñāyāmāṇavamalabhittikaṁ saṅkucitaviśiṣṭajñānatayaivoktam||3||</p><p>;;;</p><p>Является ли рабством <em>(kim... bandhaḥ)</em> только <em>(eva)</em> Анавамала (Āṇavamala) <em>(āṇavamala-ātmā)</em>, наделенная такими качествами <em>(īdṛk)</em>? [Автор Śivasūtra-s, т.е. Шива] сказал <em>(āha)</em> &quot;Нет&quot; <em>(na iti)</em> [молчаливо, обосновывая это в следующем афоризме]—</p><blockquote><h3 id="источник-yoni-и-ее-потомство-vargah-вместе-с-тем-чья-форма-sariram-есть-деятельность-kala-также-являются-рабством-3" tabindex="-1">Источник <em>(yoni)</em> [и ее] потомство <em>(vargaḥ)</em>, [вместе с] тем, чья форма <em>(śarīram)</em> есть деятельность <em>(kalā)</em>, [также являются рабством]||3|| <a class="header-anchor" href="#источник-yoni-и-ее-потомство-vargah-вместе-с-тем-чья-форма-sariram-есть-деятельность-kala-также-являются-рабством-3" aria-label="Permalink to &quot;Источник _(yoni)_ [и ее] потомство _(vargaḥ)_, [вместе с] тем, чья форма _(śarīram)_ есть деятельность _(kalā)_, [также являются рабством]||3||&quot;">​</a></h3></blockquote><p>[Термин] &quot;bandha&quot; --рабство-- <em>(bandhaḥ iti)</em> идет <em>(anuvartate)</em> [из предыдущего афоризма]. Varga <em>(vargaḥ)</em> [это] то <em>(ayam)</em>, что <em>(yaḥ)</em> связано <em>(sambandhī)</em> с Источником/Чревом (Yoni) <em>(yoneḥ)</em>, т.е. с Майей (Māyā) --шестой таттвой, или категорией-- <em>(māyāyāḥ)</em>, причиной --kāraṇa-- <em>(kāraṇasya)</em> вселенной <em>(viśva)</em>, непосредственно <em>(sākṣāt)</em> и <em>(ca)</em> через непрерывную преемственность <em>(pāramparyeṇa)</em>. Этот [varga] <em>(tad)</em> [является] фактором <em>(hetukaḥ)</em>, порождающим <em>(ārambhī)</em> тела <em>(deha)</em>, миры <em>(bhuvana)</em>, и т.д. <em>(ādi)</em>, [и состоящим из] совокупности <em>(samūha)</em> таттв --категорий-- <em>(tattva)</em>, начинающихся <em>(ādi)</em> с Калаа (Kalā) <em>(kalā)</em> (или) ограниченного <em>(kiñcid)</em> действия/деятельности <em>(kartṛtā... ātmaka)</em>, и т.п. <em>(ādi)</em>, [и] заканчивающихся <em>(antaḥ)</em> землей <em>(kṣiti)</em>^|</p><p>Эта [же самая концепция] <em>(etad)</em> была обобщена <em>(api... saṅgṛhītam)</em> посредством этого [афоризм] <em>(anena eva)</em> [в Spandakārikā-s]:</p><blockquote><p>&quot;... этот (ограниченный человек), кто является недееспособным <em>(asamarthasya)</em> из-за его собственного <em>(nija)</em> загрязнения --aśuddhi-- <em>(aśuddhyā... iti)</em> (и) кто желает --abhilāṣī-- <em>(abhilāṣiṇaḥ)</em> совершать действия --т.е. действовать-- <em>(kartavyeṣu... iti)</em>...&quot;|<br> [См. Spandakārikā-s I, 9]</p></blockquote><p>И <em>(ca)</em> что <em>(yathā)</em> это <em>(etad)</em> так <em>(tathā)</em>, может быть известно и изучено <em>(avaboddhavyam)</em> из нашей <em>(asmadīyāt)</em> Spandanirṇaya --чрезвычайно авторитетного комментария на Spandakārikā-s-- <em>(spandanirṇayāt)</em>|</p><p>Сущность <em>(sva-rūpam)</em> этих <em>(eṣāṁ ca)</em> (проявлений, т.е.) Kalā <em>(kalā)</em>, и т.п. <em>(ādīnāṁ)</em>, характеризуется <em>(lakṣaṇam)</em> ограниченной <em>(kiñcid)</em> способностью к деятельности <em>(kartṛtva)</em> и т.д. <em>(ādi)</em> [и] привязана <em>(lagnam)</em> к Анавамале <em>(āṇavamala)</em>, как [своей] основе --т.е. Āṇavamala является основой этой сущности-- <em>(bhitti)</em>. [И поскольку такая сущность] скрывает, или покрывает <em>(āvarakatayā)</em> [истинную природу] индивидумов --puruṣa-s-- <em>(puṁsām)</em>, то является [полностью] доказанным <em>(siddham)</em>, что это - состояние мала (mala), или загрязнения <em>(malatvena)</em>|</p><p>Это <em>(yad)</em> было выражено <em>(uktam)</em> в почтенном _(śrīmat) Svacchandatantra <em>(svacchande)</em>:</p><blockquote><p>&quot;При омрачении <em>(pradhvasta)</em> малой, или загрязнением --т.е. Āṇavamala вместе с ее двуми отпрысками: Майийямалой (Māyīyamala) и Кармамалой (Kārmamala)-- <em>(mala)</em>, Caitanya, или Сознание с Абсолютной Свободой знать и делать все <em>(caitanyam)</em> наделено <em>(samāśritam)</em> Калаа (Kalā) <em>(kalā)</em> (и) Видья (Vidyā) <em>(vidyā)</em>. Также <em>(tathā)</em>, Это --а именно, Caitanya-- окрашено <em>(rañjita-ātmānam)</em> Рага (Rāga) <em>(rāgeṇa)</em> [и] разделено <em>(kalitam)</em> Каала (Kāla) <em>(kālena)</em>. Оно <em>(bhūyas)</em> ограничено <em>(yamitam)</em> Нияти (Niyati) <em>(niyatyā)</em> [и] преувеличено <em>(upabṛṁhitam)</em> чувством бытия <em>(bhāvena)</em> Пуруши (Puruṣa) --индивидуальной души-- <em>(puṁs)</em>. (Caitanya) становится наделенным <em>(sampannam)</em> задатками <em>(āśaya)</em> Пракрити (Prakṛti) <em>(pradhāna)</em> [и] связанным <em>(samanvitam)</em> с тремя <em>(traya)</em> Гунами --качествами Пракрити (Prakṛti)-- <em>(guṇa)</em>. Оно пребывает <em>(samāsīnam)</em> в таттве <em>(tattva)</em>, [известной как] интеллект <em>(buddhi)</em>, [и] окружено <em>(samāvṛtam)</em> эго <em>(ahaṅkāra)</em>. [Наконец, Caitanya наделено] умом <em>(manasā)</em>, силами знания и действия <em>(buddhi-karma-akṣaiḥ)</em>, тонкими <em>(tanmātraiḥ)</em> [и] грубыми <em>(sthūla)</em> элементами <em>(bhūtakaiḥ... iti)</em>^&quot;||<br> [См. Svacchandatantra II, 39-41]</p></blockquote><p>Акт сокрытия <em>(āvarakatvaṁ)</em>, [производимый] Кармамалой (Kārmamala)_ <em>(kārma-malasya)</em>, продемонстрирован <em>(pradarśitam)</em> в почтенном <em>(śrī)</em> Mālinīvijayatantra <em>(mālinīvijaye)</em> также <em>(api)</em>:</p><blockquote><p>&quot;Дхармические --т.е. благочестивые, связанные с собственными установленными обязанностями, и т.п.-- <em>(dharma)</em> [и] адхармические --противоположные дхармическим-- <em>(adharma-ātmakaṁ)</em> деяния <em>(karma)</em> характеризуются <em>(lakṣaṇam)</em> удовольствием <em>(sukha)</em>, болью <em>(duḥkha)</em>, и т.д. <em>(ādi... iti)</em>&quot;|<br> [См. Mālinīvijayatantra I, 24]</p></blockquote><p>То же самое <em>(tad etad)</em> было сказано <em>(uktam)</em> в почтенной <em>(śrī)</em> Īśvarapratyabhijñā <em>(pratyabhijñāyām)</em>, а именно, [что] Māyīya <em>(māyīyam)</em> и <em>(ca)</em> Kārma <em>(kārmam)</em> mala-s <em>(malam)</em> имеют Анавамалу (Āṇavamala) <em>(āṇavamala)</em>, как [их] основу <em>(bhittikam)</em> [и являются] особым <em>(viśiṣṭa)</em> [видом] сокращенного, или ограниченного <em>(saṅkucita)</em> знания <em>(jñānatayā eva)</em>:</p><blockquote><p>&quot;[Когда Āṇavamala уже присутствует, возникает] Māyīyamala <em>(māyā-ākhyaṁ)</em>, [которая приводит] в этот момент к <em>(atra eva)</em> распространению, или экспансии <em>(prathā)</em> объектов <em>(vedya)</em>, отличных _(bhinna) [друг от друга]. И <em>(ca)</em> когда имеется неведение <em>(abodhe)</em> относительно делателя <em>(kartari)</em>, [появляется] Kārmamala <em>(kārmam)</em>, которая дает _(dam) рождение <em>(janma)</em> [и] переживание наслаждения и боли <em>(bhoga)</em>. Эта _(tad) группа из трех [мал] <em>(trayam)</em> [проявляется] посредством Māyāśakti <em>(māyā-śaktyā eva)</em>^ [Шивы], несомненно <em>(tu... iti)</em>&quot;||<br> [См. Īśvarapratyabhijñā III, 2, 5]</p></blockquote><p>||3||</p><hr><ol><li><p>Таттвы, или категории с 7-й по 36-ю.</p></li><li><p>См. примечание 2 к комментарию на первый афоризм.</p></li><li><p>Весь процесс проявления с 7-й таттвы по 36-ю таттву в сжатом виде выражен в этом отрывке из Svacchandatantra.</p></li><li><p>Māyāśakti является божественной Силой, создающей различные формы. Не следует путать ее с Майятаттвой (Māyātattva) (шестой категорией, являющейся проявлением, производимым Им через эту самую Māyāśakti).</p></li></ol></div>',1)]))}const y=m(r,[["render",n]]);export{h as __pageData,y as default};
