import{_ as m,c as e,d as t,o as i}from"./chunks/framework.B7ZEGzqH.js";const u=JSON.parse('{"title":"Афоризм 4","description":"Шива сутра вимаршини. Глава 1.","frontmatter":{"title":"Афоризм 4","description":"Шива сутра вимаршини. Глава 1.","toc":true,"tags":"vim-1","sans":"ज्ञानाधिष्ठानं मातृका","trans":"Jñānādhiṣṭhānaṁ mātṛkā","date":"2021-02-21T00:00:00.000Z"},"headers":[],"relativePath":"texts/shiva-sutra-vimarshini/1/4/index.md","filePath":"texts/shiva-sutra-vimarshini/1/4/index.md"}'),r={name:"texts/shiva-sutra-vimarshini/1/4/index.md"};function h(o,a,n,s,p,k){return i(),e("div",null,a[0]||(a[0]=[t('<div class="stanza"><a href="#s1.4" id="s1.4" class="num">1.4</a><p>;;; sans</p><p>अथ कथमस्याज्ञानात्मकज्ञानयोनिवर्गकलाशरीररूपस्य त्रिविधस्य मलस्य बन्धकत्वमित्याह—</p><blockquote><p><strong>ज्ञानाधिष्ठानं मातृका॥४॥</strong></p></blockquote><p>यदेतत्त्रिविधमलस्वरूपमपूर्णम्मन्यताभिन्नवेद्यप्रथाशुभाशुभवासनात्मकं विविधं ज्ञानरूपमुक्तं तस्यादिक्षान्तरूपाज्ञाता माता मातृका विश्वजननी तत्तत्सङ्कुचितवेद्याभासात्मनो ज्ञानस्य अपूर्णोऽस्मि क्षामः स्थूलो वास्म्यग्निष्टोमयाज्यस्मीत्यादि तत्तदविकल्पकसविकल्पकावभासपरामर्शमयस्य तत्तद्वाचकशब्दानुवेधद्वारेण शोकस्मयहर्षरागादिरूपतामादधाना</p><blockquote><p>करन्ध्रचितिमध्यस्था ब्रह्मपाशावलम्बिकाः|<br> पीठेश्वर्यो महाघोरा मोहयन्ति मुहुर्मुहुः॥</p></blockquote><p>इति श्रीतिमिरोद्घाटप्रोक्तनीत्या वर्गकलाद्यधिष्ठातृब्राह्म्यादिशक्तिश्रेणीशोभिनी श्रीसर्ववीराद्यागमप्रसिद्धलिपिक्रमसन्निवेशोत्थापिकाम्बाज्येष्ठारौद्रीवामाख्यशक्तिचक्रचुम्बिता शक्तिरधिष्ठात्री तदधिष्ठानादेव ह्यन्तरभेदानुसन्धिवन्ध्यत्वात्क्षणमप्यलब्धविश्रान्तीनि बहिर्मुखान्येव ज्ञानानीति युक्तैव एषां बन्धकत्वोक्तिः। एतच्च</p><blockquote><p>शब्दराशिसमुत्थस्य...।</p></blockquote><p>इति कारिकया</p><blockquote><p>स्वरूपावरणे चास्य शक्तयः सततोत्थिताः।</p></blockquote><p>इति च कारिकया सङ्गृहीतम्॥४॥ ;;;</p><p>;;; trans Atha kathamasyājñānātmakajñānayonivargakalāśarīrarūpasya trividhasya malasya bandhakatvamityāha—</p><blockquote><p><strong>Jñānādhiṣṭhānaṁ mātṛkā||4||</strong></p></blockquote><p>Yadetattrividhamalasvarūpamapūrṇammanyatābhinnavedyaprathāśubhāśubhavāsanātmakaṁ vividhaṁ jñānarūpamuktaṁ tasyādikṣāntarūpājñātā mātā mātṛkā viśvajananī tattatsaṅkucitavedyābhāsātmano jñānasya apūrṇo&#39;smi kṣāmaḥ sthūlo vāsmyagniṣṭomayājyasmītyādi tattadavikalpakasavikalpakāvabhāsaparāmarśamayasya tattadvācakaśabdānuvedhadvāreṇa śokasmayaharṣarāgādirūpatāmādadhānā</p><blockquote><p>Karandhracitimadhyasthā brahmapāśāvalambikāḥ|<br> Pīṭheśvaryo mahāghorā mohayanti muhurmuhuḥ||</p></blockquote><p>iti śrītimirodghāṭaproktanītyā vargakalādyadhiṣṭhātṛbrāhmyādiśaktiśreṇīśobhinī śrīsarvavīrādyāgamaprasiddhalipikramasanniveśotthāpikāmbājyeṣṭhāraudrīvāmākhyaśakticakracumbitā śaktiradhiṣṭhātrī tadadhiṣṭhānādeva hyantarabhedānusandhivandhyatvātkṣaṇamapyalabdhaviśrāntīni bahirmukhānyeva jñānānīti yuktaiva eṣāṁ bandhakatvoktiḥ| Etacca</p><blockquote><p>Śabdarāśisamutthasya...|</p></blockquote><p>iti kārikayā</p><blockquote><p>Svarūpāvaraṇe cāsya śaktayaḥ satatotthitāḥ|</p></blockquote><p>iti ca kārikayā saṅgṛhītam||4||</p><p>;;;</p><p>&quot;Теперь <em>(atha)</em>, каким образом <em>(katham)</em> (это) <em>(asya)</em> тройственное <em>(trividhasya)</em> mala, или загрязнение <em>(malasya)</em>, чьей формой <em>(rūpasya)</em> является знание <em>(jñāna)</em>, состоящее <em>(ātmaka)</em> из ajñāna, или неведения --т.е. ограниченного знания-- <em>(ajñāna)</em>, Йониварги (Yonivarga) --Майи (Māyā) и ее отпрысков, т.е. Майияймалы (Māyīyamala)-- <em>(yoni-varga)</em>, [и] Калашариры (Kalāśarīra) --чьей формой является деятельность, т.е. Кармамала (Kārmamala)-- <em>(kalā-śarīra)</em>, становится оковами <em>(bandhakatvam... iti)</em> [для индивидуальной души?&quot;. Автор Śivasūtra-s, т.е. Шива] сказал <em>(āha)</em>—</p><blockquote><p><strong>Основой <em>(adhiṣṭhānam)</em> [ограниченного, или сокращенного] знания <em>(jñāna)</em> [является] непонятая Мать <em>(mātṛkā)</em>||4||</strong></p></blockquote><p>То <em>(etad)</em>, что <em>(yad)</em> было описано [ранее] <em>(uktam)</em>, - [это] разнообразные <em>(vividham)</em> формы <em>(rūpam)</em> [ограниченного] знания <em>(jñāna)</em>, чьей природой <em>(sva-rūpam)</em> [является] тройственное <em>(trividha)</em> mala --загрязнение-- <em>(mala)</em>, состоящее <em>(ātmakam)</em> из [1] акта рассмотрения [себя] <em>(manyatā)</em> как несовершенного <em>(apūrṇam)</em> --т.е. Анавамала (Āṇavamala)--, [2] распространения <em>(prathā)</em> ощущения <em>(vedya)</em> дифференциации <em>(bhinna)</em> --т.е. Māyīyamala--, [3] хороших <em>(śubha)</em> [и] плохих <em>(aśubha)</em> Vāsanā-s, или тенденций, накопленных в каузальном (причинном) теле, [которые приводят к совершению хороших и плохих действий соответственно] <em>(vāsanā)</em>^ --т.е. Kārmamala--. Это [тройственное ограниченное знание] <em>(tasya)</em>, Матрика (Mātṛkā) <em>(mātṛkā)</em>, Источник <em>(jananī)</em> вселенной <em>(viśva)</em>, [является] неизвестной <em>(ajñātā)</em> Матерью <em>(mātā)</em>, [и] Ее форма <em>(rūpā)</em> начинается <em>(ādi)</em> с &quot;a&quot; <em>(a)</em> [и] заканчивается &quot;kṣa&quot; <em>(kṣa)</em>. [Она является Матерью] знания <em>(jñānasya)</em>, чья природа <em>(ātmanaḥ)</em> [есть] вспышка <em>(ābhāsa)</em> различных <em>(tad tad)</em> сокращенных/ограниченных <em>(saṅkucita)</em> познаний <em>(vedyā)</em>. [Это ограниченное знание, или познание проявляется в форме] &quot;Я <em>(asmi)</em> несовершенный <em>(apūrṇaḥ)</em>&quot;, &quot;Я <em>(asmi)</em> тонкий <em>(kṣāmaḥ)</em>&quot; или <em>(vā)</em> &quot;Я <em>(asmi)</em> толстый <em>(sthūlaḥ)</em>&quot;, &quot;Я <em>(asmi)</em> исполнитель <em>(yājī)</em> Agniṣṭoma жертвоприношения <em>(agniṣṭoma)</em>&quot; и так далее <em>(iti ādi)</em>. [Это знание] сформировано <em>(mayasya)</em> различными <em>(tad tad)</em> соображениями <em>(parāmarśa)</em>, [некоторые из которых] проявляются <em>(avabhāsa)</em> без ментальных изменений --т.е. как понятия, выходящие за пределы обычного ума-- <em>(a-vikalpaka)</em>, [в то время как другие] обладают ментальными изменениями --т.е. конкретно сформулированными мыслями-- <em>(sa-vikalpaka)</em>, [и] посредством <em>(dvāreṇa)</em> проникновения <em>(anuvedha)</em> различных <em>(tad tad)</em> звуков <em>(śabda)</em> (и) слов <em>(vācaka)</em> [в ум того, кто слышит,] порождается <em>(dhānā)</em> интоксикация <em>(māda)</em> в форме <em>(rūpatā)</em> печали <em>(śoka)</em>, высокомерия <em>(smaya)</em>, радости <em>(harṣa)</em>, страсти <em>(rāga)</em>, и т.д. <em>(ādi)</em>. Согласно наставлениям <em>(nītyā)</em>, изложенным <em>(prokta)</em> в уважаемом <em>(śrī)</em> Timirodghāṭa <em>(timirodghāṭa)</em>:</p><blockquote><p>&quot;Mahāghorā-s --т.е. &quot;невероятно ужасные&quot;-- <em>(mahāghorāḥ)</em>, [это те śakti-s, или силы], которые пребывают <em>(sthāḥ)</em> в сердцевине <em>(madhya)</em> Сознания <em>(citi)</em> в Karandhra --т.е. в Brahmarandhra-- <em>(ka-randhra)</em>^, которые подвешивают <em>(avalambikāḥ)</em> арканом Брахмы (Brahmā) <em>(brahma-pāśa)</em> [и] являются повелительницами <em>(īśvaryaḥ)</em> pīṭha-s, или опор/сидений --органов чувств-- <em>(pīṭha)</em>, вводят в заблуждение <em>(mohayanti)</em> снова и снова <em>(muhur... muhur... iti)</em>&quot;||</p></blockquote><p>[Mātṛkā] сияет <em>(śobhinī)</em> в группе <em>(śreṇī)</em> сил <em>(śakti)</em>, [таких как] Brāhmī <em>(brāhmī)</em>, и т.д. <em>(ādi)</em>, главенствующих <em>(adhiṣṭhātṛ)</em> над группами букв <em>(varga)</em>, Kalā-s --пятью первозданными силами-- <em>(kalā)</em>, и т.п. <em>(ādi)</em>. Она пробуждает, или оживляет [живых существ] <em>(utthāpikā)</em> посредством расположения <em>(sanniveśa)</em> последовательности <em>(krama)</em> букв <em>(lipi)</em>, которая известна <em>(prasiddha)</em> в Āgama-s --проявленных священных Писаниях-- <em>(āgama)</em>, [таких как] почтенный <em>(śrī)</em> Sarvavīra <em>(sarvavīra)</em>, и др. <em>(ādi)</em>. Она тесно связана <em>(cumbitā)</em> с группой <em>(cakra)</em> сил <em>(śakti)</em>, имена которых <em>(ākhya)</em> Ambā <em>(ambā)</em>, Jyeṣṭhā <em>(jyeṣṭhā)</em>, Raudrī <em>(raudrī)</em> (и) Vāmā <em>(vāmā)</em>. [Проще говоря, Mātṛkā является] главенствующей _(adhiṣṭhātrī) Śakti, или Силой <em>(śaktiḥ)</em>. Поскольку [Mātṛkā] на самом деле является основой <em>(adhiṣṭhānāt eva)</em> этого [ограниченного знания] <em>(tad)</em>, [и] так как, [по той же причине], несомненно <em>(hi)</em> отсутствует <em>(vandhyatvāt)</em> исследование <em>(anusandhi)</em> внутреннего <em>(antas)</em> единства <em>(abheda)</em>, [ограниченные] познавательные процессы <em>(jñānāni)</em> не находят <em>(alabdha)</em> никакого покоя <em>(viśrāntīni)</em> даже <em>(api)</em> на мгновение <em>(kṣaṇam)</em> (и) лишь <em>(eva)</em> направлены на внешние вещи --а именно, наружу-- <em>(bahis-mukhāni)</em>. Итак <em>(iti)</em>, утверждение <em>(uktiḥ)</em>, что такое [познание] <em>(eṣāṁ)</em> [является] оковами <em>(bandhakatva)</em>, поистине <em>(eva)</em> верно <em>(yuktā)</em>|</p><p>Эта [же самая идея] <em>(etad ca)</em> была обобщенно представлена <em>(saṅgṛhītam)</em> с помощью [следующего] афоризма [Spandakārikā-s] <em>(kārikayā)</em>:</p><blockquote><p>&quot;... произведенный <em>(samutthasya)</em> из множества <em>(rāśi)</em> слов <em>(śabda... iti)</em>...&quot;|<br> [См. Spandakārikā-s III, 13]</p></blockquote><p>и также <em>(ca)</em> в [другом] афоризме [этого же Писания] <em>(kārikayā)</em>:</p><blockquote><p>&quot;Силы <em>(śaktayaḥ)</em> постоянно <em>(satata)</em> готовы <em>(utthitāḥ)</em> скрывать, или прятать _(āvaraṇe) сущностную природу _(sva-rūpa) этого [ограниченного существа] <em>(ca asya... iti)</em>...&quot;|<br> [См. Spandakārikā-s III, 15]</p></blockquote><p>||4||</p><hr><ol><li><p>Человек обладает четырьмя телами: (1) грубым, или физическим, (2) тонким, (3) каузальным (причинным) и (4) супракаузальным. Первое хорошо знакомо всем, так ведь? Второе тело содержит в себе жизненные энергии, ум, эго и интеллект. Каузальное тело выходит за пределы тонкого и содержит накопленные впечатления. &quot;Тенденции&quot;, упомянутые в комментарии, это как своего рода контейнеры, заполненные хорошей и плохой кармой... О! это длинная тема на самом деле. Просто поймите одно: эти тенденции, накопленные в вашем каузальном теле, подталкивают вас к совершению хороших и плохих действий. В этом смысл. Ах, да, чуть не забыл о ... супракаузальном теле, являющимся знаменитым Бинду (Bindu), или Точкой... другой длинный и трудный для понимания предмет, без сомнения. И конечно, здесь нет необходимости пускаться в такие тонкости и сложности в простом пояснительном примечании.</p></li><li><p>Эти силы отвечают за введение людей в заблуждение и побуждение их к мирской жизни. Они обитают в Karandhra, также известном как Brahmarandhra (т.е. отверстие Brahmā). Хотя Brahmarandhra обычно ошибочно принимается за Сахасрара-чакру (Sahasrāra-cakra), технически он (Brahmarandhra) находится в самом центре такой чакры, в области над родничками. Согласно моему собственному опыту, он буквально является отверстием, полным божественного Сознания. Когда человек входит в него, там очевидно ничего нет, но со временем, он как бы испытывает присутствие какой-то мембраны. В этой мембране есть напряжение, или давление. И Шива является этой самой мембраной, в то время как Шакти, или божественная Сила - это вышеупомянутое напряжение. Эти тайны полностью разъяснены мной в &quot;Писания (исследование)/Śivasūtravimarśinī&quot; в разделе Трика.</p></li></ol></div>',1)]))}const v=m(r,[["render",h]]);export{u as __pageData,v as default};
