import{_ as e,c as m,d as t,o as i}from"./chunks/framework.B7ZEGzqH.js";const d=JSON.parse('{"title":"Афоризм 5","description":"Шива сутра вимаршини. Глава 1.","frontmatter":{"title":"Афоризм 5","description":"Шива сутра вимаршини. Глава 1.","toc":true,"tags":"vim-1","sans":"उद्यमो भैरवः","trans":"Udyamo bhairavaḥ","date":"2021-02-20T00:00:00.000Z"},"headers":[],"relativePath":"texts/shiva-sutra-vimarshini/1/5/index.md","filePath":"texts/shiva-sutra-vimarshini/1/5/index.md"}'),r={name:"texts/shiva-sutra-vimarshini/1/5/index.md"};function u(o,a,p,h,s,v){return i(),m("div",null,a[0]||(a[0]=[t('<div class="stanza"><a href="#s1.5" id="s1.5" class="num">1.5</a><p>;;; sans</p><p>अथैतद्बन्धप्रशमोपायमुपेयविश्रान्तिसतत्त्वमादिशति—</p><blockquote><p><strong>उद्यमो भैरवः॥५॥</strong></p></blockquote><p>योऽयं प्रसरद्रूपाया विमर्शमय्याः संविदो झगित्युच्छलनात्मकपरप्रतिभोन्मज्जनरूप उद्यमः स एव सर्वशक्तिसामरस्येनाशेषविश्वभरितत्वात्सकलकल्पनाकुलालङ्कवलनमयत्वाच्च भैरवो भैरवात्मकस्वस्वरूपाभिव्यक्तिहेतुत्वाद्भक्तिभाजामन्तर्मुखैतत्तत्त्वावधानधनानां जायत इत्युपदिष्टं भवति। उक्तं च श्रीमालिनीविजये</p><blockquote><p>अकिञ्चिच्चिन्तकस्यैव गुरुणा प्रतिबोधतः|<br> जायते यः समावेशः शाम्भवोऽसावुदीरितः॥</p></blockquote><p>इति। अत्र हि गुरुणा प्रतिबोधत इत्यत्र गुरुतः स्वस्मात्प्रतिबोधत इत्यस्यार्थो गुरुभिरादिष्टः। श्रीस्वच्छन्देऽप्युक्तम्</p><blockquote><p>आत्मनो भैरवं रूपं भावयेद्यस्तु पुरुषः|<br> तस्य मन्त्राः प्रसिद्ध्यन्ति नित्ययुक्तस्य सुन्दरि॥</p></blockquote><p>इति। भावनं ह्यत्रान्तर्मुखोद्यन्तृतापदविमर्शनमेव। एतच्च</p><blockquote><p>एकचिन्ताप्रसक्तस्य यतः स्यादपरोदयः|<br> उन्मेषः स तु विज्ञेयः स्वयं तमुपलक्षयेत्॥</p></blockquote><p>इत्यनेन सङ्गृहीतम्॥५॥ ;;;</p><p>;;; trans Athaitadbandhapraśamopāyamupeyaviśrāntisatattvamādiśati—</p><blockquote><h3 id="udyamo-bhairavah-5" tabindex="-1">Udyamo bhairavaḥ||5|| <a class="header-anchor" href="#udyamo-bhairavah-5" aria-label="Permalink to &quot;Udyamo bhairavaḥ||5||&quot;">​</a></h3></blockquote><p>Yo&#39;yaṁ prasaradrūpāyā vimarśamayyāḥ saṁvido jhagityucchalanātmakaparapratibhonmajjanarūpa udyamaḥ sa eva sarvaśaktisāmarasyenāśeṣaviśvabharitatvātsakalakalpanākulālaṅkavalanamayatvācca bhairavo bhairavātmakasvasvarūpābhivyaktihetutvādbhaktibhājāmantarmukhaitattattvāvadhānadhanānāṁ jāyata ityupadiṣṭaṁ bhavati| Uktaṁ ca śrīmālinīvijaye</p><blockquote><p>Akiñciccintakasyaiva guruṇā pratibodhataḥ|<br> Jāyate yaḥ samāveśaḥ śāmbhavo&#39;sāvudīritaḥ||</p></blockquote><p>iti| Atra hi guruṇā pratibodhata ityatra gurutaḥ svasmātpratibodhata ityasyārtho gurubhirādiṣṭaḥ| Śrīsvacchande&#39;pyuktam</p><blockquote><p>Ātmano bhairavaṁ rūpaṁ bhāvayedyastu puruṣaḥ|<br> Tasya mantrāḥ prasiddhyanti nityayuktasya sundari||</p></blockquote><p>iti| Bhāvanaṁ hyatrāntarmukhodyantṛtāpadavimarśanameva| Etacca</p><blockquote><p>Ekacintāprasaktasya yataḥ syādaparodayaḥ|<br> Unmeṣaḥ sa tu vijñeyaḥ svayaṁ tamupalakṣayet||</p></blockquote><p>ityanena saṅgṛhītam||5||</p><p>;;;</p><p>Теперь <em>(atha)</em>, [следующий афоризм] учит <em>(ādiśati)</em> средствам <em>(upāyam)</em> прекращения <em>(praśama)</em> этого <em>(etad)</em> рабства <em>(bandha)</em>, [то есть,] истинной природе <em>(satattvam)</em> пребывания <em>(viśrānti)</em> в Том, который является --Целью, т.е. Высшим Я, также известным как Bhairava, или Śiva-- <em>(upeya)</em>—</p><blockquote><h3 id="bhairava-высшее-существо-bhairavah-это-внезапная-вспышка-или-возвышение-божественного-сознания-udyamah-5" tabindex="-1">Bhairava --Высшее Существо-- <em>(bhairavaḥ)</em> - (это) внезапная вспышка, или возвышение божественного Сознания <em>(udyamaḥ)</em>||5|| <a class="header-anchor" href="#bhairava-высшее-существо-bhairavah-это-внезапная-вспышка-или-возвышение-божественного-сознания-udyamah-5" aria-label="Permalink to &quot;Bhairava --Высшее Существо-- _(bhairavaḥ)_ - (это) внезапная вспышка, или возвышение божественного Сознания _(udyamaḥ)_||5||&quot;">​</a></h3></blockquote><p>Это <em>(ayam)</em> есть udyama <em>(udyamaḥ)</em>, который <em>(yaḥ)</em> (является) появлением <em>(unmajjana-rūpaḥ)</em> наивысшей <em>(para)</em> Pratibhā --т.е. Parāvāk, или Высшей Речи-- <em>(pratibhā)</em>^. [Такая Pratibhā] состоит <em>(ātmaka)</em> из мгновенного <em>(jhagiti)</em> появления <em>(ucchalana)</em> Saṁvid --Высшего Сознания-- <em>(saṁvidaḥ)</em>, которая --т.е. Saṁvid-- сотворена <em>(mayyāḥ)</em> из Vimarśa --Śakti, или Я-Сознания-- <em>(vimarśa)</em> [и] характеризуется <em>(rūpāyāḥ)</em> расширением [в форме вселенной] <em>(prasarat)</em>. Этот <em>(saḥ)</em> самый <em>(eva)</em> [udyama является] Bhairava --также называемый Śiva-- <em>(bhairavaḥ)</em>, поскольку [Bhairava] наполнен <em>(bharitatvāt)</em> всей <em>(aśeṣa)</em> вселенной <em>(viśva)</em>, оставаясь в единстве <em>(sāmarasyena)</em> со всеми <em>(sarva)</em> śakti-s, или силами <em>(śakti)</em>, [и] поскольку Он обладает <em>(mayatvāt)</em> способностью <em>(alam)</em> поглощать <em>(kavalana)</em> целую <em>(sakala)</em> группу <em>(kula)</em> идей, [производящих дуализм] <em>(kalpanā)</em>. [Такой udyama - это также Bhairava], т.к. он является причиной <em>(hetutvāt)</em> проявления <em>(abhivyakti)</em> собственной <em>(sva)</em> сущностной природы <em>(sva-rūpa)</em>, которая есть <em>(ātmaka)</em>, [в конечном счете,] Bhairava <em>(bhairava)</em> [также. И udyama] возникает <em>(jāyate)</em> в тех, кто обладает <em>(bhājām)</em> поклонением <em>(bhakti)</em> [и] сокровищем <em>(dhanānām)</em> внимания <em>(avadhāna)</em> к этому <em>(etad)</em> внутреннему <em>(antas-mukha)</em> Принципу --т.е. Bhairava, Богу-- <em>(tattva)</em>. Это то <em>(iti... bhavati)</em>, чему учит <em>(upadiṣṭam)</em> [данный афоризм]|</p><p>Было изложено <em>(uktam ca)</em> в почтенном <em>(śrī)</em> Mālinīvijayatantra <em>(mālinīvijaye)</em>:</p><blockquote><p>&quot;Говорят <em>(ud��ritaḥ)</em>, что śāmbhava-samāveśa, или поглощение в Śiva <em>(samāveśaḥ śāmbhavaḥ)</em> - [это] то <em>(asau)</em>, что <em>(yaḥ)</em> возникает <em>(jāyate)</em> только <em>(eva)</em> в том, кто не думает <em>(cintakasya)</em> ни о чем <em>(akiñcid)</em>, благодаря пробуждению <em>(pratibodhataḥ)</em>, [полученному] от Гуру <em>(guruṇā... iti)</em>&quot;||<br> [См. Mālinīvijayatantra II, 23]</p></blockquote><p>В этом отношении <em>(atra hi)</em>, (фраза) &quot;guruṇā pratibodhataḥ&quot; <em>(guruṇā pratibodhataḥ iti)</em> [может быть также интерпретирована] здесь <em>(atra)</em> как &quot;через собственное <em>(svasmāt)</em> великое <em>(gurutaḥ)</em> пробуждение <em>(pratibodhataḥ iti)</em>&quot;^. [Альтернативное] значение <em>(arthaḥ)</em> этой [фразы] <em>(asya)</em> было преподано <em>(ādiṣṭaḥ)</em> Гуру <em>(gurubhiḥ)</em>|</p><p>[Это] провозглашается <em>(uktam)</em> в освященном веками <em>(śrī)</em> Svacchandatantra <em>(svacchande)</em> также <em>(api)</em>:</p><blockquote><p>&quot;О прекрасная <em>(sundari)</em>^, определенно <em>(tu)</em>, для того <em>(tasya)</em> человека <em>(puruṣaḥ)</em>, который <em>(yaḥ)</em> осознает, или постигает <em>(bhāvayet)</em>, что его <em>(ātmanaḥ)</em> сущностная природа <em>(rūpam)</em> [есть] Bhairava <em>(bhairavam)</em>, когда он соединяется <em>(yuktasya)</em> с Вечностью <em>(nitya)</em>, Mantra-s <em>(mantrāḥ)</em> приносят [ему] плоды <em>(prasiddhyanti... iti)</em>&quot;||</p></blockquote><p>Несомненно <em>(hi)</em>, [слово] bhāvana --подразумевающееся в спряжении &quot;bhāvayet&quot;, упоминающееся в строфе Svacchandatantra-- <em>(bhāvanam)</em>, [означает] здесь <em>(atra)</em> постижение <em>(vimarśanam eva)</em> состояния <em>(pada)</em> внутреннего <em>(antas-mukha)</em> возвышения <em>(udyantṛtā)</em>|</p><p>Эта [идея] <em>(etad ca)</em> была [также] обобщена <em>(saṅgṛhītam)</em> в этой [строфе Spandakārikā-s] <em>(anena)</em>:</p><blockquote><p>&quot;Это <em>(saḥ)</em> должно быть безусловно известно <em>(tu vijñeyaḥ)</em>, как Unmeṣa <em>(unmeṣaḥ)</em>, откуда _(yatas) происходит _(syāt) возникновение _(udayaḥ) другого [осознания --согласно Kṣemarāja-- или (другой) мысли --согласно иным мнениям--] <em>(apara)</em>^ в [человеке], который [уже] занят <em>(prasaktasya)</em> одной <em>(eka)</em> мыслью <em>(cintā)</em>. Ему следует ощутить _(upalakṣayet) это [Unmeṣa] <em>(tam)</em> самому <em>(svayam... iti)</em>&quot;||<br> [См. Spandakārikā-s III, 9]</p></blockquote><p>||5||</p><hr><ol><li><p>Высшая Речь - это уровень, на котором слова и объекты, обозначенные ими, находятся в полном единстве. На самом деле, на этом этапе они даже еще не появились как слова и объекты. Таким образом, Высшая Речь является синонимом Высшего Сознания, из которого проявляется вся вселенная.</p></li><li><p>Потому что термин &quot;гуру&quot;, помимо &quot;духовного наставника&quot;, также означает &quot;великий&quot;. Следует отметить, что хотя &quot;духовный наставник&quot; является хорошим коротким переводом слова &quot;гуру&quot;, в этом контексте это не совсем точно, по моему скромному мнению, поскольку текст говорит о Гуру, т.е. о высоко-духовном человеке, который способен привести обычного человека из рабства к окончательному Освобождению. Вото почему я &quot;в основном&quot; предпочитаю оставлять слово &quot;гуру&quot; непереведенным.</p></li><li><p>Тантры обычно создаются в форме диалога между Господом Śiva и Pārvatī (Śakti), Его женой.</p></li><li><p>Kṣemarāja является мудрецом, который комментирует прямо сейчас, конечно. Он написал комментарии на Spandakārikā-s также.</p></li></ol></div>',1)]))}const b=e(r,[["render",u]]);export{d as __pageData,b as default};
