import{_ as a,d as e,c as m,g as t}from"./chunks/framework.DF5n-Kzr.js";const h=JSON.parse('{"title":"Афоризм 10","description":"Шива сутра вимаршини. Глава 2.","frontmatter":{"title":"Афоризм 10","description":"Шива сутра вимаршини. Глава 2.","tags":"vim-2","date":"2021-02-01T00:00:00.000Z"},"headers":[],"relativePath":"texts/shiva-sutra-vimarshini/2/10/index.md","filePath":"texts/shiva-sutra-vimarshini/2/10/index.md"}'),p={name:"texts/shiva-sutra-vimarshini/2/10/index.md"},i=t('<div class="stanza"><a href="#s2.10" id="s2.10" class="num">2.10</a><p>;;; sans</p><p>यदा त्वेवं सततावहितो न भवति तदा ज्ञानवतोऽप्यस्यावधानावलेपात्—</p><blockquote><p><strong>विद्यासंहारे तदुत्थस्वप्नदर्शनम्॥१०॥</strong></p></blockquote><p>प्रोक्तज्ञानस्फाररूपायाः शुद्धविद्यायाः संहारे निमज्जने तदुत्थस्य क्रमात्क्रमन्यक्कृतविद्यासंस्कारस्य स्वप्नस्य भेदमयस्य विकल्पप्रपञ्चरूपस्य दर्शनं स्फुटमुन्मज्जनं भवति। तदुक्तं श्रीमालिनीविजये</p><blockquote><p>न चैतदप्रसन्नेन शङ्करेणोपदिश्यते।<br> कथञ्चिदुपदिष्टेऽपि वासना नैव जायते॥</p></blockquote><p>इत्युपक्रम्य</p><blockquote><p>वासनामात्रलाभेऽपि योऽप्रमत्तो न जायते।<br> तमनित्येषु भोगेषु योजयन्ति विनायकाः॥</p></blockquote><p>इति। तदेतत्</p><blockquote><p>अन्यथा तु स्वतन्त्रा स्यात्सृष्तिस्तद्धर्मकत्वतः।<br> सततं लौकिकस्येव जाग्रत्स्वप्नपदद्वये॥</p></blockquote><p>इत्यनेन स्पन्दे सङ्गृहीतम्। अतश्च नित्यं शुद्धविद्याविमर्शनपरेणैव योगिना भाव्यमित्युपदिष्टं भवति। यथोक्तम्</p><blockquote><p>तस्मान्न तेषु संसक्तिं कुर्वीतोत्तमवाञ्छया।</p></blockquote><p>इति श्रीपूर्वे। श्रिस्पन्देऽपि</p><blockquote><p>अतः सततमुद्युक्तः स्पन्दतत्त्वविविक्तये।<br> जाग्रदेव निजं भावमचिरेणाधिगच्छति॥</p></blockquote><p>इति। एवं चित्तं मन्त्रः (२-१) इत्यतः प्रभृति मन्त्रवीर्यमुद्रावीर्यानुसन्धिप्रधानम्</p><blockquote><p>उच्चाररहितं वस्तु चेतसैव विचिन्तयन्।<br> यं समावेशमाप्नोति शाक्तः सोऽत्राभिधीयते॥</p></blockquote><p>इत्याम्नातं शाक्तोपायं विविच्यावधानावलिप्तं प्रति विद्यासंहारे तदुत्थस्वप्नदर्शनम् (२-१०) इति सूत्रेणैतदनुषङ्गेणाणवोपायप्रतिपादनस्यावकाशो दत्तः। इति शिवम्॥१०॥ ;;;</p><hr><p>;;; trans</p><p>Yadā tvevaṁ satatāvahito na bhavati tadā jñānavato&#39;pyasyāvadhānāvalepāt—</p><blockquote><p><strong>Vidyāsaṁhāre tadutthasvapnadarśanam||10||</strong></p></blockquote><p>Proktajñānasphārarūpāyāḥ śuddhavidyāyāḥ saṁhāre nimajjane tadutthasya kramātkramanyakkṛtavidyāsaṁskārasya svapnasya bhedamayasya vikalpaprapañcarūpasya darśanaṁ sphuṭamunmajjanaṁ bhavati| Taduktaṁ śrīmālinīvijaye</p><blockquote><p>Na caitadaprasannena śaṅkareṇopadiśyate|<br> Kathañcidupadiṣṭe&#39;pi vāsanā naiva jāyate||</p></blockquote><p>ityupakramya</p><blockquote><p>Vāsanāmātralābhe&#39;pi yo&#39;pramatto na jāyate|<br> Tamanityeṣu bhogeṣu yojayanti vināyakāḥ||</p></blockquote><p>iti| Tadetat</p><blockquote><p>Anyathā tu svatantrā syātsṛṣtistaddharmakatvataḥ|<br> Satataṁ laukikasyeva jāgratsvapnapadadvaye||</p></blockquote><p>ityanena spande saṅgṛhītam| Ataśca nityaṁ śuddhavidyāvimarśanapareṇaiva yoginā bhāvyamityupadiṣṭaṁ bhavati| Yathoktam</p><blockquote><p>Tasmānna teṣu saṁsaktiṁ kurvītottamavāñchayā|</p></blockquote><p>iti śrīpūrve| Śrispande&#39;pi</p><blockquote><p>Ataḥ satatamudyuktaḥ spandatattvaviviktaye|<br> Jāgradeva nijaṁ bhāvamacireṇādhigacchati||</p></blockquote><p>iti| Evaṁ cittaṁ mantraḥ (2-1) ityataḥ prabhṛti mantravīryamudrāvīryānusandhipradhānam</p><blockquote><p>Uccārarahitaṁ vastu cetasaiva vicintayan|<br> Yaṁ samāveśamāpnoti śāktaḥ so&#39;trābhidhīyate||</p></blockquote><p>ityāmnātaṁ śāktopāyaṁ vivicyāvadhānāvaliptaṁ prati vidyāsaṁhāre tadutthasvapnadarśanam (2-10) iti sūtreṇaitadanuṣaṅgeṇāṇavopāyapratipādanasyāvakāśo dattaḥ| Iti śivam||10||</p><p>;;;</p><hr><p>Но <em>(tu)</em> когда <em>(yadā)</em> [Yogī] <em>(bhavati)</em> не <em>(na)</em> столь <em>(evam)</em> внимателен <em>(avahitaḥ)</em> постоянно <em>(satata)</em>, тогда <em>(tadā)</em> даже <em>(api)</em> в случае [этого] познающего [свое Высшее Я] <em>(jñānavataḥ)</em>, [случается то, чему учит следующий афоризм,] из-за уменьшения, или ослабления <em>(avalepāt)</em> его <em>(asya)</em> внимания <em>(avadhāna)</em>—</p><blockquote><p><strong>При исчезновении <em>(saṁhāre)</em> [Чистого] Знания <em>(vidyā)</em>, (происходит) появление <em>(darśanam)</em> ментальных изменений [как во сне] <em>(svapna)</em>, возникающих (uttha)_ из-за этого <em>(tad)</em>, [то есть, &quot;возникающих из-за предшествующего исчезновения Чистого Знания&quot;]||10||</strong></p></blockquote><p>При исчезновении <em>(saṁhāre)</em> [или] погружении во мрак <em>(nimajjane)</em> Чистого Знания <em>(śuddha-vidyāyāḥ)</em>, которое является <em>(rūpāyāḥ)</em> расширением <em>(sphāra)</em> вышеупомянутого <em>(prokta)</em> знания <em>(jñāna)</em> --описанного в предыдущем афоризме--<sup>1</sup></p><p>, имеет место <em>(bhavati)</em> возникновение <em>(darśanam)</em>, [или] явственное <em>(sphuṭam)</em> проявление <em>(unmajjanam)</em> svapna -- сна со сновидениями-- <em>(svapnasya)</em>, т.е. проявление многообразия <em>(prapañca)</em>, полного <em>(mayasya)</em> различий <em>(bheda)</em> (и) ментальных изменений <em>(vikalpa)</em> [как во сне. Этот svapna] возникает из-за этого <em>(tad-utthasya)</em>, а именно, поскольку остаточные впечатления <em>(saṁskārasya)</em> [Чистого] Знания <em>(vidyā)</em> угасают <em>(nyakkṛta)</em> постепенно <em>(kramāt-krama)</em><sup>2</sup> |</p><p>Это <em>(tad)</em> утверждается <em>(uktam)</em> в досточтимом Mālinīvijayatantra <em>(śrī-mālinīvijaye)</em> [Господом], начиная [со следующего] <em>(upakramya)</em>, [когда Он говорит о достижении Śivatā, или состоянии Śiva, которое совпадает с появлением Чистого Знания]:</p><blockquote><p>&quot;Этому <em>(etad)</em> учит <em>(upadiśyate)</em> Śaṅkara --т.е. Śiva-- <em>(śaṅkareṇa)</em>, когда он действительно доволен <em>(na ca... aprasannena)</em>!<sup>3</sup> [Но] даже <em>(api)</em>, когда это преподается <em>(upadiṣṭe)</em> так или иначе <em>(kathañcid)</em>, отпечатка [от этого Чистого Знания, или состояния Śiva] <em>(vāsanā)</em> не <em>(na eva)</em> остается <em>(jāyate... iti)</em>&quot;||</p></blockquote><p>[Позже, было заявлено в Mālinīvijayatantra, что]</p><blockquote><p>&quot;[Однако,] того, кто <em>(yaḥ... tam)</em> не <em>(na)</em> остается <em>(jāyate)</em> внимательным <em>(apramattaḥ)</em>, даже когда <em>(api)</em> он достигает --при достижении-- <em>(lābhe)</em> просто <em>(mātra)</em> латентных отпечатков, [порожденных ограниченными сверхъестественными силами] <em>(vāsanā)</em>, Vināyaka-s --определенный тип демонов-- <em>(vināyakāḥ)</em> побуждают и принуждают <em>(yojayanti)</em> к преходящим <em>(anityeṣu)</em> удовольствиям <em>(bhogeṣu... iti)</em>&quot;||</p></blockquote><p>Те же самые [наставления] <em>(tad etad)</em> были обобщены <em>(saṅgṛhītam)</em> этим [афоризмом] <em>(anena)</em> в Spandakārikā-s <em>(spande)</em>:</p><blockquote><p>&quot;В противном случае <em>(anyathā tu)</em><sup>4</sup>, сила проявления <em>(sṛṣṭiḥ)</em>, согласно ее <em>(tad)</em> характеристикам <em>(dharmakatvataḥ)</em>, остается <em>(syāt)</em> всегда <em>(satatam)</em> свободной <em>(svatantrā)</em> [действовать] как <em>(iva)</em> в случае обычных людей <em>(laukikasya)</em>, во время двух <em>(dvaye)</em> состояний <em>(pada)</em> бодрствования <em>(jāgrat)</em> [и] сна со сновидениями <em>(svapna)</em>&quot;||<br> [См. Spandakārikā-s III, 3]</p></blockquote><p>По этой самой [причине] <em>(atas ca)</em>, это <em>(bhavati)</em> преподается <em>(upadiṣṭam)</em> [как] &quot;[акт пребывания в своей сущностной природе] должен всегда выполняться <em>(nityam... bhāvyam)</em> Yogī <em>(yoginā)</em>, главным объектом которого <em>(pareṇa eva)</em> является осознавание <em>(vimarśana)</em> Чистого <em>(śuddha)</em> Знания <em>(vidyā... iti)</em>&quot;|</p><p>Согласно тому, что было высказано <em>(yathā-uktam)</em> в уважаемом Pūrva --т.е. в Mālinīvijayatantra-- <em>(śrī-pūrve)</em> --в третьей строке, которая завершает вышеупомянутую строфу, начиная с &quot;[Однако,] того, кто не остается внимательным...&quot;--:</p><blockquote><p>&quot;Следовательно <em>(tasmāt)</em>, посредством [своего] желания <em>(vāñchayā)</em> [достичь] Высшую Реальность <em>(uttama)</em>, [такому Yogī] не следует вступать в тесный контакт <em>(na... saṁsaktim kurvīta)</em> с этими [преходящими удовольствиями] <em>(teṣu... iti)</em>&quot;|</p></blockquote><p>Также <em>(api)</em>, в Spandakārikā-s <em>(spande)</em> [было сказано:]</p><blockquote><p>&quot;По этой причине <em>(atas)</em>, тот, кто постоянно <em>(satatam)</em> готов <em>(udyuktaḥ)</em> к распознаванию <em>(viviktaye)</em> принципа <em>(tattva)</em> Спанды <em>(spanda)</em>, достигает <em>(adhigacchati)</em> своего собственного <em>(nijam)</em> [сущностного] состояния, или природы <em>(bhāvam)</em> быстро <em>(acireṇa)</em>, [даже в] (состоянии) бодрствования <em>(jāgrat eva... iti)</em>&quot;||<br> [См. Spandakārikā-s I, 21]</p></blockquote><p>Так <em>(evam)</em>, [Вторая Глава,] начиная с этого [афоризма] <em>(atas prabhṛti)</em>: &quot;Ум [того, кто постоянно размышляет над Высшей Реальностью] <em>(cittam)</em> [есть] Мантра <em>(mantraḥ... iti)</em>&quot; —афоризм 1 второй Главы <em>(2-1)</em>—, показывает [в первую очередь] --буквально, &quot;после показа&quot;, или &quot;показ&quot;-- <em>(vivicya)</em>, что Śāktopāya <em>(śākta-upāyam)</em>, которая охарактеризована проявленными Священными Писаниями --Āgama-s-- <em>(āmnātam)</em> [следующим образом:]</p><blockquote><p>&quot;Тот, кто размышляет <em>(vicintayan)</em> посредством [своего] ума <em>(cetasā eva)</em> над Реальностью <em>(vastu)</em>, которую невозможно <em>(rahitam)</em> выразить словами <em>(uccāra)</em> , достигает <em>(āpnoti)</em> поглощения [в Высшем Я] <em>(yam samāveśam)</em>. Здесь <em>(atra)</em>, это [поглощение] <em>(saḥ)</em> следует рассматривать <em>(abhidhīyate)</em> как śākta --т.е. достигаемое через Śāktopāya-- <em>(śāktaḥ... iti)</em>&quot;||<br> [См. Mālinīvijayatantra II, 22]</p></blockquote><p>в основном относится <em>(pradhānam)</em> к интенсивному осознаванию <em>(anusandhi)</em> силы <em>(vīrya... vīrya)</em> мантры и мудры <em>(mantra... mudrā)</em>. [Позже,] посредством [последнего] афоризма [текущей Главы] <em>(sūtreṇa)</em> —афоризма 10 второй Главы <em>(2-10)</em>—: &quot;При исчезновении <em>(saṁhāre)</em> [Чистого] Знания <em>(vidyā)</em>, происходит проявление <em>(darśanam)</em> ментальных изменений [как во сне] <em>(svapna)</em>, возникающих <em>(uttha)</em> из-за этого <em>(tad... iti)</em>, [то есть, &quot;возникающих из-за предшествующего исчезновения Чистого Знания&quot;]&quot;, который обращен <em>(prati)</em> к [искателю] со слабым вниманием <em>(avadhāna-avaliptam)</em>, в этой связи <em>(etad-anuṣaṅgeṇa)</em> дается <em>(dattaḥ)</em> возможность <em>(avakāśaḥ)</em> для представления <em>(pratipādanasya)</em> Āṇavopāya <em>(āṇava-upāya)</em>, [т.е. упайи <em>(upāya)</em>, или методов, которые рассматриваются в следующей Главе]<sup>5</sup>|</p><p>Да будет благо [для всех существ] <em>(iti śivam)</em>!||10||</p><hr><ol><li><p>Чистое Знание есть ничто иное, как расширение ограниченного знания, также как последнее — это сокращение первого. Если кто-то считает, что оба отличны друг от друга, значит он еще не осознал сути Реальности.</p></li><li><p>Конечно, мой перевод последней части был адаптирован, поскольку структура английского языка не соответствует той, что имеется в санскрите. Буквально на санскрите это звучит так: &quot;tadutthasya kramātkramanyakkṛtavidyāsaṁskārasya&quot; - &quot;из того, что возникает из-за этого, того, в котором остаточные отпечатки/ впечатления от Знания постепенно исчезают&quot;. Поскольку это правильно на санскрите, но трудно читать на английском, мне пришлось выстроить предложение по другому, а также для завершения смысла добавить термины в круглых скобках: &quot;[Этот svapna] возникает из-за этого, а именно, поскольку остаточные отпечатки/впечатления [Чистого] Знания угасают постепенно&quot;.</p></li><li><p>Фраза &quot;Na ca... aprasannena&quot;, когда переводится буквально, означает: &quot;Не... тем, кто не доволен&quot;. Частица &quot;ca&quot; не означает здесь &quot;и&quot; (союз), но работает просто как дополнительная, добавляя выразительность, оставаясь не переведенной. В свою очередь, когда имеется комбинация &quot;na + na&quot; или &quot;na +привативная частица&quot;, результатом является сильное утверждение. Привативная частица в этом случае — это &quot;a&quot;в &quot;a-prasannena&quot;. Поэтому я перевел все в утвердительном и добавил &quot;действительно&quot; ради большего акцента: &quot;когда Он действительно доволен!&quot;. Ну хорошо, просто грамматический комментарий для санскритских студентов, желающих знать, почему я сделал все это.</p></li><li><p>&quot;В противном случае&quot;, то есть, &quot;когда Yogī пренебрегает его мольбой и перестает осознавать свою сущностную природу&quot;.</p></li><li><p>Последний афоризм этой Главы дает благоприятную возможность, или случай (anuṣaṅga) для представления Āṇavopāya, поскольку это означает , что Śuddhavidyā, или Чистое Знание исчезло. Чистое Знание жизненно важно для успеха в Śāktopāya, но искатель, чье внимание слабое, теряет точку зрения этой Śuddhavidyā. Тогда, к какому методу, или upāya прибегнет искатель? Ответ на это: к Āṇavopāya. Таково значение.</p></li></ol></div><div class="stanza"><h2 id="конец-главы-ii" tabindex="-1">Конец Главы II <a class="header-anchor" href="#конец-главы-ii" aria-label="Permalink to &quot;Конец Главы II&quot;">​</a></h2><p>;;; sans आदितः सूत्रसङ्ख्या॥३२॥</p><p>इति श्रीमन्माहेश्वराचार्याभिनवगुप्तपादपद्मोपजीविश्रीक्षेमराजविरचितायां शिवसूत्रविमर्शिन्यां शाक्तोपायप्रकाशनं नाम द्वितीय उन्मेषः॥२॥ ;;;</p><hr><p>;;; trans</p><p>Āditaḥ sūtrasaṅkhyā||32||</p><p>Iti śrīmanmāheśvarācāryābhinavaguptapādapadmopajīviśrīkṣemarājaviracitāyāṁ śivasūtravimarśinyāṁ śāktopāyaprakāśanaṁ nāma dvitīya unmeṣaḥ||2||</p><p>;;;</p><hr><p>С самого начала [Писания] <em>(āditaḥ)</em>, количество <em>(saṅkhyā)</em> афоризмов <em>(sūtra)</em> [теперь составляет] тридцать два <em>(32)</em> --22 в первой Главе и 10 во второй Главе--||</p><p>Здесь заканчивается <em>(iti)</em> вторая <em>(dvitīyaḥ)</em> Глава <em>(unmeṣaḥ)</em>, называемая <em>(nāma)</em> Śāktopāyaprakāśana --[Глава], раскрывающая методы, имеющие отношение к Śakti-- <em>(śākta-upāya-prakāśanam)</em>, в Шивасутравимаршини <em>(śivasūtravimarśinyām)</em>, написанная <em>(viracitāyām)</em> почтенным <em>(śrī)</em> Kṣemarāja <em>(kṣemarāja)</em>, зависящим от <em>(upajīvi)</em> лотосных стоп --т.е. стоп, прекрасных как лотос-- <em>(pāda-padma)</em> выдающегося <em>(śrīmat)</em> духовного наставника <em>(ācārya)</em> Абхинавагупты <em>(abhinavagupta)</em>, великого преданного Maheśvara, Великого Господа --эпитет Śiva-- <em>(māheśvara)</em>||2||</p></div>',2),o=[i];function u(n,r,s,v,d,y){return m(),e("div",null,o)}const q=a(p,[["render",u]]);export{h as __pageData,q as default};
