import{_ as e,c as m,d as t,o as r}from"./chunks/framework.B7ZEGzqH.js";const h=JSON.parse('{"title":"Афоризм 1","description":"Шива сутра вимаршини. Глава 2.","frontmatter":{"title":"Афоризм 1","description":"Шива сутра вимаршини. Глава 2.","tags":"vim-2","date":"2021-02-10T00:00:00.000Z"},"headers":[],"relativePath":"texts/shiva-sutra-vimarshini/2/1/index.md","filePath":"texts/shiva-sutra-vimarshini/2/1/index.md"}'),p={name:"texts/shiva-sutra-vimarshini/2/1/index.md"};function i(n,a,o,u,s,v){return r(),m("div",null,a[0]||(a[0]=[t('<div class="stanza"><a href="#s2.1" id="s2.1" class="num">2.1</a><p>;;; sans</p><p>इदानीं शाक्तोपायः प्रदर्श्यते। तत्र शक्तिर्मन्त्रवीर्यस्फाररूपेति प्रथमोन्मेषान्तसूत्रिततत्स्वरूपविवेचनपुरःसरमुन्मेषान्तरमारभमाणो मन्त्रस्वरूपं तावन्निरूपयति—</p><blockquote><p><strong>चित्तं मन्त्रः॥१॥</strong></p></blockquote><p>चेत्यते विमृश्यतेऽनेन परं तत्त्वमिति चित्तं पूर्णस्फुरत्तासतत्त्वप्रासादप्रणवादिविमर्शरूपं संवेदनं तदेव मन्त्र्यते गुप्तमन्तरभेदेन विमृश्यते परमेश्वररूपमनेनेति कृत्वा मन्त्रः। अत एव च परस्फुरत्तात्मकमननधर्मात्मता भेदमयसंसारप्रशमनात्मकत्राणधर्मता चास्य निरुच्यते।</p><p>अथ च मन्त्रदेवताविमर्शपरत्वेन प्राप्ततत्सामरस्यमाराधकचित्तमेव मन्त्रो न तु विचित्रवर्णसङ्घट्टनामात्रकम्। यदुक्तं श्रीमत्सर्वज्ञानोत्तरे</p><blockquote><p>उच्चार्यमाणा ये मन्त्रा न मन्त्रांश्चापि तान्विदुः।<br> मोहिता देवगन्धर्वा मिथ्याज्ञानेन गर्विताः॥</p></blockquote><p>इति। श्रीतन्त्रसद्भावेऽपि</p><blockquote><p>मन्त्राणां जीवभूता तु या स्मृता शक्तिरव्यया।<br> तया हीना वरारोहे निष्फलाः शरदभ्रवत्॥</p></blockquote><p>इति। श्रीश्रीकण्ठीयसंहितायां तु</p><blockquote><p>पृथङ्मन्त्रः पृथङ्मन्त्री न सिद्ध्यति कदाचन।<br> ज्ञानमूलमिदं सर्वमन्यथा नैव सिद्ध्यति॥</p></blockquote><p>इत्युक्तम्। एतच्च स्पन्दे</p><blockquote><p>सहाराधकचित्तेन तेनैते शिवधर्मिणः॥</p></blockquote><p>इति भङ्ग्या प्रतिपादितम्॥१॥ ;;;</p><hr><p>;;; trans</p><p>Idānīṁ śāktopāyaḥ pradarśyate| Tatra śaktirmantravīryasphārarūpeti prathamonmeṣāntasūtritatatsvarūpavivecanapuraḥsaramunmeṣāntaramārabhamāṇo mantrasvarūpaṁ tāvannirūpayati—</p><blockquote><p><strong>Cittaṁ mantraḥ||1||</strong></p></blockquote><p>Cetyate vimṛśyate&#39;nena paraṁ tattvamiti cittaṁ pūrṇasphurattāsatattvaprāsādapraṇavādivimarśarūpaṁ saṁvedanaṁ tadeva mantryate guptamantarabhedena vimṛśyate parameśvararūpamaneneti kṛtvā mantraḥ| Ata eva ca parasphurattātmakamananadharmātmatā bhedamayasaṁsārapraśamanātmakatrāṇadharmatā cāsya nirucyate|</p><p>Atha ca mantradevatāvimarśaparatvena prāptatatsāmarasyamārādhakacittameva mantro na tu vicitravarṇasaṅghaṭṭanāmātrakam| Yaduktaṁ śrīmatsarvajñānottare</p><blockquote><p>Uccāryamāṇā ye mantrā na mantrāṁścāpi tānviduḥ|<br> Mohitā devagandharvā mithyājñānena garvitāḥ||</p></blockquote><p>iti| Śrītantrasadbhāve&#39;pi</p><blockquote><p>Mantrāṇāṁ jīvabhūtā tu yā smṛtā śaktiravyayā|<br> Tayā hīnā varārohe niṣphalāḥ śaradabhravat||</p></blockquote><p>iti| Śrīśrīkaṇṭhīyasaṁhitāyāṁ tu</p><blockquote><p>Pṛthaṅmantraḥ pṛthaṅmantrī na siddhyati kadācana|<br> Jñānamūlamidaṁ sarvamanyathā naiva siddhyati||</p></blockquote><p>ityuktam| Etacca spande</p><blockquote><p>Sahārādhakacittena tenaite śivadharmiṇaḥ||</p></blockquote><p>iti bhaṅgyā pratipāditam||1||</p><p>;;;</p><hr><p>Сейчас <em>(idānīm)</em>, [Śāktopāya,] методы <em>(upāyaḥ)</em>, имеющие отношение к Śakti <em>(śākta)</em><sup>1</sup>, описываются <em>(pradarśyate)</em>|</p><p>Вторая <em>(antaram)</em> Глава <em>(unmeṣa)</em> в первую очередь <em>(tāvat)</em> определяет <em>(nirūpayati)</em> природу <em>(sva-rūpam)</em> мантры <em>(mantra)</em>, начиная <em>(ārabhamāṇaḥ)</em> с <em>(puraḥsaram)</em> исследования <em>(vivecana)</em> ее --т.е. мантры-- <em>(tad)</em> сущностных характеристик <em>(sva-rūpa)</em>, как заявлено в последнем афоризме <em>(sūtrita anta)</em> Первой <em>(prathama)</em> Главы <em>(unmeṣa)</em>. В этом [афоризме] <em>(tatra)</em> [кратко говорится, что] &quot;Śakti <em>(śaktiḥ)</em> является расширением <em>(sphāra-rūpā)</em> порождающего источника --могущества, или силы-- <em>(vīrya)</em> [всех] мантр <em>(mantra... iti)</em>&quot;—</p><blockquote><p><strong>Ум [того, кто постоянно размышляет над Высшей Реальностью] <em>(cittam)</em> [есть] Мантра <em>(mantraḥ)</em>||1||</strong></p></blockquote><p>Citta --в этом контексте-- <em>(cittam)</em> [— это] &quot;то, посредством чего <em>(anena)</em> Высший <em>(param)</em> Принцип <em>(tattvam)</em> обдумывается <em>(cetyate)</em> [и] постигается <em>(vimṛśyate... iti)</em>&quot;. [Т.е. это] сознание <em>(saṁvedanam)</em>, чья форма, или природа <em>(rūpam)</em> [состоит из] исследования <em>(vimarśa)</em> Prāsāda <em>(prāsāda)</em>, Praṇava <em>(praṇava)</em>, и т.д. <em>(ādi)</em><sup>2</sup></p><p>, которые являются сущностью <em>(satattva)</em> Совершенной <em>(pūrṇa)</em> Sphurattā --т.е. &quot;пульсирующей вспышки&quot;, это эпитет Śakti, которая является вашим собственным Я-Сознанием-- <em>(sphurattā)</em>. Это <em>(tad)</em> самое <em>(eva)</em> [Citta является] мантрой <em>(mantraḥ)</em>; [и мантра] рассматривается --т.е. считается-- <em>(iti kṛtvā)</em> &quot;тем, просредством чего <em>(anena)</em> природа <em>(rūpam)</em> Высшего <em>(parama)</em> Господа <em>(īśvara)</em> обдумывается <em>(mantryate)</em> втайне <em>(guptam)</em>, т.е. [посредством чего, эта природа] исследуется <em>(vimṛśyate)</em> внутренне <em>(antar)</em> в единстве <em>(abhedena... iti)</em> <sup>3</sup> &quot;|</p><p>Исходя из этого самого [факта] <em>(atas eva ca)</em>, интерпретируется, или объясняется [этимологически и акростически]<em>(nirucyate)</em> ее --т.е. мантры-- <em>(asya)</em> характеристика <em>(dharma-ātmatā)</em> manana, [или размышление над] <em>(manana)</em>, которой состоит из <em>(ātmaka)</em> Высшей <em>(para)</em> Sphurattā --или &quot;пульсирующей вспышки&quot;, как упоминалось ранее; короче, &quot;Я-Сознание&quot;-- <em>(sphurattā)</em> также как и <em>(ca)</em> [ее другая] характеристика <em>(dharmatā)</em> trāṇa, [или защита] <em>(trāṇa)</em>, состоящая из <em>(ātmaka)</em> уничтожения <em>(praśamana)</em> Самсары (Saṁsāra) --т.е. трансмиграционного существования-- <em>(saṁsāra)</em>, полного <em>(maya)</em> различий, или двойственности <em>(bheda)</em> <sup>4</sup>|</p><p>Кроме того <em>(atha ca)</em>, [это] просто <em>(eva)</em> ум <em>(cittam)</em> преданного <em>(ārādhaka)</em>, который достиг единства <em>(prāpta... sāmarasyam)</em> с Этим--Высшим Я-- <em>(tad)</em> благодаря полному посвящению себя <em>(paratvena)</em> интенсивному осознаванию <em>(vimarśa)</em> божества мантры <em>(mantra devatā)</em>, [которое является] мантрой <em>(mantraḥ)</em>, а не <em>(na tu)</em> так называемой <em>(nāma-atrakam)</em> совокупностью <em>(saṅghaṭṭa)</em> множества <em>(vicitra)</em> букв <em>(varṇa)</em>|</p><p>Это <em>(yad)</em> сказано <em>(uktam)</em> в наиболее почитаемом <em>(śrīmat)</em> Sarvajñānottara <em>(sarvajñānottare)</em>:</p><blockquote><p>&quot;[Это] не <em>(na)</em> мантры <em>(mantrāḥ)</em>, которые <em>(ye)</em> произносятся <em>(uccāryamāṇāḥ)</em>. И <em>(ca)</em> даже <em>(api)</em> обманутые <em>(mohitāḥ)</em> [и] тщеславные <em>(garvitāḥ)</em> боги и небесные музыканты <em>(deva-gandharvāḥ)</em>, из-за иллюзорного знания <em>(mithyā-jñānena)</em>, считают <em>(viduḥ)</em> их <em>(tān)</em> мантрами <em>(mantrān... iti)</em>&quot;||</p></blockquote><p>[Та же истина] также <em>(api)</em> [высказана] в досточтимом Tantrasadbhāva <em>(śrī-tantrasadbhāve)</em>:</p><blockquote><p>&quot;Она, которая <em>(yā)</em> считается <em>(smṛtā)</em> вечной <em>(avyayā)</em> Śakti <em>(śakti)</em> --т.е. Я-Сознание-- является <em>(bhūtā)</em> безусловно <em>(tu)</em> душой <em>(jīva)</em> мантр <em>(mantrāṇām)</em>. О Прекрасная <em>(vara-ārohe)</em>!, без <em>(hīnāḥ)</em> Нее <em>(tayā)</em>, они --т.е. мантры-- бесплодны <em>(niṣphalāḥ)</em> как <em>(vat)</em> осенние <em>(śarad)</em> облака <em>(abhra... iti)</em> --поскольку эти облака совсем не дают дождя--&quot;||</p></blockquote><p>Было сказано <em>(uktam)</em> в очень уважаемом Kaṇṭhīyasaṁhitā <em>(śrī-śrī-kaṇṭhīyasaṁhitāyām tu)</em>:</p><blockquote><p>&quot;[Если] мантра <em>(mantraḥ)</em> [и] практик мантры <em>(mantrī)</em> отличны друг от друга <em>(pṛthak... pṛthak)</em>, [тогда такая мантра]никогда <em>(na... kadācana)</em> не принесет плода <em>(siddhyati)</em>. Все <em>(sarvam)</em> это <em>(idam)</em> [является] корнем <em>(mūlam)</em> знаний <em>(jñāna)</em> [в Мантра-йоге.] Это не <em>(na eva)</em> принесет успеха <em>(siddhyati)</em> [в случае практики] в таком случае <em>(anyathā)</em>&quot;||</p></blockquote><p>В Spandakārikā-s <em>(spande)</em> это <em>(etad ca)</em> объясняется <em>(pratipāditam)</em> косвенно <em>(bhaṅgyā)</em>:</p><blockquote><p>&quot;... вместе <em>(saha)</em> с умом <em>(cittena)</em> [самих] поклоняющихся <em>(ārādhaka)</em>. Следовательно <em>(tena)</em>, эти [Мантры] <em>(ete)</em> имеют природу <em>(dharmiṇaḥ)</em> Шивы <em>(śiva... iti)</em>&quot;||<br> [См. Spandakārikā-s II, 2]</p></blockquote><p>||1||</p><hr><ol><li><p>Короче, началась Вторая Глава, относящаяся к средствам, или методам, использующим точку зрения Śakti.</p></li><li><p>Prāsāda — это техническое имя мантры Sauḥ, которая подробно рассматривается мной в Parāprāveśikā. Существует несколько видов Praṇava, но в этом контексте это означает Śaivapraṇava (т.е. Hūm̐).</p></li><li><p>Через мантру каждый втайне осознает природу Высшего Господа, т.е. размышляет над ней внутренне, в то же самое время сохраняя полное единство с Господом (божеством мантры) и самой мантрой. И в этом заключается секрет повторения мантры: вы сами, Господь и мантра пребывают в единстве. Если человек, повторяющий мантру не делает этого (т.е. он не может достичь и удержать это осознание о неотъемлемом единстве между ним, Господом и мантрой), тогда мантра не приносит надлежащих плодов. И нет никаких сомнений в этом.</p></li><li><p>Этимологически, термин &quot;mantra&quot; происходит из корня &quot;man&quot; (думать). Поэтому одним из первых буквальных значений этого слова является &quot;инструмент мысли&quot;. Но акростически, &quot;mantra&quot; происходит из manana + trāṇa, что означает &quot;отражение/размышление + защита&quot;. Известное определение &quot;мантры&quot; как &quot; मननात्त्रायत इति मन्त्रः&quot; - &quot;mananāttrāyata iti mantraḥ&quot;, или &quot;мантра — это то, что является защитой посредством размышления&quot;, ясно показывает акростих. Короче: мантра — это то, что защищает того, кто отражается в ней (т.е. кто размышляет над ней). Мантра защищает в том смысле, что прекращает трансмиграционное существование, т.е. продолжающееся отождествление с постоянно меняющимися мыслями, чувствами и т.д., что является признаком рабства. Так или иначе, трансмиграционное существование также понимается как родившийся, чтобы умереть, и умерший, чтобы переродиться.</p></li></ol></div>',1)]))}const c=e(p,[["render",i]]);export{h as __pageData,c as default};
