import{_ as t,c as e,o as m,d as s}from"./chunks/framework.BVeY4UVb.js";const u=JSON.parse('{"title":"Афоризм 12-14","description":"Шива сутра вимаршини. Глава 3.","frontmatter":{"title":"Афоризм 12-14","description":"Шива сутра вимаршини. Глава 3.","tags":"vim-3","date":"2021-02-03T00:00:00.000Z"},"headers":[],"relativePath":"texts/shiva-sutra-vimarshini/3/12-14/index.md","filePath":"texts/shiva-sutra-vimarshini/3/12-14/index.md"}'),p={name:"texts/shiva-sutra-vimarshini/3/12-14/index.md"};function o(i,a,r,v,n,h){return m(),e("div",null,[...a[0]||(a[0]=[s('<div class="stanza"><a href="#s3.12" id="s3.12" class="num">3.12</a><p>;;; sans</p><p>अस्य च—</p><blockquote><p><strong>धीवशात्सत्त्वसिद्धिः॥१२॥</strong></p></blockquote><p>धीः तात्त्विकस्वरूपविमर्शनविशारदा (...विशादा) धिषणा तद्वशात्सत्त्वस्य स्फुरत्तात्मनः सूक्ष्मस्यान्तरपरिस्पन्दस्य सिद्धिरभिव्यक्तिर्भवति। नाट्ये च सात्त्विकाभिनयसिद्धिर्बुद्धिकौशलादेव लभ्यते॥१२॥ ;;;</p><hr><p>;;; trans</p><p>Asya ca—</p><blockquote><p><strong>Dhīvaśātsattvasiddhiḥ||12||</strong></p></blockquote><p>Dhīḥ tāttvikasvarūpavimarśanaviśāradā (...viśādā) dhiṣaṇā tadvaśātsattvasya sphurattātmanaḥ sūkṣmasyāntaraparispandasya siddhirabhivyaktirbhavati| Nāṭye ca sāttvikābhinayasiddhirbuddhikauśalādeva labhyate||12||</p><p>;;;</p><hr><p>И <em>(ca)</em> у этого [Yogī] <em>(asya)</em>—</p><blockquote><p><strong>С помощью <em>(vaśāt)</em> высшего духовного интеллекта <em>(dhī)</em>, [происходит] осознание <em>(siddhiḥ)</em> сверкающей, тонкой внутренней вибрации совершенного Я-Сознания <em>(sattva)</em>||12||</strong></p></blockquote><p>Dhī <em>(dhī)</em> [— это] интеллект <em>(dhiṣaṇā)</em>, опытный <em>(viśāradā)</em>, [или] искусный <em>(viśādā)</em> в осознавании <em>(vimarśana)</em> истинной <em>(tāttvika)</em> сущностной природы <em>(sva-rūpa)</em>. Благодаря <em>(vaśāt)</em> этому [высшему духовному интеллекту] <em>(tad)</em>, происходит <em>(bhavati)</em> siddhi <em>(siddhiḥ)</em>, [или] осознавание --т.е. проявление, становящееся явным-- <em>(abhivyaktiḥ)</em> Саттвы (Sattva) <em>(sattvasya)</em>, а именно, тонкой <em>(sūkṣmasya)</em> [и] внутренней <em>(āntara)</em> вибрации --parispanda-- <em>(parispandasya)</em>, чьей сущностью <em>(ātmanaḥ)</em> [является] Sphurattā --сверкающее совершенное Я-Сознание-- <em>(sphurattā)</em>|</p><p>В танцевальной драме <em>(nāṭye)</em> также <em>(ca)</em>, siddhi, или совершенство <em>(siddhiḥ)</em> в представлении <em>(abhinaya)</em> Sattva, или внутреннего ощущения/чувства <em>(sāttvika)</em>, достигается <em>(labhyate)</em> только <em>(eva)</em> через мастерство <em>(kauśalāt)</em> интеллекта <em>(buddhi)</em><sup>1</sup>||12||</p><hr><ol><li>Прежде всего: Здесь термин &quot;Sattva&quot; не имеет никакого отношения к знаменитой Guṇa, или качеству Пракрити (Prakṛti) . Сейчас мудрец постулировал аналогию относительно nāṭya, или танцевальной драмы: В то время как в Йоге есть siddhi, или осознание Sattva (сверкающей, тонкой внутренней вибрации совершенного Я-Сознания) посредством dhī (высшего духовного интеллекта), то в танцевальной драме имеется siddhi, или совершенство в представлении Sattva (внутреннего ощущения, или чувства) посредством мастерства/искусности интеллекта (buddhi), т.е. через талант. Термин &quot;buddhi&quot; является синонимом с &quot; धी&quot; - &quot;dhī&quot;, но в обычном значении последнего, т.е. интеллекта. Теперь это понятно.</li></ol></div><div class="stanza"><a href="#s3.13" id="s3.13" class="num">3.13</a><p>;;; sans</p><p>एवं स्फुरत्तात्मकसत्त्वासादनादेवास्य योगिनः—</p><blockquote><p><strong>सिद्धः स्वतन्त्रभावः॥१३॥</strong></p></blockquote><p>सिद्धः सम्पन्नः स्वतन्त्रभावः सहजज्ञत्वकर्तृत्वात्मकमशेषविश्ववशीकारि स्वातन्त्र्यम्। यदुक्तं श्रीश्रीनाथपादैः</p><blockquote><p>श्रयेत्स्वातन्त्र्यशक्तिं स्वां सा श्रीकाली परा कला।</p></blockquote><p>इति। श्रीस्वच्छन्देऽपि</p><blockquote><p>सर्वतत्त्वानि भूतानि वर्णा मन्त्राश्च ये स्मृताः।<br> नित्यं तस्य वशास्ते वै शिवभावनयानया॥</p></blockquote><p>इति॥१३॥ ;;;</p><hr><p>;;; trans</p><p>Evaṁ sphurattātmakasattvāsādanādevāsya yoginaḥ—</p><blockquote><p><strong>Siddhaḥ svatantrabhāvaḥ||13||</strong></p></blockquote><p>Siddhaḥ sampannaḥ svatantrabhāvaḥ sahajajñatvakartṛtvātmakamaśeṣaviśvavaśīkāri svātantryam| Yaduktaṁ śrīśrīnāthapādaiḥ</p><blockquote><p>Śrayetsvātantryaśaktiṁ svāṁ sā śrīkālī parā kalā|</p></blockquote><p>iti| Śrīsvacchande&#39;pi</p><blockquote><p>Sarvatattvāni bhūtāni varṇā mantrāśca ye smṛtāḥ|<br> Nityaṁ tasya vaśāste vai śivabhāvanayānayā||</p></blockquote><p>iti||13||</p><p>;;;</p><hr><p>Таким образом <em>(evam)</em>, путем достижения <em>(āsādanāt eva)</em> Sattva <em>(sattva)</em>, состоящего из <em>(ātmaka)</em> Sphurattā --сверкающего совершенного Я-Сознания-- <em>(sphurattā)</em>, [в случае] этого <em>(asya)</em> Yogī <em>(yoginaḥ)</em>—</p><blockquote><p><strong>Состояние <em>(bhāvaḥ)</em> Независимости и Свободы <em>(svatantra)</em> достигнуто <em>(siddhaḥ)</em>||13||</strong></p></blockquote><p>Siddha <em>(siddhaḥ)</em> [означает] &quot;завершенный, достигнутый&quot; [и так далее] <em>(sampannaḥ)</em>, [в то время как] svatantrabhāva <em>(svatantra-bhāvaḥ)</em> [означает] Свободу <em>(svātantryam)</em>, которая подчиняет всю вселенную <em>(aśeṣa-viśva-vaśīkāri)</em> [и] чья природа <em>(ātmakam)</em> есть Естественное <em>(sahaja)</em> Знание <em>(jñatva)</em> [и] Деятельность <em>(kartṛtva)</em>|</p><p>Это же <em>(yad)</em> [было] сказано <em>(uktam)</em> наиболее уважаемым Nāthapāda <em>(śrīśrī-nāthapādaiḥ)</em>:</p><blockquote><p>&quot;Следует прибегнуть <em>(śrayet)</em> к своей собственной <em>(svām)</em> Силе <em>(śaktim)</em> Абсолютной Свободы <em>(svātantrya)</em>. Она --такая Сила-- <em>(sā)</em> [есть] почтенная Kālī <em>(śrī-kālī)</em>, Высшая <em>(parā)</em> Kalā --т.е. Śakti, или Сила-- <em>(kalā... iti)</em><sup>1</sup>&quot;|</p></blockquote><p>В уважаемом Svacchandatantra <em>(śrīsvacchande)</em> это также [было заявлено] <em>(api)</em>:</p><blockquote><p>&quot;Все <em>(sarva)</em> таттвы, или принципы --а именно, 36 категорий проявления вселенной-- <em>(tattvāni)</em>, живые существа <em>(bhūtāni)</em>, буквы <em>(varṇāḥ)</em> и <em>(ca)</em> мантры <em>(mantrāḥ)</em>, которые <em>(ye)</em> известны <em>(smṛtāḥ)</em>; [все] они <em>(te)</em> всегда подчинены <em>(nityam... vaśāḥ)</em> ему --т.е. Yogī-- <em>(tasya)</em> несомненно <em>(vai)</em>, через это созерцание <em>(bhāvanayā anayā)</em> Śiva <em>(śiva... iti)</em>&quot;|<br> [См. VII, 245 в Svacchandatantra]</p></blockquote><p>||13||</p><hr><ol><li>Термин Kalā — это еще один термин, который любой переводчик всегда предпочел бы оставить не переведенным. Почему? Потому что количество возможных &quot;многословных&quot; значений слишком велико. Кроме того, имеется ряд дополнительных значений, полученных из корней. Но на данный момент, перевода &quot;Śakti, или Сила&quot; более, чем достаточно. Термин Parā означает здесь &quot;Высшая&quot;, но возможен и другой перевод, полученный из корня. О Господи! Будь милостив к нам, простым смертным! Хорошо, продолжайте читать.</li></ol></div><div class="stanza"><a href="#s3.14" id="s3.14" class="num">3.14</a><p>;;; sans</p><p>एष स्वतन्त्रभावोऽस्य—</p><blockquote><p><strong>यथा तत्र तथान्यत्र॥१४॥</strong></p></blockquote><p>यत्र देहे योगिनः स्वाभिव्यक्तिर्जाता तत्र यथा तथान्यत्र सर्वत्र सदावहितस्य सा भवति। यथोक्तं श्रीस्वच्छन्दे</p><blockquote><p>स्वच्छन्दश्चैव स्वच्छन्दः स्वच्छन्दो विचरेत्सदा।</p></blockquote><p>इति। श्रीस्पन्देऽपि</p><blockquote><p>लभते तत्प्रयत्नेन परीक्ष्यं तत्त्वमादरात्।<br> यतः स्वतन्त्रता तस्य सर्वत्रेयमकृत्रिमा॥</p></blockquote><p>इति॥१४॥ ;;;</p><hr><p>;;; trans</p><p>Eṣa svatantrabhāvo&#39;sya—</p><blockquote><p><strong>Yathā tatra tathānyatra||14||</strong></p></blockquote><p>Yatra dehe yoginaḥ svābhivyaktirjātā tatra yathā tathānyatra sarvatra sadāvahitasya sā bhavati| Yathoktaṁ śrīsvacchande</p><blockquote><p>Svacchandaścaiva svacchandaḥ svacchando vicaretsadā|</p></blockquote><p>iti| Śrīspande&#39;pi</p><blockquote><p>Labhate tatprayatnena parīkṣyaṁ tattvamādarāt|<br> Yataḥ svatantratā tasya sarvatreyamakṛtrimā||</p></blockquote><p>iti||14||</p><p>;;;</p><hr><p>Его <em>(asya)</em> состояние <em>(eṣaḥ... bhāvaḥ)</em> Независимости и Свободы <em>(svatantra)</em><sup>1</sup>—</p><blockquote><p><strong>Как <em>(yathā)</em> там <em>(tatra)</em>, так и <em>(tathā)</em> где-либо еще <em>(anyatra)</em>, [то есть, &quot;так же как этот независимый Yogī способен продемонстрировать Свободу в своем собственном теле, он также может сделать это в любом другом месте&quot;; таков смысл]||14||</strong></p></blockquote><p>Точно также, как <em>(yathā)</em> его собственная <em>(sva)</em> [сила] манифестации <em>(abhivyaktiḥ)</em> проявляется <em>(jātā)</em> там <em>(tatra)</em> —в этом теле <em>(yatra dehe)</em> Yogī <em>(vāhinīnām)</em>—, таким же образом <em>(tathā)</em> эта [сила манифестации] <em>(apāna)</em> всегда бдительного [Yogī] <em>(sadā-avahitasya)</em> имеет место <em>(bhavati)</em> в любом другом месте <em>(anyatra)</em> [также], т.е. везде <em>(sarvatra)</em>|</p><p>Как <em>(yathā)</em> [было] сказано <em>(uktam)</em> в уважаемом Svacchandatantra <em>(śrī-svacchande)</em>:</p><blockquote><p>&quot;[Повелитель Yogī-s] бродит <em>(vicaret)</em> всегда <em>(sadā)</em> свободным <em>(svacchandaḥ ca eva svacchandaḥ svacchandaḥ)</em>, [будь то в начале, в середине или в конце]<sup>2</sup>&quot;||<br> [См. VII, 260 в Svacchandatantra]</p></blockquote><p>В Spandakārikā-s <em>(spande)</em> [было] также [указано] <em>(api)</em>:</p><blockquote><p>&quot;Тот <em>(tad)</em> принцип [Спанды] <em>(tattvam)</em> должен быть изучен <em>(parīkṣyam)</em> с осторожностью <em>(prayatnena)</em> [и] уважением <em>(ādarāt)</em>, [благодаря которому эта группа органов, или инструментов --интеллект, эго, ум, сила восприятия и сила действия--, [будучи] неодушевленной, [ведет себя] как [если бы она была] одушевленной сама по себе, [и] вместе с внутренней группой [Karaṇeśvarī-s, или богинь чувств]] входит <em>(labhate)</em> [в состояния &quot;Pravṛtti&quot; --устремления к внешним объектам--, &quot;Sthiti&quot; --т.е. поддержания этих самых внешних объектов какое-то время-- [и] &quot;Saṁhṛti&quot; --растворения этих объектов в своем собственном Высшем Я--], ввиду того, что <em>(yatas)</em> такая <em>(iyam)</em> естественная <em>(akṛtrimā)</em> Свобода <em>(svatantratā)</em> Этого <em>(tasya)</em> [существует] повсеместно <em>(sarvatra... iti)</em><sup>3</sup>&quot;||<br> [См. Spandakārikā-s I, 7]</p></blockquote><p>||14||</p><hr><ol><li><p>Буквально: Это <em>(eṣaḥ)</em> состояние <em>(bhāvaḥ)</em> Независимости и Свободы <em>(svatantra)</em> его --принадлежит ему-- <em>(asya)</em>. Поскольку это звучит странно, я решил перевести фразу более понятным образом.</p></li><li><p>Чтобы понять, почему я перевел таким образом, я цитирую сейчас комментарий Кшемараджи в его Svacchandoddyota: &quot; नित्यमादिमध्यान्तकोटिष्वयं श्रीस्वच्छन्दभैरव एव स्फुरन् स्थितः - इति यावत्॥&quot; - &quot;Nityamādimadhyāntakoṭiṣvayaṁ śrīsvacchandabhairava eva sphuran sthitaḥ - iti yāvat||&quot; - &quot;Этот Святой Свободный Бхайрава (Bhairava) --Высшее Существо-- продолжает сиять, или становится проявленным постоянно, т.е. в начале, в середине и в конце. Таково объяснение&quot;.</p></li></ol><p>Другими словами, Высшее Существо (Bhairava) продолжает проявляться в повелителе Yogī-s, будь то в начале, в середине или в конце всего (в прошлом, настоящем и будущем). Короче, такой великий Yogī странствует постоянно в состоянии Свободы.</p><ol start="3"><li>Так как строфа I, 7 является чрезвычайно связанной с предыдущей (I, 6), я должен был заключить последнюю в скобки. Проверьте эти строфы (I, 6-7) в Spandakārikā-s, чтобы полностью понять, о чем я говорю.</li></ol></div>',3)])])}const c=t(p,[["render",o]]);export{u as __pageData,c as default};
