import{_ as m,c as e,d as t,o as r}from"./chunks/framework.B7ZEGzqH.js";const h=JSON.parse('{"title":"Афоризм 15-16","description":"Шива сутра вимаршини. Глава 3.","frontmatter":{"title":"Афоризм 15-16","description":"Шива сутра вимаршини. Глава 3.","tags":"vim-3","date":"2021-02-02T00:00:00.000Z"},"headers":[],"relativePath":"texts/shiva-sutra-vimarshini/3/15-16/index.md","filePath":"texts/shiva-sutra-vimarshini/3/15-16/index.md"}'),i={name:"texts/shiva-sutra-vimarshini/3/15-16/index.md"};function p(o,a,u,n,s,v){return r(),e("div",null,a[0]||(a[0]=[t('<div class="stanza"><a href="#s3.15" id="s3.15" class="num">3.15</a><p>;;; sans</p><p>न चैवमप्युदासीनेनानेन भाव्यमपि तु—</p><blockquote><p><strong>बीजावधानम्॥१५॥</strong></p></blockquote><p>कर्तव्यमिति शेषः। बीजं विश्वकारणं स्फुरत्तात्मा परा शक्तिः। यदुक्तं श्रीमृत्युजिद्भट्टारके</p><blockquote><p>सा योनिः सर्वदेवानां शक्तीनां चाप्यनेकधा।<br> अग्नीषोमात्मिका योनिस्तस्याः सर्वं प्रवर्तते॥</p></blockquote><p>इत्यादि। तत्र परशक्त्यात्मनि बीज अवधानं भूयो भूयश्चित्तनिवेशनं कार्यम्॥१५॥ ;;;</p><hr><p>;;; trans</p><p>Na caivamapyudāsīnenānena bhāvyamapi tu—</p><blockquote><p><strong>Bījāvadhānam||15||</strong></p></blockquote><p>Kartavyamiti śeṣaḥ| Bījaṁ viśvakāraṇaṁ sphurattātmā parā śaktiḥ| Yaduktaṁ śrīmṛtyujidbhaṭṭārake</p><blockquote><p>Sā yoniḥ sarvadevānāṁ śaktīnāṁ cāpyanekadhā|<br> Agnīṣomātmikā yonistasyāḥ sarvaṁ pravartate||</p></blockquote><p>ityādi| Tatra paraśaktyātmani bīja avadhānaṁ bhūyo bhūyaścittaniveśanaṁ kāryam||15||</p><p>;;;</p><hr><p>Тем не менее --т.е. даже после получения вышеупомянутых сверхъестественных сил-- <em>(evam api)</em>, этому [Yogī] не следует становиться безразличным <em>(na ca eva... udāsīnena anena bhāvyam)</em> , а скорее <em>(api tu)</em>—</p><blockquote><p><strong>[Тот Yogī должен сфокусировать все] внимание <em>(avadhānam)</em> на Семени, [другими словами, &quot;на Высшей Śakti, или совершенном Я-Сознании, являющимся источником всего проявления&quot;] <em>(bīja)</em>||15||</strong></p></blockquote><p>&quot;Должно быть отдано, или уделено&quot; <em>(kartavyam)</em>, это должно быть подставлено [в афоризм для полноты смысла] <em>(iti śeṣaḥ)</em> --другими словами, &quot;внимание должно быть отдано, или уделено Семени&quot;--|</p><p>Семя <em>(bījam)</em> [— это] Высшая <em>(parā)</em> Śakti, или Сила <em>(śaktiḥ)</em>, которая является причиной <em>(kāraṇam)</em> вселенной <em>(viśva)</em> [и] чья природа <em>(ātmā)</em> [состоит из] Sphurattā --сверкающего совершенного Я-Сознания-- <em>(sphurattā)</em>|</p><p>Это <em>(yad)</em> заявлено <em>(uktam)</em> в наиболее почитаемом Mṛtyujit --т.е. Netratantra-- <em>(śrī-mṛtyujit-bhaṭṭārake)</em>:</p><blockquote><p>&quot;Она --Высшая Śakti-- <em>(sā)</em> различными способами [является] <em>(anekadhā)</em>, Источником <em>(yoniḥ)</em> всех богов <em>(sarva-devānām)</em>, а <em>(ca)</em> также <em>(api)</em> śakti-s, или сил <em>(śaktīnām)</em>. [Этот] Источник <em>(yoniḥ)</em> имеет огонь и луну по своей природе <em>(agnī-soma-ātmikā)</em><sup>1</sup>. Все <em>(sarvam)</em> берет свое начало <em>(pravartate)</em> в нем --из такого Источника-- <em>(tasyāḥ)</em>, и т.п. <em>(iti ādi)</em>&quot;||<br> [См. VII, 40 в Netratantra]</p></blockquote><p>Внимание <em>(avadhānam)</em> [должно быть отдано, или уделено] тому <em>(tatra)</em> Семени <em>(bīje)</em>, чья сущность <em>(ātmani)</em> [есть] Высшая <em>(parā)</em> Śakti, или Сила <em>(śakti)</em>, т.е. умственная концентрация <em>(citta-niveśanam)</em> [на том Семени] должна быть выполнена <em>(kāryam)</em> многократно <em>(bhūyas bhūyas)</em>||15||</p><hr><ol><li>Огонь и луна имеют различные значения в зависимости от контекста: например, знание и действие, и т.д.</li></ol></div><div class="stanza"><a href="#s3.16" id="s3.16" class="num">3.16</a><p>;;; sans</p><p>एवं हि सत्यसौ योगी—</p><blockquote><p><strong>आसनस्थः सुखं ह्रदे निमज्जति॥१६॥</strong></p></blockquote><p>आस्यते नित्यमैकात्म्येन स्थीयत अस्मिनित्यासनं परं शाक्तं बलं यस्तत्र तिष्ठति परिहृतपरापरध्यानधारणादिसर्वक्रियाप्रयासो नित्यमन्तर्मुखतया तदेव परामृशति यः स सुखमनायासतया ह्रदे विश्वप्रवाहप्रसरहेतौ स्वच्छोच्छलत्तादियोगिनि परामृतसमुद्रे निमज्जति देहादिसङ्कोचसंस्कारब्रोडनेन तन्मयीभवति। यदुक्तं श्रीमृत्युजिद्भट्टारक एव</p><blockquote><p>नोर्ध्वे ध्यानं प्रयुञ्जीत नाधस्तान्न च मध्यतः।<br> नाग्रतः पृष्ठतः किञ्चिन्न पार्श्वोरुभयोरपि॥<br> नान्तःशरीरसंस्थाने न बाह्ये भावयेत्क्वचित्।<br> नाकाशे बन्धयेल्लक्ष्यं नाधो दृष्टिं निवेशयेत्॥<br> न चाक्ष्णोर्मीलनं किञ्चिन्न किञ्चिद्दृष्टिबन्धनम्।<br> अवलम्बं निरालम्बं सालम्बं न च भावयेत्॥<br> नेन्द्रियाणि न भूतानि शब्दस्पर्शरसादि यत्।<br> सर्वं त्यक्त्वा समाधिस्थः केवलं तन्मयो भवेत्॥<br> सावस्था परमा प्रोक्ता परस्य परमात्मनः।<br> निराभासं पदं तत्तु तत्प्राप्य विनिवर्तते॥</p></blockquote><p>इति॥१६॥ ;;;</p><hr><p>;;; trans</p><p>Evaṁ hi satyasau yogī—</p><blockquote><p><strong>Āsanasthaḥ sukhaṁ hrade nimajjati||16||</strong></p></blockquote><p>Āsyate nityamaikātmyena sthīyata asminityāsanaṁ paraṁ śāktaṁ balaṁ yastatra tiṣṭhati parihṛtaparāparadhyānadhāraṇādisarvakriyāprayāso nityamantarmukhatayā tadeva parāmṛśati yaḥ sa sukhamanāyāsatayā hrade viśvapravāhaprasarahetau svacchocchalattādiyogini parāmṛtasamudre nimajjati dehādisaṅkocasaṁskārabroḍanena tanmayībhavati| Yaduktaṁ śrīmṛtyujidbhaṭṭāraka eva</p><blockquote><p>Nordhve dhyānaṁ prayuñjīta nādhastānna ca madhyataḥ|<br> Nāgrataḥ pṛṣṭhataḥ kiñcinna pārśvorubhayorapi||<br> Nāntaḥśarīrasaṁsthāne na bāhye bhāvayetkvacit|<br> Nākāśe bandhayellakṣyaṁ nādho dṛṣṭiṁ niveśayet||<br> Na cākṣṇormīlanaṁ kiñcinna kiñciddṛṣṭibandhanam|<br> Avalambaṁ nirālambaṁ sālambaṁ na ca bhāvayet||<br> Nendriyāṇi na bhūtāni śabdasparśarasādi yat|<br> Sarvaṁ tyaktvā samādhisthaḥ kevalaṁ tanmayo bhavet||<br> Sāvasthā paramā proktā parasya paramātmanaḥ|<br> Nirābhāsaṁ padaṁ tattu tatprāpya vinivartate||</p></blockquote><p>iti||16||</p><p>;;;</p><hr><p>Добившийся [этого] <em>(sati)</em> таким образом <em>(evam hi)</em>, тот <em>(asau)</em> Yogī <em>(yogī)</em>—</p><blockquote><p><strong>Укорененный <em>(sthaḥ)</em> в силе Высшей Śakti [или совершенном Я-Сознании, которое является &quot;сиденьем&quot; для него] <em>(āsana)</em>, [просветленный Yogī] с легкостью <em>(sukham)</em> погружается <em>(nimajjati)</em> в Озеро [божественного и бессмертного Сознания] <em>(hrade)</em>||16||</strong></p></blockquote><p>Āsana, или сиденье — это <em>(iti āsanam)</em> [то], на чем он --Yogī-- восседает <em>(āsyate)</em>, т.е то, на чем он пребывает <em>(sthīyate asmin)</em> постоянно <em>(nityam)</em> с [чувством] единства, или тождественности <em>(aikātmyena)</em>. [Здесь Āsana, или сиденье означает] силу <em>(balam)</em> Высшей Śakti <em>(param śāktam)</em>. Он <em>(saḥ)</em>, кто <em>(yaḥ)</em> пребывает <em>(tiṣṭhati)</em> там <em>(tatra)</em> [и] кто <em>(yaḥ)</em> всегда <em>(nityam)</em> осознает <em>(parāmṛśati)</em> ту самую [Реальность] --т.е. силу Высшей Śakti-- <em>(tad eva)</em> интроспективным способом <em>(antar-mukhatayā)</em>, после отказа от <em>(parihṛta)</em> всех <em>(sarva)</em> усилий <em>(prayāsaḥ)</em>, [включающих в себя] деятельность <em>(kriyā)</em>, [такую как], высшую --т.е. относящуюся к Śāktopāya-- <em>(para)</em> [и] низшую --т.е. относящуюся к Āṇavopāya-- <em>(apara)</em> медитацию <em>(dhyāna)</em>, концентрацию <em>(dhāraṇā)</em>, и т.д. <em>(ādi)</em>, легко <em>(sukham)</em> —без всяких усилий <em>(anāyāsatayā)</em>— погружается <em>(nimajjati)</em> в Озеро <em>(hrade)</em> —становясь <em>(bhavati)</em> тождественным <em>(mayī)</em> с этим [Озером] <em>(tad)</em> путем уменьшения <em>(broḍanena)</em> остаточных впечатлений <em>(saṁskāra)</em> от ограничения <em>(saṅkoca)</em> [в форме] тела <em>(deha)</em> и т.п. <em>(ādi)</em>—. [Другими словами, он погружается] в высший океан бессмертия <em>(para-amṛta-samudre)</em>, обладающий [характеристикой] <em>(yogini)</em> прозрачности <em>(svaccha)</em>, возникновения <em>(ucchalattā)</em>, и т.д. <em>(ādi)</em> [и] являющийся причиной <em>(hetau)</em> расширения <em>(prasara)</em> непрерывного всеобъемлющего потока <em>(viśva-pravāha)</em>|</p><p>То же <em>(yad)</em> [было] сказано <em>(uktam)</em> в самом уважаемом Mṛtyujit --Netratantra-- <em>(śrī-mṛtyujit-bhaṭṭārake eva)</em>:</p><blockquote><p>&quot;Медитация <em>(dhyānam)</em> не должна быть направлена <em>(prayuñjīta)</em> ни <em>(na)</em> вверх <em>(ūrdhve)</em>, ни <em>(na)</em> вниз <em>(adhastāt)</em>, ни <em>(na ca)</em> к середине <em>(madhyataḥ)</em>, ни <em>(na)</em> перед собой <em>(agratas)</em> [или] назад <em>(pṛṣṭhatas)</em>, ни <em>(na)</em> даже <em>(api)</em> на что-то <em>(kiñcid)</em>, [расположенное] с обеих сторон <em>(pārśvoḥ ubhayoḥ)</em><sup>1</sup>.</p></blockquote><blockquote><p>Не <em>(na)</em> следует созерцать <em>(bhāvayet)</em> что-то <em>(kvacid)</em>, расположенное <em>(saṁsthāne)</em> как внутри <em>(antar)</em> тела <em>(śarīra)</em>, так и *(na)*вовне <em>(bāhye)</em>. Не <em>(na)</em> следует фиксировать [свой] взор <em>(bandhayet lakṣyam)</em> на небе <em>(ākāśe)</em>, и не <em>(na)</em> следует направлять <em>(niveśayet)</em> [свой] взгляд <em>(dṛṣṭi)</em> на [что-либо] внизу <em>(adhas)</em><sup>2</sup>.</p></blockquote><blockquote><p>Никакого <em>(na kiñcid)</em> закрытия <em>(mīlanam)</em> [своих] глаз <em>(cākṣṇoḥ)</em> [и] никакой <em>(na kiñcid)</em> фиксации <em>(bandhanam)</em> [своего] взгляда <em>(dṛṣṭi)</em> [вообще] --что подразумевает, что ваши немигающие глаза широко открыты--; не <em>(na ca)</em> следует созерцать <em>(bhāvayet)</em> что-либо ни как опору <em>(avalambam)</em>, [ни] как отрицание опоры <em>(nirālamba)</em>, [ни] как опору снова и снова <em>(sālambam)</em><sup>3</sup>.</p></blockquote><blockquote><p>Не [следует созерцать] <em>(na)</em> ни Indriya-s --Силы восприятия и действия, таттвы с 17 по 26-- <em>(indriyāṇi)</em>, ни <em>(na)</em> Mahābhūta-s --Грубые элементы, таттвы с 32 по 36-- <em>(bhūtāni)</em>, [ни] то, что <em>(yad)</em> [состоит] из Звука-как-такового <em>(śabda)</em>, Касания-как-такового <em>(sparśa)</em>, Вкуса-как-такового <em>(rasa)</em>, и т.д. <em>(ādi)</em> --т.е. пять Танматр (Tanmātra-s), или тонких элементов, таттвы с 27 по 31--. Отказавшись от <em>(tyaktvā)</em> всего [этого] <em>(sarvam)</em>, установившись <em>(sthaḥ)</em> в Samādhi <em>(samādhi)</em>, вы должны стать <em>(kevalam... bhavet)</em> тождественным <em>(mayaḥ)</em> с Этим --т.е. с Высшей Реальностью-- <em>(tad)</em><sup>4</sup>.</p></blockquote><blockquote><p>Это <em>(sā)</em> называется <em>(proktā)</em> высшим <em>(paramā)</em> Состоянием <em>(avasthā)</em> Всевышнего <em>(parasya)</em>, т.е. Высшей Сущности <em>(parama-ātmanaḥ)</em>. Это <em>(tad)</em> Состояние <em>(padam)</em> лишено [внешних] проявлений <em>(nis-ābhāsam)</em> --т.е. ничто в этом Состоянии не проявляется, или становится манифестированным--. Добившись <em>(prāpya)</em> Этого --или обретя это Состояние-- <em>(tad)</em>, прекращается [трансмиграция] <em>(vinivartate... iti)</em><sup>5</sup>&quot;||<br> [См. VIII, 41-45 в Netratantra]</p></blockquote><p>||16||</p><hr><ol><li>Мудрец Kṣemarāja подробно комментирует все эти строфы Netratantra в своей Netroddyota (своем научном комментарии на Netratantra), но тема такая длинная и сложная, что я не могу здесь это продемонстрировать &quot;полностью на санскрите&quot; через простые пояснения (например, чтобы объяснить некоторые из пяти строф этого цикла, он цитирует несколько строф из Vijñānabhairavatantra, каждая из которых потребовала бы моих &quot;особых&quot; объяснений помимо перевода). Чтобы получить более подробную информацию об этой сложной и длинной теме, обращайтесь всегда к разделу &quot;Писания (исследование)/Śivasūtravimarśinī&quot; в разделе Трика. Так или иначе, в этой первой строфе (41), мудрец указывает следующее: &quot; ऊर्ध्वे द्वादशान्तेऽधः कन्दादौ मध्ये हृदादावग्रतः पृष्ठतः पार्श्वयोस्तत्पुरुषसद्योजातादिरूपम्।&quot; - &quot;Ūrdhve dvādaśānte&#39;dhaḥ kandādau madhye hṛdādāvagrataḥ pṛṣṭhataḥ pārśvayostatpuruṣasadyojātādirūpam|&quot; - &quot;Вверх —по направлению к [ūrdhva] dvādaśānta [или точке конца 12-ти пальцев от пространства между бровями до Brahmarandhra на макушке головы]—; вниз —к Kanda --яйцеподобному источнику всех каналов в тонком теле--, и т.п.—; к середине —в направлении к сердцу, и т.д.—, вперед, назад, к [чему-то расположенному] по обе стороны, к тому, чьей формой является Tatpuruṣa, Sadyojāta, и т.д. --т.е. Tatpuruṣa, Sadyojāta, Vāmadeva и Aghora--—&quot;.</li></ol><p>Имеется пять аспектов, или голов Śiva: (1) Tatpuruṣa (лицом на восток --вперед--, и символизирующий Śakti, вторую таттву, или категорию), (2) Sadyojāta (лицом на запад --назад--, и символизирующий Sadāśiva, третью таттву, или категорию), (3) Vāmadeva (лицом на север --налево--, и символизирующий Īśvara, четвертую таттву, или категорию), (4) Aghora (лицом на юг --направо--, и символизирующий Sadvidyā, четвертую таттву, или категорию) —эти первые четыре аспекта, или головы были отмечены Kṣemarāja касательно медитации, направленной вперед (перед собой), назад и к чему-либо расположенному с обеих сторон (слева и справа)—, и наконец, (5) Īśāna (обращенный вверх и символизирующий Śiva, первую таттву, или категорию). Безусловно, эта тема намного сложнее и массивнее... Я лишь едва прикоснулся к ее поверхности. Поэтому, просто поверьте мне, пожалуйста.</p><ol start="2"><li><p>Это строфа VIII, 42 из Netratantra. Великий Kṣemarāja цитирует 28<sup>ую</sup> строфу из Vijñānabhairavatantra, чтобы объяснить созерцание чего-либо,находящегося внутри тела. Эта строфа разъясняет медитацию на Kuṇḍalinī, поднимающуюся из Mūlādhāra (чакра, расположенная в корне позвоночного столба) и движущуюся к Brahmarandhra на макушке головы. Затем, он цитирует строфу 122 из той же книги, чтобы объяснить созерцание чего-либо, находящегося снаружи. Эта строфа относится к достижению безмыслия при концентрации на конкретном объекте. Наконец, чтобы объяснить концентрацию на небе и на чем-то внизу, он приводит цитаты 76 и 115 из Vijñānabhairavatantra, соответственно. Первая имеет дело с простым фиксированием взгляда на части неба, в то время как последняя объясняет, каким образом зафиксировать глаза на пространстве внутри очень глубокого колодца, чтобы можно было растворить ум.</p></li><li><p>Эта строфа VIII, 43 из Netratantra. Kṣemarāja цитирует строфу 88 из Vijñānabhairavatantra, чтобы описать закрытие глаз. Эта строфа относится к созерцанию ужасной формы Bhairava (Господа Śiva), сначала закрывая глаза и созерцая ужасную тьму перед собой, и т.д. Позже мудрец использует строфу 60 этого Писания, чтобы привести пример фиксации взгляда. Эта строфа рекомендует смотрение на область, в которой отсутствуют деревья, на гору, на стену и пр., чтобы умственные колебания прекратились.</p></li></ol><p>Затем, чтобы объяснить глубокое значение слова &quot;avalamba&quot; он говорит: &quot;अवलम्ब्यत इति अवलम्बो ध्येय आकारस्&quot; - &quot;avalambyata iti avalambo dhyeya ākāras&quot; - &quot;Avalamba, или опора— это форма, [используемая] как объект медитации для удерживания на ней [своего внимания]&quot;. Мудрец цитирует строфу 62 из Vijñānabhairavatantra, чтобы проиллюстрировать это. Эта строфа описывает ментальную концентрацию на промежутке между двумя bhāva-s, или объектами, возникающими в вашем уме. Когда первый bhāva возникает, необходимо удерживать его, что позволит никогда не проявляться второму. В этом смысле, первый называется &quot;позитивный&quot;, в то время как второй — &quot;негативный&quot;. По мере того как вы твердо продолжаете удерживать свое внимание на первом bhāva, вы скользите к промежутку между двумя bhāva-s (одним возникшим и ни разу не возникшим другим... да, звучит безумно, но я говорю правду).</p><p>И мудрец объясняет &quot;nirālamba&quot;, цитируя 61<sup>ую</sup> строфу, которая описывает концентрацию на промежутке между двумя bhāva-s, или объектами. Оба bhāva-s позитивны, т.е. им позволено возникать, но в то же время они &quot;отрицаются/отвергаются&quot; в том смысле, что ваше внимание направлено на промежуток между ними. Таким образом, это называется nirālamba, или отрицание опоры, или ālamba.</p><p>Наконец, Kṣemarāja разъясняет &quot;sālamba&quot; следующим образом: &quot;सहालम्बेन वर्तते सालम्बं साकारं ज्ञानम्&quot; - &quot;sahālambena vartate sālambaṁ sākāraṁ jñānam&quot; - &quot;sālamba [— это] основанное на форме знание, или восприятие, которое [в результате] имеет место через использование чего-либо в качестве опоры&quot;. После этого, он цитирует 98<sup>ую</sup> строфу из Vijñānabhairavatantra, которая описывает концентрацию на bhāva, проявляющегося как желание, или знание в уме. Этот bhāva является позитивным, поскольку ему позволено возникнуть. Затем, если вы фиксируете свое внимание на этом, считая это своей собственной Самостью, сияющей наружу таким образом, вы имеете видение этой самой Самости. Тем не менее, опора, или āvalamba постоянно ускользает и вам необходимо возвращать ее снова и снова, чтобы продолжать концентрацию. Поэтому я перевел так, как написано выше, а именно, &quot;... как опору снова и снова&quot;. В этом заключается разница между &quot;avalamba&quot; и &quot;sālamba&quot;, т.е. в то время как в первом случае мы имеем устойчивую концентрацию на bhāva, или объекте, возникшем в уме (всегда пресекая появление второго bhāva), во втором случае bhāva постоянно теряется, и его необходимо восстанавливать, чтобы продолжать (концентрацию). Естественно, подавляющее большинство людей использует метод sālamba, сидя в медитации. Достаточно на сейчас.</p><ol start="4"><li>Мне нужно перевести комментарий Kṣemarāja о значении Samādhi в этом контексте:</li></ol><p>&quot;... समाधिस्थ इत्यकिञ्चिच्चिन्तकत्वेन स्वस्वरूपविमर्शनप्रवणस्तन्मय इत्यानन्दपदसंलीनसमरसज्ञानमयः॥४४॥&quot; - &quot;... samādhistha ityakiñciccintakatvena svasvarūpavimarśanapravaṇastanmaya ityānandapadasaṁlīnasamarasajñānamayaḥ||44||&quot; - &quot;&#39;... Установленный в Samādhi&#39;, т.е. преданный осознанию своей собственной сущностной пироды [и следовательно] отождествленный с Этим благодаря полному отсутствию мыслей. [Другими словами,] полный однородного Знания, тесно связанного с состоянием Ānanda, или [божественного] Блаженства||44||&quot;.</p><p>Я должен был серьезно прояснить это с тем, чтобы читатель не рассматривал здесь Samādhi, как это определено у Patañjali в его Yogasūtra-s.</p><ol start="5"><li>Kṣemarāja кратко комментирует VIII, 45 в своей Netroddyota:</li></ol><p>&quot;सांसारिकीं स्थितिमुज्झति॥४५॥&quot; - &quot;Sāṁsārikīṁ sthitimujjhati||45||&quot; - &quot;Человек покидает трансмиграционное состояние||45||&quot;.</p></div><div class="stanza"><a href="#s3.17" id="s3.17" class="num">3.17</a><p>;;; sans</p><p>तदेवं नाडीसंहाराद्याणवोपायक्रमासादितमोहजयोन्मज्जच्छुद्धविद्यात्मकशाक्तबलासादनप्रकर्षादात्मीकृतपरामृतह्रदात्मकशाम्भवपदो योगी—</p><blockquote><p><strong>स्वमात्रानिर्माणमापादयति॥१७॥</strong></p></blockquote><p>स्वस्य चैतन्यस्य सम्बन्धिनी मात्रा चिद्रसाश्यानतात्मांशस्तद्रूपं यथेष्टवेद्यवेदकावभासात्मकं निर्माणमापादयति निर्मितत्वेन दर्शयति। यदुक्तं श्रीस्वच्छन्दे</p><blockquote><p>तदेव भवति स्थूलं स्थूलोपाधिवशात्प्रिये।<br> स्थूलसूक्ष्मविभेदेन तदेकं संव्यवस्थितम्॥</p></blockquote><p>इति। प्रत्यभिज्ञायामपि</p><blockquote><p>आत्मानमत एवायं ज्ञेयीकुर्यात्पृथक्स्थिति।<br> ज्ञेयं न तु...॥</p></blockquote><p>इति। आगमेऽपि</p><blockquote><p>जलं हिमं च यो वेद गुरुवक्त्रागमात्प्रिये।<br> नास्त्येव तस्य कर्तव्यं तस्यापश्चिमजन्मता॥</p></blockquote><p>इत्यनेनैवाशयेनोक्तम्। एतदेव</p><blockquote><p>इति वा यस्य संवित्तिः क्रीडात्वेनाखिलं जगत्।<br> स पश्यन्सततं युक्तो जीवन्मुक्तो न संशयः॥</p></blockquote><p>इत्यनेन स्पन्दे प्रतिपादितम्॥१७॥ ;;;</p><hr><p>;;; trans</p><p>Tadevaṁ nāḍīsaṁhārādyāṇavopāyakramāsāditamohajayonmajjacchuddhavidyātmakaśāktabalāsādanaprakarṣādātmīkṛtaparāmṛtahradātmakaśāmbhavapado yogī—</p><blockquote><p><strong>Svamātrānirmāṇamāpādayati||17||</strong></p></blockquote><p>Svasya caitanyasya sambandhinī mātrā cidrasāśyānatātmāṁśastadrūpaṁ yatheṣṭavedyavedakāvabhāsātmakaṁ nirmāṇamāpādayati nirmitatvena darśayati| Yaduktaṁ śrīsvacchande</p><blockquote><p>Tadeva bhavati sthūlaṁ sthūlopādhivaśātpriye|<br> Sthūlasūkṣmavibhedena tadekaṁ saṁvyavasthitam||</p></blockquote><p>iti| Pratyabhijñāyāmapi</p><blockquote><p>Ātmānamata evāyaṁ jñeyīkuryātpṛthaksthiti|<br> Jñeyaṁ na tu...||</p></blockquote><p>iti| Āgame&#39;pi</p><blockquote><p>Jalaṁ himaṁ ca yo veda guruvaktrāgamātpriye|<br> Nāstyeva tasya kartavyaṁ tasyāpaścimajanmatā||</p></blockquote><p>ityanenaivāśayenoktam| Etadeva</p><blockquote><p>Iti vā yasya saṁvittiḥ krīḍātvenākhilaṁ jagat|<br> Sa paśyansatataṁ yukto jīvanmukto na saṁśayaḥ||</p></blockquote><p>ityanena spande pratipāditam||17||</p><p>;;;</p><hr><p>Это <em>(tad)</em> [существо] таким образом <em>(evam)</em>, через преимущество и превосходство <em>(prakarṣāt)</em>, достигнутые <em>(āsādana)</em> посредством силы <em>(bala)</em> Śakti <em>(śākta)</em>, состоящей из <em>(ātmaka)</em> Śuddhavidyā --Чистого Знания<sup>1</sup>-- <em>(śuddha-vidyā)</em>, возникшего <em>(unmajjat)</em> из покорения <em>(jaya)</em> Moha, или Наваждения --Māyā-- <em>(moha)</em>, которое достигается <em>(āsādita)</em> последовательностью <em>(krama)</em> методов <em>(upāya)</em> в āṇava --т.е. относящихся к Āṇavopāya-- <em>(āṇava)</em>, [таких как] растворение [жизненной энергии] <em>(saṁhāra)</em> в тонких каналах <em>(nāḍī)</em>, и т.д. <em>(ādi)</em> --См. III, 5--, Yogī <em>(yogī)</em> овладевает состоянием <em>(ātmī-kṛta... padaḥ)</em> Śiva <em>(śāmbhava)</em>, которое есть [ничто иное, как] <em>(ātmaka)</em> Озеро <em>(hrada)</em> высшего <em>(para)</em> бессмертия <em>(amṛta)</em>—</p><blockquote><p><strong>[Этот освобожденный Yogī способен] создать <em>(nirmāṇam āpādayati)</em> [любую форму, согласно] мере, или аспекту созидательного Сознания [служащего ему &quot;āsana&quot;, или &quot;сиденьем&quot; --см. 16<sup>ый</sup> афоризм--] <em>(svamātrā)</em>||17||</strong></p></blockquote><p>[Термин svamātrā означает] меру <em>(mātrā)</em>, связанную с <em>(sambandhinī)</em> его собственным <em>(svasya)</em> [созидательным] Сознанием <em>(caitanyasya)</em>, [короче говоря,] с аспектом <em>(aṁśaḥ)</em>, чьей природой является <em>(ātmā)</em> коагуляция <em>(āśyānatā)</em> сущности <em>(rasa)</em> Сознания <em>(cit)</em><sup>2</sup>. В соответствии с мерой этого [созидательного Сознания] <em>(tad-rūpam)</em>, [освобожденный Yogī] осуществляет <em>(āpādayati)</em> постановку <em>(nirmāṇam)</em>, состоящую из <em>(ātmakam)</em> проявления <em>(avabhāsa)</em> познающих --т.е. существ-- <em>(vedaka)</em> [и] познаваемого --т.е. объектов-- <em>(vedya)</em> как <em>(yathā)</em> [он] пожелает <em>(iṣṭa)</em>. [Другими словами,] он показывает [все эти вещи] <em>(darśayati)</em> путем [их] манифестации <em>(nirmitatvena)</em>|</p><p>Это <em>(yad)</em> говорится <em>(uktam)</em> в уважаемом Svacchandatantra <em>(śrī-svacchande)</em>:</p><blockquote><p>&quot;О, Дорогая <em>(priye)</em>!, Тот <em>(tad)</em><sup>3</sup> один <em>(eva)</em> существует <em>(bhavati)</em> как грубое <em>(sthūlam)</em> из-за [Его] желания *(vaśāt)*проявления <em>(upādhi)</em> в качестве того, что является грубым <em>(sthūla)</em>. Тот <em>(tad)</em> один <em>(ekam)</em> проявляется как <em>(saṁvyavasthitam)</em> разнообразие, или разделение <em>(vibhedena)</em> грубого <em>(sthūla)</em> [и] тонкого <em>(sūkṣma... iti)</em>&quot;||<br> [См. IV, 295 в Svacchandatantra]</p></blockquote><p>В Īśvarapratyabhijñā <em>(pratyabhijñāyām)</em> [это] также [было провозглашено] <em>(api)</em>:</p><blockquote><p>&quot;Благодаря этой самой [Vimarśaśakti --а именно, силе проявляющего сознания--] <em>(atas eva)</em>, Он <em>(ayam)</em> делает себя jñeya, или познаваемым --т.е. объектом-- <em>(ātmānam... jñeyī-kuryāt)</em>. Познаваемое <em>(jñeyam)</em> не <em>(na tu)</em> имеет независимого существования <em>(pṛthak-sthiti... iti)</em>. [Если бы [Он] был зависим --букв. связанным с-- от этого [познаваемого], то Его Абсолютная свобода была бы нарушена]<sup>4</sup>&quot;|<br> [См. I, 5, 15 в Īśvarapratyabhijñā]</p></blockquote><p>В Āgama --проявленном Писании-- <em>(āgame)</em> также <em>(api)</em>, сказано [следующее] <em>(uktam)</em> с тем же самым намерением <em>(anena eva āśayena)</em>:</p><blockquote><p>&quot;О, Дорогая <em>(priye)</em>!, тот, кто <em>(yaḥ)</em> знает <em>(veda)</em> посредством того, что исходит из <em>(āgamāt)</em> уст <em>(vaktra)</em> [его] духовного наставника <em>(guru)</em>, что вода <em>(jalam)</em> и <em>(ca)</em> лед <em>(himam)</em> [являются одной и той же вещью, — полностью освобожден]. Для него <em>(tasya)</em> нет ничего, что должно быть сделано <em>(na asti eva... kartavyam)</em>. Он находится <em>(tasya)</em> в состоянии последнего рождения <em>(apaścima-janmatā)</em> --т.е. он не имеет ни единого последующего рождения, потому что, достигнув цели существования, ему больше не требуется Saṁsāra, или Трансмиграция--&quot;||</p></blockquote><p>Это же самое [учение] <em>(etad)</em> было объяснено <em>(pratipāditam)</em> этим [афоризмом] <em>(anena)</em> в Spandakārikā-s <em>(spande)</em>:</p><blockquote><p>&quot;Или <em>(iti vā)</em> тот <em>(saḥ)</em>, кто обладает этим <em>(yasya)</em> знанием, или осознанием <em>(saṁvittiḥ)</em> [и] постоянно пребывает <em>(satatam)</em> в единстве [с Высшим Я] <em>(yuktaḥ)</em>, видит <em>(paśyan)</em> весь <em>(akhilam)</em> мир <em>(jagat)</em>, как [божественную] игру <em>(krīḍātvena)</em>. Он является освобожденным при жизни <em>(jīvanmuktaḥ)</em>, нет никаких <em>(na)</em> сомнений <em>(saṁśayaḥ... iti)</em> [в этом]&quot;||<br> [См. Spandakārikā-s II, 5]</p></blockquote><p>||17||</p><hr><ol><li><p>Это вовсе не пятая таттва, или категория универсального проявления (также известная как Sadvidyā). Чистое Знание в этом контексте — не что иное, как расширение ограниченного знания. II, 10 настоящего Писания, чтобы полностью разобраться с этим.</p></li><li><p>Созидание в Трике не похоже на работу ремесленника, т.е. как нечто, созданное кем-то, кто использовал внешние инструменты и наконец, создал что-то, отличное от него самого. Нет. Только Сознание, или Caitanya постоянно становятся всем и каждым, т.е. Оно коагулирует, принимает форму вещей и существ, не прибегая ни к чему другому, кроме своей собственной сущности. Поэтому, термин &quot;проявление&quot; более уместен, или правилен, чем &quot;созидание&quot;в контексте Трики.</p></li><li><p>В своей Svacchandoddyota, Kṣemarāja характеризует &quot;Тот&quot; как &quot; महाप्रकाशात्मा श्रीस्वतन्त्रनाथः&quot; - &quot;Mahāprakāśātmā śrīsvatantranāthaḥ&quot; - &quot;Досточтимый свободный Господь, чья природа — Великий Свет&quot;. Так или иначе, Он — никто иной, как Caitanya, или Сознание в абсолютной Свободе.</p></li><li><p>Я должен был перевести всю строфу, чтобы показать полное значение. Оставшаяся часть, которая была отброшена и отмечена многоточием, это: &quot; तदौन्म्य्ख्यात्खण्ड्येतास्य स्वतन्त्रता॥१५॥ &quot; - &quot;tadaunmykhyātkhaṇḍyetāsya svatantratā||15||&quot;. Этот перевод приводится в самом тексте выше. Кроме того, я добавил в мой перевод термин &quot;Vimarśaśakti&quot;. Почему? Потому что до того, как знаменитый мудрец Abhinavagupta (Гуру Кшемараджи) прокомментировал эту строфу (фактически, он одновременно прокомментировал строфы 15 и 16) в своей Īśvarapratyabhijñāvimarśinī (великий научный комментарий на Īśvarapratyabhijñā Утпаладевы), он выдвинул возражение, в котором встречается термин Vimarśaśakti. Затем, строфы 15 и 16, конечно, рассеивают сомнения, выдвинутые им. Эти авторы (такие как, Abhinavagupta, Kṣemarāja, и др.) обычно используют метод выдвижения возражений, чтобы сделать их комментарии более интересными и дидактическими (дидактическими для санскритских ученых, безусловно, ха-ха!). ОК, теперь все понятно. Просто доверяй мне, читатель, поскольку в целом, я читаю много &quot;безумно длинных&quot; писаний одновременно, пока перевожу настоящее. Иногда я не могу объяснить все подробно, вы знаете, иначе этот перевод превратится в целую энциклопедию.</p></li></ol></div><div class="stanza"><a href="#s3.18" id="s3.18" class="num">3.18</a><p>;;; sans</p><p>न चैवं स्वशक्तिनिर्मितभूतभावशरीरवतोऽस्य जन्मादिबन्धः कश्चिदित्याह—</p><blockquote><p><strong>विद्याविनाशे जन्मविनाशः॥१८॥</strong></p></blockquote><p>प्रोक्तायाः सहजविद्याया अविनाशे सततोन्मग्नतया स्फुरणे जन्मनोऽज्ञानसहकारिकर्महेतुकस्य दुःखमयस्य देहेन्द्रियादिसमुदायस्य नाशो विध्वंसः सम्पन्न एव। यदुक्तं श्रीकण्ठ्याम्</p><blockquote><p>सप्रपञ्चं परित्यज्य हेयोपादेयलक्षणम्।<br> तृणादिकं तथा पर्णं पाषाणं सचराचरम्॥<br> शिवाद्यवनिपर्यन्तं भावाभावोपबृंहितम्।<br> सर्वं शिवमयं ध्यात्वा भूयो जन्म न प्राप्नुयात्॥</p></blockquote><p>इति। श्रीस्वच्छन्दे</p><blockquote><p>सुनिर्वाणं परं शुद्धं गुरुपारम्परागतम्।<br> तद्विदित्वा विमुच्येत गत्वा भूयो न जायते॥</p></blockquote><p>इति। श्रीमृत्युजित्यपि</p><blockquote><p>तत्त्वत्रयविनिर्मुक्तं शाश्वतं त्वचलं ध्रुवम्।<br> दिव्येन योगमार्गेण दृष्ट्वा भूयो न जायते॥</p></blockquote><p>इति॥१८॥ ;;;</p><hr><p>;;; trans</p><p>Na caivaṁ svaśaktinirmitabhūtabhāvaśarīravato&#39;sya janmādibandhaḥ kaścidityāha—</p><blockquote><p><strong>Vidyāvināśe janmavināśaḥ||18||</strong></p></blockquote><p>Proktāyāḥ sahajavidyāyā avināśe satatonmagnatayā sphuraṇe janmano&#39;jñānasahakārikarmahetukasya duḥkhamayasya dehendriyādisamudāyasya nāśo vidhvaṁsaḥ sampanna eva| Yaduktaṁ śrīkaṇṭhyām</p><blockquote><p>Saprapañcaṁ parityajya heyopādeyalakṣaṇam|<br> Tṛṇādikaṁ tathā parṇaṁ pāṣāṇaṁ sacarācaram||<br> Śivādyavaniparyantaṁ bhāvābhāvopabṛṁhitam|<br> Sarvaṁ śivamayaṁ dhyātvā bhūyo janma na prāpnuyāt||</p></blockquote><p>iti| Śrīsvacchande</p><blockquote><p>Sunirvāṇaṁ paraṁ śuddhaṁ gurupāramparāgatam|<br> Tadviditvā vimucyeta gatvā bhūyo na jāyate||</p></blockquote><p>iti| Śrīmṛtyujityapi</p><blockquote><p>Tattvatrayavinirmuktaṁ śāśvataṁ tvacalaṁ dhruvam|<br> Divyena yogamārgeṇa dṛṣṭvā bhūyo na jāyate||</p></blockquote><p>iti||18||</p><p>;;;</p><hr><p>Таким образом <em>(evam)</em>, не существует <em>(na ca... kaścid)</em> рабство <em>(bandhaḥ)</em> [в форме] рождения <em>(janma)</em>, и т.д. <em>(ādi)</em>, для того, кто приобрел тело <em>(śarīravataḥ)</em>, сотворенное <em>(nirmita)</em> из его собственной <em>(sva)</em> Śakti, или Силы <em>(śakti)</em>, которое --т.е. тело-- состоит из bhūta-s --т.е. Mahābhūta-s, или грубых элементов (tattva-s 32 to 36)-- <em>(bhūta)</em> [и] bhāva-s --т.е. Tanmātra-s, или Тонких элементов (таттвы с 27 по 31), эмоций и т.д.-- <em>(bhāva)</em>. [Автор Śivasūtra-s, т.е. Śiva,] сказал так <em>(iti āha)</em>—</p><blockquote><p><strong>До тех пор пока Sahajavidyā, или Śuddhavidyā, [Естественное, или Чистое Знание] <em>(vidyā)</em> не исчезает <em>(avināśe)</em>, [возможность другого] рождения <em>(janma)</em> утрачивается [для этого возвышенного Yogī] <em>(vināśaḥ)</em>||18||</strong></p></blockquote><p>До тех пор пока не исчезает <em>(avināśe)</em> вышеупомянутое <em>(proktāyāḥ)</em> Естественное Знание <em>(sahajavidyāyāḥ)</em>, т.е. пока оно продолжает проявляться <em>(sphuraṇe)</em> как постоянно возникающая реальность <em>(unmagnatayā)</em>, исчезновение <em>(nāśaḥ)</em>, [или] прекращение <em>(vidhvaṁsaḥ)</em> [другого] рождения <em>(janmanaḥ)</em>, состоящего из совокупности <em>(samudāyasya)</em> тела <em>(deha)</em>, чувств <em>(indriya)</em>, и т.д. <em>(ādi)</em>, полного <em>(mayasya)</em> боли <em>(duḥkha)</em>, [и] обусловленного <em>(hetukasya)</em> [его собственными] действиями <em>(karma)</em> во взаимодействии с <em>(sahakāri)</em> Неведением, [или Māyā и ее потомством] <em>(ajñāna)</em>, осуществляется <em>(sampannaḥ)</em> несомненно <em>(eva)</em><sup>1</sup>|</p><p>Это <em>(yad)</em> сформулировано <em>(uktam)</em> в уважаемом Kaṇṭhī <em>(śrī-kaṇṭhyām)</em>:</p><blockquote><p>&quot;Оставив <em>(parityajya)</em> [мир] с [его] феноменами <em>(sa-prapañcam)</em>, появляющимися как <em>(lakṣaṇam)</em> что-либо желаемое <em>(upādeya)</em> [или] отвергаемое <em>(heya)</em>, и также <em>(tathā)</em> [как] травинка <em>(tṛṇa)</em> и как <em>(ādikam)</em>, лист <em>(parṇam)</em>, камень <em>(pāṣāṇam)</em>, наряду с <em>(sa)</em> подвижными и неподвижными существами <em>(cara-acaram)</em>, начиная с <em>(ādi)</em> Śiva --таттвы, или категории 1-- <em>(śiva)</em> [и] заканчивая <em>(paryantam)</em> землей --таттвой, или категорией 36-- <em>(avani)</em>, сопровождаемыми <em>(upabṛṁhitam)</em> позитивными и негативными сущностями <em>(bhāva-abhāva)</em>; [и] после медитации <em>(dhyātvā)</em> на всем <em>(sarvam)</em>, как тождественном <em>(mayam)</em> Шиве <em>(śiva)</em>, он не получает <em>(na prāpnuyāt)</em> рождения <em>(janma)</em> снова <em>(bhūyas... iti)</em>&quot;||</p></blockquote><p>[И] в уважаемом Svacchandatantra <em>(śrī-svacchande)</em>:</p><blockquote><p>&quot;Превосходное <em>(su)</em> освобождение <em>(nirvāṇam)</em>, приходящее от <em>(āgatam)</em> традиции <em>(pārampara)</em> духовных наставников <em>(guru)</em>, [является] невероятно <em>(param)</em> чистым <em>(śuddham)</em>. Обладая знанием этого [освобождения] --т.е. испытав это-- <em>(tad-viditvā)</em> он становится освобожденным [при жизни] <em>(vimucyeta)</em><sup>2</sup></p></blockquote><p>, [и] после смерти <em>(gatvā)</em> он не родится <em>(na jāyate)</em> вновь <em>(bhūyas... iti)</em><sup>3</sup>&quot;| [См. IV, 240-241 в Svacchandatantra]</p><p>[Это] также [было сказано] <em>(api)</em> в досточтимом Mṛtyujit --Netratantra-- <em>(śrī-mṛtyujiti)</em>:</p><blockquote><p>&quot;Пройдя <em>(dṛṣṭvā)</em>, божественным <em>(divyena)</em> путем <em>(mārgeṇa)</em> Йоги <em>(yoga)</em>, свободный <em>(vinirmuktam)</em> от трех таттв, или принципов <em>(tattva-traya)</em>, несомненно вечный <em>(śāśvatam tu)</em>, неизменный <em>(acalam)</em> [и] перманентный <em>(dhruvam)</em>, он не рождается <em>(na jāyate)</em> вновь <em>(bhūyas... iti)</em><sup>4</sup>&quot;||<br> [См. VIII, 26-27 в Netratantra]</p></blockquote><p>||18||</p><hr><ol><li><p>Хотя Естественное и Чистое Знание продолжает проявляться как постоянно возникающая реальность, для такого великого Yogī больше не будет другого рождения. Рождение состоит из множества вещей (например, тело, чувства и пр.), наполнено болью и происходит по закону Кармы (т.е. создается вашими действиями). Оно также получает содействие, или помощь Неведения в форме Māyīyamala (майическое загрязнение, порождающее различие и разделение) и Kārmamala (загрязнение, вызывающее путаницу относительно природы истинного Делателя). В заключение, поскольку тела, проявленные освобожденным Yogī возникают из его собственной Icchāśakti, а не из закона Кармы, перерождение становится совершенно невозможным для этого возвышенного Yogī, потому что все действия, выполненные этими телами, не являются для него кармическими и поэтому не приведут к другому рождению.</p></li><li><p>Мудрец Kṣemarāja комментирует в своей Svacchandoddyota: &quot; विमुच्यत इति जीवन्नेव विमुक्तः स्यात्&quot; - &quot;vimucyata iti jīvanneva vimuktaḥ syāt&quot; - &quot;&#39;vimucyate&#39; [означает] того, кто освободился при жизни&quot;. Поэтому я добавил &quot;при жизни&quot; в свой перевод, несмотря на то, что &quot;vimucyate&quot; прямо означает &quot;становится освобожденным&quot;.</p></li><li><p>Kṣemarāja объясняет эту последнюю часть строфы в своем комментарии на Svacchandatantra: &quot;गत्वेति देहान्ते तदैकात्म्यमेति॥&quot; - &quot;gatveti dehānte tadaikātmyameti||&quot; - &quot;&#39;gatvā&#39; [означает] &#39;в конце тела&#39; --т.е. когда наступит смерть--, [в этот момент,] он достигнет единства с Тем --с Высшей Реальностью, и следовательно, он не родится вновь--&quot;. Согласно Трике и большинству философских систем Индии, человек рождается как человеческое существо только для достижения Освобождения. Каждая система, безусловно, постулирует Освобождение по-разному. В Трике, окончательное Освобождение — это то же самое, что достижение единства с Высшей Реальностью. Вот почему другое рождение не требуется в случае освобожденной души.</p></li><li><p>Мудрец Kṣemarāja комментирует в своей Netroddyota несколько вещей некоторыми противоречивыми терминами и выражениями в этой строфе Netratantra:</p></li><li><p>&quot; दृष्ट्वा साक्षात्कृत्य&quot; - &quot;dṛṣṭvā sākṣātkṛtya&quot; - &quot;После видения, т.е. после осознания&quot;;</p></li><li><p>&quot; दिव्येन योगमार्गेण विकल्पहानोन्मिषदविकल्पविमर्शावष्टम्भोपायेन&quot; - &quot;divyena yogamārgeṇa vikalpahānonmiṣadavikalpavimarśāvaṣṭambhopāyena&quot; - &quot;божественным путем Йоги, т.е. посредством метода, или средства, основанного на avikalpavimarśa, или &#39;сознании, лишенном мыслей&#39; , которое сияет с прекращением мыслей&quot;;</p></li><li><p>&quot;तत्त्वत्रयं नरशक्तिशिवाख्यम्।&quot; - &quot;Tattvatrayaṁ naraśaktiśivākhyam|&quot; - &quot;Три таттвы, или принципа, известные как nara (человек), Śakti и Śiva&quot;, где последние два есть проявление Того (Paramaśiva), который свободен от этого тройного разделения, короче, Это даже не Господь и Его Сила, не говоря уже о человеке (загадочная тема, которую я не могу подробно здесь объяснять);</p></li><li><p>&quot;अचलमपरिणामि&quot; - &quot;acalamapariṇāmi&quot; - &quot;acala, т.е. неизменный&quot;; и наконец</p></li><li><p>&quot;ध्रुवं नित्यम्&quot; - &quot;dhruvaṁ nityam&quot; - &quot;dhruva, а именно, перманентный&quot;.</p></li></ol></div><div class="stanza"><a href="#s3.19" id="s3.19" class="num">3.19</a><p>;;; sans</p><p>यदा तु शुद्धविद्यास्वरूपमस्य निमज्जति तदा—</p><blockquote><p><strong>कवर्गादिषु माहेश्वर्याद्याः पशुमातरः॥१९॥</strong></p></blockquote><p>अधिष्ठात्र्यो भवन्तीति शेषः।</p><blockquote><p>या सा शक्तिर्जगद्धातुः कथिता समवायिनी।<br> इच्छात्वं तस्य सा देवि सिसृक्षोः प्रतिपद्यते।<br> सैकापि सत्यनेकत्वं यथा गच्छति तच्छृणु॥<br> एवमेतदिति ज्ञेयं नान्यथेति सुनिश्चितम्।<br> ज्ञापयन्ती जगत्यत्र ज्ञानशक्तिर्निगद्यते॥<br> एवम्भूतमिदं वस्तु भवत्विति यदा पुनः।<br> जाता तदैव तत्तद्वत्कुर्वत्यत्र क्रियोच्यते॥<br> एवं सैषा द्विरूपापि पुनर्भेदैरनन्तताम्।<br> अर्थोपाधिवशात्प्राप्ता चिन्तामणिरिवेश्वरी॥<br> तत्र तावत्समापन्नमातृभावा विभिद्यते।<br> द्विधा च नवधा चैव पञ्चाशद्धा च मालिनी॥<br> बीजयोन्यात्मकाद्भेदाद्द्विधा बीजं स्वरा मताः।<br> कादिभिश्च स्मृता योनिर्नवधा वर्गभेदतः॥<br> बीजमत्र शिवः शक्तिर्योनिरित्यभिधीयते।<br> वर्गाष्टकविभेदेन माहेश्वर्यादि चाष्टकम्॥<br> प्रतिवर्णविभेदेन शतार्धकिरणोज्ज्वला।<br> रुद्राणां वाचकत्वेन तत्सङ्ख्यानां निवेशिता॥</p></blockquote><p>इति श्रीमालिनीविजयनिरूपितनीत्या पारमेश्वरी परावाक् प्रसरन्तीच्छाज्ञानक्रियारूपतां श्रित्वा बीजयोनिवर्गवर्ग्यादिरूपा शिवशक्तिमाहेश्वर्यादिवाचकादिक्षान्तरूपां मातृकात्मतां श्रित्वा सर्वप्रमातृष्वविकल्पकसविकल्पकतत्तत्संवेदनदशास्वन्तःपरामर्शात्मना स्थूलसूक्ष्मशब्दानुवेधनं विदधाना वर्गवर्ग्यादिदेवताधिष्ठानादिद्वारेण स्मयहर्षभयरागद्वेषादिप्रपञ्चं प्रपञ्चयन्त्यसङ्कुचितस्वतन्त्रचिद्घनस्वस्वरूपमावृण्वाना सङ्कुचितपरतन्त्रदेहादिमयत्वमापादयति। तदुक्तं श्रीतिमिरोद्घाटेऽपि</p><blockquote><p>करन्ध्रचितिमध्यस्था ब्रह्मपाशावलम्बिकाः।<br> पीठेश्वर्यो महाघोरा मोहयन्त्यो मुहुर्मुहुः॥</p></blockquote><p>इति पूर्वमपि संवादितम्। ज्ञानाधिष्ठानं मातृका (१-४) इति सामान्येनोक्तमिदं तु प्राप्ततत्त्वोऽपि प्रमाद्यन् माहेश्यादिभिः पशुजनाधिष्ठातृभूताभिरपि शब्दानुवेधद्वारेण मोह्यत इत्याशयेनेति विशेषः॥१९॥ ;;;</p><hr><p>;;; trans</p><p>Yadā tu śuddhavidyāsvarūpamasya nimajjati tadā—</p><blockquote><p><strong>Kavargādiṣu māheśvaryādyāḥ paśumātaraḥ||19||</strong></p></blockquote><p>Adhiṣṭhātryo bhavantīti śeṣaḥ|</p><blockquote><p>Yā sā śaktirjagaddhātuḥ kathitā samavāyinī|<br> Icchātvaṁ tasya sā devi sisṛkṣoḥ pratipadyate|<br> Saikāpi satyanekatvaṁ yathā gacchati tacchṛṇu||<br> Evametaditi jñeyaṁ nānyatheti suniścitam|<br> Jñāpayantī jagatyatra jñānaśaktirnigadyate||<br> Evambhūtamidaṁ vastu bhavatviti yadā punaḥ|<br> Jātā tadaiva tattadvatkurvatyatra kriyocyate||<br> Evaṁ saiṣā dvirūpāpi punarbhedairanantatām|<br> Arthopādhivaśātprāptā cintāmaṇiriveśvarī||<br> Tatra tāvatsamāpannamātṛbhāvā vibhidyate|<br> Dvidhā ca navadhā caiva pañcāśaddhā ca mālinī||<br> Bījayonyātmakādbhedāddvidhā bījaṁ svarā matāḥ|<br> Kādibhiśca smṛtā yonirnavadhā vargabhedataḥ||<br> Bījamatra śivaḥ śaktiryonirityabhidhīyate|<br> Vargāṣṭakavibhedena māheśvaryādi cāṣṭakam||<br> Prativarṇavibhedena śatārdhakiraṇojjvalā|<br> Rudrāṇāṁ vācakatvena tatsaṅkhyānāṁ niveśitā||</p></blockquote><p>iti śrīmālinīvijayanirūpitanītyā pārameśvarī parāvāk prasarantīcchājñānakriyārūpatāṁ śritvā bījayonivargavargyādirūpā śivaśaktimāheśvaryādivācakādikṣāntarūpāṁ mātṛkātmatāṁ śritvā sarvapramātṛṣvavikalpakasavikalpakatattatsaṁvedanadaśāsvantaḥparāmarśātmanā sthūlasūkṣmaśabdānuvedhanaṁ vidadhānā vargavargyādidevatādhiṣṭhānādidvāreṇa smayaharṣabhayarāgadveṣādiprapañcaṁ prapañcayantyasaṅkucitasvatantracidghanasvasvarūpamāvṛṇvānā saṅkucitaparatantradehādimayatvamāpādayati| Taduktaṁ śrītimirodghāṭe&#39;pi</p><blockquote><p>Karandhracitimadhyasthā brahmapāśāvalambikāḥ|<br> Pīṭheśvaryo mahāghorā mohayantyo muhurmuhuḥ||</p></blockquote><p>iti pūrvamapi saṁvāditam| Jñānādhiṣṭhānaṁ mātṛkā (1-4) iti sāmānyenoktamidaṁ tu prāptatattvo&#39;pi pramādyan māheśyādibhiḥ paśujanādhiṣṭhātṛbhūtābhirapi śabdānuvedhadvāreṇa mohyata ityāśayeneti viśeṣaḥ||19||</p><p>;;;</p><hr><p>Но <em>(tu)</em>, когда <em>(yadā)</em> сущностная природа <em>(sva-rūpam)</em> Śuddhavidyā, или Чистое Знание <em>(śuddha-vidyā)</em>, принадлежащее этому [Yogī] <em>(asya)</em>, погружается вниз <em>(nimajjati)</em>, тогда <em>(tadā)</em>—</p><blockquote><p><strong>Māheśvarī <em>(māheśvarī)</em> и другие богини <em>(ādyāḥ)</em>, [чьей сферой влияния является] &quot;ka&quot; <em>(ka)</em> группа <em>(varga)</em>, и т.д. <em>(ādiṣu)</em>, [и являющиеся] матерями <em>(mātaraḥ)</em> ограниченных существ <em>(paśu)</em>, [становятся его главными божествами]||19||</strong></p></blockquote><p>&quot;[Эти богини] становятся <em>(bhavanti)</em> [его --такого Yogī--] главными божествами <em>(adhiṣṭhātryaḥ... iti)</em>&quot;, это следует добавить [к афоризму для полноты смысла] <em>(iti śeṣaḥ)</em>|</p><blockquote><p>&quot;Говорится <em>(kathitā)</em>, что эта <em>(yā sā)</em> Śakti, или Сила <em>(śaktiḥ)</em> Создателя <em>(dhātuḥ)</em> мира <em>(jagat)</em> [является] присущей <em>(samavāyinī)</em> [Ему]. О Богиня <em>(devi)</em>!, Она <em>(sā)</em> становится <em>(pratipadyate)</em> Волей <em>(icchātvam)</em> этого [Создателя] <em>(tasya)</em>, [когда] Он желает проявить <em>(sisṛkṣoḥ)</em> [вселенную]. Слушай <em>(tad śṛṇu)</em>, как <em>(yathā)</em> Она <em>(sā)</em> становится <em>(gacchati)</em> множеством <em>(anekatvam)</em>, несмотря на то, что <em>(api)</em> Она <em>(satī)</em> одна <em>(ekā)</em>:</p></blockquote><blockquote><p>Объявляя <em>(jñāpayantī)</em> с определенностью <em>(suniścitam)</em>, что &#39;Это <em>(etad)</em> познаваемое, или объект <em>(jñeyam)</em> подобно этому <em>(evam... iti)</em>, [и] не <em>(na)</em> иначе <em>(anyathā... iti)</em>!&#39;, Она зовется <em>(nigadyate)</em> Силой Знания <em>(jñāna-śaktiḥ)</em> в этом мире <em>(jagati atra)</em>.</p></blockquote><blockquote><p>Однако <em>(punar)</em>, когда <em>(yadā)</em> Она становится проявленной <em>(jātā)</em> [как] &#39;Пусть эта вещь станет <em>(idam vastu bhavatu)</em> такой природы <em>(evam-bhūtam... iti)</em>!&#39;, тогда <em>(tadā eva)</em>, создавая <em>(kurvatī)</em> эту [вещь] <em>(tad)</em> таким образом <em>(tad-vat)</em> здесь <em>(atra)</em>, говорится, что Она является <em>(ucyate)</em> [Силой] Действия <em>(kriyā)</em>.</p></blockquote><blockquote><p>Таким образом <em>(evam)</em>, хотя <em>(api)</em> эта самая [Śakti] <em>(sā eṣā)</em> имеет две [главные] формы <em>(dvi-rūpā)</em>, Она претерпевает <em>(prāptā)</em> бесконечные <em>(anantatām)</em> изменения <em>(bhedaiḥ)</em>, согласно <em>(vaśāt)</em> характеристикам <em>(upādhi)</em> [желаемых] объектов <em>(artha)</em>. [Следовательно, эта] Госпожа <em>(īśvarī)</em> подобна <em>(iva)</em> драгоценному камню <em>(cintā-maṇiḥ)</em> --который исполняет все желания его обладателя--.</p></blockquote><blockquote><p>Затем <em>(tatra)</em>, [когда Госпожа] вначале принимает состояние <em>(tāvat samāpanna... bhāvā)</em> матери <em>(mātṛ)</em>, Она разделяется <em>(vibhidyate)</em> двумя путями <em>(dvidhā)</em> и <em>(ca... ca eva)</em> девятью путями <em>(navadhā)</em>, и <em>(ca)</em> [когда такая Госпожа разделяется] пятьюдесятью путями <em>(pañcāśaddhā)</em>, [Она становится] носителем гирлянды <em>(mālinī)</em>, [сделанной из пятидесяти букв санскритского алфавита].</p></blockquote><blockquote><p>При разделении, состоящем <em>(ātmakāt bhedāt)</em> из bīja <em>(bīja)</em> [и] yoni <em>(yoni)</em>, Она проявляется двумя способами <em>(dvidhā)</em>. Гласные <em>(svarāḥ)</em> рассматриваются как <em>(matāḥ)</em> bīja, или семя <em>(bījam)</em>. Начиная с ka, и т.д. <em>(ka-ādibhiḥ)</em> о Ней говорят как о <em>(smṛtā)</em> yoni, или лоне <em>(yoniḥ)</em> --т.е. согласные считаются yoni--. [Следовательно,] в соответствии с разделением <em>(bhedatas)</em> на varga-s, или группы букв <em>(varga)</em>, [Она проявляется] девятью путями <em>(navadhā)</em><sup>1</sup>.</p></blockquote><blockquote><p>В этом [контексте] <em>(atra)</em>, bīja --гласная-- <em>(bījam)</em> [и] yoni --согласная-- <em>(yoniḥ)</em> называются <em>(iti abhidhīyate)</em> Śiva <em>(śivaḥ)</em> [и] Śakti <em>(śaktiḥ)</em>. Кстати <em>(ca)</em>, при делении <em>(vibhedena)</em> на восемь <em>(aṣṭaka)</em> групп букв <em>(varga)</em> [имеет место] группа из восьми [божеств] <em>(aṣṭakam)</em>, [таких как] Māheśvarī <em>(māheśvarī)</em>, и т.д. <em>(ādi)</em> .</p></blockquote><blockquote><p>[И] в соответствии с разделением букв <em>(prativarṇa-vibhedena)</em>, Она сияет <em>(ujjvalā)</em> как пятьдесят <em>( śata-ardha)</em> лучей <em>(kiraṇa)</em> --т.е. Она проявляется как пятьдесят букв санскритского алфавита--. Являясь указателем <em>(vācakatvena)</em> на Rudra-s <em>(rudrāṇām)</em>, количество которых <em>(saṅkhyānām)</em> такое же --т.е. пятьдесят-- <em>(tad)</em>, Она укреплена [в этом самом числе] <em>(niveśitā... iti)</em><sup>2</sup>&quot;||<br> [См. Mālinīvijayatantra III, 5-13]</p></blockquote><p>Согласно наставлениям <em>(nītyā)</em>, обозначенным и удостоверенным <em>(nirūpita)</em> в уважаемом Mālinīvijayatantra <em>(śrī-mālinīvijaya)</em>, Высшая Речь <em>(parā-vāk)</em>, относящаяся к Высшему Господу <em>(pārameśvarī)</em> --т.е. Его Śakti, или Силе-- развертывается <em>(prasarantī)</em> посредством того <em>(śritvā)</em>, что сформировано <em>(rūpatām)</em> Волей <em>(icchā)</em>, Знанием <em>(jñāna)</em> [и] Действием <em>(kriyā)</em>. [Позже, Она продолжает расширение], приняв форму <em>(rūpā)</em> гласных <em>(bīja)</em>, согласных <em>(yoni)</em>, групп букв <em>(varga)</em>, букв, принадлежащих к этим группам <em>(vargī)</em>, и т.д. <em>(ādi)</em> после принятия <em>(śritvā)</em> природы <em>(ātmatām)</em> Mātṛkā <em>(mātṛkā)</em>, чья форма <em>(rūpām)</em> начинается с &quot;a&quot; <em>(a-ādi)</em> и заканчивается в &quot;kṣa&quot; <em>(kṣa-anta)</em> --а именно, весь санскритский алфавит--, [все из которых --т.е. все пятьдесят букв--] указывают на <em>(vācaka)</em> Śiva <em>(śiva)</em>, Śakti <em>(śakti)</em>, Māheśvarī <em>(māheśvarī)</em>, и т.д. <em>(ādi)</em><sup>3</sup></p><p>. Во всех воспринимающих <em>(sarva-pramātṛṣu)</em><sup>4</sup>, когда имеются различные состояния <em>(tad-tad-daśāsu)</em> знания <em>(saṁvedana)</em> с мыслями <em>(savikalpaka)</em> [и] без мыслей <em>(avikalpaka)</em>, Она совершает <em>(vidadhānā)</em>, посредством внутреннего осознавания <em>(antar-parāmarśa-ātmanā)</em>, применение --т.е. проникновение, поскольку слова проникают внутрь-- <em>(anuvedhanam)</em> грубых <em>(sthūla)</em> [и] тонких <em>(sūkṣma)</em> слов <em>(śabda)</em>. Посредством <em>(dvāreṇa)</em> управления, или силы <em>(adhiṣṭhāna)</em>, и т.п. <em>(ādi)</em> божеств <em>(devatā)</em> групп букв <em>(varga)</em>, букв, принадлежащих к таким группам <em>(vargī)</em>, и т.д. <em>(ādi)</em>, Она демонстрирует <em>(prapañcayantī)</em> проявление <em>(prapañcam)</em> изумления <em>(smaya)</em>, наслаждения <em>(harṣa)</em>, страха <em>(bhaya)</em>, привязанности <em>(rāga)</em>, отвращения <em>(dveṣa)</em> и тому подобное <em>(ādi)</em>. [И] через сокрытие <em>(āvṛṇvānā)</em> своей <em>(sva)</em> сущностной природы <em>(sva-rūpam)</em>, которая является несокращенной --необусловленной-- <em>(asaṅkucita)</em> независимой <em>(svatantra)</em> массой <em>(ghana)</em> Сознания <em>(cit)</em>, Она производит <em>(āpādayati)</em> отождествление <em>(mayatvam)</em> со сжатым --обусловленным-- <em>(saṅkucita)</em> зависимым <em>(paratantra)</em> телом <em>(deha)</em>, и т.д. <em>(ādi)</em>|</p><p>Это <em>(tad)</em> было сказано <em>(uktam)</em> в уважаемом Timirodghāṭa <em>(śrī-timirodghāṭe)</em> too <em>(api)</em>:</p><blockquote><p>&quot;Mahāghorā-s --буквально, &quot;очень страшные&quot;-- <em>(mahāghorāḥ)</em>, [то есть, śakti-s, или силы], которые пребывают <em>(sthāḥ)</em> в сердцевине <em>(madhya)</em> Сознания <em>(citi)</em> в Karandhra --т.е. в Brahmarandhra-- <em>(ka-randhra)</em>, которые подвешивают <em>(avalambikāḥ)</em> арканом Брахмы <em>(brahma-pāśa)</em> [и] являются Владычицами <em>(īśvaryaḥ)</em> pīṭha-s, или опор/сидений --органов чувств-- <em>(pīṭha)</em>, вводят в заблуждение <em>(mohayanti)</em> вновь и вновь <em>(muhur... muhur... iti)</em><sup>5</sup>&quot;||</p></blockquote><p>[То, что было процитировано выше] было провозглашено <em>(saṁvāditam)</em> ранее <em>(pūrvam)</em> также <em>(api)</em> --в I, 4--|</p><p>&quot;Основой <em>(adhiṣṭhānam)</em> [ограниченного и сокращенного] знания <em>(jñāna)</em> [является] непонятая Мать <em>(mātṛkā)</em>. (I, 4) [настоящего Писания] <em>(1-4 iti)</em>&quot;. Это <em>(idam)</em> было определенно выражено <em>(uktam... tu)</em> в общем виде <em>(sāmānyena)</em>. [Здесь, в настоящем афоризме --III, 19--, Mātṛkā рассматривается более конкретно], с намерением <em>(āśayena)</em> [показать, что] &quot;[если] даже <em>(api)</em> тот, кто достиг [Высшего] Принципа <em>(prāpta-tattvaḥ)</em>, не осторожен и не внимателен <em>(pramādyan)</em>, также вводится в заблуждение <em>(api... mohyate)</em> посредством Māheśī --или Māheśvarī--, и т.д. <em>(māheśī-ādibhiḥ)</em>, которые являются <em>(bhūtābhiḥ)</em> [богинями], контролирующими <em>(adhiṣṭhātṛ)</em> ограниченных людей <em>(paśu-jana)</em> путем применения --проникновения-- <em>(anuvedha-dvāreṇa)</em> слов <em>(śabda... iti)</em>&quot;. В этом разница <em>(iti viśeṣaḥ)</em> [между двумя исследованиями --анализом, выраженном в I, 4 и в настоящем афоризме--]||19||</p><hr><ol><li>Санскритский алфавит в его традиционной форме содержит 8 varga-s, или групп букв:</li></ol><ul><li><p>A-varga (Гласные), где правящая богиня — Yogīśvarī (также называемая Mahālakṣmī)</p></li><li><p>Ka-varga (Гортанные/заднеязычные), где правящая богиня — Brāhmī</p></li><li><p>Ca-varga (Палатальные/Небные), где правящая богиня — Māheśvarī</p></li><li><p>Ṭa-varga (Церебральные), где правящая богиня — Kaumārī</p></li><li><p>Ta-varga (Зубные), где правящая богиня — Vaiṣṇavī</p></li><li><p>Pa-varga (Губные), где правящая богиня — Vārahī</p></li><li><p>Ya-varga (Неносовые сонанты/полугласные), где правящая богиня — Aindrī (также называемая Indrāṇī)</p></li><li><p>Śa-varga (Шипящие, придыхательная сонанта + Kūṭabīja --объединенная kṣa, образованная из &quot;ka + sa&quot;, но очень часто считающаяся отдельной буквой--), где правящая богиня — Cāmuṇḍā</p><p>В настоящее время алфавит, представленный в его традиционной форме, не включает Kūṭabīja (букву kṣa), но когда это происходит, Kūṭabīja обычно приписывается к восьмой varga, или группе (Śa-varga). Итак, вопрос на миллион: &quot;Если имеется 8 varga-s, или групп, почему Господь говорил об этом выше?: &quot; [Да потому, что] в соответствии с разделением varga-s, или групп букв, [Она проявляется] девятью путями. Упрощенно: иногда Kūṭabīja рассматривается как формирование собственной отдельной varga, т.е. Kṣa-varga. В этом особом случае, общее количество varga-s будет равно 9, а не 8. Теперь, эта часть текста понятна, так ведь?</p></li></ul><ol start="2"><li><p>Чтобы полностью понять этот вопрос, и учитель, и ученик должны быть очень хорошо знакомы со знаниями, отображенными в Mālinīvijayatantra. Тем не менее, здесь говорится: Индикатор/указатель означает, что Она указывает, или описывает пятьдесят Rudra-s, или божеств, описывающих каждую из пятидесяти букв санскритского алфавита. Господь сделал Ее такой, т.е. пятидесяти-кратной, и вот почему Она укреплена в этом количестве. Помимо Rudra-s имеются пятьдесят супруг для каждого из них, т.е. пятьдесят Rudrāṇī-s. Ах, этот предмет — очень длинный и сложный. Для получения более детальной информации, вы всегда можете обратиться к &quot;Писания (исследование)/Śivasūtravimarśinī&quot; в разделе Трика.</p></li><li><p>Чтобы получить больше информации обо всем этом, читайте мое объяснение в II, 7 настоящего Писания.</p></li><li><p>Имеется семь типов воспринимающих, или скорее семь этапов для одного воспринимающего:</p><ol><li>Śiva (его сфера — это Śiva и Śakti таттвы, первые две категории);</li><li>Mantramaheśvara (его сфера — это Sadāśiva-таттва, третья категория);</li><li>Mantreśvara (его сфера — это Īśvara-таттва, четвертая категория);</li><li>Mantra (его сфера — это Sadvidyā, или Śuddhavidyā таттва, пятая категория);</li><li>Vijñānākala (его сфера находится между Sadvidyā и Māyā таттвами, т.е. между пятой и шестой категориями);</li><li>Pralayākala (его сфера — это Māyā и ее пять Kañcuka-s, с шестой по одиннадцатую категории);</li><li>Sakala (его сфера — начиная с Puruṣa --двенадцатой категории-- и заканчивая последней).</li></ol></li><li><p>Поскольку эта строфа, которую я перевожу, цитировалась ранее Kṣemarāja в I, 4 настоящего Писания, читайте там пояснение 2, чтобы понять ее смысл.</p></li></ol></div><div class="stanza"><a href="#s3.20" id="s3.20" class="num">3.20</a><p>;;; sans</p><p>यत एवमतः शुद्धविद्यास्वरूपमुक्तयुक्तिभिरासादितमपि यथा न नश्यति तथा सर्वदशासु योगिना सावधानेन भवितव्यमित्याह—</p><blockquote><p><strong>त्रिषु चतुर्थं तैलवदासेच्यम्॥२०॥</strong></p></blockquote><p>त्रिषु जागरादिषु पदेषु चतुर्थं शुद्धविद्याप्रकाशरूपं तुर्यानन्दरसात्मकं धाम तैलवदिति यथा तैलं क्रमेणाधिकमधिकं प्रसरदाश्रयं व्याप्नोति तथासेच्यम्। त्रिष्वपि पदेषून्मेषोपशान्त्यात्मकाद्यन्तकोट्योः परिस्फुरता तुर्यरसेन मध्यदशामप्यवष्टम्भयुक्त्या व्याप्नुयाद्येन तन्मयीभावमाप्नुयात्। जाग्रत्स्वप्नसुषुप्तेषु तुर्याभोगसम्भवः (१-७) इत्यनेनोद्यमशक्तिचक्रानुसन्ध्यवष्टम्भभाजः स्वरसप्रसरज्जागरादिपदेषु सत्तामात्रं तुर्यस्योक्तम्। त्रितयभोक्ता वीरेशः (१-११) इति शाम्भवोपायानुगुणहठपाकयुक्त्या जागरादिसंहारो दर्शितः। अनेन तु सूत्रेणाणवोचितावष्टम्भयुक्त्या दलकल्पं जागरादित्रयं तुर्यरसासिक्तं कार्यमित्युक्तमिति विशेषः॥२०॥ ;;;</p><hr><p>;;; trans</p><p>Yata evamataḥ śuddhavidyāsvarūpamuktayuktibhirāsāditamapi yathā na naśyati tathā sarvadaśāsu yoginā sāvadhānena bhavitavyamityāha—</p><blockquote><p><strong>Triṣu caturthaṁ tailavadāsecyam||20||</strong></p></blockquote><p>Triṣu jāgarādiṣu padeṣu caturthaṁ śuddhavidyāprakāśarūpaṁ turyānandarasātmakaṁ dhāma tailavaditi yathā tailaṁ krameṇādhikamadhikaṁ prasaradāśrayaṁ vyāpnoti tathāsecyam| Triṣvapi padeṣūnmeṣopaśāntyātmakādyantakoṭyoḥ parisphuratā turyarasena madhyadaśāmapyavaṣṭambhayuktyā vyāpnuyādyena tanmayībhāvamāpnuyāt| Jāgratsvapnasuṣupteṣu turyābhogasambhavaḥ (1-7) ityanenodyamaśakticakrānusandhyavaṣṭambhabhājaḥ svarasaprasarajjāgarādipadeṣu sattāmātraṁ turyasyoktam| Tritayabhoktā vīreśaḥ (1-11) iti śāmbhavopāyānuguṇahaṭhapākayuktyā jāgarādisaṁhāro darśitaḥ| Anena tu sūtreṇāṇavocitāvaṣṭambhayuktyā dalakalpaṁ jāgarāditrayaṁ turyarasāsiktaṁ kāryamityuktamiti viśeṣaḥ||20||</p><p>;;;</p><hr><p>Поскольку <em>(yatas)</em> [это обстоит] таким образом <em>(evam)</em> --а именно, поскольку даже тот, кто достиг Высшего Принципа, может быть все еще обманут такими божествами--, следовательно <em>(atas)</em> Yogī должен быть внимателен и осторожен <em>(tathā... yoginā sāvadhānena bhavitavyam)</em> при всех обстоятельствах <em>(sarva-daśāsu)</em>, чтобы <em>(yathā)</em> сущностная природа <em>(sva-rūpam)</em> Чистого Знания <em>(śuddha-vidyā)</em>, приобретенного [им] <em>(āsāditam api)</em>, не исчезла <em>(na naśyati)</em>. [Śiva, автор Śivasūtra-s,] сказал так <em>(iti āha)</em>—</p><blockquote><p><strong>Четвертое состояние сознания, [которое является Свидетелем] <em>(caturtham)</em>, следует вливать <em>(āsecyam)</em> подобно <em>(vat)</em> [непрерывному потоку] масла <em>(taila)</em> в [другие] три <em>(triṣu)</em>, [т.е. в бодрствование, сон со сновидениями и глубокий сон]||20||</strong></p></blockquote><p>[Термин] triṣu <em>(triṣu)</em> [означает] в [другие три] состояния <em>(padeṣu)</em> бодрствования и пр.<em>(jāgarā-ādiṣu)</em>. [Слово] caturtha --т.е. четвертое-- <em>(caturtham)</em> [означает] состояние <em>(dhāma)</em>, состоящее из <em>(ātmakam)</em> эликсира <em>(rasa)</em> блаженства Турьи --т.е. блаженства четвертого состояния сознания-- <em>(turya-ānanda)</em>, чьей природой <em>(rūpam)</em> является Свет <em>(prakāśa)</em> Чистого Знания <em>(śuddha-vidyā)</em>. [Выражение] &quot;tailavat&quot; <em>(taila-vat iti)</em> [означает, что] точно также, как <em>(yathā)</em> [непрерывный поток] масла <em>(tailam)</em>, постепенно распространяясь <em>(krameṇa... prasarat)</em> больше и больше <em>(adhikam adhikam)</em>, заполняет <em>(vyāpnoti)</em> получателя <em>(āśrayam)</em>, также <em>(tathā)</em> и [четвертое состояние сознания] должно вливаться <em>(āsecyam)</em> [в бодрствование, сон со сновидениями и глубокий сон]|</p><p>Посредством метода <em>(yuktyā)</em> непоколебимого удерживания --а именно, устойчивого внимания к Самости, являющейся Свидетелем-- <em>(avaṣṭambha)</em>, [Yogī] должен также пропитывать <em>(api... vyāpnuyāt)</em> промежуточное <em>(madhya)</em> состояние <em>(daśām)</em> эликсиром <em>(rasena)</em> Турьи (Turya) --Четвертого Состояния-- <em>(uktam)</em>, которое проявляется <em>(parisphuratā)</em> на начальной и конечной точках <em>(ādi-anta-koṭyoḥ)</em> —где происходит прекращение <em>(upaśānti-ātmaka)</em> unmeṣa --т.е. отображения всеобъемлющего проявления-- <em>(unmeṣa)</em>— во время трех <em>(triṣu api)</em> состояний [бодрствования, сна со сновидениями и глубокого сна] <em>(padeṣu)</em>. Посредством этого --т.е. делая так-- <em>(yena)</em>, [группа трех обычных состояний сознания] достигает <em>(āpnuyāt)</em> состояния <em>(bhāvam)</em> отождествления <em>(mayī)</em> с Этим --с Турьей, или Четвертым Состоянием-- <em>(tad)</em><sup>1</sup>|</p><p>&quot;[Даже] во время этих разных [состояний сознания] <em>(bhede)</em>, как бодрствование <em>(jāgrat)</em>, сон со сновидениями <em>(svapna)</em> и глубокий сон <em>(suṣupta)</em>, имеют место <em>(sambhavaḥ)</em> восторг и наслаждение <em>(ābhoga)</em> Четвертого Состояния <em>(turya)</em>. (I, 7) [данного Писания] <em>(1-7 iti)</em>&quot;. Этим [афоризмом] <em>(anena)</em>, было провозглашено <em>(uktam)</em> только <em>(mātra)</em> существование <em>(sattā)</em> Турьи <em>(turyasya)</em>, которая распространяет <em>(prasarat)</em> свой собственный <em>(sva)</em> эликсир <em>(rasa)</em> в состояния <em>(padeṣu)</em> бодрствования <em>(jāgarā)</em>, и т.д. <em>(ādi)</em>, [в случае] того, кто предан <em>(bhājaḥ)</em> непоколебимому удерживанию <em>(avaṣṭambha)</em> непрерывного осознания <em>(anusandhi)</em> группы <em>(cakra)</em> сил <em>(śakti)</em> [или проще говоря, того, в ком имеется] udyama, или спонтанное возникновения [Четвертого Состояния] <em>(udyama)</em>|</p><p>&quot;Он является повелителем <em>(īśaḥ)</em> [своих] чувств <em>(vīra)</em>, тем, кто наслаждается [вышеупомянутым &quot;ābhoga&quot;, или божественным восторгом] <em>(bhoktā)</em> в триаде [бодрствования, сна со сновидениями и глубокого сна] <em>(tritaya)</em>. (I, 11) [данного Писания] <em>(1-11 iti)</em>&quot;. [В этом афоризме,] растворение <em>(saṁhāraḥ)</em> бодрствования <em>(jāgarā)</em>, и пр. <em>(ādi)</em> было показано <em>(darśitaḥ)</em> посредством метода <em>(yuktyā)</em> haṭhapāka --буквально, ассимиляцией силы и упорства-- <em>(haṭha-pāka)</em> в соответствии с <em>(anuguṇa)</em> Śāmbhavopāya --средствами, или методами, относящимися к Śiva-- <em>(śāmbhava-upāya)</em><sup>2</sup></p><p>. Однако <em>(tu)</em>, в этом <em>(anena)</em> афоризме --т.е. в рассматриваемом-- <em>(sūtreṇa)</em> говорится, что <em>(iti uktam)</em> триада <em>(trayam)</em> бодрствование <em>(jāgarā)</em>, и пр. <em>(ādi)</em>, подобная <em>(kalpam)</em> ножнам меча <em>(dala)</em><sup>3</sup>, должна быть пропитана <em>(āsiktam kāryam)</em> эликсиром <em>(rasa)</em> Четвертого Состояния <em>(turya)</em> посредством метода <em>(yuktyā)</em> [вышеуказанного] непоколебимого удерживания <em>(avaṣṭambha)</em>, свойственного <em>(ucita)</em> Āṇavopāya --средств, или методов, относящихся к aṇu, или ограниченной личности-- <em>(āṇava)</em>. В этом различие <em>(iti viśeṣaḥ)</em> [между этими афоризмами, т.е. между I, 7, I, 11 и настоящим --III, 20--]||20||</p><hr><ol><li>Чтобы полностью понять, что мудрец хотел выразить в этом абзаце, вам надо быть Yogī, хорошо знакомым с этими процессами. В любом случае, если вы еще не являетесь Yogī, вы можете попытаться понять это интеллектуально. Слушайте: Большинство людей испытывают свое собственное Высшее Я только в начале и в конце трех обычных состояний сознания (бодрствования, сна со сновидениями и глубокого сна). Конечно, на начальном этапе своей практики человек — такой же как и все остальные. Поэтому, его восприятие Самости, как Свидетеля, имеет место только в начальных и конечных точках вышеупомянутых состояний. Состояние, в котором пребывает его собственное Я, известно как Turya, или Четвертое Состояние, поскольку оно отличается от трех других. Когда обычное состояние сознания (будь то бодрствование, сон со сновидениями или глубокий сон) начинается или заканчивается, в этот момент происходит прекращение унмеши (unmeṣa), или отображения всеобъемлющего проявления. Под unmeṣa подразумевается любое проявление в состояниях бодрствования (например, дерево), сна со сновидениями (например, странные цвета) и глубокого сна (например, пустота). Unmeṣa присутствует только &quot;посередине&quot; обычных состояний сознания. В их начале и в конце unmeṣa отсутствует.</li></ol><p>С другой стороны, Turya, или Четвертое Состояние никогда не отходит от своей собственной природы, т.к. длится вечно. Это не похоже на остальные три состояния, которые являются преходящими. Turya становится очевидной для большинства людей, только когда unmeṣa отсутствует, но когда она проявляется, эти люди больше не осознают свое Высшее Я. Поскольку Yogī продвигается в своей практике, однажды появляется Чистое Знание (Śuddhavidyā), и он полностью осознает свою Самость, как Свидетеля всех своих переживаний, происходящих с ним в бодрствовании, во сне со сновидениями и глубоком сне. Это знаменует собой его достижение Турьи. С этого момента ему следует заставить Turya постепенно пропитывать остальные три состояния, подобно непрерывному потоку масла, наполняющему получателя. В конце концов, все эти три обычных состояния сознания (бодрствование, сон со сновидениями и глубокий сон) станут тождественны с Turya и останется только &quot;одно Состояние&quot;, т.е. Yogī будет полностью установлен в своем Высшем Я постоянно. На самом деле, только это Состояние и существует вечно. Другие три — это просто блаженная игра Śakti. Они возникают, когда Она (по всей видимости) маскирует Turya и экспонирует unmeṣa, или отображение всеобъемлющего проявления. Помимо моих объяснений, все это — вопрос личного опыта. Поэтому, становитесь настоящим Yogī и испытайте вашу собственную безграничность. OK, достаточно.</p><ol start="2"><li><p>Поскольку этот афоризм (I, 11) — из первой главы, относящейся к Śāmbhavopāya, растворение обычных состояний сознания (бодрствования, сна со сновидениями, глубокого сна) показано посредством метода haṭhapāka, или ассимиляции опыта силой и настойчивостью, (выражаясь фигурально), &quot;одним махом&quot;. Этот метод относится к kramātikrama, поскольку он выходит за рамки krama, или последовательности, т.к. Yogī достигает Четвертого Состояния за один шаг. Конечно, это требует невероятной духовной силы, которая отсутствует у подавляющего большинства учеников. Большинство людей начинают свой духовный путь с Āṇavopāya и со временем они достигнут Śāktopāya и Śāmbhavopāya (обычно это занимает много лет, десятилетий или даже всю жизнь). Только единицы начнут с Śāktopāya, и только самые достойные начнут свое духовное путешествие с Śāmbhavopāya. Очевидно, чем менее достоен ученик, тем с более высокой upāya (средства, или метода) он хочет начать, ха-ха! В целом, это обычная проблема с людьми: они переоценивают свои собственные способности, имея дело с духовными вопросами. В классе по квантовой физике большинство из них наверняка будут оставаться совершенно безмолвными, но в классе Йоги они все время высказывают свое мнение почти обо всем. Почему? Из-за их собственного невежества, проявляющегося в виде высокомерия. Поскольку Йога — очень сложная/глубокая наука, только эксперты Йоги и ученые могут говорить что-то, что действительно стоит услышать.</p></li><li><p>Здесь термин &quot;dala&quot; означает &quot;खड्गकोश&quot; - &quot;khaḍgakośa&quot;, или ножны меча. Подобно тому, как ножны меча отличаются от меча, но они могут удерживать его, точно также три состояния сознания отличаются от Turya (Четвертого Состояния), но они могут удерживать и наполняться им.</p></li></ol></div><div class="stanza"><a href="#s3.21" id="s3.21" class="num">3.21</a><p>;;; sans</p><p>अत्रोपायमाह—</p><blockquote><p><strong>मग्नः स्वचित्तेन प्रविशेत्॥२१॥</strong></p></blockquote><blockquote><p>प्राणादिस्थूलभावं तु त्यक्त्वा सूक्ष्ममथान्तरम्।<br> सूक्ष्मातीतं तु परमं स्पन्दनं लभ्यते यतः॥</p></blockquote><p>इत्युपक्रम्य</p><blockquote><p>... प्रविशेत्तत्स्वचेतसा॥</p></blockquote><p>इति श्रीमृत्युजिद्भट्टारकनिरूपितनीत्या प्राणायामध्यानधारणादिस्थूलोपायान्परित्यज्य स्वचित्तेनाविकल्पकरूपेणान्तर्मुखान्तरविमर्शचमत्कारात्मना संवेदनेन प्रविशेत्समाविशेत्। कीदृक्सन् मग्नः शरीरप्राणादिप्रमातृतां तत्रैव चिच्चमत्काररसे मज्जनेन प्रशमयन्। तदुक्तम् श्रीस्वच्छन्दे</p><blockquote><p>व्यापारं मानसं त्यक्त्वा बोधरूपेण योजयेत्।<br> तदा शिवत्वमायाति पशुर्मुक्तो भवार्णवात्॥</p></blockquote><p>इति। श्रीविज्ञानभट्टारकेऽपि</p><blockquote><p>मानसं चेतना शक्तिरात्मा चेति चतुष्टयम्।<br> यदा प्रिये परिक्षीणं तदा तद्भैरवं वपुः॥</p></blockquote><p>इति। एतदेव ज्ञानगर्भे स्तोत्रे</p><blockquote><p>विहाय सकलाः क्रिया जननि मानसीः सर्वतो विमुक्तकरणक्रियानुसृतिपारतन्त्र्योज्ज्वलम्।<br> स्थितैस्त्वदनुभावतः सपदि वेद्यते सा परा दशा नृभिरतन्द्रितासमसुखामृतस्यन्दिनी॥</p></blockquote><p>इत्यनेन महागुरुभिर्निबद्धम्॥२१॥ ;;;</p><hr><p>;;; trans</p><p>Atropāyamāha—</p><blockquote><p><strong>Magnaḥ svacittena praviśet||21||</strong></p></blockquote><blockquote><p>Prāṇādisthūlabhāvaṁ tu tyaktvā sūkṣmamathāntaram|<br> Sūkṣmātītaṁ tu paramaṁ spandanaṁ labhyate yataḥ||</p></blockquote><p>ityupakramya</p><blockquote><p>... praviśettatsvacetasā||</p></blockquote><p>iti śrīmṛtyujidbhaṭṭārakanirūpitanītyā prāṇāyāmadhyānadhāraṇādisthūlopāyānparityajya svacittenāvikalpakarūpeṇāntarmukhāntaravimarśacamatkārātmanā saṁvedanena praviśetsamāviśet| Kīdṛksan magnaḥ śarīraprāṇādipramātṛtāṁ tatraiva ciccamatkārarase majjanena praśamayan| Taduktam śrīsvacchande</p><blockquote><p>Vyāpāraṁ mānasaṁ tyaktvā bodharūpeṇa yojayet|<br> Tadā śivatvamāyāti paśurmukto bhavārṇavāt||</p></blockquote><p>iti| Śrīvijñānabhaṭṭārake&#39;pi</p><blockquote><p>Mānasaṁ cetanā śaktirātmā ceti catuṣṭayam|<br> Yadā priye parikṣīṇaṁ tadā tadbhairavaṁ vapuḥ||</p></blockquote><p>iti| Etadeva jñānagarbhe stotre</p><blockquote><p>Vihāya sakalāḥ kriyā janani mānasīḥ sarvato vimuktakaraṇakriyānusṛtipāratantryojjvalam|<br> Sthitaistvadanubhāvataḥ sapadi vedyate sā parā daśā nṛbhiratandritāsamasukhāmṛtasyandinī||</p></blockquote><p>ityanena mahāgurubhirnibaddham||21||</p><p>;;;</p><hr><p>Касательно этого --т.е. в отношении пропитывания трех обычных состояний сознания Турьей (Turya)-- <em>(atra)</em>, [Śiva] упомянул <em>(āha)</em> средства <em>(upāyam)</em>—</p><blockquote><p><strong>Следует входить в [это четвертое состояние сознания --см. афоризм 20--] <em>(praviśet)</em>, погружаясь [в него] <em>(magnaḥ)</em> своим собственным <em>(sva)</em> умом <em>(cittena)</em>, [который, очевидно, должен быть лишен всякой мысли]||21||</strong></p></blockquote><p>[В Mṛtyujit —также известной как Netratantra— было заявлено,] начиная с <em>(upakramya)</em>:</p><blockquote><p>&quot;Оставляя <em>(tu tyaktvā)</em> грубое <em>(sthūla)</em> состояние <em>(bhāvam)</em> праны <em>(prāṇa)</em>, и т.п. <em>(ādi)</em>, и затем <em>(atha)</em> [отказываясь даже от] внутренней <em>(āntaram)</em> тонкой <em>(sūkṣmam)</em> [prāṇāyāma], Высшая <em>(paramam)</em> Вибрация <em>(spandanam)</em>, пребывающая вне <em>(ātītam tu)</em> тонкой [prāṇāyāma] <em>(sūkṣma)</em>, достигается <em>(labhyate)</em> благодаря этому --из-за отказа от грубой и тонкой prāṇāyāma-s-- <em>(yatas... iti)</em><sup>1</sup>&quot;||<br> [См. VIII, 12 в Netratantra]</p></blockquote><p>[и заканчивая на:]</p><blockquote><p>&quot;... он входит в <em>(praviśet)</em> [Высшее Состояние] своим собственным умом <em>(sva-cetasā)</em> подобно этому --т.е. как познающий-- <em>(tad.. iti)</em><sup>2</sup>&quot;|<br> [См. первую часть строфы VIII, 14 в Netratantra]</p></blockquote><p>Согласно наставлениям <em>(nītyā)</em>, высказанным <em>(nirūpita)</em> в наиболее уважаемом <em>(śrī... bhaṭṭāraka)</em> Mṛtyujit --Netratantra-- <em>(mṛtyujit)</em>, после полного отказа от <em>(parityajya)</em> грубых <em>(sthūla)</em> методов <em>(upāyān)</em>, [таких как] prāṇāyāma <em>(prāṇāyāma)</em>, медитация <em>(dhyāna)</em>, концентрация <em>(dhāraṇā)</em>, и т.п. <em>(ādi)</em>, необходимо войти <em>(praviśet)</em> [или] проникнуть <em>(samāviśet)</em> [в Турью, или Четвертое Состояние] своим собственным умом <em>(svacittena)</em>, а именно, с осознанием <em>(saṁvedanena)</em>, лишенным мыслей <em>(avikalpaka-rūpeṇa)</em>, и чья природа <em>(ātmanā)</em> — интровертная <em>(antarmukha)</em> camatkāra --блаженство, полное изумления-- <em>(bhūtebhyaḥ)</em> внутреннего <em>(āntara)</em> сознания <em>(vimarśa)</em> --т.е. Я-Сознания--|</p><p>Каким образом <em>(kīdṛk san)</em>? Погружаясь [в это] <em>(magnaḥ)</em>, [другими словами,] уничтожая <em>(praśamayan)</em> [представление], что это тело <em>(śarīra)</em>, жизненная энергия <em>(prāṇa)</em> и т.д. <em>(ādi)</em> являются Pramātā, или воспринимающим --вашим собственным Я, как Свидетелем-- <em>(pramātṛtām)</em>, через погружение <em>(majjanena)</em> в это самое <em>(tatra eva)</em>, т.е в эликсир <em>(rase)</em> блаженства <em>(camatkāra)</em> Сознания <em>(cit)</em> --короче, &quot;в Турью (Turya), или Четвертое Состояние&quot;--|</p><p>То же <em>(tad)</em> было сказано <em>(uktam)</em> в досточтимом Svacchandatantra <em>(śrī-svacchande)</em>:</p><blockquote><p>&quot;Отбросив <em>(tyaktvā)</em> [всякую] ментальную <em>(mānasam)</em> активность <em>(vyāpāram)</em>, [Yogī] должен стать единым <em>(yojayet)</em> [с Śiva] при [прикосновении] осознанности без каких-либо мыслей <em>(bodharūpeṇa)</em>. Тогда <em>(tadā)</em>, ограниченная личность <em>(paśuḥ)</em>, будучи освобожденной <em>(muktaḥ)</em> от океана <em>(arṇavāt)</em> трансмиграционного существования --т.е. страданий-- <em>(bhava)</em>, достигает <em>(āyāti)</em> состояния Śiva <em>(śivatvam... iti)</em><sup>3</sup>&quot;||<br> [См. IV, 437 в Svacchandatantra]</p></blockquote><p>[И] в самом досточтимом Vijñānabhairava <em>(śrī-vijñāna-bhaṭṭārake)</em> [было] также [высказано следующее] <em>(api)</em>:</p><blockquote><p>&quot;О, Дорогая <em>(priye)</em>!, когда <em>(yadā)</em> группа четырех <em>(catuṣṭayam)</em>, [состоящая из] &#39;ума, полного идей и желаний <em>(mānasam)</em>, убежденного интеллекта <em>(cetanā)</em>, жизненной энергии <em>(śaktiḥ)</em> и <em>(ca)</em> ограниченного опыта <em>(ātmā)</em>&#39; полностью растворена <em>(parikṣīṇam)</em>, тогда <em>(tadā)</em> [проявляется] эта [вышеупомянутая] <em>(tad)</em> vapus, или сущностная природа <em>(vapus)</em>, [известная как] Bhairava --Господь-- <em>(bhairavam... iti)</em>&quot;||</p></blockquote><p>Эта <em>(etad)</em> же самая [истина] <em>(eva)</em> [была высказана] в гимне Jñānagarbha <em>(jñānagarbhe stotre)</em> этой [строфой] <em>(anena)</em>, которая была составлена <em>(nibaddham)</em> великим духовным наставником <em>(mahā-gurubhiḥ)</em> --это относится к Pradyumnabhaṭṭa, одному из главных учеников Kallaṭa, выдающемуся ученику самого Vasugupta--:</p><blockquote><p>&quot;О, Мать <em>(janani)</em>!, после полного <em>(sarvatas)</em> отказа от <em>(vihāya)</em> всякой <em>(sakalāḥ)</em> ментальной <em>(mānasīḥ)</em> активности <em>(kriyāḥ)</em>, [происходит проявление] великолепного состояния <em>(ujjvalam)</em>, которое свободно <em>(vimukta)</em> от зависимости <em>(pāratantrya)</em> следования за <em>(anusṛti)</em> деятельностью <em>(kriyā)</em> органов чувств <em>(karaṇa)</em>. Это <em>(sā)</em> Высшее <em>(parā)</em> Состояние <em>(daśā)</em>, источающее <em>(syandinī)</em> нектар <em>(amṛta)</em> бесподобного <em>(asama)</em> счастья <em>(sukha)</em> без какого бы то ни было ошеломления или недоумения <em>(atandrita)</em>, осознается <em>(vedyate)</em> немедленно <em>(sapadi)</em>, благодаря Твоей Милости <em>(tvat-anubhāvataḥ)</em>, людьми <em>(nṛbhiḥ)</em>, пребывающими [в вышеупомянутом великолепном состоянии] <em>(sthitaiḥ... iti)</em>&quot;||</p></blockquote><p>||21||</p><hr><ol><li>Чтобы вы поняли, почему я перевел строфу таким образом, мне необходимо показать вам, как Kṣemarāja комментирует эту строфу в своей Netroddyota: &quot; प्राणादौ प्राणापानसमानेषु यः स्थूलो रेचकपूरकादिर्भावः स्वभावस्तं त्यक्त्वा उज्झित्वा अथेति एतत्स्थूलप्राणायामानन्तरभावि सूक्ष्ममान्तरमिति मध्यपथेन रेचनाचमनादिरूपं च तं त्यक्त्वा यतो यस्मात्सूक्ष्ममप्यतीतं परममिति प्राणाद्यचित्स्फुरत्तात्म स्पन्दनं लभ्यते...&quot; - &quot;Prāṇādau prāṇāpānasamāneṣu yaḥ sthūlo recakapūrakādirbhāvaḥ svabhāvastaṁ tyaktvā ujjhitvā atheti etatsthūlaprāṇāyāmānantarabhāvi sūkṣmamāntaramiti madhyapathena recanācamanādirūpaṁ ca taṁ tyaktvā yato yasmātsūkṣmamapyatītaṁ paramamiti prāṇādyacitsphurattātma spandanaṁ labhyate...&quot; - &quot;[Выражение &#39;Prāṇādisthūlabhāvaṁ tu tyaktvā&#39; означает] &#39;оставление, или отказ от грубого bhāva —состояния— которое состоит из выдоха, вдоха и т.д. и [сосредоточено] на prāṇa, и т.д., т.е. на prāṇa, apāna и samāna&#39;. [Фраза &#39;sūkṣmamathāntaram&#39; означает] &#39;и затем отказавшись от внутренней тонкой [prāṇāyāma], которая происходит сразу после грубой prāṇāyāma [и] чьей природой является выдох, поглощение и т.д. через срединный путь (центральный канал) --т.е. Suṣumnā--&#39;. &#39;Yatas&#39;, т.е. &#39;из-за чего&#39; --из-за отказа от грубой и тонкой prāṇāyāma-s--, достигается Высшая Вибрация [или Spanda], которая превосходит даже тонкую prāṇāyāma --поэтому Высшая-- и чьей сущностной природой является Sphurattā, или Сознание, расположенное непосредственно перед жизненной энергией --Prāṇa--...&quot;.</li></ol><p>Другими словами, Спанда — это ничто иное как Высшее Я-Сознание, которое всегда предшествует любым проявлениям, таким как жизненная энергия и пр. Таково значение. OK, теперь все совершенно ясно... конечно, если вы знаете, что такое на самом деле означает prāṇāyāma, Suṣumnā, и т.д. Если вы не имеете представления об этом, вам необходимо перечитать комментарии на предыдущий афоризм этого Писания. Мои извинения, но я не могу все время объяснять одни и те же вещи, вы знаете, хе-хе. Убедитесь, что вы очень хорошо знакомы с этими общепринятыми санскритскими терминами, поскольку вы будете постоянно сталкиваться с ними в процессе изучения Трики и других философских систем.</p><ol start="2"><li>Кажется, что Kṣemarāja упростил эту маленькую часть или имел другое прочтение. Я прочитал в Netratantra VIII, 14: &quot; ... प्रविशेद्धाम परमं तत्स्वचेतसा।&quot; - &quot;... praviśeddhāma paramaṁ tatsvacetasā|&quot; - &quot;... он входит в Высшее Состояние своим собственным умом подобно этому --т.е. как познающий--&quot;.</li></ol><p>Поскольку &quot;praviśet&quot; — это спряжение глагола &quot;praviś&quot; (входить) в Потенциальном Наклонении, 3<sup>ем</sup> лице, ед. ч. (Parasmaipada), обычно переводится как &quot;ему/ей следует входить&quot;. Но согласно контексту, Потенциальное Наклонение имеет силу настоящего времени и, следовательно, &quot;praviśet&quot; может быть также иметь правильный перевод, как &quot;он/она/оно входит&quot;. Поэтому мой вышеприведенный перевод &quot;praviśet&quot; — в настоящем времени (&quot;он входит&quot;), в то время как в самом афоризме 21 я перевел это как &quot; следует входить&quot;. Теперь вы понимаете, почему я перевел таким образом.</p><ol start="3"><li>Снова, поскольку в этой строфе значение термина &quot;bodha&quot; неясно, я объясню это, прибегнув к помощи Svacchandoddyota выдающегося Kṣemarāja: &quot; बोधरूपेणेति अविकल्पसंवित्स्पर्शेनैव&quot; - &quot;bodharūpeṇeti avikalpasaṁvitsparśenaiva&quot; - &quot;[выражение] &#39;bodharūpeṇa&#39; [означает] &#39;при прикосновении осознанности без каких-либо мыслей&#39;&quot;.</li></ol></div><div class="stanza"><a href="#s3.22" id="s3.22" class="num">3.22</a><p>;;; sans</p><p>इत्थं च परमपदप्रविष्टस्यास्य वस्तुस्वाभाव्याद्यदा पुनः प्रसरणं भवति तदा—</p><blockquote><p><strong>प्राणसमाचारे समदर्शनम्॥२२॥</strong></p></blockquote><p>परस्फुरत्तात्मकशाक्तपरिमलसंस्कृतस्य प्राणस्य सम्यगिति विकसितसमग्रग्रन्थ्यात्मकान्तरावष्टम्भबलाद् आ ईषद्बहिर्मन्दमन्दं चारे प्रसरणे समं चिदानन्दघनात्मतयैकरूपं दर्शनं संवेदनमर्थात्सर्वदशास्वस्य भवतीत्यर्थः। उक्तं च श्रीमदानन्दभैरवे</p><blockquote><p>उत्सृज्य लौकिकाचारमद्वैतं मुक्तिदं श्रयेत्।<br> स समं सर्वदेवानां तथा वर्णाश्रमादिके॥<br> द्रव्याणां समतादर्शी स मुक्तः सर्वबन्धनैः।</p></blockquote><p>इति। अत एव श्रीप्रत्यभिज्ञायाम्</p><blockquote><p>बुद्धिप्राणप्रसरेऽपि बाह्यदेशाद्युपादानानाहितसङ्कोचानां विश्वात्मस्वरूपलाभ एव॥</p></blockquote><p>इत्युक्तम्॥२२॥ ;;;</p><hr><p>;;; trans</p><p>Itthaṁ ca paramapadapraviṣṭasyāsya vastusvābhāvyādyadā punaḥ prasaraṇaṁ bhavati tadā—</p><blockquote><p><strong>Prāṇasamācāre samadarśanam||22||</strong></p></blockquote><p>Parasphurattātmakaśāktaparimalasaṁskṛtasya prāṇasya samyagiti vikasitasamagragranthyātmakāntarāvaṣṭambhabalād ā īṣadbahirmandamandaṁ cāre prasaraṇe samaṁ cidānandaghanātmatayaikarūpaṁ darśanaṁ saṁvedanamarthātsarvadaśāsvasya bhavatītyarthaḥ| Uktaṁ ca śrīmadānandabhairave</p><blockquote><p>Utsṛjya laukikācāramadvaitaṁ muktidaṁ śrayet|<br> Sa samaṁ sarvadevānāṁ tathā varṇāśramādike||<br> Dravyāṇāṁ samatādarśī sa muktaḥ sarvabandhanaiḥ|</p></blockquote><p>iti| Ata eva śrīpratyabhijñāyām</p><blockquote><p>Buddhiprāṇaprasare&#39;pi bāhyadeśādyupādānānāhitasaṅkocānāṁ viśvātmasvarūpalābha eva||</p></blockquote><p>ityuktam||22||</p><p>;;;</p><hr><p>Таким образом <em>(ittham ca)</em>, в случае этого [Yogī] <em>(asya)</em>, который вошел в <em>(praviṣṭasya)</em> Высшее <em>(parama)</em> Состояние <em>(pada)</em>, когда <em>(yadā)</em> происходит <em>(bhavati)</em> распространение --проявление-- <em>(prasaraṇam)</em> снова <em>(punar)</em>, обусловленное сущностной природой <em>(svābhāvyāt)</em> Реальности <em>(vastu)</em>, тогда <em>(tadā)</em>—</p><blockquote><p><strong>Когда [происходит] медленное, но устойчивое распространение <em>(samācāre)</em> жизненной энергии [Yogī] <em>(prāṇa)</em>, наступает равное <em>(sama)</em> видение <em>(darśanam)</em>, [то есть Yogī осознает единство, лежащее в основе всего]||22||</strong></p></blockquote><p>&quot;Жизненной энергии [Yogī] <em>(prāṇasya)</em>&quot; [означает] &quot;той, которая была освящена <em>(saṁskṛtasya)</em> ароматом <em>(parimala)</em> Śakti <em>(śākta)</em>, являющейся <em>(ātmaka)</em> Высшей <em>(para)</em> Sphurattā, или светящимся Я-Сознанием <em>(sphurattā)</em>&quot;. [В афоризме, термин &quot;samācāre&quot; — местный (локатив) падеж единственного числа от &quot;samācāra&quot;. Приставка &quot;sam&quot; в &quot;samācāre&quot; подразумевает] &quot;samyak&quot; --полученный от &quot;samyañc&quot;-- <em>(samyak iti)</em> [или] &quot;посредством силы <em>(balāt)</em> непоколебимого удерживания <em>(avaṣṭambha)</em> внутренней [Самости] <em>(antara)</em>, в которой все <em>(samagra)</em> узлы <em>(granthi)</em> открыты --т.е развязаны-- <em>(vikasita)</em>&quot;<sup>1</sup>. [Приставка] &quot;ā&quot; [в &quot;samācāre&quot;] <em>(ātmani)</em> [указывает на] &quot;небольшую <em>(īṣat)</em> медленную <em>(manda-mandam)</em> направленность вовне <em>(bahis)</em>&quot;, [в то время как] &quot;cāre&quot; [в &quot;samācāre&quot;] <em>(cāre)</em> [означает] &quot;в распространении <em>(prasaraṇe)</em>&quot; --т.е. когда происходит распространение--. [Термин] &quot;sama&quot; [в &quot;samadarśanam&quot;] <em>(samam)</em> [указывает на] &quot;то, что появляется единообразно <em>(eka-rūpam)</em> как <em>(ātmatayā)</em> масса <em>(ghana)</em> Сознания --т.е. Śiva-- <em>(cit)</em> и Блаженства --т.е. Śakti-- <em>(ānanda)</em>&quot;. [В заключение,] &quot;darśana&quot; <em>(darśanam)</em> [означает] &quot;осознанность <em>(saṁvedanam)</em>&quot;. [Итак,] согласно смыслу [слов] —которые были подробно разъяснены— <em>(arthāt)</em>, [samadarśana, или осознанность, которая воспринимает все единообразно как массу Сознания и Блаженства, т.е. &quot;единообразное видение&quot;,] имеет место <em>(bhavati)</em> у этого [Yogī] <em>(asya)</em> при любых обстоятельствах --а именно, при всех состояниях сознания, поскольку они полностью пронизаны Турьей, или Четвертым Состоянием-- <em>(sarva-daśāsu)</em>. Таково значение <em>(iti arthaḥ)</em>|</p><p>[Это же] также сказано <em>(uktam ca)</em> в досточтимом Ānandabhairava <em>(śrīmat-ānandabhairave)</em>:</p><blockquote><p>&quot;Отказавшись от <em>(utsṛjya)</em> привычных <em>(laukika)</em> практик <em>(ācāram)</em>, следует прибегнуть <em>(śrayet)</em> к недвойственности <em>(advaitam)</em>, которая дает Освобождение <em>(mukti-dam)</em>. Он <em>(saḥ)</em> [видит] равностно <em>(samam)</em> всех богов <em>(sarva-devānām)</em>, а также <em>(tathā)</em> касты <em>(varṇa)</em>, этапы жизни <em>(āśrama)</em>, и т.п. <em>(ādike)</em><sup>2</sup>. Он наблюдает <em>(darśī)</em> [все] вещи <em>(dravyāṇām)</em> с невозмутимостью <em>(samatā)</em>. Он <em>(saḥ)</em> свободен <em>(muktaḥ)</em> от всех оков <em>(sarva-bandhanaiḥ... iti)</em>&quot;||</p></blockquote><p>По этой причине <em>(atas eva)</em>, заявлено <em>(uktam)</em> в [комментарии на] уважаемую Īśvarapratyabhijñā <em>(śrī-pratyabhijñāyām)</em>:</p><blockquote><p>&quot;Для тех, у кого нет ограничений <em>(anāhita-saṅkocānām)</em>, [произведенных] предположением <em>(upādāna)</em> о внешнем <em>(bāhya)</em> пространстве <em>(deśa)</em>, и т.д. <em>(ādi)</em>, даже если <em>(api)</em> происходит проявление <em>(prasare)</em> интеллекта <em>(buddhi)</em> [и] жизненной энергии <em>(prāṇa)</em>, [имеет место] осознание <em>(lābhaḥ eva)</em> их собственной сущностной природы <em>(sva-rūpa)</em> как Самости <em>(ātma)</em> вселенной <em>(viśva... iti)</em> --короче говоря, они видят, что их Я есть в действительности Самость, или Душа, из которой состоит вселенная--&quot;||</p></blockquote><p>||22||</p><hr><ol><li><p>В тонком теле имеется три узла (granthi-s), называемых Brahmagranthi, Viṣṇugranthi и Rudragranthi.Каждый из этих узлов обозначает особое ограничение (длинная тема!). Поскольку внутрення Самость — полностью свободна, Она поэтически обозначается Кшемараджей, как Та, у кого все узлы развязаны. Таково значение.</p></li><li><p>Система каст и этапов жизни — это длинная и сложная тема, которая глубоко рассматривается во многих Писаниях (например, свод (законов) Manu). Тем не менее, привожу резюме:</p></li></ol><p>Имеется четыре касты, а именно, (1) Brāhmaṇa —священнослужители—, (2) Kṣatriya —члены военного или царствующего рода—, (3) Vaiśya —люди, занятые торговлей и сельским хозяйством— и (4) Śūdra —слуги—. И имеют место четыре этапа жизни, а именно, (1) Brahmacārin —ученики, изучающие Веды и следующие безбрачию, до 25 лет—, (2) Gṛhastha —домовладельцы; с 25 до 50 лет—, (3) Vānaprastha —затворники, оставившие свои дома и семьи для аскетической жизни в лесу; с 50 до 75 лет— и (4) Sannyāsin —те, кто отрекся от мирских забот, от 75 лет—.</p></div>',8)]))}const y=m(i,[["render",p]]);export{h as __pageData,y as default};
