import{_ as e,c as m,o as t,d as p}from"./chunks/framework.Dij-6R1w.js";const d=JSON.parse('{"title":"Афоризм 17-18","description":"Шива сутра вимаршини. Глава 3.","frontmatter":{"title":"Афоризм 17-18","description":"Шива сутра вимаршини. Глава 3.","tags":"vim-3","date":"2021-02-01T00:00:00.000Z"},"headers":[],"relativePath":"texts/shiva-sutra-vimarshini/3/17-18/index.md","filePath":"texts/shiva-sutra-vimarshini/3/17-18/index.md"}'),i={name:"texts/shiva-sutra-vimarshini/3/17-18/index.md"};function o(u,a,r,n,s,v){return t(),m("div",null,[...a[0]||(a[0]=[p('<div class="stanza"><a href="#s3.17" id="s3.17" class="num">3.17</a><p>;;; sans</p><p>तदेवं नाडीसंहाराद्याणवोपायक्रमासादितमोहजयोन्मज्जच्छुद्धविद्यात्मकशाक्तबलासादनप्रकर्षादात्मीकृतपरामृतह्रदात्मकशाम्भवपदो योगी—</p><blockquote><p><strong>स्वमात्रानिर्माणमापादयति॥१७॥</strong></p></blockquote><p>स्वस्य चैतन्यस्य सम्बन्धिनी मात्रा चिद्रसाश्यानतात्मांशस्तद्रूपं यथेष्टवेद्यवेदकावभासात्मकं निर्माणमापादयति निर्मितत्वेन दर्शयति। यदुक्तं श्रीस्वच्छन्दे</p><blockquote><p>तदेव भवति स्थूलं स्थूलोपाधिवशात्प्रिये।<br> स्थूलसूक्ष्मविभेदेन तदेकं संव्यवस्थितम्॥</p></blockquote><p>इति। प्रत्यभिज्ञायामपि</p><blockquote><p>आत्मानमत एवायं ज्ञेयीकुर्यात्पृथक्स्थिति।<br> ज्ञेयं न तु...॥</p></blockquote><p>इति। आगमेऽपि</p><blockquote><p>जलं हिमं च यो वेद गुरुवक्त्रागमात्प्रिये।<br> नास्त्येव तस्य कर्तव्यं तस्यापश्चिमजन्मता॥</p></blockquote><p>इत्यनेनैवाशयेनोक्तम्। एतदेव</p><blockquote><p>इति वा यस्य संवित्तिः क्रीडात्वेनाखिलं जगत्।<br> स पश्यन्सततं युक्तो जीवन्मुक्तो न संशयः॥</p></blockquote><p>इत्यनेन स्पन्दे प्रतिपादितम्॥१७॥ ;;;</p><hr><p>;;; trans</p><p>Tadevaṁ nāḍīsaṁhārādyāṇavopāyakramāsāditamohajayonmajjacchuddhavidyātmakaśāktabalāsādanaprakarṣādātmīkṛtaparāmṛtahradātmakaśāmbhavapado yogī—</p><blockquote><p><strong>Svamātrānirmāṇamāpādayati||17||</strong></p></blockquote><p>Svasya caitanyasya sambandhinī mātrā cidrasāśyānatātmāṁśastadrūpaṁ yatheṣṭavedyavedakāvabhāsātmakaṁ nirmāṇamāpādayati nirmitatvena darśayati| Yaduktaṁ śrīsvacchande</p><blockquote><p>Tadeva bhavati sthūlaṁ sthūlopādhivaśātpriye|<br> Sthūlasūkṣmavibhedena tadekaṁ saṁvyavasthitam||</p></blockquote><p>iti| Pratyabhijñāyāmapi</p><blockquote><p>Ātmānamata evāyaṁ jñeyīkuryātpṛthaksthiti|<br> Jñeyaṁ na tu...||</p></blockquote><p>iti| Āgame&#39;pi</p><blockquote><p>Jalaṁ himaṁ ca yo veda guruvaktrāgamātpriye|<br> Nāstyeva tasya kartavyaṁ tasyāpaścimajanmatā||</p></blockquote><p>ityanenaivāśayenoktam| Etadeva</p><blockquote><p>Iti vā yasya saṁvittiḥ krīḍātvenākhilaṁ jagat|<br> Sa paśyansatataṁ yukto jīvanmukto na saṁśayaḥ||</p></blockquote><p>ityanena spande pratipāditam||17||</p><p>;;;</p><hr><p>Это <em>(tad)</em> [существо] таким образом <em>(evam)</em>, через преимущество и превосходство <em>(prakarṣāt)</em>, достигнутые <em>(āsādana)</em> посредством силы <em>(bala)</em> Śakti <em>(śākta)</em>, состоящей из <em>(ātmaka)</em> Śuddhavidyā --Чистого Знания<sup>1</sup>-- <em>(śuddha-vidyā)</em>, возникшего <em>(unmajjat)</em> из покорения <em>(jaya)</em> Moha, или Наваждения --Māyā-- <em>(moha)</em>, которое достигается <em>(āsādita)</em> последовательностью <em>(krama)</em> методов <em>(upāya)</em> в āṇava --т.е. относящихся к Āṇavopāya-- <em>(āṇava)</em>, [таких как] растворение [жизненной энергии] <em>(saṁhāra)</em> в тонких каналах <em>(nāḍī)</em>, и т.д. <em>(ādi)</em> --См. III, 5--, Yogī <em>(yogī)</em> овладевает состоянием <em>(ātmī-kṛta... padaḥ)</em> Śiva <em>(śāmbhava)</em>, которое есть [ничто иное, как] <em>(ātmaka)</em> Озеро <em>(hrada)</em> высшего <em>(para)</em> бессмертия <em>(amṛta)</em>—</p><blockquote><p><strong>[Этот освобожденный Yogī способен] создать <em>(nirmāṇam āpādayati)</em> [любую форму, согласно] мере, или аспекту созидательного Сознания [служащего ему &quot;āsana&quot;, или &quot;сиденьем&quot; --см. 16<sup>ый</sup> афоризм--] <em>(svamātrā)</em>||17||</strong></p></blockquote><p>[Термин svamātrā означает] меру <em>(mātrā)</em>, связанную с <em>(sambandhinī)</em> его собственным <em>(svasya)</em> [созидательным] Сознанием <em>(caitanyasya)</em>, [короче говоря,] с аспектом <em>(aṁśaḥ)</em>, чьей природой является <em>(ātmā)</em> коагуляция <em>(āśyānatā)</em> сущности <em>(rasa)</em> Сознания <em>(cit)</em><sup>2</sup>. В соответствии с мерой этого [созидательного Сознания] <em>(tad-rūpam)</em>, [освобожденный Yogī] осуществляет <em>(āpādayati)</em> постановку <em>(nirmāṇam)</em>, состоящую из <em>(ātmakam)</em> проявления <em>(avabhāsa)</em> познающих --т.е. существ-- <em>(vedaka)</em> [и] познаваемого --т.е. объектов-- <em>(vedya)</em> как <em>(yathā)</em> [он] пожелает <em>(iṣṭa)</em>. [Другими словами,] он показывает [все эти вещи] <em>(darśayati)</em> путем [их] манифестации <em>(nirmitatvena)</em>|</p><p>Это <em>(yad)</em> говорится <em>(uktam)</em> в уважаемом Svacchandatantra <em>(śrī-svacchande)</em>:</p><blockquote><p>&quot;О, Дорогая <em>(priye)</em>!, Тот <em>(tad)</em><sup>3</sup> один <em>(eva)</em> существует <em>(bhavati)</em> как грубое <em>(sthūlam)</em> из-за [Его] желания *(vaśāt)*проявления <em>(upādhi)</em> в качестве того, что является грубым <em>(sthūla)</em>. Тот <em>(tad)</em> один <em>(ekam)</em> проявляется как <em>(saṁvyavasthitam)</em> разнообразие, или разделение <em>(vibhedena)</em> грубого <em>(sthūla)</em> [и] тонкого <em>(sūkṣma... iti)</em>&quot;||<br> [См. IV, 295 в Svacchandatantra]</p></blockquote><p>В Īśvarapratyabhijñā <em>(pratyabhijñāyām)</em> [это] также [было провозглашено] <em>(api)</em>:</p><blockquote><p>&quot;Благодаря этой самой [Vimarśaśakti --а именно, силе проявляющего сознания--] <em>(atas eva)</em>, Он <em>(ayam)</em> делает себя jñeya, или познаваемым --т.е. объектом-- <em>(ātmānam... jñeyī-kuryāt)</em>. Познаваемое <em>(jñeyam)</em> не <em>(na tu)</em> имеет независимого существования <em>(pṛthak-sthiti... iti)</em>. [Если бы [Он] был зависим --букв. связанным с-- от этого [познаваемого], то Его Абсолютная свобода была бы нарушена]<sup>4</sup>&quot;|<br> [См. I, 5, 15 в Īśvarapratyabhijñā]</p></blockquote><p>В Āgama --проявленном Писании-- <em>(āgame)</em> также <em>(api)</em>, сказано [следующее] <em>(uktam)</em> с тем же самым намерением <em>(anena eva āśayena)</em>:</p><blockquote><p>&quot;О, Дорогая <em>(priye)</em>!, тот, кто <em>(yaḥ)</em> знает <em>(veda)</em> посредством того, что исходит из <em>(āgamāt)</em> уст <em>(vaktra)</em> [его] духовного наставника <em>(guru)</em>, что вода <em>(jalam)</em> и <em>(ca)</em> лед <em>(himam)</em> [являются одной и той же вещью, — полностью освобожден]. Для него <em>(tasya)</em> нет ничего, что должно быть сделано <em>(na asti eva... kartavyam)</em>. Он находится <em>(tasya)</em> в состоянии последнего рождения <em>(apaścima-janmatā)</em> --т.е. он не имеет ни единого последующего рождения, потому что, достигнув цели существования, ему больше не требуется Saṁsāra, или Трансмиграция--&quot;||</p></blockquote><p>Это же самое [учение] <em>(etad)</em> было объяснено <em>(pratipāditam)</em> этим [афоризмом] <em>(anena)</em> в Spandakārikā-s <em>(spande)</em>:</p><blockquote><p>&quot;Или <em>(iti vā)</em> тот <em>(saḥ)</em>, кто обладает этим <em>(yasya)</em> знанием, или осознанием <em>(saṁvittiḥ)</em> [и] постоянно пребывает <em>(satatam)</em> в единстве [с Высшим Я] <em>(yuktaḥ)</em>, видит <em>(paśyan)</em> весь <em>(akhilam)</em> мир <em>(jagat)</em>, как [божественную] игру <em>(krīḍātvena)</em>. Он является освобожденным при жизни <em>(jīvanmuktaḥ)</em>, нет никаких <em>(na)</em> сомнений <em>(saṁśayaḥ... iti)</em> [в этом]&quot;||<br> [См. Spandakārikā-s II, 5]</p></blockquote><p>||17||</p><hr><ol><li><p>Это вовсе не пятая таттва, или категория универсального проявления (также известная как Sadvidyā). Чистое Знание в этом контексте — не что иное, как расширение ограниченного знания. II, 10 настоящего Писания, чтобы полностью разобраться с этим.</p></li><li><p>Созидание в Трике не похоже на работу ремесленника, т.е. как нечто, созданное кем-то, кто использовал внешние инструменты и наконец, создал что-то, отличное от него самого. Нет. Только Сознание, или Caitanya постоянно становятся всем и каждым, т.е. Оно коагулирует, принимает форму вещей и существ, не прибегая ни к чему другому, кроме своей собственной сущности. Поэтому, термин &quot;проявление&quot; более уместен, или правилен, чем &quot;созидание&quot;в контексте Трики.</p></li><li><p>В своей Svacchandoddyota, Kṣemarāja характеризует &quot;Тот&quot; как &quot; महाप्रकाशात्मा श्रीस्वतन्त्रनाथः&quot; - &quot;Mahāprakāśātmā śrīsvatantranāthaḥ&quot; - &quot;Досточтимый свободный Господь, чья природа — Великий Свет&quot;. Так или иначе, Он — никто иной, как Caitanya, или Сознание в абсолютной Свободе.</p></li><li><p>Я должен был перевести всю строфу, чтобы показать полное значение. Оставшаяся часть, которая была отброшена и отмечена многоточием, это: &quot; तदौन्म्य्ख्यात्खण्ड्येतास्य स्वतन्त्रता॥१५॥ &quot; - &quot;tadaunmykhyātkhaṇḍyetāsya svatantratā||15||&quot;. Этот перевод приводится в самом тексте выше. Кроме того, я добавил в мой перевод термин &quot;Vimarśaśakti&quot;. Почему? Потому что до того, как знаменитый мудрец Abhinavagupta (Гуру Кшемараджи) прокомментировал эту строфу (фактически, он одновременно прокомментировал строфы 15 и 16) в своей Īśvarapratyabhijñāvimarśinī (великий научный комментарий на Īśvarapratyabhijñā Утпаладевы), он выдвинул возражение, в котором встречается термин Vimarśaśakti. Затем, строфы 15 и 16, конечно, рассеивают сомнения, выдвинутые им. Эти авторы (такие как, Abhinavagupta, Kṣemarāja, и др.) обычно используют метод выдвижения возражений, чтобы сделать их комментарии более интересными и дидактическими (дидактическими для санскритских ученых, безусловно, ха-ха!). ОК, теперь все понятно. Просто доверяй мне, читатель, поскольку в целом, я читаю много &quot;безумно длинных&quot; писаний одновременно, пока перевожу настоящее. Иногда я не могу объяснить все подробно, вы знаете, иначе этот перевод превратится в целую энциклопедию.</p></li></ol></div><div class="stanza"><a href="#s3.18" id="s3.18" class="num">3.18</a><p>;;; sans</p><p>न चैवं स्वशक्तिनिर्मितभूतभावशरीरवतोऽस्य जन्मादिबन्धः कश्चिदित्याह—</p><blockquote><p><strong>विद्याविनाशे जन्मविनाशः॥१८॥</strong></p></blockquote><p>प्रोक्तायाः सहजविद्याया अविनाशे सततोन्मग्नतया स्फुरणे जन्मनोऽज्ञानसहकारिकर्महेतुकस्य दुःखमयस्य देहेन्द्रियादिसमुदायस्य नाशो विध्वंसः सम्पन्न एव। यदुक्तं श्रीकण्ठ्याम्</p><blockquote><p>सप्रपञ्चं परित्यज्य हेयोपादेयलक्षणम्।<br> तृणादिकं तथा पर्णं पाषाणं सचराचरम्॥<br> शिवाद्यवनिपर्यन्तं भावाभावोपबृंहितम्।<br> सर्वं शिवमयं ध्यात्वा भूयो जन्म न प्राप्नुयात्॥</p></blockquote><p>इति। श्रीस्वच्छन्दे</p><blockquote><p>सुनिर्वाणं परं शुद्धं गुरुपारम्परागतम्।<br> तद्विदित्वा विमुच्येत गत्वा भूयो न जायते॥</p></blockquote><p>इति। श्रीमृत्युजित्यपि</p><blockquote><p>तत्त्वत्रयविनिर्मुक्तं शाश्वतं त्वचलं ध्रुवम्।<br> दिव्येन योगमार्गेण दृष्ट्वा भूयो न जायते॥</p></blockquote><p>इति॥१८॥ ;;;</p><hr><p>;;; trans</p><p>Na caivaṁ svaśaktinirmitabhūtabhāvaśarīravato&#39;sya janmādibandhaḥ kaścidityāha—</p><blockquote><p><strong>Vidyāvināśe janmavināśaḥ||18||</strong></p></blockquote><p>Proktāyāḥ sahajavidyāyā avināśe satatonmagnatayā sphuraṇe janmano&#39;jñānasahakārikarmahetukasya duḥkhamayasya dehendriyādisamudāyasya nāśo vidhvaṁsaḥ sampanna eva| Yaduktaṁ śrīkaṇṭhyām</p><blockquote><p>Saprapañcaṁ parityajya heyopādeyalakṣaṇam|<br> Tṛṇādikaṁ tathā parṇaṁ pāṣāṇaṁ sacarācaram||<br> Śivādyavaniparyantaṁ bhāvābhāvopabṛṁhitam|<br> Sarvaṁ śivamayaṁ dhyātvā bhūyo janma na prāpnuyāt||</p></blockquote><p>iti| Śrīsvacchande</p><blockquote><p>Sunirvāṇaṁ paraṁ śuddhaṁ gurupāramparāgatam|<br> Tadviditvā vimucyeta gatvā bhūyo na jāyate||</p></blockquote><p>iti| Śrīmṛtyujityapi</p><blockquote><p>Tattvatrayavinirmuktaṁ śāśvataṁ tvacalaṁ dhruvam|<br> Divyena yogamārgeṇa dṛṣṭvā bhūyo na jāyate||</p></blockquote><p>iti||18||</p><p>;;;</p><hr><p>Таким образом <em>(evam)</em>, не существует <em>(na ca... kaścid)</em> рабство <em>(bandhaḥ)</em> [в форме] рождения <em>(janma)</em>, и т.д. <em>(ādi)</em>, для того, кто приобрел тело <em>(śarīravataḥ)</em>, сотворенное <em>(nirmita)</em> из его собственной <em>(sva)</em> Śakti, или Силы <em>(śakti)</em>, которое --т.е. тело-- состоит из bhūta-s --т.е. Mahābhūta-s, или грубых элементов (tattva-s 32 to 36)-- <em>(bhūta)</em> [и] bhāva-s --т.е. Tanmātra-s, или Тонких элементов (таттвы с 27 по 31), эмоций и т.д.-- <em>(bhāva)</em>. [Автор Śivasūtra-s, т.е. Śiva,] сказал так <em>(iti āha)</em>—</p><blockquote><p><strong>До тех пор пока Sahajavidyā, или Śuddhavidyā, [Естественное, или Чистое Знание] <em>(vidyā)</em> не исчезает <em>(avināśe)</em>, [возможность другого] рождения <em>(janma)</em> утрачивается [для этого возвышенного Yogī] <em>(vināśaḥ)</em>||18||</strong></p></blockquote><p>До тех пор пока не исчезает <em>(avināśe)</em> вышеупомянутое <em>(proktāyāḥ)</em> Естественное Знание <em>(sahajavidyāyāḥ)</em>, т.е. пока оно продолжает проявляться <em>(sphuraṇe)</em> как постоянно возникающая реальность <em>(unmagnatayā)</em>, исчезновение <em>(nāśaḥ)</em>, [или] прекращение <em>(vidhvaṁsaḥ)</em> [другого] рождения <em>(janmanaḥ)</em>, состоящего из совокупности <em>(samudāyasya)</em> тела <em>(deha)</em>, чувств <em>(indriya)</em>, и т.д. <em>(ādi)</em>, полного <em>(mayasya)</em> боли <em>(duḥkha)</em>, [и] обусловленного <em>(hetukasya)</em> [его собственными] действиями <em>(karma)</em> во взаимодействии с <em>(sahakāri)</em> Неведением, [или Māyā и ее потомством] <em>(ajñāna)</em>, осуществляется <em>(sampannaḥ)</em> несомненно <em>(eva)</em><sup>1</sup>|</p><p>Это <em>(yad)</em> сформулировано <em>(uktam)</em> в уважаемом Kaṇṭhī <em>(śrī-kaṇṭhyām)</em>:</p><blockquote><p>&quot;Оставив <em>(parityajya)</em> [мир] с [его] феноменами <em>(sa-prapañcam)</em>, появляющимися как <em>(lakṣaṇam)</em> что-либо желаемое <em>(upādeya)</em> [или] отвергаемое <em>(heya)</em>, и также <em>(tathā)</em> [как] травинка <em>(tṛṇa)</em> и как <em>(ādikam)</em>, лист <em>(parṇam)</em>, камень <em>(pāṣāṇam)</em>, наряду с <em>(sa)</em> подвижными и неподвижными существами <em>(cara-acaram)</em>, начиная с <em>(ādi)</em> Śiva --таттвы, или категории 1-- <em>(śiva)</em> [и] заканчивая <em>(paryantam)</em> землей --таттвой, или категорией 36-- <em>(avani)</em>, сопровождаемыми <em>(upabṛṁhitam)</em> позитивными и негативными сущностями <em>(bhāva-abhāva)</em>; [и] после медитации <em>(dhyātvā)</em> на всем <em>(sarvam)</em>, как тождественном <em>(mayam)</em> Шиве <em>(śiva)</em>, он не получает <em>(na prāpnuyāt)</em> рождения <em>(janma)</em> снова <em>(bhūyas... iti)</em>&quot;||</p></blockquote><p>[И] в уважаемом Svacchandatantra <em>(śrī-svacchande)</em>:</p><blockquote><p>&quot;Превосходное <em>(su)</em> освобождение <em>(nirvāṇam)</em>, приходящее от <em>(āgatam)</em> традиции <em>(pārampara)</em> духовных наставников <em>(guru)</em>, [является] невероятно <em>(param)</em> чистым <em>(śuddham)</em>. Обладая знанием этого [освобождения] --т.е. испытав это-- <em>(tad-viditvā)</em> он становится освобожденным [при жизни] <em>(vimucyeta)</em><sup>2</sup></p></blockquote><p>, [и] после смерти <em>(gatvā)</em> он не родится <em>(na jāyate)</em> вновь <em>(bhūyas... iti)</em><sup>3</sup>&quot;| [См. IV, 240-241 в Svacchandatantra]</p><p>[Это] также [было сказано] <em>(api)</em> в досточтимом Mṛtyujit --Netratantra-- <em>(śrī-mṛtyujiti)</em>:</p><blockquote><p>&quot;Пройдя <em>(dṛṣṭvā)</em>, божественным <em>(divyena)</em> путем <em>(mārgeṇa)</em> Йоги <em>(yoga)</em>, свободный <em>(vinirmuktam)</em> от трех таттв, или принципов <em>(tattva-traya)</em>, несомненно вечный <em>(śāśvatam tu)</em>, неизменный <em>(acalam)</em> [и] перманентный <em>(dhruvam)</em>, он не рождается <em>(na jāyate)</em> вновь <em>(bhūyas... iti)</em><sup>4</sup>&quot;||<br> [См. VIII, 26-27 в Netratantra]</p></blockquote><p>||18||</p><hr><ol><li><p>Хотя Естественное и Чистое Знание продолжает проявляться как постоянно возникающая реальность, для такого великого Yogī больше не будет другого рождения. Рождение состоит из множества вещей (например, тело, чувства и пр.), наполнено болью и происходит по закону Кармы (т.е. создается вашими действиями). Оно также получает содействие, или помощь Неведения в форме Māyīyamala (майическое загрязнение, порождающее различие и разделение) и Kārmamala (загрязнение, вызывающее путаницу относительно природы истинного Делателя). В заключение, поскольку тела, проявленные освобожденным Yogī возникают из его собственной Icchāśakti, а не из закона Кармы, перерождение становится совершенно невозможным для этого возвышенного Yogī, потому что все действия, выполненные этими телами, не являются для него кармическими и поэтому не приведут к другому рождению.</p></li><li><p>Мудрец Kṣemarāja комментирует в своей Svacchandoddyota: &quot; विमुच्यत इति जीवन्नेव विमुक्तः स्यात्&quot; - &quot;vimucyata iti jīvanneva vimuktaḥ syāt&quot; - &quot;&#39;vimucyate&#39; [означает] того, кто освободился при жизни&quot;. Поэтому я добавил &quot;при жизни&quot; в свой перевод, несмотря на то, что &quot;vimucyate&quot; прямо означает &quot;становится освобожденным&quot;.</p></li><li><p>Kṣemarāja объясняет эту последнюю часть строфы в своем комментарии на Svacchandatantra: &quot;गत्वेति देहान्ते तदैकात्म्यमेति॥&quot; - &quot;gatveti dehānte tadaikātmyameti||&quot; - &quot;&#39;gatvā&#39; [означает] &#39;в конце тела&#39; --т.е. когда наступит смерть--, [в этот момент,] он достигнет единства с Тем --с Высшей Реальностью, и следовательно, он не родится вновь--&quot;. Согласно Трике и большинству философских систем Индии, человек рождается как человеческое существо только для достижения Освобождения. Каждая система, безусловно, постулирует Освобождение по-разному. В Трике, окончательное Освобождение — это то же самое, что достижение единства с Высшей Реальностью. Вот почему другое рождение не требуется в случае освобожденной души.</p></li><li><p>Мудрец Kṣemarāja комментирует в своей Netroddyota несколько вещей некоторыми противоречивыми терминами и выражениями в этой строфе Netratantra:</p></li><li><p>&quot; दृष्ट्वा साक्षात्कृत्य&quot; - &quot;dṛṣṭvā sākṣātkṛtya&quot; - &quot;После видения, т.е. после осознания&quot;;</p></li><li><p>&quot; दिव्येन योगमार्गेण विकल्पहानोन्मिषदविकल्पविमर्शावष्टम्भोपायेन&quot; - &quot;divyena yogamārgeṇa vikalpahānonmiṣadavikalpavimarśāvaṣṭambhopāyena&quot; - &quot;божественным путем Йоги, т.е. посредством метода, или средства, основанного на avikalpavimarśa, или &#39;сознании, лишенном мыслей&#39; , которое сияет с прекращением мыслей&quot;;</p></li><li><p>&quot;तत्त्वत्रयं नरशक्तिशिवाख्यम्।&quot; - &quot;Tattvatrayaṁ naraśaktiśivākhyam|&quot; - &quot;Три таттвы, или принципа, известные как nara (человек), Śakti и Śiva&quot;, где последние два есть проявление Того (Paramaśiva), который свободен от этого тройного разделения, короче, Это даже не Господь и Его Сила, не говоря уже о человеке (загадочная тема, которую я не могу подробно здесь объяснять);</p></li><li><p>&quot;अचलमपरिणामि&quot; - &quot;acalamapariṇāmi&quot; - &quot;acala, т.е. неизменный&quot;; и наконец</p></li><li><p>&quot;ध्रुवं नित्यम्&quot; - &quot;dhruvaṁ nityam&quot; - &quot;dhruva, а именно, перманентный&quot;.</p></li></ol></div>',2)])])}const k=e(i,[["render",o]]);export{d as __pageData,k as default};
