import{_ as m,l as e,v as t,k as r}from"./chunks/framework.Dx1fS_nX.js";const q=JSON.parse('{"title":"Афоризм 21-22","description":"Шива сутра вимаршини. Глава 3.","frontmatter":{"title":"Афоризм 21-22","description":"Шива сутра вимаршини. Глава 3.","tags":"vim-3","date":"2021-01-29T00:00:00.000Z"},"headers":[],"relativePath":"texts/shiva-sutra-vimarshini/3/21-22/index.md","filePath":"texts/shiva-sutra-vimarshini/3/21-22/index.md"}'),p={name:"texts/shiva-sutra-vimarshini/3/21-22/index.md"};function o(u,a,i,s,n,d){return r(),e("div",null,a[0]||(a[0]=[t('<div class="stanza"><a href="#s3.21" id="s3.21" class="num">3.21</a><p>;;; sans</p><p>अत्रोपायमाह—</p><blockquote><p><strong>मग्नः स्वचित्तेन प्रविशेत्॥२१॥</strong></p></blockquote><blockquote><p>प्राणादिस्थूलभावं तु त्यक्त्वा सूक्ष्ममथान्तरम्।<br> सूक्ष्मातीतं तु परमं स्पन्दनं लभ्यते यतः॥</p></blockquote><p>इत्युपक्रम्य</p><blockquote><p>... प्रविशेत्तत्स्वचेतसा॥</p></blockquote><p>इति श्रीमृत्युजिद्भट्टारकनिरूपितनीत्या प्राणायामध्यानधारणादिस्थूलोपायान्परित्यज्य स्वचित्तेनाविकल्पकरूपेणान्तर्मुखान्तरविमर्शचमत्कारात्मना संवेदनेन प्रविशेत्समाविशेत्। कीदृक्सन् मग्नः शरीरप्राणादिप्रमातृतां तत्रैव चिच्चमत्काररसे मज्जनेन प्रशमयन्। तदुक्तम् श्रीस्वच्छन्दे</p><blockquote><p>व्यापारं मानसं त्यक्त्वा बोधरूपेण योजयेत्।<br> तदा शिवत्वमायाति पशुर्मुक्तो भवार्णवात्॥</p></blockquote><p>इति। श्रीविज्ञानभट्टारकेऽपि</p><blockquote><p>मानसं चेतना शक्तिरात्मा चेति चतुष्टयम्।<br> यदा प्रिये परिक्षीणं तदा तद्भैरवं वपुः॥</p></blockquote><p>इति। एतदेव ज्ञानगर्भे स्तोत्रे</p><blockquote><p>विहाय सकलाः क्रिया जननि मानसीः सर्वतो विमुक्तकरणक्रियानुसृतिपारतन्त्र्योज्ज्वलम्।<br> स्थितैस्त्वदनुभावतः सपदि वेद्यते सा परा दशा नृभिरतन्द्रितासमसुखामृतस्यन्दिनी॥</p></blockquote><p>इत्यनेन महागुरुभिर्निबद्धम्॥२१॥ ;;;</p><hr><p>;;; trans</p><p>Atropāyamāha—</p><blockquote><p><strong>Magnaḥ svacittena praviśet||21||</strong></p></blockquote><blockquote><p>Prāṇādisthūlabhāvaṁ tu tyaktvā sūkṣmamathāntaram|<br> Sūkṣmātītaṁ tu paramaṁ spandanaṁ labhyate yataḥ||</p></blockquote><p>ityupakramya</p><blockquote><p>... praviśettatsvacetasā||</p></blockquote><p>iti śrīmṛtyujidbhaṭṭārakanirūpitanītyā prāṇāyāmadhyānadhāraṇādisthūlopāyānparityajya svacittenāvikalpakarūpeṇāntarmukhāntaravimarśacamatkārātmanā saṁvedanena praviśetsamāviśet| Kīdṛksan magnaḥ śarīraprāṇādipramātṛtāṁ tatraiva ciccamatkārarase majjanena praśamayan| Taduktam śrīsvacchande</p><blockquote><p>Vyāpāraṁ mānasaṁ tyaktvā bodharūpeṇa yojayet|<br> Tadā śivatvamāyāti paśurmukto bhavārṇavāt||</p></blockquote><p>iti| Śrīvijñānabhaṭṭārake&#39;pi</p><blockquote><p>Mānasaṁ cetanā śaktirātmā ceti catuṣṭayam|<br> Yadā priye parikṣīṇaṁ tadā tadbhairavaṁ vapuḥ||</p></blockquote><p>iti| Etadeva jñānagarbhe stotre</p><blockquote><p>Vihāya sakalāḥ kriyā janani mānasīḥ sarvato vimuktakaraṇakriyānusṛtipāratantryojjvalam|<br> Sthitaistvadanubhāvataḥ sapadi vedyate sā parā daśā nṛbhiratandritāsamasukhāmṛtasyandinī||</p></blockquote><p>ityanena mahāgurubhirnibaddham||21||</p><p>;;;</p><hr><p>Касательно этого --т.е. в отношении пропитывания трех обычных состояний сознания Турьей (Turya)-- <em>(atra)</em>, [Śiva] упомянул <em>(āha)</em> средства <em>(upāyam)</em>—</p><blockquote><p><strong>Следует входить в [это четвертое состояние сознания --см. афоризм 20--] <em>(praviśet)</em>, погружаясь [в него] <em>(magnaḥ)</em> своим собственным <em>(sva)</em> умом <em>(cittena)</em>, [который, очевидно, должен быть лишен всякой мысли]||21||</strong></p></blockquote><p>[В Mṛtyujit —также известной как Netratantra— было заявлено,] начиная с <em>(upakramya)</em>:</p><blockquote><p>&quot;Оставляя <em>(tu tyaktvā)</em> грубое <em>(sthūla)</em> состояние <em>(bhāvam)</em> праны <em>(prāṇa)</em>, и т.п. <em>(ādi)</em>, и затем <em>(atha)</em> [отказываясь даже от] внутренней <em>(āntaram)</em> тонкой <em>(sūkṣmam)</em> [prāṇāyāma], Высшая <em>(paramam)</em> Вибрация <em>(spandanam)</em>, пребывающая вне <em>(ātītam tu)</em> тонкой [prāṇāyāma] <em>(sūkṣma)</em>, достигается <em>(labhyate)</em> благодаря этому --из-за отказа от грубой и тонкой prāṇāyāma-s-- <em>(yatas... iti)</em><sup>1</sup>&quot;||<br> [См. VIII, 12 в Netratantra]</p></blockquote><p>[и заканчивая на:]</p><blockquote><p>&quot;... он входит в <em>(praviśet)</em> [Высшее Состояние] своим собственным умом <em>(sva-cetasā)</em> подобно этому --т.е. как познающий-- <em>(tad.. iti)</em><sup>2</sup>&quot;|<br> [См. первую часть строфы VIII, 14 в Netratantra]</p></blockquote><p>Согласно наставлениям <em>(nītyā)</em>, высказанным <em>(nirūpita)</em> в наиболее уважаемом <em>(śrī... bhaṭṭāraka)</em> Mṛtyujit --Netratantra-- <em>(mṛtyujit)</em>, после полного отказа от <em>(parityajya)</em> грубых <em>(sthūla)</em> методов <em>(upāyān)</em>, [таких как] prāṇāyāma <em>(prāṇāyāma)</em>, медитация <em>(dhyāna)</em>, концентрация <em>(dhāraṇā)</em>, и т.п. <em>(ādi)</em>, необходимо войти <em>(praviśet)</em> [или] проникнуть <em>(samāviśet)</em> [в Турью, или Четвертое Состояние] своим собственным умом <em>(svacittena)</em>, а именно, с осознанием <em>(saṁvedanena)</em>, лишенным мыслей <em>(avikalpaka-rūpeṇa)</em>, и чья природа <em>(ātmanā)</em> — интровертная <em>(antarmukha)</em> camatkāra --блаженство, полное изумления-- <em>(bhūtebhyaḥ)</em> внутреннего <em>(āntara)</em> сознания <em>(vimarśa)</em> --т.е. Я-Сознания--|</p><p>Каким образом <em>(kīdṛk san)</em>? Погружаясь [в это] <em>(magnaḥ)</em>, [другими словами,] уничтожая <em>(praśamayan)</em> [представление], что это тело <em>(śarīra)</em>, жизненная энергия <em>(prāṇa)</em> и т.д. <em>(ādi)</em> являются Pramātā, или воспринимающим --вашим собственным Я, как Свидетелем-- <em>(pramātṛtām)</em>, через погружение <em>(majjanena)</em> в это самое <em>(tatra eva)</em>, т.е в эликсир <em>(rase)</em> блаженства <em>(camatkāra)</em> Сознания <em>(cit)</em> --короче, &quot;в Турью (Turya), или Четвертое Состояние&quot;--|</p><p>То же <em>(tad)</em> было сказано <em>(uktam)</em> в досточтимом Svacchandatantra <em>(śrī-svacchande)</em>:</p><blockquote><p>&quot;Отбросив <em>(tyaktvā)</em> [всякую] ментальную <em>(mānasam)</em> активность <em>(vyāpāram)</em>, [Yogī] должен стать единым <em>(yojayet)</em> [с Śiva] при [прикосновении] осознанности без каких-либо мыслей <em>(bodharūpeṇa)</em>. Тогда <em>(tadā)</em>, ограниченная личность <em>(paśuḥ)</em>, будучи освобожденной <em>(muktaḥ)</em> от океана <em>(arṇavāt)</em> трансмиграционного существования --т.е. страданий-- <em>(bhava)</em>, достигает <em>(āyāti)</em> состояния Śiva <em>(śivatvam... iti)</em><sup>3</sup>&quot;||<br> [См. IV, 437 в Svacchandatantra]</p></blockquote><p>[И] в самом досточтимом Vijñānabhairava <em>(śrī-vijñāna-bhaṭṭārake)</em> [было] также [высказано следующее] <em>(api)</em>:</p><blockquote><p>&quot;О, Дорогая <em>(priye)</em>!, когда <em>(yadā)</em> группа четырех <em>(catuṣṭayam)</em>, [состоящая из] &#39;ума, полного идей и желаний <em>(mānasam)</em>, убежденного интеллекта <em>(cetanā)</em>, жизненной энергии <em>(śaktiḥ)</em> и <em>(ca)</em> ограниченного опыта <em>(ātmā)</em>&#39; полностью растворена <em>(parikṣīṇam)</em>, тогда <em>(tadā)</em> [проявляется] эта [вышеупомянутая] <em>(tad)</em> vapus, или сущностная природа <em>(vapus)</em>, [известная как] Bhairava --Господь-- <em>(bhairavam... iti)</em>&quot;||</p></blockquote><p>Эта <em>(etad)</em> же самая [истина] <em>(eva)</em> [была высказана] в гимне Jñānagarbha <em>(jñānagarbhe stotre)</em> этой [строфой] <em>(anena)</em>, которая была составлена <em>(nibaddham)</em> великим духовным наставником <em>(mahā-gurubhiḥ)</em> --это относится к Pradyumnabhaṭṭa, одному из главных учеников Kallaṭa, выдающемуся ученику самого Vasugupta--:</p><blockquote><p>&quot;О, Мать <em>(janani)</em>!, после полного <em>(sarvatas)</em> отказа от <em>(vihāya)</em> всякой <em>(sakalāḥ)</em> ментальной <em>(mānasīḥ)</em> активности <em>(kriyāḥ)</em>, [происходит проявление] великолепного состояния <em>(ujjvalam)</em>, которое свободно <em>(vimukta)</em> от зависимости <em>(pāratantrya)</em> следования за <em>(anusṛti)</em> деятельностью <em>(kriyā)</em> органов чувств <em>(karaṇa)</em>. Это <em>(sā)</em> Высшее <em>(parā)</em> Состояние <em>(daśā)</em>, источающее <em>(syandinī)</em> нектар <em>(amṛta)</em> бесподобного <em>(asama)</em> счастья <em>(sukha)</em> без какого бы то ни было ошеломления или недоумения <em>(atandrita)</em>, осознается <em>(vedyate)</em> немедленно <em>(sapadi)</em>, благодаря Твоей Милости <em>(tvat-anubhāvataḥ)</em>, людьми <em>(nṛbhiḥ)</em>, пребывающими [в вышеупомянутом великолепном состоянии] <em>(sthitaiḥ... iti)</em>&quot;||</p></blockquote><p>||21||</p><hr><ol><li>Чтобы вы поняли, почему я перевел строфу таким образом, мне необходимо показать вам, как Kṣemarāja комментирует эту строфу в своей Netroddyota: &quot; प्राणादौ प्राणापानसमानेषु यः स्थूलो रेचकपूरकादिर्भावः स्वभावस्तं त्यक्त्वा उज्झित्वा अथेति एतत्स्थूलप्राणायामानन्तरभावि सूक्ष्ममान्तरमिति मध्यपथेन रेचनाचमनादिरूपं च तं त्यक्त्वा यतो यस्मात्सूक्ष्ममप्यतीतं परममिति प्राणाद्यचित्स्फुरत्तात्म स्पन्दनं लभ्यते...&quot; - &quot;Prāṇādau prāṇāpānasamāneṣu yaḥ sthūlo recakapūrakādirbhāvaḥ svabhāvastaṁ tyaktvā ujjhitvā atheti etatsthūlaprāṇāyāmānantarabhāvi sūkṣmamāntaramiti madhyapathena recanācamanādirūpaṁ ca taṁ tyaktvā yato yasmātsūkṣmamapyatītaṁ paramamiti prāṇādyacitsphurattātma spandanaṁ labhyate...&quot; - &quot;[Выражение &#39;Prāṇādisthūlabhāvaṁ tu tyaktvā&#39; означает] &#39;оставление, или отказ от грубого bhāva —состояния— которое состоит из выдоха, вдоха и т.д. и [сосредоточено] на prāṇa, и т.д., т.е. на prāṇa, apāna и samāna&#39;. [Фраза &#39;sūkṣmamathāntaram&#39; означает] &#39;и затем отказавшись от внутренней тонкой [prāṇāyāma], которая происходит сразу после грубой prāṇāyāma [и] чьей природой является выдох, поглощение и т.д. через срединный путь (центральный канал) --т.е. Suṣumnā--&#39;. &#39;Yatas&#39;, т.е. &#39;из-за чего&#39; --из-за отказа от грубой и тонкой prāṇāyāma-s--, достигается Высшая Вибрация [или Spanda], которая превосходит даже тонкую prāṇāyāma --поэтому Высшая-- и чьей сущностной природой является Sphurattā, или Сознание, расположенное непосредственно перед жизненной энергией --Prāṇa--...&quot;.</li></ol><p>Другими словами, Спанда — это ничто иное как Высшее Я-Сознание, которое всегда предшествует любым проявлениям, таким как жизненная энергия и пр. Таково значение. OK, теперь все совершенно ясно... конечно, если вы знаете, что такое на самом деле означает prāṇāyāma, Suṣumnā, и т.д. Если вы не имеете представления об этом, вам необходимо перечитать комментарии на предыдущий афоризм этого Писания. Мои извинения, но я не могу все время объяснять одни и те же вещи, вы знаете, хе-хе. Убедитесь, что вы очень хорошо знакомы с этими общепринятыми санскритскими терминами, поскольку вы будете постоянно сталкиваться с ними в процессе изучения Трики и других философских систем.</p><ol start="2"><li>Кажется, что Kṣemarāja упростил эту маленькую часть или имел другое прочтение. Я прочитал в Netratantra VIII, 14: &quot; ... प्रविशेद्धाम परमं तत्स्वचेतसा।&quot; - &quot;... praviśeddhāma paramaṁ tatsvacetasā|&quot; - &quot;... он входит в Высшее Состояние своим собственным умом подобно этому --т.е. как познающий--&quot;.</li></ol><p>Поскольку &quot;praviśet&quot; — это спряжение глагола &quot;praviś&quot; (входить) в Потенциальном Наклонении, 3<sup>ем</sup> лице, ед. ч. (Parasmaipada), обычно переводится как &quot;ему/ей следует входить&quot;. Но согласно контексту, Потенциальное Наклонение имеет силу настоящего времени и, следовательно, &quot;praviśet&quot; может быть также иметь правильный перевод, как &quot;он/она/оно входит&quot;. Поэтому мой вышеприведенный перевод &quot;praviśet&quot; — в настоящем времени (&quot;он входит&quot;), в то время как в самом афоризме 21 я перевел это как &quot; следует входить&quot;. Теперь вы понимаете, почему я перевел таким образом.</p><ol start="3"><li>Снова, поскольку в этой строфе значение термина &quot;bodha&quot; неясно, я объясню это, прибегнув к помощи Svacchandoddyota выдающегося Kṣemarāja: &quot; बोधरूपेणेति अविकल्पसंवित्स्पर्शेनैव&quot; - &quot;bodharūpeṇeti avikalpasaṁvitsparśenaiva&quot; - &quot;[выражение] &#39;bodharūpeṇa&#39; [означает] &#39;при прикосновении осознанности без каких-либо мыслей&#39;&quot;.</li></ol></div><div class="stanza"><a href="#s3.22" id="s3.22" class="num">3.22</a><p>;;; sans</p><p>इत्थं च परमपदप्रविष्टस्यास्य वस्तुस्वाभाव्याद्यदा पुनः प्रसरणं भवति तदा—</p><blockquote><p><strong>प्राणसमाचारे समदर्शनम्॥२२॥</strong></p></blockquote><p>परस्फुरत्तात्मकशाक्तपरिमलसंस्कृतस्य प्राणस्य सम्यगिति विकसितसमग्रग्रन्थ्यात्मकान्तरावष्टम्भबलाद् आ ईषद्बहिर्मन्दमन्दं चारे प्रसरणे समं चिदानन्दघनात्मतयैकरूपं दर्शनं संवेदनमर्थात्सर्वदशास्वस्य भवतीत्यर्थः। उक्तं च श्रीमदानन्दभैरवे</p><blockquote><p>उत्सृज्य लौकिकाचारमद्वैतं मुक्तिदं श्रयेत्।<br> स समं सर्वदेवानां तथा वर्णाश्रमादिके॥<br> द्रव्याणां समतादर्शी स मुक्तः सर्वबन्धनैः।</p></blockquote><p>इति। अत एव श्रीप्रत्यभिज्ञायाम्</p><blockquote><p>बुद्धिप्राणप्रसरेऽपि बाह्यदेशाद्युपादानानाहितसङ्कोचानां विश्वात्मस्वरूपलाभ एव॥</p></blockquote><p>इत्युक्तम्॥२२॥ ;;;</p><hr><p>;;; trans</p><p>Itthaṁ ca paramapadapraviṣṭasyāsya vastusvābhāvyādyadā punaḥ prasaraṇaṁ bhavati tadā—</p><blockquote><p><strong>Prāṇasamācāre samadarśanam||22||</strong></p></blockquote><p>Parasphurattātmakaśāktaparimalasaṁskṛtasya prāṇasya samyagiti vikasitasamagragranthyātmakāntarāvaṣṭambhabalād ā īṣadbahirmandamandaṁ cāre prasaraṇe samaṁ cidānandaghanātmatayaikarūpaṁ darśanaṁ saṁvedanamarthātsarvadaśāsvasya bhavatītyarthaḥ| Uktaṁ ca śrīmadānandabhairave</p><blockquote><p>Utsṛjya laukikācāramadvaitaṁ muktidaṁ śrayet|<br> Sa samaṁ sarvadevānāṁ tathā varṇāśramādike||<br> Dravyāṇāṁ samatādarśī sa muktaḥ sarvabandhanaiḥ|</p></blockquote><p>iti| Ata eva śrīpratyabhijñāyām</p><blockquote><p>Buddhiprāṇaprasare&#39;pi bāhyadeśādyupādānānāhitasaṅkocānāṁ viśvātmasvarūpalābha eva||</p></blockquote><p>ityuktam||22||</p><p>;;;</p><hr><p>Таким образом <em>(ittham ca)</em>, в случае этого [Yogī] <em>(asya)</em>, который вошел в <em>(praviṣṭasya)</em> Высшее <em>(parama)</em> Состояние <em>(pada)</em>, когда <em>(yadā)</em> происходит <em>(bhavati)</em> распространение --проявление-- <em>(prasaraṇam)</em> снова <em>(punar)</em>, обусловленное сущностной природой <em>(svābhāvyāt)</em> Реальности <em>(vastu)</em>, тогда <em>(tadā)</em>—</p><blockquote><p><strong>Когда [происходит] медленное, но устойчивое распространение <em>(samācāre)</em> жизненной энергии [Yogī] <em>(prāṇa)</em>, наступает равное <em>(sama)</em> видение <em>(darśanam)</em>, [то есть Yogī осознает единство, лежащее в основе всего]||22||</strong></p></blockquote><p>&quot;Жизненной энергии [Yogī] <em>(prāṇasya)</em>&quot; [означает] &quot;той, которая была освящена <em>(saṁskṛtasya)</em> ароматом <em>(parimala)</em> Śakti <em>(śākta)</em>, являющейся <em>(ātmaka)</em> Высшей <em>(para)</em> Sphurattā, или светящимся Я-Сознанием <em>(sphurattā)</em>&quot;. [В афоризме, термин &quot;samācāre&quot; — местный (локатив) падеж единственного числа от &quot;samācāra&quot;. Приставка &quot;sam&quot; в &quot;samācāre&quot; подразумевает] &quot;samyak&quot; --полученный от &quot;samyañc&quot;-- <em>(samyak iti)</em> [или] &quot;посредством силы <em>(balāt)</em> непоколебимого удерживания <em>(avaṣṭambha)</em> внутренней [Самости] <em>(antara)</em>, в которой все <em>(samagra)</em> узлы <em>(granthi)</em> открыты --т.е развязаны-- <em>(vikasita)</em>&quot;<sup>1</sup>. [Приставка] &quot;ā&quot; [в &quot;samācāre&quot;] <em>(ātmani)</em> [указывает на] &quot;небольшую <em>(īṣat)</em> медленную <em>(manda-mandam)</em> направленность вовне <em>(bahis)</em>&quot;, [в то время как] &quot;cāre&quot; [в &quot;samācāre&quot;] <em>(cāre)</em> [означает] &quot;в распространении <em>(prasaraṇe)</em>&quot; --т.е. когда происходит распространение--. [Термин] &quot;sama&quot; [в &quot;samadarśanam&quot;] <em>(samam)</em> [указывает на] &quot;то, что появляется единообразно <em>(eka-rūpam)</em> как <em>(ātmatayā)</em> масса <em>(ghana)</em> Сознания --т.е. Śiva-- <em>(cit)</em> и Блаженства --т.е. Śakti-- <em>(ānanda)</em>&quot;. [В заключение,] &quot;darśana&quot; <em>(darśanam)</em> [означает] &quot;осознанность <em>(saṁvedanam)</em>&quot;. [Итак,] согласно смыслу [слов] —которые были подробно разъяснены— <em>(arthāt)</em>, [samadarśana, или осознанность, которая воспринимает все единообразно как массу Сознания и Блаженства, т.е. &quot;единообразное видение&quot;,] имеет место <em>(bhavati)</em> у этого [Yogī] <em>(asya)</em> при любых обстоятельствах --а именно, при всех состояниях сознания, поскольку они полностью пронизаны Турьей, или Четвертым Состоянием-- <em>(sarva-daśāsu)</em>. Таково значение <em>(iti arthaḥ)</em>|</p><p>[Это же] также сказано <em>(uktam ca)</em> в досточтимом Ānandabhairava <em>(śrīmat-ānandabhairave)</em>:</p><blockquote><p>&quot;Отказавшись от <em>(utsṛjya)</em> привычных <em>(laukika)</em> практик <em>(ācāram)</em>, следует прибегнуть <em>(śrayet)</em> к недвойственности <em>(advaitam)</em>, которая дает Освобождение <em>(mukti-dam)</em>. Он <em>(saḥ)</em> [видит] равностно <em>(samam)</em> всех богов <em>(sarva-devānām)</em>, а также <em>(tathā)</em> касты <em>(varṇa)</em>, этапы жизни <em>(āśrama)</em>, и т.п. <em>(ādike)</em><sup>2</sup>. Он наблюдает <em>(darśī)</em> [все] вещи <em>(dravyāṇām)</em> с невозмутимостью <em>(samatā)</em>. Он <em>(saḥ)</em> свободен <em>(muktaḥ)</em> от всех оков <em>(sarva-bandhanaiḥ... iti)</em>&quot;||</p></blockquote><p>По этой причине <em>(atas eva)</em>, заявлено <em>(uktam)</em> в [комментарии на] уважаемую Īśvarapratyabhijñā <em>(śrī-pratyabhijñāyām)</em>:</p><blockquote><p>&quot;Для тех, у кого нет ограничений <em>(anāhita-saṅkocānām)</em>, [произведенных] предположением <em>(upādāna)</em> о внешнем <em>(bāhya)</em> пространстве <em>(deśa)</em>, и т.д. <em>(ādi)</em>, даже если <em>(api)</em> происходит проявление <em>(prasare)</em> интеллекта <em>(buddhi)</em> [и] жизненной энергии <em>(prāṇa)</em>, [имеет место] осознание <em>(lābhaḥ eva)</em> их собственной сущностной природы <em>(sva-rūpa)</em> как Самости <em>(ātma)</em> вселенной <em>(viśva... iti)</em> --короче говоря, они видят, что их Я есть в действительности Самость, или Душа, из которой состоит вселенная--&quot;||</p></blockquote><p>||22||</p><hr><ol><li><p>В тонком теле имеется три узла (granthi-s), называемых Brahmagranthi, Viṣṇugranthi и Rudragranthi.Каждый из этих узлов обозначает особое ограничение (длинная тема!). Поскольку внутрення Самость — полностью свободна, Она поэтически обозначается Кшемараджей, как Та, у кого все узлы развязаны. Таково значение.</p></li><li><p>Система каст и этапов жизни — это длинная и сложная тема, которая глубоко рассматривается во многих Писаниях (например, свод (законов) Manu). Тем не менее, привожу резюме:</p></li></ol><p>Имеется четыре касты, а именно, (1) Brāhmaṇa —священнослужители—, (2) Kṣatriya —члены военного или царствующего рода—, (3) Vaiśya —люди, занятые торговлей и сельским хозяйством— и (4) Śūdra —слуги—. И имеют место четыре этапа жизни, а именно, (1) Brahmacārin —ученики, изучающие Веды и следующие безбрачию, до 25 лет—, (2) Gṛhastha —домовладельцы; с 25 до 50 лет—, (3) Vānaprastha —затворники, оставившие свои дома и семьи для аскетической жизни в лесу; с 50 до 75 лет— и (4) Sannyāsin —те, кто отрекся от мирских забот, от 75 лет—.</p></div>',2)]))}const h=m(p,[["render",o]]);export{q as __pageData,h as default};
