import{_ as t,c as e,o as m,d as o}from"./chunks/framework.Dij-6R1w.js";const q=JSON.parse('{"title":"Афоризм 24","description":"Шива сутра вимаршини. Глава 3.","frontmatter":{"title":"Афоризм 24","description":"Шива сутра вимаршини. Глава 3.","tags":"vim-3","date":"2021-01-27T00:00:00.000Z"},"headers":[],"relativePath":"texts/shiva-sutra-vimarshini/3/24/index.md","filePath":"texts/shiva-sutra-vimarshini/3/24/index.md"}'),u={name:"texts/shiva-sutra-vimarshini/3/24/index.md"};function i(r,a,p,n,y,s){return m(),e("div",null,[...a[0]||(a[0]=[o('<div class="stanza"><a href="#s3.24" id="s3.24" class="num">3.24</a><p>;;; sans</p><p>एवमेव प्रसवेऽपि प्रवृत्ते यदि तुर्यरसावष्टम्भेन मध्यपदं सिञ्चति पुनरपि तदा—</p><blockquote><p><strong>मात्रास्वप्रत्ययसन्धाने नष्टस्य पुनरुत्थानम्॥२४॥</strong></p></blockquote><p>मात्रासु पदार्थेषु स्वप्रत्ययसन्धानम्।</p><blockquote><p>चक्षुषा यश्च दृश्येत वाचा वा यश्च गोचरः।<br> मनश्चिन्तयते यानि बुद्धिर्यानि व्यवस्यति॥<br> अहङ्कृतानि यान्येव यच्च वेद्यतया स्थितम्।<br> यश्च नास्ति स तत्रैव त्वन्वेष्टव्यः प्रयत्नतः॥</p></blockquote><p>इति श्रीस्वच्छन्दनिरूपितनीत्या विश्वमिदमहमिति चिद्घनात्मरूपतां सर्वत्रानुसन्दधतः पूर्वोक्तावरप्रसवान्नष्टस्यापहारिततुर्यैकघनचमत्कारमयस्वभावस्य पुनरुत्थानमुन्मज्जनं तदैक्यसम्पत्सम्पूर्णत्वं योगिनो भवतीत्यर्थः। तदुक्तं श्रीस्वच्छन्दे</p><blockquote><p>प्रसह्य चञ्चलीत्येव योगिनामपि यन्मनः।</p></blockquote><p>इत्युपक्रम्य</p><blockquote><p>यस्य ज्ञेयमयो भावः स्थिरः पूर्णः समन्ततः।<br> मनो न चलते तस्य सर्वावस्थागतस्य तु॥<br> यत्र यत्र मनो याति ज्ञेयं तत्रैव चिन्तयेत्।<br> चलित्वा यास्यते कुत्र सर्वं शिवमयं यतः॥</p></blockquote><p>इति।</p><blockquote><p>विषयेषु च सर्वेषु इन्द्रियार्थेषु च स्थितः।<br> यत्र यत्र निरूप्येत नाशिवं विद्यते क्वचित्॥</p></blockquote><p>इति च॥२४॥ ;;;</p><hr><p>;;; trans</p><p>Evameva prasave&#39;pi pravṛtte yadi turyarasāvaṣṭambhena madhyapadaṁ siñcati punarapi tadā—</p><blockquote><p><strong>Mātrāsvapratyayasandhāne naṣṭasya punarutthānam||24||</strong></p></blockquote><p>Mātrāsu padārtheṣu svapratyayasandhānam|</p><blockquote><p>Cakṣuṣā yaśca dṛśyeta vācā vā yaśca gocaraḥ|<br> Manaścintayate yāni buddhiryāni vyavasyati||<br> Ahaṅkṛtāni yānyeva yacca vedyatayā sthitam|<br> Yaśca nāsti sa tatraiva tvanveṣṭavyaḥ prayatnataḥ||</p></blockquote><p>iti śrīsvacchandanirūpitanītyā viśvamidamahamiti cidghanātmarūpatāṁ sarvatrānusandadhataḥ pūrvoktāvaraprasavānnaṣṭasyāpahāritaturyaikaghanacamatkāramayasvabhāvasya punarutthānamunmajjanaṁ tadaikyasampatsampūrṇatvaṁ yogino bhavatītyarthaḥ| Taduktaṁ śrīsvacchande</p><blockquote><p>Prasahya cañcalītyeva yogināmapi yanmanaḥ|</p></blockquote><p>ityupakramya</p><blockquote><p>Yasya jñeyamayo bhāvaḥ sthiraḥ pūrṇaḥ samantataḥ|<br> Mano na calate tasya sarvāvasthāgatasya tu||<br> Yatra yatra mano yāti jñeyaṁ tatraiva cintayet|<br> Calitvā yāsyate kutra sarvaṁ śivamayaṁ yataḥ||</p></blockquote><p>iti|</p><blockquote><p>Viṣayeṣu ca sarveṣu indriyārtheṣu ca sthitaḥ|<br> Yatra yatra nirūpyeta nāśivaṁ vidyate kvacit||</p></blockquote><p>iti ca||24||</p><p>;;;</p><hr><p>Таким образом <em>(evam eva)</em>, даже <em>(api)</em> когда происходит порождение [низших ментальных состояний] <em>(prasave... pravṛtte)</em>, если <em>(yadi)</em> [Yogī] также <em>(punar api)</em> окропит <em>(siñcati)</em> промежуточное состояние, или стадию <em>(madhya-padam)</em> устойчивым удержанием <em>(avaṣṭambhena)</em> эликсира <em>(rasa)</em> Turya --Четвертого Состояния-- <em>(turya)</em>, тогда <em>(tadā)</em>—</p><blockquote><p><strong>При единении <em>(sandhāne)</em> истинного Я-Сознания <em>(svapratyaya)</em> с объектами <em>(mātrā)</em>, [также] вновь возникает <em>(punar utthānam)</em> [Блаженство четвертого состояния сознания, которое] исчезло <em>(naṣṭasya)</em> [из-за появления вышеупомянутых низших состояний ума --см. афоризм 23--] ||24||</strong></p></blockquote><p>Союз <em>(sandhānam)</em> истинного Я-Сознания <em>(svapratyaya)</em> с mātrā-s <em>(mātrāsu)</em> [или] объектами <em>(padārtheṣu)</em>|</p><blockquote><p>&quot;Все, что <em>(yaḥ ca)</em> воспринимается <em>(dṛśyeta)</em> через глаз [а] <em>(cakṣuṣā)</em> или <em>(vā)</em> все, что <em>(yaḥ ca)</em> [становится] объектом <em>(gocaraḥ)</em> посредством речи <em>(vācā)</em>; все, что <em>(yāni)</em> ум <em>(manas)</em> думает <em>(cintayate)</em>, все, что <em>(yāni)</em> интеллект <em>(buddhiḥ)</em> определяет <em>(vyavasyati)</em>; все, что <em>(yāni eva)</em> присвоено эго <em>(ahaṅkṛtāni)</em>, все, что <em>(yaḥ ca)</em> существует <em>(sthitam)</em> как познаваемое <em>(vedyatayā)</em>; и [даже] <em>(ca)</em> что <em>(yaḥ)</em> не <em>(na)</em> существует <em>(asti)</em>, Он --т.е. Paramaśiva, который является компактной массой, сотворенной из Света Сознания-- <em>(saḥ)</em> должен быть исследован <em>(anveṣṭavyaḥ)</em> усердно и внимательно <em>(prayatnataḥ)</em> в тех (реалиях) <em>(tatra)</em>, несомненно <em>(eva tu... iti)</em><sup>1</sup>&quot;||<br> [См. XII, 163-164 в Svacchandatantra]</p></blockquote><p>В соответствии с наставлениями <em>(nītyā)</em>, определенными <em>(nirūpita)</em> уважаемым <em>(śrī)</em> Svacchandatantra <em>(svacchanda)</em>, в случае [Yogī], который везде и всегда постоянно и интенсивно осознает <em>(sarvatra anusandadhataḥ)</em> свою собственную природу <em>(ātma-rūpatām)</em> как компактную массу <em>(ghana)</em> Сознания <em>(cit)</em> [через осознание, что] &quot;Я <em>(aham)</em> [есть] эта <em>(idam)</em> вселенная <em>(viśvam... iti)</em>&quot;, [происходит] возрождение <em>(punar utthānam)</em> [или] появление <em>(unmajjanam)</em> своей собственной сущностной природы <em>(sva-bhāvasya)</em>, полной <em>(maya)</em> Блаженства <em>(camatkāra)</em>, [видимо] покинутой <em>(apahārita)</em> Turya <em>(turya)</em> —единственной компактной массы [Сознания] <em>(eka-ghana)</em>— --поскольку Yogī не был достаточно внимателен, Turya, или Четвертое Состояние скрыло Себя от него--, которая --т.е. его собственная сущностная природа-- исчезла <em>(naṣṭasya)</em> из-за вышеупомянутого возникновения <em>(pūrva-ukta... prasavāt)</em> низших [ментальных состояний] <em>(avara)</em>. [Вкратце,] полное приобретение, или достижение <em>(sampad-sampūrṇatvam)</em> [его] единства <em>(aikya)</em> с тем --с Turya-- <em>(tad)</em> происходит <em>(bhavati)</em> с Yogī <em>(yoginaḥ)</em>. Таково значение <em>(iti arthaḥ)</em>|</p><p>Это [же самое учение] <em>(tad)</em> было дано <em>(uktam)</em> в уважаемом Svacchandatantra <em>(śrīsvacchande)</em>, начиная с <em>(upakramya)</em>:</p><blockquote><p>&quot;Поскольку <em>(yad)</em> даже <em>(api)</em> ум <em>(manas)</em> Yogī-s <em>(yoginām)</em> сильно <em>(prasahya)</em> движется туда и сюда <em>(cañcalīti eva... iti)</em><sup>2</sup>&quot;||<br> [См. заключительную часть IV, 311 в Svacchandatantra]</p></blockquote><p>[продолжая с]</p><blockquote><p>&quot;Чей <em>(yasya)</em> ум --как сиденье чувств и мыслей-- <em>(bhāvaḥ)</em> полон <em>(mayaḥ)</em> Высшего Принципа --как его единственного объекта познания-- <em>(jñeya)</em>, устойчив <em>(sthiraḥ)</em> [и] полностью <em>(samantatas)</em> лишен желаний <em>(pūrṇaḥ)</em>. Его <em>(tasya)</em> ум <em>(manas)</em> не <em>(na)</em> движется <em>(calate)</em>, --т.е. не отступает от Высшего Принципа--, даже когда <em>(tu)</em> он падает во <em>(gatasya)</em> все *(sarva)*состояния <em>(avasthā)</em> --т.е. хотя он проходит через всевозможные обстоятельства--. Куда бы <em>(yatra yatra)</em> [его] ум <em>(manas)</em> ни двигался <em>(yāti)</em>, [Yogī] должен размышлять <em>(cintayet)</em> о Высшем Принципе <em>(jñeyam)</em> прямо там <em>(tatra eva)</em>. Но если <em>(yatas)</em> все <em>(sarvam)</em> заполнено <em>(mayam)</em> Śiva <em>(śiva)</em>, куда <em>(kutra)</em> будет двигаться [его ум] <em>(yāsyate)</em> после перемещения <em>(calitvā... iti)</em>?<sup>3</sup>&quot;||<br> [См. IV, 312-13 в Svacchandatantra]</p></blockquote><p>и <em>(ca)</em> [заканчивая с]:</p><blockquote><p>&quot;Во всех объектах <em>(viṣayeṣu... sarveṣu)</em> и <em>(ca... ca)</em> во всех намерениях/стремлениях <em>(sarveṣu... artheṣu)</em> чувств <em>(indriya)</em> --а именно, наслаждения объектами-- —; [лучший из Yogī-s тот], кто может быть вовлеченным <em>(sthitaḥ)</em> [в эти реалии], где бы <em>(yatra yatra)</em> он ни постигал <em>(nirūpyeta)</em>, нигде <em>(kvacid... iti)</em> не обнаруживается <em>(na... vidyate)</em> отсутствия Śiva <em>(aśivam)</em> --не существует места, где бы не существовал Śiva--<sup>4</sup>&quot;||<br> [См. IV, 314 в Svacchandatantra]</p></blockquote><p>||24||</p><hr><ol><li><p>Давайте сейчас четко сформулируем некоторые моменты.... ну, настолько ясно, насколько это возможно (ха-ха!):</p><ol><li><p>&quot;vācā vā yaśca gocaraḥ&quot; - &quot;или все, что [становится] объектом посредством речи&quot;: Имеется четыре уровня речи, а именно, Parāvāk, Paśyantī, Madhyamā и Vaikharī. С одной стороны, Parāvāk (букв. высшая речь) — это стадия, в которой звук (vācaka) и объект (vācya), который обозначается (проявляется) таким звуком, находятся в абсолютном единстве. С другой стороны, Vaikharī — это стадия, в которой vācaka и vācya полностью разделены. Vaikharī есть грубый уровень речи, т.е. тот, который выражается физическим языком. В Vaikharī и звук, и объект различны и полностью разделены. Посередине находятся два промежуточных уровня, называемых Paśyantī и Madhyamā. В первом, звук и обозначенный им объект отличны друг от друга, но не разделены; и в последнем, звук и объект различны и разделены, но они отделены &quot;только&quot; на ментальном уровне. Слово &quot;gocara&quot;, или объект, упомянутый в строфе Svacchandatantra — это vācya. В свою очередь, слово &quot;vācā&quot;, или &quot;через речь&quot; подразумевается &quot;через Paśyantī, Madhyamā и Vaikharī&quot;, поскольку в Parāvāk полностью отсутствуют объекты. Так просто! О, да! Ха-ха! Это действительно весело. Так много скрытых значений в такой короткой фразе!</p></li><li><p>&quot;Manaścintayate yāni&quot; - &quot;все, что ум думает&quot;: Это &quot;yāni&quot;, или &quot;все, что/что бы ни&quot; стоит во множественном числе (среднего рода). Это относится к sukha (удовольствие), и т.д.</p></li><li><p>&quot;Ahaṅkṛtāni yānyeva&quot; - &quot;все, что присвоено эго&quot;: Здесь опять &quot;yāni&quot; — во множественном числе (среднего рода). Эго — это таттва, или категория 15 в системе универсального проявления согласно Трике. Эго постоянно присваивает вещи, свойства/качества/характеристики, настроения и т.д. Например: &quot;Это мое&quot; (эго овладевает вещью) или &quot;Я такой умный&quot; (здесь происходит завладение качеством), или &quot;Мне грустно&quot; (сейчас он присваивает характеристики настроения, т.е. пребывание грустным), и т.д. Если вам нужно больше примеров, просто проанализируйте, что ваше эго делает внутри вас прямо сейчас. Но несмотря на интенсивную деятельность в присвоении качеств нашим прекрасным неутомимым эго, &quot;Я&quot; остается в одиночестве и никогда не затрагивается никакими вещами, качествами, настроениями и пр.</p></li><li><p>&quot;yacca vedyatayā sthitam&quot; - &quot;все, что существует как познаваемое&quot;: Познаваемое — это просто объект. Он называется &quot;познаваемое&quot;, потому что может быть познан Субъектом (Высшим Господом). Все, что может быть познано Субъектом, есть познаваемое. Правда просто?</p></li><li><p>&quot;Yaśca nāsti&quot; - &quot;и [даже] что не существует&quot;: Другими словами, &quot;и даже что не воспринимается как проявленная реальность&quot;.</p></li><li><p>&quot;sa...&quot; - &quot;Он --т.е. Paramaśiva, который является компактной массой, сотворенной из Света Сознания--...&quot;: Это не мое изобретение, а то, что мудрец Kṣemarāja комментирует в этой строфе своей Svacchandoddyota: &quot;स इति चित्प्रकाशघनः परमशिवो...&quot; - &quot;Sa iti citprakāśaghanaḥ paramaśivo...&quot; - &quot;[Термин] &#39;Он&#39; [означает] Paramaśiva, который является компактной массой, сотворенной из Света Сознания&quot;. Оригинальное слово для &quot;Он&quot; — &quot;saḥ&quot;, но &quot;ḥ&quot; (Visarga) следует отбросить перед согласной (&quot;t&quot; в &quot;tatraiva&quot; в строфе выше) из-за 10<sup>ого</sup> Правила Visarga Сандхи.</p></li></ol><p>В двух словах, Высшее Существо должно быть исследовано усердно и внимательно во всех вышеперечисленных реалиях.</p></li><li><p>Вначале, сообщение, предназначенное для санскритских учеников: Прежде чем заполнять мой почтовый ящик, хе-хе, вопросами и/или жалобами об этом странном спряжении &quot;cañcalīti&quot;, позвольте объяснить шаг за шагом: &quot;cañcalīti&quot;— это Фреквентатив (также называемый Интенсивной формой) корня &quot;cal&quot; (двигаться), спрягаемый в 3<sup>ем</sup> лице единтсвенного числа, настоящего времени. Как вы знаете, Фреквентатив усиливает действие глагола, т.е. &quot;calati&quot; означает &quot;он/она/оно двигается&quot; но &quot;cañcalīti&quot; означает &quot;он/она/оно двигается интенсивно, туда сюда, взад вперед и т.д.&quot;. Вы могли бы сказать, &quot;Нет, это должно быть cañcalyate так&quot;, но тема не так уж проста. Имеется два типа Фреквентатива, первый — Ātmanepadī и второй — Parasmaipadī. Форма &quot;cañcalyate&quot;относится к Ātmanepadī, в то время как &quot;cañcalīti&quot; — к Parasmaipadī. Первый является обычным для классического санскрита, а второй &quot;обычно&quot; встречается в Ведах. Поэтому вы скорее всего не найдете никаких объяснений о Parasmaipadī Фреквентативе в элементарной санскритской грамматике. Для этого вам нужен кто-то сведущий. Но учтите также, что корень &quot;cal&quot; (двигаться) формирует свой Parasmaipadī Фреквентатив нерегулярно. А если у кого-то есть блестящая идея, что &quot;cañcalīti&quot; есть &quot;cañcalī iti&quot;, то это никак невозможно. Почему? Потому что даже если вы считаете, что&quot;cañcalī&quot; женского рода от &quot;cañcala&quot; (движение туда сюда), хотя &quot;cañcalā&quot; было бы более правильным, &quot;manas&quot; (ум) является среднего рода. Следовательно, структура предложения полностью нарушается. Поэтому верите вы мне или нет, здесь вы имеете этот Parasmaipadī Фреквентатив, встречающийся в Тантре, в то время когда в основном они встречаются в Ведах. OK, достаточно. С этим кратким объяснением, я предотвратил заполнение моего почтового ящика, отлично!</p><p>Теперь, для всех людей, читающих документ, Kṣemarāja уточняет в своей Svacchandoddyota: &quot;कुटिलं चलति भोगाभिलाषेण व्युत्थानमेव धावति नाभिष्टं पदमवष्टभ्नाति यदिति यस्मादेवम्॥३११॥&quot; - &quot;Kuṭilaṁ calati bhogābhilāṣeṇa vyutthānameva dhāvati nābhiṣṭaṁ padamavaṣṭabhnāti yaditi yasmādevam||311||&quot; - &quot;[Термин] &#39;yad&#39; [в строфе означает] &#39;так как [даже ум Yogī-s] таков&#39;. [Поэтому, поскольку такой ум] не захватывает желаемое Состояние --т.е. Nirvyutthānasamādhi, или Samādhi, лишенное любого обычного состояния сознания--, он движется и бежит за извращенной vyutthāna --обычным состоянием сознания-- из-за желания наслаждаться&quot;.</p><p>Я добавил слово Nirvyutthānasamādhi, потому что мудрец указывает это в процессе комментария на первую часть этой строфы, (которая не была процитирована в настоящем Писании). В этом контексте, Samādhi следует понимать как Высшее Состояние, в котором пребывает Yogī, пронизывая все. Суммируя, Samādhi — это достижение единства со всеми вещами. Прекрасно!</p></li><li><p>Как обычно со Svacchandatantra, перевод является сложным. Поэтому, мне надо процитировать снова Svacchandoddyota, чтобы вы все поняли как следует. Вначале первые две строки из строфы IV, 312. Kṣemarāja объясняет следующее в своей превосходной Svacchandoddyota: &quot;ज्ञेयं च यथा व्याख्यातं परतत्त्वं भाव आशयः स्थिरो निश्चलः पूर्णो निराकाङ्क्षः समन्ततः सर्वंसर्विकया तुरप्यर्थे॥३१२॥ &quot; - &quot;Jñeyaṁ ca yathā vyākhyātaṁ paratattvaṁ bhāva āśayaḥ sthiro niścalaḥ pūrṇo nirākāṅkṣaḥ samantataḥ sarvaṁsarvikayā turapyarthe||312||&quot; - &quot;Как было объяснено, &#39;jñeya&#39; --т.е. познаваемое--есть Высший Принцип --т.е. Высшее Существо как единственный объект познания, так сказать, для такого великого Yogī--. [Термин] &#39;bhāva&#39; [означает] ум как место чувств и мыслей. &#39;Sthira&#39; [— это] устойчивый, неподвижный. [Значение этого слова] &#39;pūrṇa&#39; [—] без желаний, ничего не желающий. &#39;Samantatas&#39; [означает] &#39;полностью, всецело&#39;. [И] &#39;tu&#39; [должен быть истолкован] в смысле &#39;даже когда&#39;&quot;.</p><p>Третья и четвертая строки относятся к строфе IV, 313. Мудрец комментирует следующее: &quot;मन एव परतत्त्वैक्यभावनावासितं कर्तृ यथोक्तं ज्ञेयं सर्वत्र चिन्तयत्येव।&quot; - &quot;Mana eva paratattvaikyabhāvanāvāsitaṁ kartṛ yathoktaṁ jñeyaṁ sarvatra cintayatyeva|&quot; - &quot;Manas --ум--, делатель, находясь под влиянием созерцания единства с Высшим Принципом, думает все время только лишь о вышеупомятом jñeya --Высшем Существе как единственном объекте знания для Yogī--&quot;.</p></li><li><p>Теперь весь комментарий Kṣemarāja на эту строфу в его Svacchandoddyota:</p><p>&quot;इन्द्रियाणि चार्थश्चैन्द्रियकं प्रयोजनं विषयोपभोगस्तेषु स्थितो योगिवरो यो निरूप्येत विचार्येत यत्र कुत्रापि नास्याशिवमस्ति सर्वस्यास्य प्रकाशमानतया प्रकाशघनशिवैकात्म्यात्॥३१४॥&quot;</p><p>&quot;Indriyāṇi cārthaścaindriyakaṁ prayojanaṁ viṣayopabhogasteṣu sthito yogivaro yo nirūpyeta vicāryeta yatra kutrāpi nāsyāśivamasti sarvasyāsya prakāśamānatayā prakāśaghanaśivaikātmyāt||314||&quot;</p><p>&quot;Чувства и &#39;artha&#39; или &#39;назначение/цель чувств&#39;, т.е. наслаждение объектами—; лучший из Yogī-s, который может быть затронут этими [реальностями], где бы он не постигал, или исследовал, нет для него ни единого состояния, лишенного Śiva. Все в его случае проявляется как Свет Сознания из-за его отождествления с Śiva, являющимся компактной массой Света||314||&quot;.</p><p>Надеюсь, я был достаточно искусен и интерпретировал смысл надлежащим образом. Теперь вы можете понять мой способ перевода строфы IV, 314 в Svacchandatantra.</p></li></ol></div>',1)])])}const v=t(u,[["render",i]]);export{q as __pageData,v as default};
