import{_ as e,c as t,d as m,o as i}from"./chunks/framework.B7ZEGzqH.js";const d=JSON.parse('{"title":"Афоризм 25-26","description":"Шива сутра вимаршини. Глава 3.","frontmatter":{"title":"Афоризм 25-26","description":"Шива сутра вимаршини. Глава 3.","tags":"vim-3","date":"2021-01-26T00:00:00.000Z"},"headers":[],"relativePath":"texts/shiva-sutra-vimarshini/3/25-26/index.md","filePath":"texts/shiva-sutra-vimarshini/3/25-26/index.md"}'),p={name:"texts/shiva-sutra-vimarshini/3/25-26/index.md"};function o(r,a,u,n,s,v){return i(),t("div",null,a[0]||(a[0]=[m('<div class="stanza"><a href="#s3.25" id="s3.25" class="num">3.25</a><p>;;; sans</p><p>इत्थमासादितप्रकर्षो योगी—</p><blockquote><p><strong>शिवतुल्यो जायते॥२५॥</strong></p></blockquote><p>तुर्यपरिशीलनप्रकर्षात्प्राप्ततुर्यातीतपदः परिपूर्णस्वच्छस्वच्छन्दचिदानन्दघनेन शिवेन भगवता तुल्यो देहकलाया अविगलनात्तत्समो जायते। तद्विगलनेन साक्षाच्छिव एवासावित्यर्थः। तथा च श्रीकालिकाक्रमे</p><blockquote><p>तस्मान्नित्यमसन्दिग्धं बुद्ध्वा योगं गुरोर्मुखात्।<br> अविकल्पेन भावेन भावयेत्तन्मयत्वतः॥<br> यावत्तत्समतां याति भगवान्भैरवोऽब्रवीत्।</p></blockquote><p>इति॥२५॥ ;;;</p><hr><p>;;; trans</p><p>Itthamāsāditaprakarṣo yogī—</p><blockquote><p><strong>Śivatulyo jāyate||25||</strong></p></blockquote><p>Turyapariśīlanaprakarṣātprāptaturyātītapadaḥ paripūrṇasvacchasvacchandacidānandaghanena śivena bhagavatā tulyo dehakalāyā avigalanāttatsamo jāyate| Tadvigalanena sākṣācchiva evāsāvityarthaḥ| Tathā ca śrīkālikākrame</p><blockquote><p>Tasmānnityamasandigdhaṁ buddhvā yogaṁ gurormukhāt|<br> Avikalpena bhāvena bhāvayettanmayatvataḥ||<br> Yāvattatsamatāṁ yāti bhagavānbhairavo&#39;bravīt|</p></blockquote><p>iti||25||</p><p>;;;</p><hr><p>Таким образом <em>(ittham)</em>, Yogī <em>(yogī)</em> который добился <em>(āsādita)</em> превосходства и мастерства <em>(prakarṣaḥ)</em>—</p><blockquote><p><strong>[Этот возвышенный Yogī, достигший Четвертого Состояния сознания], становится <em>(jāyate)</em> равным <em>(tulyaḥ)</em> Śiva <em>(śiva)</em>||25||</strong></p></blockquote><p>Через интенсивность <em>(prakarṣāt)</em> контакта <em>(pariśīlana)</em> с Turya, или Четвертым Состоянием <em>(turya)</em>, он достигает состояния <em>(prāpta... padaḥ)</em> Turyātīta --т.е. за пределами Turya-- <em>(turya-atīta)</em> [и] становится <em>(jāyate)</em> равным <em>(tulyaḥ)</em> божественному и восхитительному <em>(bhagavatā)</em> Śiva <em>(śivena)</em>, который является совершенно <em>(paripūrṇa)</em> ясной <em>(svaccha)</em> [и] свободной <em>(svacchanda)</em> массой <em>(ghanena)</em> Сознания <em>(cit)</em> [и] Блаженства <em>(ānanda)</em>; [и] до тех пор, пока телесный аспект <em>(deha-kalāyā)</em> не исчезает <em>(avigalanāt)</em> --пока он остается живым--, [такой Yogī] <em>(jāyate)</em> подобен <em>(samaḥ)</em> Ему <em>(tad)</em>|</p><p>После исчезновения <em>(vigalanena)</em> того --тела после смерти-- <em>(tad)</em>, этот [Yogī] <em>(asau)</em> [является] Самим Śiva <em>(śivaḥ eva)</em> лично <em>(sākṣāt)</em>|</p><p>[Было] так <em>(tathā ca)</em> [сказано] в уважаемом Kālikākrama <em>(śrī-kālikākrame)</em> [также]:</p><blockquote><p>&quot;Великолепный <em>(bhagavān)</em> Bhairava --т.е. Высший Повелитель-- <em>(bhairavaḥ)</em> сказал <em>(abravīt)</em>: &#39;Поэтому <em>(tasmāt)</em>, всегда <em>(nityam)</em> обладая пониманием <em>(buddhvā)</em> [и] без всяких сомнений <em>(asandigdham)</em> средств единения [с Śiva] <em>(yogam)</em>, (исходящих) из уст <em>(mukhāt)</em> guru --духовного учителя-- <em>(guroḥ)</em>, ему следует созерцать [ Śiva] <em>(bhāvayet)</em> без мыслей <em>(avikalpena)</em>, с эмоцией --с чувством-- <em>(bhāvena)</em><sup>1</sup> [и] с ощущением отождествления <em>(mayatvataḥ)</em> с Ним <em>(tad)</em>, пока <em>(yāvat)</em> не достигнет <em>(yāti)</em> тождественности, или тождества <em>(samatām)</em> с Ним <em>(tad... iti)</em>&#39;&quot;|</p></blockquote><p>||25||</p><hr><ol><li>&quot;Avikalpena bhāvena&quot; необязательно может быть переведено как &quot;с состоянием отсутствия мыслей&quot;, потому что слово &quot;bhāva&quot;, помимо&quot;чувство, эмоция&quot; и другой огромной кучи возможных значений, означает &quot;состояние, условие&quot;. OK, я просто хотел прояснить этот момент.</li></ol></div><div class="stanza"><a href="#s3.26" id="s3.26" class="num">3.26</a><p>;;; sans</p><p>एवमपि च येनेदं तद्धि भोगत इत्याद्युक्तरीत्या उपनतभोगातिवाहनमात्रप्रयोजनाद्देहस्थितिरस्य नातिक्रमणीयेत्याह—</p><blockquote><p><strong>शरीरवृत्तिर्व्रतम्॥२६॥</strong></p></blockquote><p>प्रोक्तदृशा शिवतुल्यस्य योगिनः शिवाहम्भावेन वर्तमानस्य शरीरे वृत्तिर्वर्तनं यत् तदेव व्रतं स्वस्वरूपविमर्शात्मकनित्योदितपरपूजातत्परस्य नियमेनानुष्ठेयमस्य। तथा च श्रीस्वच्छन्दे</p><blockquote><p>सुप्रदीप्ते यथा वह्नौ शिखा दृश्येत चाम्बरे।<br> देहप्राणस्थितो ह्यात्मा तद्वल्लीयेत तत्पदे॥</p></blockquote><p>इत्युक्त्या देहप्राणाद्यवस्थितस्यैव शिवसमाविष्टत्वमुक्तम्। न पुनस्तस्य देहस्थितिव्यतिरिक्तं व्रतमुपयुक्तम्। यदुक्तं श्रीत्रिकसारे</p><blockquote><p>देहोत्थिताभिर्मुद्राभिर्यः सदा मुद्रितो बुधः।<br> स तु मुद्राधरः प्रोक्तः शेषा वा अस्थिधारकाः॥</p></blockquote><p>इति। श्रीकुलपञ्चाशिकायामपि</p><blockquote><p>अव्यक्तलिङ्गिनं दृष्ट्वा सम्भाषन्ते मरीचयः।<br> लिङ्गिनं नोपसर्पन्त्यतिगुप्ततरा यतः॥</p></blockquote><p>इति॥२६॥ ;;;</p><hr><p>;;; trans</p><p>Evamapi ca yenedaṁ taddhi bhogata ityādyuktarītyā upanatabhogātivāhanamātraprayojanāddehasthitirasya nātikramaṇīyetyāha—</p><blockquote><p><strong>Śarīravṛttirvratam||26||</strong></p></blockquote><p>Proktadṛśā śivatulyasya yoginaḥ śivāhambhāvena vartamānasya śarīre vṛttirvartanaṁ yat tadeva vrataṁ svasvarūpavimarśātmakanityoditaparapūjātatparasya niyamenānuṣṭheyamasya| Tathā ca śrīsvacchande</p><blockquote><p>Supradīpte yathā vahnau śikhā dṛśyeta cāmbare|<br> Dehaprāṇasthito hyātmā tadvallīyeta tatpade||</p></blockquote><p>ityuktyā dehaprāṇādyavasthitasyaiva śivasamāviṣṭatvamuktam| Na punastasya dehasthitivyatiriktaṁ vratamupayuktam| Yaduktaṁ śrītrikasāre</p><blockquote><p>Dehotthitābhirmudrābhiryaḥ sadā mudrito budhaḥ|<br> Sa tu mudrādharaḥ proktaḥ śeṣā vā asthidhārakāḥ||</p></blockquote><p>iti| Śrīkulapañcāśikāyāmapi</p><blockquote><p>Avyaktaliṅginaṁ dṛṣṭvā sambhāṣante marīcayaḥ|<br> Liṅginaṁ nopasarpantyatiguptatarā yataḥ||</p></blockquote><p>iti||26||</p><p>;;;</p><hr><p>И <em>(ca)</em> даже <em>(api)</em> таким образом <em>(evam)</em> --т.е. даже когда он достиг полного отождествления с Высшим Господом--, согласно методу <em>(rītyā)</em>, описанному <em>(ukta)</em> [в Писании], а именно &quot;Поскольку <em>(yena)</em> [имеется] это [тело] <em>(idam)</em>, поэтому <em>(tad hi)</em> посредством bhoga, или объекта наслаждения <em>(bhogataḥ)</em> и т.п. <em>(iti ādi)</em>&quot;, так как [пока тело живет,] цель <em>(prayojanāt)</em> единственно состоит в <em>(mātra)</em> позволении объектам наслаждения выпадать на его долю <em>(upanata-bhoga-ativāhana)</em><sup>1</sup>, не <em>(na)</em> следует пренебрегать <em>(atikramaṇīyā)</em> продолжением (жизни) <em>(sthitiḥ)</em> тела <em>(deha)</em> этого [Yogī] <em>(asya)</em>. [Śiva] сказал <em>(āha)</em> так <em>(iti)</em> [в следующем афоризме]—</p><blockquote><p><strong>Пребывание <em>(vṛttiḥ)</em> в теле <em>(śarīra)</em> [— это его] обет <em>(vratam)</em>, [другими словами, он сохраняет физическую форму из-за его безмерного сострадания к человечеству; это поистине благочестивый поступок с его стороны]||26||</strong></p></blockquote><p>В соответствии с точкой зрения <em>(dṛśā)</em>, упомянутой [ранее] <em>(prokta)</em> относительно Yogī <em>(yoginaḥ)</em>, подобного <em>(tulyasya)</em> Śiva <em>(śiva)</em> [и] живущего <em>(vartamānasya)</em> в состоянии <em>(bhāvena)</em> &quot;Я <em>(aham)</em> [есть] Śiva <em>(śiva)</em>&quot;, [и названного] <em>(yad)</em> остающимся <em>(vṛttiḥ)</em> [или] существующим <em>(vartanam)</em> в теле <em>(śarīre)</em>, то это [является] несомненно <em>(tad eva)</em> [его] обетом <em>(vratam)</em>. [Поэтому,] в [конкретном] случае этого [Yogī] <em>(asya)</em>, который полностью предан <em>(tatparasya)</em> поклонению <em>(pūjā)</em> вездесущему <em>(nitya-udita)</em> Высшему [Я] <em>(para)</em> в форме <em>(ātmaka)</em> осознания <em>(vimarśa)</em> своей собственной <em>(sva)</em> сущностной природы <em>(sva-rūpa)</em>, [такой обет] соблюдается <em>(anuṣṭheyam)</em> как акт благочестия <em>(niyamena)</em>|</p><p>Так [было] <em>(tathā ca)</em> [заявлено] в досточтимом Svacchandatantra <em>(śrī-svacchande)</em> [также]:</p><blockquote><p>&quot;Подобно тому, как <em>(yathā)</em> в хорошо разгоревшемся костре <em>(su-pradīpte... vahnau)</em> пламя <em>(śikhā)</em> видно <em>(dṛśyeta ca)</em> в небе <em>(ambare)</em>, также и <em>(tad-vat)</em> Самость <em>(ātmā)</em>, [несмотря на] несомненное <em>(hi)</em> существование <em>(sthitaḥ)</em> в теле и жизненной энергии <em>(deha-prāṇa)</em>, является поглощенной <em>(līyeta)</em> в Его Состоянии <em>(tad-pade... iti)</em> --в состоянии Śiva--<sup>2</sup>&quot;||<br> [См. IV, 398 в Svacchandatantra]</p></blockquote><p>В соответствии с тем, что было выражено [в строфе Svacchandatantra] <em>(uktyā)</em>, провозглашается, что <em>(uktam)</em> имеется состояние, наполненное <em>(samāviṣṭatvam)</em> Śiva <em>(bhavati)</em> в [Yogī], который [еще] существует <em>(avasthitasya eva)</em> в теле <em>(deha)</em>, жизненной энергии <em>(prāṇa)</em>, и т.д. <em>(ādi)</em> --хотя он все еще остается в теле, жизненной энергии и пр., он полностью слит с Господом!--|</p><p>Нет никакого другого <em>(na punar)</em> обета <em>(vratam)</em>, который являлся бы подходящим <em>(upayuktam)</em> для него <em>(tasya)</em>,</p><p>кроме <em>(vyatiriktam)</em> поддержания <em>(sthiti)</em> тела <em>(deha)</em>|</p><p>Это <em>(yad)</em> [было] сказано <em>(uktam)</em> в уважаемом Trikasāra <em>(śrī-trikasāre)</em>:</p><blockquote><p>&quot;Тот, кто <em>(yaḥ)</em> мудр <em>(budhaḥ)</em>, всегда отмечен <em>(sadā mudritaḥ)</em> Mudrā-s --Замками-- <em>(mudrābhiḥ)</em><sup>3</sup>, возникающими <em>(utthitābhiḥ)</em> из тела <em>(deha)</em>. Он единственный <em>(saḥ tu)</em>, как говорится, является <em>(proktaḥ)</em> обладателем <em>(dharaḥ)</em> Mudrā-s <em>(mudrā)</em>. Остальные люди <em>(śeṣāḥ)</em>, без сомнения <em>(vai)</em>, являются держателями <em>(dhārakāḥ)</em> костей <em>(asthi... iti)</em>&quot;||</p></blockquote><p>В драгоценной Kulapañcāśikā <em>(śrī-kulapañcāśikāyām)</em> также <em>(api)</em>, [было указано, что]:</p><blockquote><p>&quot;Увидев <em>(dṛṣṭvā)</em> кого-либо, кто не имеет никаких ощутимых [религиозных] знаков <em>(a-vyakta-liṅginam)</em>, Marīci-s --Высшие Силы-- <em>(marīcayaḥ)</em> общаются <em>(sambhāṣante)</em> [с ним]. Они не встречаются с <em>(na upasarpanti)</em> тем, кто имеет [религиозные] знаки*(liṅginam)*, поскольку <em>(yatas)</em> [эти Высшие Силы] — очень таинственные и непостижимые <em>(atiguptatarāḥ... iti)</em>&quot;||</p></blockquote><p>||26||</p><hr><ol><li><p>Позвольте мне более конкретно сказать следующее: &quot;úpanata&quot; означает &quot;выпавший на чью-то долю&quot;, &quot;bhoga&quot; — это &quot;объект наслаждения&quot; и &quot;ativāhana&quot; есть &quot;позволение происходить&quot;. Словом, &quot;позволение объектам наслаждения выпадать на его долю&quot;, в смысле &quot;наслаждаться этими объектами, поскольку они естественным образом приходят, и затем отпускать их&quot;. В этом процессе нет ни привязанности, ни отвращения. Конечно, мудрец говорит о великом Yogī, который получил окончательное Освобождение при жизни. Подавляющее большинство людей стремятся только к большему количеству объектов, чем те, что &quot;естественно&quot; выпадают на их долю. Более того, в процессе они развивают привязанность и отвращение. Все это невежественное поведение не приносит им ничего, кроме безмерных горя и боли. Ну, вы, наверно, знаете эту историю, хе-хе.</p></li><li><p>Весь комментарий Kṣemarāja на строфу в его Svacchandoddyota гласит:</p><p>&quot;देहमन्त्रप्राणात्मशिवपदानामौचित्यात् काष्ठारणिवह्नितच्छिखाम्बराणि दृष्टान्तः। तेनारणिमन्थनयुक्त्या सुप्रदीप्ते प्रज्वलिते वह्नौ सति यथा शिखा ज्वाला दाह्यं दग्ध्वाम्बरे दृश्यते तत्र लयात् तदात्मभावं प्राप्तावलोक्यते तद्वद्दिव्यकरणमन्त्रारणिसमुत्तेजनेन देहे यः प्राणस्तस्मिन् सुप्रदीप्ते मध्योर्ध्ववाह्युदानाग्नितामायाति देहे स्थितो य आत्मा प्रोक्त शुद्धविज्ञानकेवलरूपो वह्निशिखातुल्यः समनान्तं समस्तं देहदारुं दघ्द्वा तस्मिन्पदे लीयते निरुपाधिपरमशिवैकात्म्यमेत्येवेत्यर्थः॥३९८॥&quot;</p><p>&quot;Dehamantraprāṇātmaśivapadānāmaucityāt kāṣṭhāraṇivahnitacchikhāmbarāṇi dṛṣṭāntaḥ| Tenāraṇimanthanayuktyā supradīpte prajvalite vahnau sati yathā śikhā jvālā dāhyaṁ dagdhvāmbare dṛśyate tatra layāt tadātmabhāvaṁ prāptāvalokyate tadvaddivyakaraṇamantrāraṇisamuttejanena dehe yaḥ prāṇastasmin supradīpte madhyordhvavāhyudānāgnitāmāyāti dehe sthito ya ātmā prokta śuddhavijñānakevalarūpo vahniśikhātulyaḥ samanāntaṁ samastaṁ dehadāruṁ daghdvā tasminpade līyate nirupādhiparamaśivaikātmyametyevetyarthaḥ||398||&quot;</p><p>&quot;Для удобства, было приведено сравнение между деревом, [двумя] кусками дерева, используемыми для разжигания огня трением, огнем, его пламенем --т.е. пламенем от огня--небом и состоянием тела, мантрой, жизненной энергией, Самостью и Śiva (соответственно) --короче, аналогия такова: &#39;дерево - тело&#39;, &#39;[два] куска дерева для разжигания огня трением - мантра&#39;, &#39;огонь - жизненная энергия&#39;, &#39;пламя - Самость&#39; и &#39;небо - Śiva&#39;--. Поэтому, подобно тому, когда есть хорошо разожженный, или зажженный огонь, śikhā, или пламя, сжигающее топливо хорошо видно в небе, а именно [такое пламя], по видимому, достигает своей истинной природы, сливаясь с этим --с небом--, точно также жизненная энергия, пребывающая в теле, когда она хорошо разжигается, будучи сильно воспламененной [двумя] кусками дерева в форме мантры или божественного звука, превращается в огонь udāna, текущий вверх через серединный канал --т.е. через Suṣumnā. Говорят, что Самость, чьей природой является только чистое Сознание, пребывает [также] в теле. Он подобен пламени огня. Сжигая всю древесину, известную как тело, которое заканчивается в Samanā, [эта Самость] соединяется с этим Состоянием, т.е. Он, несомненно достигает единства с Nirupādhiparamaśiva, или Paramaśiva без атрибутов. Таково значение.&quot;</p></li><li><p>В этом конкретном контексте термин Mudrā относится к Замкам, выполняемым частями тела (например, руками), или даже всем телом. Вы, наверно, слышите от многих людей, которые говорят (и даже пишут) о Йоге, что значение Mudrā есть Замок, поскольку он, по видимому, закрывает/запечатывает цепь в вашей системе с тем, чтобы энергия не вышла и таким образом была бы сохранена внутри. Так или иначе, это хороший пример того, почему я всегда высказываю мнение, что только эксперты Йоги и ученые могут сказать что-то, что стоит услышать. Во-первых, энергия не может покинуть тело, поскольку нет ни &quot;снаружи&quot; ни &quot;внутри&quot; в случае Śakti. Это сложно понять для обычных людей, которые убеждены, что существует вселенная, отличная от них самих, и стремления которых по большей части мирские, но любой искатель, имеющий достаточный опыт в Йоге, может прекрасно понять меня. Если кто-то, практикующий Йогу все еще не может понять меня, ну тогда он или она — недостаточно зрелые с духовной точки зрения.</p><p>Итак, не должно быть страха &quot;потери&quot; энергии, потому что система &quot;открыта&quot;. На самом деле, понятие &quot;энергии&quot; является также весьма каверзным, поскольку в действительности — это &quot;сила&quot; (Śakti). Śakti всегда принадлежит Śiva (Вам!) и никогда не может быть потеряна никоим образом. Это всего лишь Māyā, или Невежество, которые заставляют вас поверить, что Śakti может выйти из системы, когда она открыта. О Боже! Я сам годами слышал эту ошибочную концепцию &quot;Mudrā-s, которые препятствуют тому, чтобы Śakti вырвалась из системы&quot;, хе-хе, да, моя собственная Māyā, конечно!</p><p>Поэтому, чтобы устранить эту майическую концепцию, я могу сказать следующее: Реальная цель Mudrā — это заставить ум соединиться с Самостью. Вот почему это называется &quot;Замок&quot;. Существуют и другие значения, полученные из разных источников, но то (значение), которое я дал вам, является основным в подавляющем большинстве контекстов. И, на самом деле, никто не может выполнять &quot;подлинные&quot; Mudrā-s по своему желанию, поскольку они происходят только благодаря Милости Господа. Поэтому в строфе говорится, что только великий Yogī является обладателем Mudrā-s, тогда как остальные люди выполняющие эти Замки, всего лишь удерживают свои кости.</p></li></ol></div>',2)]))}const y=e(p,[["render",o]]);export{d as __pageData,y as default};
