import{_ as a,v as e,b as m,R as t}from"./chunks/framework.5eef4778.js";const y=JSON.parse('{"title":"Афоризм 27-28","description":"Шива сутра вимаршини. Глава 3.","frontmatter":{"title":"Афоризм 27-28","description":"Шива сутра вимаршини. Глава 3.","tags":"vim-3","date":"2021-01-25T00:00:00.000Z"},"headers":[],"relativePath":"texts/shiva-sutra-vimarshini/3/27-28/index.md","filePath":"texts/shiva-sutra-vimarshini/3/27-28/index.md"}'),p={name:"texts/shiva-sutra-vimarshini/3/27-28/index.md"},o=t('<div class="stanza"><a href="#s3.27" id="s3.27" class="num">3.27</a><p>;;; sans</p><p>एवंविधस्यास्य—</p><blockquote><p><strong>कथा जपः॥२७॥</strong></p></blockquote><blockquote><p>अहमेव परो हंसः शिवः परमकारणम्।</p></blockquote><p>इति श्रीस्वच्छन्दनिरूपितनीत्या नित्यमेव पराहंभावनामयत्वात्।</p><blockquote><p>तस्य देवातिदेवस्य परबोधस्वरूपिणः।<br> विमर्शः परमा शक्तिः सर्वज्ञा ज्ञानशालिनी॥</p></blockquote><p>इति श्रीकालिकाक्रमनिरूपितनीत्या महामन्त्रात्मकाकृतकाहंविमर्शारूढस्य यद्यदालापादि तत्तदस्य स्वात्मदेवताविमर्शानवरतावर्तनात्मा जपो जायते। यदुक्तं श्रीविज्ञानभैरवे</p><blockquote><p>भूयो भूयः परे भावे भावना भाव्यते हि या।<br> जपः सोऽत्र स्वयं नादो मन्त्रात्मा जप्य ईदृशः॥</p></blockquote><p>इति। तथा</p><blockquote><p>सकारेण बहिर्याति हकारेण विशेत्पुनः।<br> हंसहंसेत्यमुं मन्त्रं जीवो जपति नित्यशः॥<br> षट्शतानि दिवारात्रौ सहस्राण्येकविंशतिः।<br> जपो देव्या विनिर्दिष्टः सुलभो दुर्लभो जडैः॥</p></blockquote><p>इति॥२७॥ ;;;</p><hr><p>;;; trans</p><p>Evaṁvidhasyāsya—</p><blockquote><p><strong>Kathā japaḥ||27||</strong></p></blockquote><blockquote><p>Ahameva paro haṁsaḥ śivaḥ paramakāraṇam|</p></blockquote><p>iti śrīsvacchandanirūpitanītyā nityameva parāhaṁbhāvanāmayatvāt|</p><blockquote><p>Tasya devātidevasya parabodhasvarūpiṇaḥ|<br> Vimarśaḥ paramā śaktiḥ sarvajñā jñānaśālinī||</p></blockquote><p>iti śrīkālikākramanirūpitanītyā mahāmantrātmakākṛtakāhaṁvimarśārūḍhasya yadyadālāpādi tattadasya svātmadevatāvimarśānavaratāvartanātmā japo jāyate| Yaduktaṁ śrīvijñānabhairave</p><blockquote><p>Bhūyo bhūyaḥ pare bhāve bhāvanā bhāvyate hi yā|<br> Japaḥ so&#39;tra svayaṁ nādo mantrātmā japya īdṛśaḥ||</p></blockquote><p>iti| Tathā</p><blockquote><p>Sakāreṇa bahiryāti hakāreṇa viśetpunaḥ|<br> Haṁsahaṁsetyamuṁ mantraṁ jīvo japati nityaśaḥ||<br> Ṣaṭśatāni divārātrau sahasrāṇyekaviṁśatiḥ|<br> Japo devyā vinirdiṣṭaḥ sulabho durlabho jaḍaiḥ||</p></blockquote><p>iti||27||</p><p>;;;</p><hr><p>Что касается такого [Yogī] <em>(evaṁvidhasya asya)</em>—</p><blockquote><p><strong>[Его] речь <em>(kathā)</em> — это начитывание [Мантры или молитвы] <em>(japaḥ)</em>||27||</strong></p></blockquote><blockquote><p>&quot;Несомненно <em>(eva)</em>, Я <em>(aham)</em> [есть] Высшая <em>(paraḥ)</em> Самость, или Сущность <em>(haṁsaḥ)</em>, [Я есть] Śiva <em>(śivaḥ)</em>, Высшая <em>(parama)</em> Причина <em>(kāraṇam... iti)</em>&quot;||<br> [См. заключительную часть IV, 399 в Svacchandatantra]</p></blockquote><p>как определено <em>(nirūpita-nītyā)</em> в досточтимом <em>(śrī)</em> Svacchandatantra <em>(svacchanda)</em>, [его речь — это начитывание Мантры или молитвы], поскольку [такой Yogī] постоянно полон <em>(nityam eva... mayatvāt)</em> высшего <em>(para)</em> созерцания <em>(bhāvanā)</em> Я-[Сознания] <em>(aham)</em>|</p><blockquote><p>&quot;[Я]-Сознание <em>(vimarśaḥ)</em> [или] Высшая <em>(paramā)</em> Сила <em>(śaktiḥ)</em>, которая является всеведающей <em>(sarvajñā)</em>, т.е. наполненной <em>(śālinī)</em> знанием <em>(jñāna)</em>, принадлежит Тому <em>(tasya)</em>, кто является величайшим Богом <em>(atidevasya)</em> среди богов <em>(deva)</em>, [и] чья природа <em>(sva-rūpiṇaḥ)</em> есть Высшее <em>(para)</em> Сознание <em>(bodha... iti)</em>&quot;||</p></blockquote><p>Как определено <em>(nirūpita-nītyā)</em> в досточтимом <em>(śrī)</em> Kālikākrama <em>(kālikākrama)</em>, любой <em>(yad yad)</em> разговор <em>(ālāpa)</em>, и т.д. <em>(ādi)</em>[Yogī], достигшего <em>(ārūḍhasya)</em> несотворенного <em>(akṛtaka)</em> Я-Сознания <em>(ahaṁ-vimarśa)</em>, представляет собой <em>(ātma)</em> великую <em>(mahā)</em> Мантру <em>(mantra)</em>, все это <em>(tad tad)</em> составляет <em>(jāyate)</em> его <em>(asya)</em> japa --начитывание Мантры или молитвы-- <em>(japaḥ)</em>, суть которой <em>(ātmā)</em> — непрестанное <em>(avarata)</em> повторение <em>(āvartana)</em> осознания <em>(vimarśa)</em> божества [великой Мантры] <em>(devatā)</em>, которое является его собственной <em>(sva)</em> Самостью <em>(ātma)</em><sup>1</sup>|</p><p>Это [же самое учение] <em>(yad)</em> было выражено <em>(uktam)</em> в уважаемом Vijñānabhairavatantra <em>(śrī-vijñānabhairave)</em>:</p><blockquote><p>&quot;Это <em>(yā)</em> созерцание <em>(bhāvanā)</em>, которое выполняется <em>(bhāvyate hi)</em> снова и снова <em>(bhūyas bhūyas)</em> в отношении Высшего <em>(pare)</em> Состояния <em>(bhāve)</em>, [есть] japa <em>(japaḥ saḥ)</em> в этом [контексте] <em>(atra)</em>. Такой <em>(īdṛśaḥ)</em> спонтанный --т.е. звучащий сам по себе-- <em>(svayam)</em> звук <em>(nādaḥ)</em>, чьей природой <em>(ātmā)</em> является Мантра <em>(mantra)</em>, [составляет] объект japa <em>(japyaḥ... iti)</em> --т.е. такой спонтанный звук следует созерцать снова и снова--<sup>2</sup>&quot;||</p></blockquote><p>Также <em>(tathā)</em>, [это заявлено в том же Писании]:</p><blockquote><p>&quot;[Тонкая мантра] &#39;Haṁsa, Haṁsa&#39; <em>(haṁsahaṁsa iti)</em> выходит <em>(bahis yāti)</em> со звуком <em>(kāreṇa)</em> &#39;Sa&#39; <em>(sa)</em> и входит <em>(viśet)</em> снова <em>(punar)</em> со звуком <em>(kāreṇa)</em> &#39;Ha&#39; <em>(ha)</em>. [По этой причине], живые существа <em>(jīvaḥ)</em> постоянно <em>(nityaśaḥ)</em> произносят <em>(japati)</em> эту <em>(amum)</em> мантру <em>(mantram)</em>. День и ночь <em>(divārātrau)</em> [эти живые существа начитывают ее] 21,600 [раз] <em>(ṣaṭśatāni... sahasrāṇi ekaviṁśatiḥ)</em>. Начитывание <em>(japaḥ)</em> Богини <em>(devyāḥ)</em> было указано <em>(vinirdiṣṭaḥ)</em>, как легкое <em>(sulabhaḥ)</em> [для мудрого, но] трудное <em>(durlabhaḥ)</em> для глупого <em>(jaḍaiḥ... iti)</em>&quot;||</p></blockquote><p>||27||</p><hr><ol><li><p>Божество великой Мантры — это Śiva (ваша собственная Самость). Этот Śiva является неотделимым от Своей Śakti, или божественной Силы, проявляющейся как &quot;Я-Сознание&quot;. Следовательно, великая Мантра повторяется автоматически и постоянно в форме &quot;Aham&quot;, или &quot;Я&quot;. Без &quot;Я&quot; ничто не могло бы проявиться. Итак, поскольку этот возвышенный Yogī постоянно осознает &quot;Aham&quot;, все его разговоры подобны его japa.</p></li><li><p>Как указывалось в предыдущем примечании, japa в случае этого великого Yogī является даже не начитыванием мантры, как это обычно понимается, а только созерцанием Высшего Состояния (Состояния Śiva). Aham, или Я — это звук, который продолжает звучать сам по себе непрерывно. Поэтому, созерцание Aham, или Я (Высшего Состояния) является тем, что составляет japa этого возвышенного Yogī. Теперь понятно, так ведь?</p></li></ol></div><div class="stanza"><a href="#s3.28" id="s3.28" class="num">3.28</a><p>;;; sans</p><p>ईदृग्जपव्रतवतोऽस्य चर्यामाह—</p><blockquote><p><strong>दानमात्मज्ञानम्॥२८॥</strong></p></blockquote><p>प्रोक्तचैतन्यरूपस्यात्मनो यज्ज्ञानं साक्षात्कारस्तदस्य दानं दीयते परिपूर्णं स्वरूपं दीयते खण्ड्यते विश्वभेदो दायते शोध्यते मायास्वरूपं दीयते रक्ष्यते लब्धः शिवात्मा स्वभावश्चानेनेति कृत्वा।</p><p>अथ च दीयत इति दानमात्मज्ञानमेवानेनान्तेवासिभ्यो दीयते। तदुक्तम्</p><blockquote><p>दर्शनात्स्पर्शनाद्वापि वितताद्भवसागरात्।<br> तारयिष्यन्ति योगीन्द्राः कुलाचारप्रतिष्ठिताः॥</p></blockquote><p>इति॥२८॥ ;;;</p><hr><p>;;; trans</p><p>Īdṛgjapavratavato&#39;sya caryāmāha—</p><blockquote><p><strong>Dānamātmajñānam||28||</strong></p></blockquote><p>Proktacaitanyarūpasyātmano yajjñānaṁ sākṣātkārastadasya dānaṁ dīyate paripūrṇaṁ svarūpaṁ dīyate khaṇḍyate viśvabhedo dāyate śodhyate māyāsvarūpaṁ dīyate rakṣyate labdhaḥ śivātmā svabhāvaścāneneti kṛtvā|</p><p>Atha ca dīyata iti dānamātmajñānamevānenāntevāsibhyo dīyate| Taduktam</p><blockquote><p>Darśanātsparśanādvāpi vitatādbhavasāgarāt|<br> Tārayiṣyanti yogīndrāḥ kulācārapratiṣṭhitāḥ||</p></blockquote><p>iti||28||</p><p>;;;</p><hr><p>[Через следующий афоризм, Śiva] говорил <em>(āha)</em> об образе действий, или поведении <em>(caryām)</em> этого [Yogī] <em>(asya)</em>, который практикует такие japa и vrata --Смотрите предыдущие два афоризма-- <em>(īdṛk-japavratavataḥ)</em>—</p><blockquote><p><strong>Знание <em>(jñānam)</em> Самости <em>(ātma)</em> — [его] дар <em>(dānam)</em> [всем нам]||28||</strong></p></blockquote><p>Это <em>(yad)</em> знание <em>(jñānam)</em> Самости (Высшего Я) <em>(ātmanaḥ)</em>, которое является <em>(rūpasya)</em> упомянутым ранее --См. I, 1-- <em>(prokta)</em> Абсолютным Сознанием <em>(caitanya)</em>, состоит в осознании [этой Самости] <em>(sākṣātkāraḥ)</em>. Такое [знание] <em>(tad)</em> [является] его <em>(asya)</em> даром <em>(dānam)</em>. И принимая во внимание [корни, из которых термин dāna, или дар произошел] <em>(iti kṛtvā)</em>: &quot;Совершенная <em>(paripūrṇa)</em> сущностная природа <em>(sva-rūpam)</em> дана [в качестве дара] <em>(dīyate)</em>. [Благодаря этому дару,] разница <em>(bhedaḥ)</em> [между Śiva и] вселенной <em>(viśva)</em> устранена <em>(dīyate)</em> [или] рассечена <em>(khaṇḍyate)</em> --т.е. двойственность прекращается--; сущность <em>(sva-rūpam)</em> Māyā <em>(māyā)</em> очищена <em>(dāyate)</em> [или] очищается <em>(śodhyate)</em>, и <em>(ca)</em> естественное состояние <em>(sva-bhāvaḥ)</em> —чья природа <em>(ātmā)</em> — Śiva <em>(śiva)</em>— которое было достигнуто <em>(labdhaḥ)</em> этим [великим Yogī] <em>(anena)</em>, сохраняется <em>(dīyate)</em> [или] защищается <em>(rakṣyate)</em><sup>1</sup>&quot;|</p><p>Тем не менее --т.е несмотря на возможные значения термина dāna, или дара согласно различным корням-- <em>(atha ca)</em>, знание <em>(jñānam eva)</em> Самости <em>(ātma)</em> [является] даром <em>(dānam)</em>, потому что оно дается <em>(dīyate iti)</em> [или] передается <em>(dīyate)</em> им <em>(anena)</em> ученикам, которые живут поблизости или в его доме <em>(antevāsibhyaḥ)</em><sup>2</sup> --тогда основное значение — это то, что происходит от корня &quot;dā&quot;, или &quot;давать&quot;--|</p><p>Это [же самое учение] <em>(tad)</em> было [ясно] выражено <em>(uktam)</em> [следующей строфой]:</p><blockquote><p>&quot;Лучшие Yogī-s <em>(yogi-indrāḥ)</em>, прочно установленные <em>(pratiṣṭhitāḥ)</em> в Kulācāra --единстве Śiva (Господа), Śakti (Его Силы) и nara (индивидуальной души)-- <em>(kula-ācāra)</em>, будут спасать <em>(tārayiṣyanti)</em> [ограниченных существ] из обширного <em>(vitatāt)</em> океана трансмиграционного существования <em>(bhava-sāgarāt)</em> посредством [их простого] присутствия <em>(darśanāt)</em> или <em>(vā api)</em> прикосновения <em>(sparśanāt... iti)</em><sup>3</sup>&quot;||</p></blockquote><p>||28||</p><hr><ol><li><p>Кратко для санскритских студентов, корни следующие: dā (давать, дом, или класс 3), do (отрезать, дом или класс 2), dai (очищать, дом или класс 1) и dī (сохранять, дом или класс 2).</p></li><li><p>Слово &quot;antevāsibhyaḥ&quot; — Дательный падеж (мн. ч. мужского рода) от &quot;antevāsin&quot;. Именительный падеж (ед.ч. мужского рода) — &quot;antevāsī&quot;, или &quot;ученик, находящийся поблизости или в доме своего учителя&quot;. Это очень интересно, что написал мудрец Kṣemarāja, потому что можно было бы ожидать &quot;śiṣyebhyaḥ&quot; или &quot;śiṣya-s или ученикам&quot;, в общем, независимо от того, находятся ли они рядом или далеко от своего учителя. Я мог бы забыть об этом и перевести &quot;ученикам&quot;, но это было бы не точно. Согласно приведенной затем строфе, мудрец придает большое значение Благословению, передаваемому через зрение или прикосновение. Следовательно, необходимо иметь учеников в пределах досягаемости, с физической точки зрения.</p></li><li><p>Здесь строфа учит, что главные среди Yogī-s будут освобождать обусловленных существ своим простым присутствием (т.е. взглядом), и также через их прикосновение. Другое имя &quot;знания о Высшем Я&quot; — &quot;Милость&quot;, т.е. &quot;божественная Милость&quot;, или &quot;anugraha&quot;. Согласно моей традиции, Милость может быть &quot;дополнительно&quot; дарована ученикам через мантру, или посредством простой saṅkalpa, или волевым актом, т.е. по желанию. Очевидно, эти два дополнительных метода в основном используются с теми учениками, которые не являются antevāsī-s, а именно, кто не живет поблизости или в доме их гуру (как например, в моем случае!). Поскольку Kṣemarāja говорит о antevāsī-s в этом контексте, единственными из упомянутых являются методы инициирования учеников через взгляд (dṛk) и прикосновение (sparśa).</p></li></ol></div>',2),i=[o];function r(s,n,u,v,d,h){return e(),m("div",null,i)}const b=a(p,[["render",r]]);export{y as __pageData,b as default};
