import{_ as a,o as e,c as m,b as t}from"./chunks/framework.08bce705.js";const h=JSON.parse('{"title":"Афоризм 3-4","description":"Шива сутра вимаршини. Глава 3.","frontmatter":{"title":"Афоризм 3-4","description":"Шива сутра вимаршини. Глава 3.","tags":"vim-3","date":"2021-02-09T00:00:00.000Z"},"headers":[],"relativePath":"texts/shiva-sutra-vimarshini/3/3-4/index.md","filePath":"texts/shiva-sutra-vimarshini/3/3-4/index.md"}'),p={name:"texts/shiva-sutra-vimarshini/3/3-4/index.md"},i=t('<div class="stanza"><a href="#s3.3" id="s3.3" class="num">3.3</a><p>;;; sans</p><p>ननु च</p><blockquote><p>ज्ञानं प्रकाशकं लोक आत्मा चैव प्रकाशकः।<br> अनयोरपृथग्भावाज्ज्ञाने ज्ञानी प्रकाशते॥</p></blockquote><p>इति श्रीविज्ञानभैरवोक्तदृष्ट्या ज्ञानमपि प्रकाशमयमेवेति कथमस्य बन्धरूपत्वम्। सत्यमेतद्यदि परमेश्वरप्रसादादेवं प्रत्यभिज्ञायेत यदा तु तन्मायाशक्तितो नैवं विमर्शस्तदा—</p><blockquote><p><strong>कलादीनां तत्त्वानामविवेको माया॥३॥</strong></p></blockquote><p>किञ्चित्कर्तृतादिरूपकलादिक्षित्यन्तानां तत्त्वानां कञ्चुकपुर्यष्टकस्थूलदेहत्वेनावस्थितानां योऽयमविवेकः पृथक्त्वाभिमतानामेवापृथगात्मत्वेन प्रतिपत्तिः सा माया तत्त्वाख्यातिमयः प्रपञ्चः। तदुक्तं श्रीतन्त्रसद्भावे</p><blockquote><p>कलोद्वलितचैतन्यो विद्यादर्शितगोचरः।<br> रागेण रञ्जितात्मासौ बुद्ध्यादिकरणैर्युतः॥<br> एवं मायात्मको बन्धः प्रोक्तस्तस्य दरात्मकः।<br> तदाश्रयगुणो धर्मोऽधर्मश्चैव समासतः॥<br> तत्रासौ संस्थितः पाश्यः पाशितस्तैस्तु तिष्ठति।</p></blockquote><p>इति। स्पन्दे तु</p><blockquote><p>अप्रबुद्धधियस्त्वेते स्वस्थितिस्थगनोद्यताः।</p></blockquote><p>इत्यनेनैतद्भङ्ग्योक्तम्॥३॥ ;;;</p><p>;;; trans</p><p>Nanu ca</p><blockquote><p>Jñānaṁ prakāśakaṁ loka ātmā caiva prakāśakaḥ|<br> Anayorapṛthagbhāvājjñāne jñānī prakāśate||</p></blockquote><p>iti śrīvijñānabhairavoktadṛṣṭyā jñānamapi prakāśamayameveti kathamasya bandharūpatvam| Satyametadyadi parameśvaraprasādādevaṁ pratyabhijñāyeta yadā tu tanmāyāśaktito naivaṁ vimarśastadā—</p><blockquote><p><strong>Kalādīnāṁ tattvānāmaviveko māyā||3||</strong></p></blockquote><p>Kiñcitkartṛtādirūpakalādikṣityantānāṁ tattvānāṁ kañcukapuryaṣṭakasthūladehatvenāvasthitānāṁ yo&#39;yamavivekaḥ pṛthaktvābhimatānāmevāpṛthagātmatvena pratipattiḥ sā māyā tattvākhyātimayaḥ prapañcaḥ| Taduktaṁ śrītantrasadbhāve</p><blockquote><p>Kalodvalitacaitanyo vidyādarśitagocaraḥ|<br> Rāgeṇa rañjitātmāsau buddhyādikaraṇairyutaḥ||<br> Evaṁ māyātmako bandhaḥ proktastasya darātmakaḥ|<br> Tadāśrayaguṇo dharmo&#39;dharmaścaiva samāsataḥ||<br> Tatrāsau saṁsthitaḥ pāśyaḥ pāśitastaistu tiṣṭhati|</p></blockquote><p>iti| Spande tu</p><blockquote><p>Aprabuddhadhiyastvete svasthitisthaganodyatāḥ|</p></blockquote><p>ityanenaitadbhaṅgyoktam||3||</p><p>;;;</p><p>Возражение <em>(nanu ca)</em>! Ввиду того <em>(dṛṣṭyā)</em>, что было сказано <em>(ukta)</em> в досточтимом Vijñānabhairava <em>(śrī-vijñānabhairava)</em>:</p><blockquote><p>&quot;В мире <em>(loke)</em>, знание <em>(jñānam)</em> [— это то], что дает свет <em>(prakāśakam)</em> и <em>(ca eva)</em> ātmā <em>(ātmā)</em> [— это то], что дает свет <em>(prakāśakaḥ)</em>. Познающий <em>(jñānī)</em> проявляется, или раскрывается <em>(prakāśate)</em> в знании <em>(jñāne)</em>, поскольку нет <em>(a... bhāvāt)</em> разницы <em>(pṛthak)</em> между этими двумя <em>(anayoḥ... iti)</em> --буквально, между знанием и познающим--<sup>1</sup>&quot;||</p></blockquote><p>[поэтому,] даже <em>(api)</em> &quot; [порожденное умом] знание <em>(jñānam)</em> наполнено <em>(mayam eva)</em> светом <em>(prakāśa... iti)</em>&quot;, [тогда,] как <em>(katham)</em> оно [может быть] --т.е. порожденное умом знание--рабством <em>(asya bandharūpatvam)</em>?<sup>2</sup>|</p><p>Это <em>(etad)</em> правда <em>(satyam)</em>, если <em>(yadi)</em>, благодаря милости <em>(prasādāt)</em> Всевышнего <em>(parama)</em> Господа <em>(īśvara)</em>, можно было бы осознавать <em>(pratyabhijñāyeta)</em> таким образом <em>(evam)</em> --т.е. сознавать, что даже порожденное умом знание наполнено светом--. Однако <em>(tu)</em>, когда <em>(yadā)</em> из-за Его māyāśakti <em>(tad-māyāśaktitaḥ)</em><sup>3</sup> отсутствует <em>(na)</em> осознание <em>(vimarśaḥ)</em> подобное этому <em>(evam)</em>, тогда <em>(tadā)</em>—</p><blockquote><p><strong>Не-различение <em>(avivekaḥ)</em> [таких] принципов <em>(tattvānām)</em> как Kalā <em>(kalā)</em> и т.д. <em>(ādīnām)</em> [есть] Майя <em>(māyā)</em>||3||</strong></p></blockquote><p>Это <em>(ayam)</em> не-различение <em>(avivekaḥ)</em> таттв, или принципов <em>(tattvānām)</em>, начиная с Kalā --таттва 7-- <em>(kalā-ādi)</em> и заканчивая землей --таттва 36-- <em>(kṣiti-antānām)</em> —природой которых <em>(rūpa)</em> является ограниченный <em>(kiñcid)</em> деятель <em>(kartṛtā)</em>, и т.д. <em>(ādi)</em>— классифицированных <em>(avasthitānām)</em> как Канчуки (Kañcuka-s), пурьяштака (puryaṣṭaka) [и] грубое тело <em>(kañcuka-puryaṣṭaka-sthūladehatvena)</em><sup>4</sup>, и которое --т.е. не-различение-- <em>(yaḥ)</em> [состоит из] восприятия <em>(pratipattiḥ)</em> как тождественных <em>(apṛthak-ātmatvena)</em> этих [таттв], которые рассматриваются <em>(abhimatānām eva)</em> как отдельные <em>(pṛthaktva)</em>, [есть] Māyā <em>(sā māyā)</em>, [или] манифестация --т.е. таттва 6-- <em>(prapañcaḥ)</em>, полная <em>(mayaḥ)</em> изначального неведения <em>(akhyāti)</em> о Высшем Принципе <em>(tattva)</em>|</p><p>Это <em>(tad)</em> было сказано <em>(uktam)</em> в уважаемом Tantrasadbhāva <em>(śrī-tantrasadbhāve)</em>:</p><blockquote><p>&quot;Этот [ограниченный индивид] <em>(asau)</em> имеет сознание <em>(caitanyaḥ)</em>, охваченное <em>(udvalita)</em> ограниченным действием Kalā <em>(kalā)</em>, объекты его чувств <em>(gocaraḥ)</em> отображаются <em>(darśita)</em> посредством ограниченного знания Vidyā <em>(vidyā)</em>; он окрашен --т.е. [эмоционально] затронут-- <em>(rañjita-ātmā)</em> посредством ограниченной воли Rāga <em>(rāgeṇa)</em>, [и] наделен <em>(yutaḥ)</em> органами <em>(karaṇaiḥ)</em> восприятия <em>(buddhi)</em>, и т.д. <em>(ādi)</em>. Таким образом <em>(evam)</em>, майическое <em>(māyā-ātmakaḥ)</em> рабство <em>(bandhaḥ)</em>, как говорится <em>(proktaḥ)</em>, горестно <em>(dara-ātmakaḥ)</em> для него <em>(tasya)</em>. Короче говоря <em>(samāsatas)</em>, Гуны (Guṇa-s) --Раджас (Rajas), Саттва (Sattva) и Тамас (Tamas)-- <em>(guṇaḥ)</em> зависят <em>(āśraya)</em> от этого --т.е. от Māyā-- <em>(tad)</em>, а также <em>(ca eva)</em> праведная и неправедная деятельность <em>(dharmaḥ adharmaḥ)</em> [зависят от Māyā]. Постоянно пребывающий <em>(saṁsthitaḥ)</em> в этом [Māyā] <em>(tatra)</em>, он <em>(asau)</em> находится в рабстве <em>(pāśyaḥ)</em>, [и] остается <em>(tiṣṭhati)</em> связанным <em>(pāśitaḥ)</em> этими [вышеупомянутыми вещами] <em>(taiḥ)</em>, несомненно <em>(tu... iti)</em>&quot;|</p></blockquote><p>В Spandakārikā-s <em>(spande tu)</em> это [учение] <em>(etad)</em> было выражено <em>(uktam)</em> косвенно <em>(bhaṅgyā)</em> через эту [первую строку афоризма] <em>(anena)</em>:</p><blockquote><p>&quot;Тем не менее <em>(tu)</em>, эти [самые эманации Спанды] <em>(ete)</em>, работая неустанно и непрерывно <em>(udyatāḥ)</em>, чтобы покрыть, или завуалировать <em>(sthagana)</em> их --т.е. людей с непробужденным интеллектом-- <em>(sva)</em> [истинное] состояние, или природу <em>(sthiti)</em>, [служат причиной] [падения] людей с непробужденным интеллектом <em>(aprabuddha-dhiyaḥ... iti)</em>&quot;|<br> [См. Spandakārikā-s I, 20]</p></blockquote><p>||3||</p><hr><ol><li><p>Другой возможный перевод для prakāśaka — это &quot;открывающий&quot;, или даже &quot;открывающий свет&quot;. Познающий — это не кто иной, как ātmā, чьей природой является &quot;свет&quot;, потому что тот, кто проливает свет не отличается от самого света. Следовательно, знание (свет) и познающий (дающий свет) всегда являются одним и тем же.</p></li><li><p>Для санскритских студентов: Фраза &quot;kathamasya bandharūpatvam&quot; буквально означает &quot;Как (katham) этого --т.е. его-- (asya) состояния наличия природы рабства (bandharūpatvam)?&quot;. Конечно, я не могу переводить таким образом, ох!</p></li><li><p>Майяшакти (Māyāśakti) (букв. майическая сила) — не то же самое, что Майятаттва (Māyātattva) (или просто Māyā), шестая категория в процессе проявления вселенной, как учит Трика. Нет! Māyāśakti есть Его сила порождения различий (двойственности), где существует вечное единство. Следовательно, Его благоволение, или prasāda — это просто удаление Им māyāśakti, которое раскрывает неотъемлемое лежащее в основе единство. Поэтому, если кто-то наслаждается Его Милостью, порожденное умом знание никогда не сможет связать его, поскольку такой человек осознает, что даже такое убогое знание также является светом. Свобода и рабство, таким образом, относятся к удалению Им Майяшакти (māyāśakti) или к ее наличию. Очень просто!</p></li><li><p>Kalā — это седьмая таттва (категория, или принцип) в универсальном проявлении. Природа Kalā — это ограниченная способность действовать. Под &quot;и т.д.&quot;, автор подразумевает природу остальных таттв (начиная с 8-й и заканчивая 36-й -землей--). Канчуки (Kañcuka-s) являются оболочками неведения, в то время как puryaṣṭaka — это тонкое тело, состоящее из буддхи (интеллекта), аханкары (ahaṅkāra), или эго, манаса (обычного ума) и пяти Танматр (Tanmātra-s) — тонких элементов от таттвы 27 до таттвы 31.</p></li></ol></div><div class="stanza"><a href="#s3.4" id="s3.4" class="num">3.4</a><p>;;; sans</p><p>अतश्चैतत्प्रशमाय—</p><blockquote><p><strong>शरीरे संहारः कलानाम्॥४॥</strong></p></blockquote><p>महाभूतात्मकं पुर्यष्टकरूपं समनान्तं यत्स्थूलं सुक्ष्मं परं शरीरं तत्र याः पृथिव्यादिशिवान्ततत्त्वरूपाः कला भागास्तासां संहारः स्वकारणे लयभावनया दाहादिचिन्तनयुक्त्या वा ध्यातव्य इति शेषः। यदुक्तं श्रीविज्ञानभैरवे</p><blockquote><p>भुवनाध्वादिरूपेण चिन्तयेत्क्रमशोऽखिलम्।<br> स्थूलसूक्ष्मपरस्थित्या यावदन्ते मनोलयः॥</p></blockquote><p>इति। तथा</p><blockquote><p>कालाग्निना कालपदादुत्थितेन स्वकं पुरम्।<br> प्लुष्टं विचिन्तयेदन्ते शान्ताभासः प्रजायते॥</p></blockquote><p>इति। एवमादि च सर्वागमेष्वस्ति। अत एव</p><blockquote><p>उच्चारकरणध्यानवर्णस्थानप्रकल्पनैः।<br> यो भवेत्स समावेशः सम्यगाणव उच्यते॥</p></blockquote><p>इति श्रीपूर्वशास्त्रे ध्यानाद्येवाणवत्वेनोक्तम्। एतच्च स्थूलत्वात्शाक्तोपायप्रकाशात्मनि स्पन्दशास्त्रे न सङ्गृहीतम्। यत्त्वत्र पर्यवसानभङ्ग्या शाक्ताद्यस्ति तदस्माभिरत्रापि स्पन्दग्रन्थात्संवादितं संवादयिष्यते च किञ्चित्॥४॥ ;;;</p><p>;;; trans</p><p>Ataścaitatpraśamāya—</p><blockquote><p><strong>Śarīre saṁhāraḥ kalānām||4||</strong></p></blockquote><p>Mahābhūtātmakaṁ puryaṣṭakarūpaṁ samanāntaṁ yatsthūlaṁ sukṣmaṁ paraṁ śarīraṁ tatra yāḥ pṛthivyādiśivāntatattvarūpāḥ kalā bhāgāstāsāṁ saṁhāraḥ svakāraṇe layabhāvanayā dāhādicintanayuktyā vā dhyātavya iti śeṣaḥ| Yaduktaṁ śrīvijñānabhairave</p><blockquote><p>Bhuvanādhvādirūpeṇa cintayetkramaśo&#39;khilam|<br> Sthūlasūkṣmaparasthityā yāvadante manolayaḥ||</p></blockquote><p>iti| Tathā</p><blockquote><p>Kālāgninā kālapadādutthitena svakaṁ puram|<br> Pluṣṭaṁ vicintayedante śāntābhāsaḥ prajāyate||</p></blockquote><p>iti| Evamādi ca sarvāgameṣvasti| Ata eva</p><blockquote><p>Uccārakaraṇadhyānavarṇasthānaprakalpanaiḥ|<br> Yo bhavetsa samāveśaḥ samyagāṇava ucyate||</p></blockquote><p>iti śrīpūrvaśāstre dhyānādyevāṇavatvenoktam| Etacca sthūlatvātśāktopāyaprakāśātmani spandaśāstre na saṅgṛhītam| Yattvatra paryavasānabhaṅgyā śāktādyasti tadasmābhiratrāpi spandagranthātsaṁvāditaṁ saṁvādayiṣyate ca kiñcit||4||</p><p>;;;</p><p>Поэтому <em>(atas ca)</em>, чтобы положить конец <em>(praśamāya)</em> этому [рабству] <em>(etad)</em>—</p><blockquote><p><strong>Растворение <em>(saṁhāraḥ)</em> частей <em>(kalānām)</em> --таттв, или принципов проявления-- в теле --физическом, тонком, каузальном-- <em>(śarīre)</em> [должно выполняться с помощью Bhāvanā, или творческого созерцания]||4||</strong></p></blockquote><p>Śarīra --букв. тело-- <em>(śarīram)</em> [означает здесь] грубое [тело] <em>(sthūlam)</em>, которое <em>(yad)</em> состоит <em>(ātmakam)</em> из великих <em>(mahā)</em> элементов <em>(bhūta)</em> --или таттв с 32-й до 36-й--, тонкое [тело] <em>(sukṣma)</em>, которое <em>(yad)</em> является <em>(rūpam)</em> пурьяштакой (puryaṣṭaka) <em>(puryaṣṭaka)</em> --а именно, интеллект, эго, обычный ум и Танмантры (Tanmātra-s)--, [и] высшее [тело] --т.е. каузальное (причинное)-- <em>(param)</em>, которое <em>(yad)</em> заканчивается <em>(antam)</em> в Samanā <em>(samanā)</em> --короче говоря, тело, образованное жизненной энергией и тонким умом вплоть до Samanā, последняя представляет собой Шакти, или Силу Господа, проявляющую себя как мышление от śūnya, или состояния пустоты до таттвы 36--. В этом [śarīra, или теле] <em>(tatra)</em> эти <em>(yāḥ)</em> kalā-s <em>(kalāḥ)</em>, [или] части <em>(bhāgāḥ)</em> являются <em>(rūpāḥ)</em> таттвами --принципами, или категориями-- <em>(tattva)</em>, начиная с <em>(ādi)</em> земли -- в таттве 36-- <em>(pṛthivī)</em> [и] заканчивая в <em>(anta)</em> Śiva <em>(śiva)</em>. Растворение <em>(saṁhāraḥ)</em> этих [частей] <em>(tāsām)</em> в их собственных причинах <em>(sva-kāraṇe)</em> должно осуществляться посредством медитации <em>(dhyātavyaḥ)</em>, [будь то] через Bhāvanā --творческое созерцание-- <em>(bhāvanayā)</em> на растворении [каждой таттвы в предшествующей] <em>(laya)</em>, или <em>(vā)</em> через <em>(yuktyā)</em> размышление <em>(cintana)</em> о сжигании <em>(dāha)</em>, и т.д. <em>(ādi)</em>--.. Это должно быть добавлено [в афоризм для понимания смысла] <em>(iti śeṣaḥ)</em>|</p><p>Это <em>(yad)</em> было сказано <em>(uktam)</em> в уважаемом Vijñānabhairava <em>(śrī-vijñānabhairave)</em>:</p><blockquote><p>&quot;Следует думать <em>(cintayet)</em> последовательно --т.е. постепенно-- <em>(kramaśas)</em> про всю [вселенную] <em>(akhilam)</em>, проявляющуюся как <em>(rūpeṇa)</em> Bhuvanādhvā <em>(bhuvana-adhvā)</em>, и т.д. <em>(ādi)</em>, оставаясь <em>(sthityā)</em> в грубом, тонком и причинном (телах) --т.е. через последовательное растворение одного в другом-- <em>(sthūla-sūkṣma-para)</em>, пока <em>(yāvat)</em> [не произойдет], наконец <em>(ante)</em>, растворение <em>(laya)</em> ума <em>(manas... iti)</em>&quot;||</p></blockquote><p>Поэтому также <em>(tathā)</em>:</p><blockquote><p>&quot;Надо представить <em>(vicintayet)</em>, что ваше собственное <em>(svakam)</em> тело <em>(puram)</em> было сожжено <em>(pluṣṭam)</em> огнем времени Kālāgni <em>(kāla-agninā)</em>, поднимающимся <em>(utthitena)</em> от большого пальца правой ноги <em>(kālapadāt)</em>. И в конце <em>(ante)</em>, возникает <em>(prajāyate... iti)</em> свет <em>(ābhāsaḥ)</em> Śānta --букв. Мирный, т.е. Шива-- <em>(ante)</em> <sup>1</sup>&quot;||</p></blockquote><p>Такое [описание растворения] <em>(evam-ādi ca)</em> имеется <em>(asti)</em> во всех Āgama-s --проявленных Священных Писаниях-- <em>(sarva-āgameṣu)</em>|</p><p>По этой причине <em>(atas eva)</em>, в досточтимом Pūrvaśāstra --i.e. Mālinīvijayatantra-- <em>(śrī-pūrva-śāstre)</em>, говорится <em>(uktam)</em> , что dhyāna <em>(dhyāna)</em>, и т.д. <em>(ādi eva)</em> являются āṇava --т.е. принадлежат к Āṇavopāya-- <em>(āṇavatvena)</em>:</p><blockquote><p>&quot;Полное <em>(saḥ... samyak)</em> поглощение в собственной сущностной природе <em>(samāveśaḥ)</em>, которое <em>(yaḥ)</em> происходит <em>(bhavet)</em> посредством Uccāra, Karaṇa, Dhyāna, Varṇa и Sthānakalpanā <em>(uccāra-karaṇa-dhyāna-varṇa-sthānaprakalpanaiḥ)</em>, известно как <em>(ucyate)</em> Āṇava <em>(āṇavaḥ... iti)</em>&quot;||<br> [См. II, 21 в Mālinīvijaya]</p></blockquote><p>Этот [тип методов] --т.е. методы, принадлежащие Āṇavopāya-- <em>(etad ca)</em>, будучи грубыми <em>(sthūlatvāt)</em>, не были обобщены <em>(na saṅgṛhītam)</em> в Spandaśāstra --т.е. Spandakārikā-s-- <em>(spanda-śāstre)</em>, раскрывающим <em>(prakāśa-ātmani)</em> Śāktopāya <em>(śāktopāya)</em> --короче, Spandaśāstra рассматривает вопросы, касающиеся Śāktopāya--|</p><p>Тем не менее <em>(tu)</em>, все <em>(yad)</em>, что относится к Śāktopāya <em>(śākta)</em>, и т.д. <em>(ādi)</em>, может быть найдено <em>(asti)</em> здесь <em>(atra)</em> как завершающие методы <em>(paryavasāna-bhaṅgyā)</em>, что <em>(tad)</em> даже <em>(api)</em> в этом [контексте] <em>(atra)</em> нами <em>(asmābhiḥ)</em> было сделано согласованным [с Āṇavopāya] <em>(saṁvāditam)</em> и книгой <em>(granthāt)</em>, посвященной Спанде <em>(spanda)</em> --т.е. цитатами, извлеченными из самих Spandakārikā-s--, и <em>(ca)</em> будет сделано согласованным <em>(saṁvādayiṣyate)</em> [в будущем также] немного <em>(kiñcid)</em>||4||</p><hr><ol><li><p>Термин &quot;Śānta&quot; также означает &quot;Мир&quot;. Таким образом, в переводе есть такой твист. Но, конечно, Высший Господь также есть этот Мир. Кроме того, в некоторых версиях Vijñānabhairava, заключительная часть варьируется от &quot;prajāyate&quot; (возникает) до &quot;तदा भवेत्&quot; - &quot;tadā bhavet&quot; (тогда, имеется). Следовательно, перевод видоизменяется опять: &quot;Тогда, наконец, имеет место вспышка Мира&quot;. Здесь вы имеете это... вам не захочется быть переводчиком после всего этого, хаха. Кроме шуток, иногда я даже могу размышлять около часа над самым точным/возможным значением одного единственного термина. Некоторые странные параграфы могут занимать часы, и не потому, что я не понимаю смысл быстро (чтение Санскрита не является трудным для меня... а обычно, поскольку по меньшей мере иногда текст — настолько кошмарный, что я застреваю на некоторое время), а потому, что хочется быть настолько точным/буквальным, насколько это возможно, чтобы в то же самое время читатель мог бы меня понять. Таким образом, речь идет не просто о работе со словарем, но, О уважаемые! чтобы у вас волшебным образом был точный перевод. Не так ли?</p><p>Первая и основная проблема состоит в том, что каждый термин может содержать гигантское количество возможных значений в соответствии с различными контекстами, и вам, в общем, придется выбирать только одно из них. Некоторые значения не могут быть найдены в &quot;обычном&quot; словаре, и вам необходимо прибегать к другим источникам (например, к комментариям на санскрите и пр.). В других случаях, переводы очень сложных отрывков должны делаться через божественное откровение, или вы просто застрянете и не сможете больше продвигаться вперед. Тем не менее, повторяющиеся обращения к Господу радуют Его, и Милосердный дает, наконец, перевод, что по большей части является всего лишь Его ahaitukī karuṇā, или беспричинным состраданием. Я имел в виду, что из чистого сострадания Он дает сокрытые значения, освещая их из глубин Своего собственного Бытия, и в сознании &quot;кажущегося&quot;переводчика происходит откровение. Это свет Śānta, вспышка Мира, о чем свидетельствуют возможные переводы вышеупомянутой строфы Vijñānabhairava. В конце концов, все это просто Его работа и мастерство. Кроме того, это Его Священные Писания. По этой причине, когда вы переводите этот священный язык, ваша индивидуальность исчезает, и остается только Он один. Не удивительно, поскольку божественный Господь здесь единственный! Того, кого вы называете &quot;ты&quot; не есть &quot;ты&quot;, а &quot;Он&quot; всегда.</p></li></ol></div>',2),r=[i];function o(s,n,k,u,v,d){return e(),m("div",null,r)}const l=a(p,[["render",o]]);export{h as __pageData,l as default};
