import{_ as e,c as m,d as t,o as p}from"./chunks/framework.B7ZEGzqH.js";const y=JSON.parse('{"title":"Афоризм 31-33","description":"Шива сутра вимаршини. Глава 3.","frontmatter":{"title":"Афоризм 31-33","description":"Шива сутра вимаршини. Глава 3.","tags":"vim-3","date":"2021-01-23T00:00:00.000Z"},"headers":[],"relativePath":"texts/shiva-sutra-vimarshini/3/31-33/index.md","filePath":"texts/shiva-sutra-vimarshini/3/31-33/index.md"}'),u={name:"texts/shiva-sutra-vimarshini/3/31-33/index.md"};function o(i,a,s,n,r,k){return p(),m("div",null,a[0]||(a[0]=[t('<div class="stanza"><a href="#s3.31" id="s3.31" class="num">3.31</a><p>;;; sans</p><p>न केवलं सृष्टिदशायां निजशक्तिविकासोऽस्य विश्वं यत्तत्पृष्ठपातिनौ—</p><blockquote><p><strong>स्थितिलयौ॥३१॥</strong></p></blockquote><p>स्वशक्तिप्रचय इत्यनुवर्तते। क्रियाशक्त्याभासितस्य विश्वस्य तत्तत्प्रमात्रपेक्षं कञ्चित्कालं बहिर्मुखत्वावभासनरूपा या स्थितिश्चिन्मयप्रमातृविश्रान्त्यात्मा च यो लयस्तावेतावेतस्य स्वशक्तिप्रचय एव तत्तद्वेद्यं ह्याभासमानं विलीयमानं च निजसंविच्छक्त्यात्मकमेवान्यथास्य संवेदनानुपपत्तेः। अत एव श्रीकालिकाक्रमे</p><blockquote><p>अस्तिनास्तिविभागेन...।</p></blockquote><p>इत्यादि स्थितिलयपरत्वेनोक्तम्। तथा</p><blockquote><p>सर्वं शुद्धं निरालम्बं ज्ञानं स्वप्रत्ययात्मकम्।<br> यः पश्यति स मुक्तात्मा जीवन्नेव न संशयः॥</p></blockquote><p>इति॥३१॥ ;;;</p><hr><p>;;; trans</p><p>Na kevalaṁ sṛṣṭidaśāyāṁ nijaśaktivikāso&#39;sya viśvaṁ yattatpṛṣṭhapātinau—</p><blockquote><p><strong>Sthitilayau||31||</strong></p></blockquote><p>Svaśaktipracaya ityanuvartate| Kriyāśaktyābhāsitasya viśvasya tattatpramātrapekṣaṁ kañcitkālaṁ bahirmukhatvāvabhāsanarūpā yā sthitiścinmayapramātṛviśrāntyātmā ca yo layastāvetāvetasya svaśaktipracaya eva tattadvedyaṁ hyābhāsamānaṁ vilīyamānaṁ ca nijasaṁvicchaktyātmakamevānyathāsya saṁvedanānupapatteḥ| Ata eva śrīkālikākrame</p><blockquote><p>Astināstivibhāgena...|</p></blockquote><p>ityādi sthitilayaparatvenoktam| Tathā</p><blockquote><p>Sarvaṁ śuddhaṁ nirālambaṁ jñānaṁ svapratyayātmakam|<br> Yaḥ paśyati sa muktātmā jīvanneva na saṁśayaḥ||</p></blockquote><p>iti||31||</p><p>;;;</p><hr><p>Не <em>(na)</em> только <em>(kevalam)</em> вселенная [является] <em>(viśva)</em> расширением [или развертыванием] <em>(vikāsaḥ)</em> его собственной <em>(nija... asya)</em> силы <em>(śakti)</em> на этапе, или (в) состоянии <em>(daśāyām)</em> [ее] проявления <em>(sṛṣṭi)</em>, [но это также] верно в отношении <em>(yad)</em> двух других [состояний], следующих <em>(pṛṣṭhapātinau)</em> за этим <em>(tad)</em>—</p><blockquote><p><strong>Поддержание [вселенной] <em>(sthiti)</em> [и ее] растворение <em>(layau)</em> [являются также развертыванием его Силы]||31||</strong></p></blockquote><p>[Фраза] &quot;[являются также] развертыванием <em>(pracayaḥ)</em> его <em>(sva)</em> Силы <em>(śakti... iti)</em>&quot; [должна] следовать за <em>(anuvartate)</em> [афоризмом для полноты смысла] --вот почему она была добавлена в круглые скобки к переводу афоризма--|</p><p>Sthiti, или поддержание <em>(sthitiḥ)</em> вселенной <em>(viśvasya)</em>, проявленное <em>(ābhāsitasya)</em> Силой <em>(śaktyā)</em> Действия <em>(kriyā)</em>, которое --т.е. sthiti-- <em>(yā)</em> является <em>(rūpā)</em> внешним <em>(bahirmukhatva)</em> проявлением <em>(avabhāsana)</em> [последней] в течение некоторого <em>(kañcid)</em> времени <em>(kālam)</em>, в отношении <em>(apekṣam)</em> различных <em>(tad-tad)</em> воспринимающих <em>(pramātṛ)</em><sup>1</sup></p><p>, и <em>(ca)</em> laya, или растворение <em>(layaḥ)</em>, которое <em>(yaḥ)</em> является <em>(ātmā)</em> покоем <em>(viśrānti)</em> Воспринимающего --вашей собственной Самости-- <em>(pramātṛ)</em>, кто есть чистое Сознание <em>(cit-maya)</em>, эти самые два --sthiti и laya-- <em>(tau etau)</em><sup>2</sup> [— это] только <em>(eva)</em> расширение, или развертывание <em>(pracayaḥ)</em> его собственной <em>(etasya sva)</em> Силы <em>(śakti)</em> --Силы Yogī, который равен Śiva--. Бесспорно <em>(hi)</em>, различные <em>(tad-tad)</em> познаваемые --объекты-- <em>(vedyam)</em>, которые появляются <em>(ābhāsamānam)</em> и <em>(ca)</em> исчезают <em>(vilīyamānam)</em> --т.е. которые становятся проявленными и непроявленными-- состоят только из <em>(ātmakam eva)</em> его собственной <em>(nija)</em> Сознательной Силы --т.е. Силы Сознания-- <em>(saṁvid-śakti)</em>, поскольку было бы невозможно <em>(anyathā... anupapatteḥ)</em> понимание, или восприятие <em>(saṁvedana)</em> этой [вселенной] <em>(asya)</em> --будучи менее буквальным, но более точным&quot;поскольку понимание, или восприятие этой вселенной иначе было бы невозможным&quot;, т.е. если бы появляющиеся и исчезающие объекты не были бы только его собственной Силой, акт их восприятия был бы невозможен--|</p><p>По той же самой причине <em>(atas eva)</em>, [когда] в уважаемом Kālikākrama <em>(śrī-kālikākrame)</em> [заявлено, что]:</p><blockquote><p>&quot;Имеется разделение --т.е. с/через деление-- <em>(vibhāgena)</em> на позитивные и негативные bhāva-s, или объекты --а именно, проявленные и непроявленные--, и т.д. <em>(asti-na-asti... ityādi)</em>&quot;||</p></blockquote><p>[такое заявление] было высказано <em>(uktam)</em> в отношении <em>(paratvena)</em> поддержания и растворения [вселенной] <em>(sthiti-laya)</em>|</p><p>Поэтому <em>(tathā)</em>:</p><blockquote><p>&quot;Он <em>(saḥ)</em>, кто <em>(yaḥ)</em> воспринимает <em>(paśyati)</em> полностью <em>(sarvam)</em> чистое <em>(śuddham)</em> Сознание <em>(jñānam)</em>, которое лишено поддержки --а именно, не зависит от какого-либо объекта для существования-- <em>(nis-ālambam)</em> [и] чья природа <em>(ātmakam)</em> — это собственное <em>(sva)</em> Я - Сознание, или pratyaya <em>(pratyaya)</em>, [является] освобожденной душой <em>(mukta-ātmā)</em> при жизни <em>(jīvan eva)</em><sup>3</sup>; [относительно этого] нет никаких сомнений <em>(na saṁśayaḥ... iti)</em>&quot;||</p></blockquote><p>||31||</p><hr><ol><li><p>Для получения большего количества информации о семи воспринимающих, читайте примечание 4 в III, 19 настоящего Писания.</p></li><li><p>Для санскритских студентов: &quot;tau&quot; — это двойной номинатив (муж.р.) от &quot;tad&quot; и &quot;etau&quot; — двойной номинатив (муж.р.) от &quot;etad&quot;. Обычно &quot;tad&quot; означает &quot;тот&quot;, а &quot;этот&quot; обозначается словом &quot;etad&quot;. Тем не менее, &quot;tad&quot; может также обозначать &quot;этот&quot; в зависимости от контекста. В этом случае, перевод &quot;tau&quot; как &quot;эти два&quot; является более подходящим, чем его перевод как &quot;те два&quot;. И конечно, выражение содержит &quot;самый&quot;, когда &quot;tad&quot; и &quot;etad&quot; встречаются вместе, т.е. tau etau (который согласно 6^ому Первичному Правилу Гласных Сандхи, превращается в &quot;tāvetau&quot;... но автор мог по своему выбору написать &quot;tā etau&quot; согласно 7<sup>ому</sup> Первичному Правилу Гласных Сандхи): &quot;те самые два&quot; или иначе, как в этом случае, &quot;эти самые два&quot;. Вы также можете утверждать, что я сказал о &quot;tad&quot; также в значении &quot;это&quot; просто обратившись к любому приличному санскритскому словарю.</p></li><li><p>Для санскритских студентов опять: Когда &quot;n&quot; находится в конце слова и ему предшествует &quot;короткая&quot; гласная, он меняется на &quot;nn&quot;, если следовать гласной согласно 2<sup>ому</sup> под-правилу в 15<sup>ом</sup> Правиле Согласных Сандхи. Следовательно, исходное выражение &quot;jīvan eva&quot; превратилось в &quot;jīvanneva&quot; в этой строфе. Очевидно, автор знал это правило! (шучу).</p></li></ol></div><div class="stanza"><a href="#s3.32" id="s3.32" class="num">3.32</a><p>;;; sans</p><p>नन्वेवं सृष्टिस्थितिलयावस्थास्वन्योन्यभेदावभासमयीष्वस्य स्वरूपान्यथात्वमायातमित्याशङ्काशान्त्यर्थमाह—</p><blockquote><p><strong>तत्प्रवृत्तावप्यनिरासः संवेत्तृभावात्॥३२॥</strong></p></blockquote><p>तेषां सृष्ट्यादीनां प्रवृत्तावप्युन्मज्जनेऽपि नास्य योगिनः संवेत्तृभावात्तुर्यचमत्कारात्मकविमर्शमयादुपलब्धृत्वान्निरासश्चलनं तन्निरासे कस्यचिदप्यप्रकाशनात्। यदुक्तं तत्रैव</p><blockquote><p>नाशेऽविद्याप्रपञ्चस्य स्वभावो न विनश्यति।<br> उत्पत्तिध्वंसविरहात्तस्मान्नाशो न वास्तवः॥<br> यतोऽविद्या समुत्पत्तिध्वंसाभ्यामुपचर्यते।<br> यत्स्वभावेन नष्टं न तन्नष्टं कथमुच्यते॥</p></blockquote><p>इति। एतदेव स्पन्दे</p><blockquote><p>अवस्थायुगलं चात्र कार्यकर्तृत्वशब्दितम्।<br> कार्यता क्षयिणी तत्र कर्तृत्वं पुनरक्षयम्॥</p></blockquote><p>तथा।</p><blockquote><p>कार्योन्मुखः प्रयत्नो यः केवलं सोऽत्र लुप्यते।<br> तस्मिँल्लुप्ते विलुप्तोऽस्मीत्यबुधः प्रतिपद्यते॥<br> न तु योऽन्तर्मुखो भावः सर्वज्ञत्वगुणास्पदम्।<br> तस्य लोपः कदाचित्स्यादन्यस्यानुपलम्भनात्॥</p></blockquote><p>इत्यनेनोक्तम्॥३२॥ ;;;</p><hr><p>;;; trans</p><p>Nanvevaṁ sṛṣṭisthitilayāvasthāsvanyonyabhedāvabhāsamayīṣvasya svarūpānyathātvamāyātamityāśaṅkāśāntyarthamāha—</p><blockquote><p><strong>Tatpravṛttāvapyanirāsaḥ saṁvettṛbhāvāt||32||</strong></p></blockquote><p>Teṣāṁ sṛṣṭyādīnāṁ pravṛttāvapyunmajjane&#39;pi nāsya yoginaḥ saṁvettṛbhāvātturyacamatkārātmakavimarśamayādupalabdhṛtvānnirāsaścalanaṁ tannirāse kasyacidapyaprakāśanāt| Yaduktaṁ tatraiva</p><blockquote><p>Nāśe&#39;vidyāprapañcasya svabhāvo na vinaśyati|<br> Utpattidhvaṁsavirahāttasmānnāśo na vāstavaḥ||<br> Yato&#39;vidyā samutpattidhvaṁsābhyāmupacaryate|<br> Yatsvabhāvena naṣṭaṁ na tannaṣṭaṁ kathamucyate||</p></blockquote><p>iti| Etadeva spande</p><blockquote><p>Avasthāyugalaṁ cātra kāryakartṛtvaśabditam|<br> Kāryatā kṣayiṇī tatra kartṛtvaṁ punarakṣayam||</p></blockquote><p>tathā|</p><blockquote><p>Kāryonmukhaḥ prayatno yaḥ kevalaṁ so&#39;tra lupyate|<br> Tasmim̐̐llupte vilupto&#39;smītyabudhaḥ pratipadyate||<br> Na tu yo&#39;ntarmukho bhāvaḥ sarvajñatvaguṇāspadam|<br> Tasya lopaḥ kadācitsyādanyasyānupalambhanāt||</p></blockquote><p>ityanenoktam||32||</p><p>;;;</p><hr><p>Возражение <em>(nanu)</em>!: &quot;Так <em>(evam)</em>, [в случае великого Yogī, ориентированного на объекты,] его [внутреннее состояние] <em>(asya)</em> переживает anyathātva --т.е. различие-- или перерыв в осознании <em>(anyathātvam āyātam)</em> своей сущностной природы <em>(sva-rūpa)</em> в состояниях <em>(avasthāsu)</em> проявления, поддержания и растворения [вселенной] <em>(sṛṣṭi-sthiti-laya)</em>, чьи проявления <em>(avabhāsa-mayīṣu)</em> отличаются <em>(bheda)</em> друг от друга <em>(anyonya... iti)</em>&quot; --короче говоря, поскольку проявление, поддержание и растворение вселенной отличны друг от друга касательно их внешнего появления, внутреннее состояние великого Yogī должно также меняться каждый раз, когда он испытывает эти три, один за другим--. Чтобы <em>(artham)</em> уничтожить <em>(śānti)</em> [это] сомнение <em>(āśaṅkā)</em>, [Śiva] сказал <em>(āha)</em>—</p><blockquote><p><strong>Даже если <em>(api)</em> могут возникнуть <em>(pravṛttau)</em> те [три процесса, а именно проявление, поддержание и растворение вселенной] <em>(tad)</em>, [внутреннее состояние великого Yogī] не прерывается <em>(anirāsaḥ)</em> из-за [его] состояния <em>(bhāvāt)</em> как [Высшего] Познающего <em>(saṁvettṛ)</em>||32||</strong></p></blockquote><p>Даже если <em>(api... api)</em> могут появиться <em>(pravṛttau)</em>, [или] возникнуть <em>(unmajjane)</em> те <em>(teṣām)</em>, т.е. проявление и пр. <em>(sṛṣṭi-ādīnām)</em>, нет никакого <em>(na)</em> перерыва <em>(nirāsaḥ)</em> [или] движения <em>(calanam)</em> [во внутреннем состоянии] этого Yogī <em>(asya yoginaḥ)</em>, из-за [его] состояния <em>(bhāvāt)</em> как [Высшего] Познающего <em>(svapratyaya)</em>, [то есть,] поскольку он является Воспринимающим, полным <em>(mayāt upalabdhṛtvāt)</em> блаженного <em>(camatkāra-ātmaka)</em> осознания <em>(vimarśa)</em> Turya --Четвертого Состояния-- <em>(turya)</em>, [и] потому, что не было бы проявления <em>(aprakāśanāt)</em> чего бы то ни было совсем <em>(kasyacidapi)</em> в случае прерывания <em>(nirāse)</em> в том --в его состоянии как Высшего Познающего-- <em>(tad)</em><sup>1</sup>|</p><p>Та же [истина] <em>(yad)</em> была продекларирована <em>(uktam)</em> в этой же самой [книге] --в Kālikākrama-- <em>(tatra eva)</em>:</p><blockquote><p>&quot;Когда имеет место исчезновение <em>(nāśe)</em> того, что было проявлено <em>(prapañcasya)</em> Неведением, или Māyā <em>(avidyā)</em>, собственная сущностная природа --сущностная природа Сознания-- <em>(sva-bhāvaḥ)</em> не <em>(na)</em> исчезает <em>(vinaśyati)</em>. Поскольку имеется отсутствие <em>(virahāt)</em> внешней видимости --проявления-- <em>(utpatti)</em> [и] исчезновения <em>(dhvaṁsa)</em> [что касается Сознания], следовательно <em>(tasmāt)</em> [в этом случае] реальное <em>(vāstavaḥ)</em> исчезновение <em>(nāśaḥ)</em> отсутствует <em>(na)</em>. Поскольку <em>(yatas)</em> [даже] Неведение <em>(avidyā)</em> образно обозначено <em>(upacaryate)</em> как появляющееся и исчезающее <em>(samutpatti-dhvaṁsābhyām)</em>, как тогда <em>(katham)</em> [может] говориться об <em>(ucyate)</em> исчезновении <em>(naṣṭam)</em> Этого --т.е. Сознания-- <em>(tad)</em>, [когда] это [Сознание] <em>(yad)</em>, согласно природе <em>(sva-bhāvena)</em>, [никогда] не <em>(na)</em> исчезало <em>(naṣṭam... iti)</em>?&quot;||</p></blockquote><p>Это <em>(etad)</em> было упомянуто <em>(uktam)</em> в этом [афоризме] <em>(anena)</em> в Spandakārikā-s <em>(spande)</em>:</p><blockquote><p>&quot;Говорится <em>(śabditam)</em>, что [имеются] два <em>(yugalam)</em> состояния <em>(avasthā)</em> в этом [принципе Spanda] <em>(ca atra)</em>, [т.е.] состояние делания <em>(kārya... tva)</em> [и] состояние делателя <em>(kartṛtva)</em>. Из них <em>(tatra)</em>, состояние делания <em>(kāryatā)</em> является преходящим <em>(kṣayiṇī)</em>, а <em>(punar)</em> состояние делателя <em>(kartṛtvam)</em> [—] вечным/нерушимым <em>(akṣayam... iti)</em>&quot;||<br> [См. Spandakārikā-s I, 14]</p></blockquote><p>и также <em>(tathā)</em> [в этих двух афоризмах]:</p><blockquote><p>&quot;Только <em>(kevalam)</em> усилие <em>(prayatnaḥ... saḥ)</em>, которое <em>(yaḥ)</em> направлено <em>(unmukhaḥ)</em> на действие <em>(kārya)</em>, исчезает <em>(lupyate)</em> в этом [состоянии Samādhi] <em>(atra)</em>. Когда это [усилие] исчезло <em>(tasmin lupte)</em>, [только] глупец <em>(abudhaḥ)</em> [может] думать <em>(pratipadyate)</em> &#39;Я исчез&#39; <em>(viluptaḥ asmi iti)</em>.<br> Никогда <em>(na tu... kadācid)</em> не бывает <em>(syāt)</em> прекращения <em>(lopaḥ)</em> того <em>(tasya)</em> внутреннего <em>(antarmukhaḥ)</em> состояния, или природы <em>(bhāvaḥ)</em>, которое <em>(yaḥ)</em> [является] обителью <em>(āspadam)</em> качества <em>(guṇa)</em> всеведения <em>(sarvajñatva)</em>, вследствие невосприятия <em>(anupalambhanāt)</em> другого <em>(anyasya... iti)</em><sup>2</sup>&quot;||<br> [См. Spandakārikā-s I, 15-16]</p></blockquote><p>||32||</p><hr><ol><li><p>Изменения обнаруживаются при наличии чего-либо неизменного. Если бы Высший Познающий, или Воспринимающий, т.е. Сознание (Ты!), когда-либо изменился, то есть если бы Он испытал прерывание, или движение в Своей сущностной природе, кто бы тогда заметил другие изменения? Поскольку эти другие изменения воспринимаются все время, когда они происходят (например, бодрствование, сны, удовольствие, боль, пустота, счастье, внешние стареющие вещи и т.д.), Высшая Реальность (Ты!) никогда не покидает Свое собственное сущностное состояние. Это — sadodita, или nityodita (букв. постоянно возникающее), т.е. вездесущее. Что нельзя сказать обо всем, что было создано Неведением, или Māyā, полным меняющихся реалий. Следовательно, те, кто постулирует, что Сознание изменяется, или мутирует, ошибаются от начала и до конца.</p></li><li><p>Не только в этом случае, но каждый раз, когда вы видите цитату из Spandakārikā-s, читайте соответствующее объяснение, написанное мудрецом Kṣemarāja в Spandanirṇaya (его научном комментарии на Spandakārikā-s).</p></li></ol></div><div class="stanza"><a href="#s3.33" id="s3.33" class="num">3.33</a><p>;;; sans</p><p>अस्य योगिनः—</p><blockquote><p><strong>सुखदुःखयोर्बहिर्मननम्॥३३॥</strong></p></blockquote><p>वेद्यस्पर्शजातयोः सुखदुःखयोर्बहिरिव नीलादिवदिदन्ताभासतया मननं संवेदनं न तु लौकिकवदहन्तास्पर्शनेनास्य हि स्वशक्तिप्रचयोऽस्य विश्वम् (३-३०) इत्युक्तसूत्रार्थनीत्या सर्वमहन्ताच्छादितत्वेन स्फुरति न तु नियतं सुखदुःखाद्येवेत्येवम्परमेतत्। योगी हि प्रशान्तपुर्यष्टकप्रमातृभावः कथं सुखदुःखाभ्यां स्पृश्यते। तथा च श्रीप्रत्यभिज्ञासूत्रविमर्शिन्याम्।</p><blockquote><p>ग्राहकभूमिकोत्तीर्णानां वास्तवप्रमातृदशाप्रपन्नानां तत्तत्स्वहेतूपस्थापितसुखदुःखसाक्षात्कारेऽपि न तेषां सुखदुःखादि नोत्पद्यत एव वा सुखादि हेतुवैकल्यात्सहजानन्दाविर्भावस्तु तदा स्यात्॥</p></blockquote><p>इत्युक्तम्। अत एव</p><blockquote><p>न दुःखं न सुखं यत्र न ग्राह्यं ग्राहको न च।<br> न चास्ति मूढभावोऽपि तदस्ति परमार्थतः॥</p></blockquote><p>इति स्पन्दे निरूपितम्॥३३॥ ;;;</p><hr><p>;;; trans</p><p>Asya yoginaḥ—</p><blockquote><p><strong>Sukhaduḥkhayorbahirmananam||33||</strong></p></blockquote><p>Vedyasparśajātayoḥ sukhaduḥkhayorbahiriva nīlādivadidantābhāsatayā mananaṁ saṁvedanaṁ na tu laukikavadahantāsparśanenāsya hi svaśaktipracayo&#39;sya viśvam (3-30) ityuktasūtrārthanītyā sarvamahantācchāditatvena sphurati na tu niyataṁ sukhaduḥkhādyevetyevamparametat| Yogī hi praśāntapuryaṣṭakapramātṛbhāvaḥ kathaṁ sukhaduḥkhābhyāṁ spṛśyate| Tathā ca śrīpratyabhijñāsūtravimarśinyām|</p><blockquote><p>Grāhakabhūmikottīrṇānāṁ vāstavapramātṛdaśāprapannānāṁ tattatsvahetūpasthāpitasukhaduḥkhasākṣātkāre&#39;pi na teṣāṁ sukhaduḥkhādi notpadyata eva vā sukhādi hetuvaikalyātsahajānandāvirbhāvastu tadā syāt||</p></blockquote><p>ityuktam| Ata eva</p><blockquote><p>Na duḥkhaṁ na sukhaṁ yatra na grāhyaṁ grāhako na ca|<br> Na cāsti mūḍhabhāvo&#39;pi tadasti paramārthataḥ||</p></blockquote><p>iti spande nirūpitam||33||</p><p>;;;</p><hr><p>Что касается этого Yogī <em>(asya yoginaḥ)</em>—</p><blockquote><p><strong>[Этот возвышенный Yogī] рассматривает <em>(mananam)</em> наслаждение и боль <em>(sukhaduḥkhayoḥ)</em> как нечто внешнее <em>(bahis)</em>||33||</strong></p></blockquote><p>[Этот великий Yogī] рассматривает --букв. рассмотрение-- <em>(mananam)</em> [или] воспринимает --букв. восприятие-- <em>(saṁvedanam)</em> наслаждение и боль <em>(sukha-duḥkhayoḥ)</em>, рожденные <em>(jātayoḥ)</em> от контакта <em>(sparśa)</em> с объектами <em>(vedya)</em>, как просто --букв. как простое появление-- <em>(ābhāsatayā)</em> &quot;Это&quot; --т.е. как нечто воспринимаемое собственной сущностной природой, которая является &quot;Я&quot;, или &quot;Субъектом&quot;-- <em>(idantā)</em>. [Вкратце, он рассматривает, или воспринимает эти два] как <em>(iva)</em> нечто внешнее <em>(bahis)</em>, [такое] как <em>(vat)</em> голубой <em>(nīla)</em>, и т.д. <em>(ādi)</em>, а не так <em>(na tu)</em>, как <em>(vat)</em> обычные люди <em>(laukika)</em> [рассматривают эту пару,] как нечто, касающееся --вступающее в контакт-- <em>(sparśanena)</em> с &quot;Я&quot; <em>(ahantā)</em>. В этом случае <em>(asya hi)</em>, как выражено <em>(ukta... nītyā)</em> значением <em>(artha)</em> афоризма <em>(sūtra)</em> &quot;Вселенная <em>(viśvam)</em> [является] расширением, или развертыванием <em>(pracayaḥ)</em> его собственной <em>(sva... asya)</em> Силы <em>(śakti)</em>. (III, 30) [рассматриваемого Писания] <em>(3-30 iti)</em>&quot;, все <em>(sarvam)</em> сияет наружу <em>(sphurati)</em> как одетое --в смысле &quot;состоящее из, полное&quot;-- <em>(ācchāditatvena)</em> в [его] Я-Сознание <em>(ahantā)</em>, а не <em>(na tu)</em> [как если] удовольствие, боль и т.д. <em>(sukha-duḥkha-ādi eva)</em> были бы связаны [с его Я-Сознанием] --как если эти переживания каким-то образом затрагивали бы его &quot;Я&quot;, или сущностную природу-- <em>(niyatam)</em>. Таково объяснение согласно <em>(iti evam)</em> этому <em>(etad)</em> предыдущему [афоризму] <em>(param)</em>|</p><p>Как <em>(katham)</em> [может] Yogī <em>(yogī hi)</em>, в котором состояние <em>(bhāvaḥ)</em>, [касательно] тонкого тела --состоящего из интеллекта, эго, ума и тонких элементов-- <em>(puryaṣṭaka)</em> как Воспринимающего, или Познающего --Самости-- <em>(pramātṛ)</em>, было устранено <em>(praśānta)</em>, быть затронутым --т.е. быть под воздействием-- <em>(spṛśyate)</em> удовольствием и болью <em>(sukha-duḥkhābhyām)</em>, [когда эти два соотносятся с интеллектом, эго и умом]?|</p><p>То же самое было сказано <em>(tathā ca... uktam)</em> в досточтимой Pratyabhijñāsūtravimarśinī <em>(śrī-pratyabhijñāsūtravimarśinyām)</em>:</p><blockquote><p>&quot;Не существует <em>(na)</em> удовольствия, боли и т.д. <em>(sukhaduḥkhādi)</em> для тех <em>(teṣām)</em>, кто превзошел <em>(uttīrṇānām)</em> уровень <em>(bhūmikā)</em> [ограниченного] воспринимающего --контролируемого Неведением, или Māyā-- <em>(grāhaka)</em> [и] достиг <em>(prapannānām)</em> состояния <em>(daśā)</em> Реального <em>(vāstava)</em> Воспринимающего --т.е. Śiva-- <em>(pramātṛ)</em>, даже когда <em>(api)</em> может быть ясное восприятие <em>(sākṣātkāre)</em> наслаждения <em>(sukha)</em> [и] боли <em>(duḥkha)</em>, вызванное <em>(upasthāpita)</em> их <em>(sva)</em> различными <em>(tad-tad)</em> причинами <em>(hetu)</em><sup>1</sup></p></blockquote><p>. Или еще <em>(vā)</em>, [из-за полного погружения в Свет Сознания,] удовольствие и т.д. <em>(sukha-ādi)</em> не производятся --букв. не производится-- <em>(na utpadyate eva)</em>, поскольку отсутствуют их причины <em>(hetu-vaikalyāt)</em><sup>2</sup>. Тогда <em>(tadā)</em>, [будь то в Turya, или в Turyātīta], есть [только] <em>(syāt)</em> проявление, или наличие <em>(āvis-bhāvaḥ)</em> естественного <em>(sahaja)</em> Блаженства <em>(ānanda)</em> несомненно <em>(tu... iti)</em>&quot;||</p><p>Следовательно <em>(atas eva)</em>, этим [афоризмом] <em>(anena)</em>, была констатирована [та же самая истина] <em>(nirūpitam)</em> в Spandakārikā-s <em>(spande)</em>:</p><blockquote><p>&quot;Где <em>(yatra)</em> [нет] ни <em>(na)</em> боли <em>(duḥkham)</em>, ни <em>(na)</em> наслаждения <em>(sukham)</em>, ни <em>(na)</em> объекта <em>(grāhyam)</em>, ни <em>(na ca)</em> субъекта <em>(grāhakam)</em>; [где] состояние <em>(bhāvaḥ)</em> бессознательности <em>(mūḍha)</em> даже <em>(api)</em> не <em>(na ca)</em> существует <em>(asti)</em>... это <em>(tad)</em> является <em>(asti)</em>, в высшем смысле <em>(paramārthatas... iti)</em>, [принципом Spanda]&quot;||<br> [См. Spandakārikā-s I, 5]</p></blockquote><p>||33||</p><hr><ol><li><p>Эти великие существа не испытывают наслаждения и боли даже в процессе бодрствования, сна со сновидениями и глубокого сна, поскольку они опираются на Turya (Четвертое Состояние), которое является состоянием свидетеля. Другими словами, они воспринимают драму мирового процесса как Свидетели, и следовательно не могут испытывать наслаждение и боль как что-то, связанное с их истинным &quot;Я&quot;. Они воспринимают эту пару как нечто внешнее.</p></li><li><p>Когда великие Yogī-s достигают Turyātīta, состояния, находящегося за пределами Четвертого (т.е. когда они достигают чистого Сознания, лишенного какого-либо универсального проявления), причины удовольствия и боли полностью исчезают. Привязанность (rāga) и отвращение (dveṣa) являются соответствующими причинами наслаждения (sukha) и боли (duḥkha). Когда причины полностью отсутствуют, поскольку человек полностью погружен в Свет своей собственной Самости, последствия вообще не могут возникнуть. Просто!</p></li></ol></div>',3)]))}const h=e(u,[["render",o]]);export{y as __pageData,h as default};
