import{_ as e,c as m,d as t,o as u}from"./chunks/framework.B7ZEGzqH.js";const k=JSON.parse('{"title":"Афоризм 34-40","description":"Шива сутра вимаршини. Глава 3.","frontmatter":{"title":"Афоризм 34-40","description":"Шива сутра вимаршини. Глава 3.","tags":"vim-3","date":"2021-01-22T00:00:00.000Z"},"headers":[],"relativePath":"texts/shiva-sutra-vimarshini/3/34-40/index.md","filePath":"texts/shiva-sutra-vimarshini/3/34-40/index.md"}'),p={name:"texts/shiva-sutra-vimarshini/3/34-40/index.md"};function o(r,a,i,n,s,h){return u(),m("div",null,a[0]||(a[0]=[t('<div class="stanza"><a href="#s3.34" id="s3.34" class="num">3.34</a><p>;;; sans</p><p>यतश्चोत्तीर्णपुर्यष्टकप्रमातृभावस्य योगिनो नान्तः सुखदुःखसंस्पर्शोऽत एवासौ—</p><blockquote><p><strong>तद्विमुक्तस्तु केवली॥३४॥</strong></p></blockquote><p>ताभ्यां सुखदुःखाभ्यां विशेषेण मुक्तः संस्कारमात्रेणाप्यन्तरसंस्पृष्टः केवली केवलं चिन्मात्रप्रमातृतारूपं यस्य। तदुक्तं श्रीकालिकाक्रमे</p><blockquote><p>सुखदुःखादिविज्ञानविकल्पानल्पकल्पितम्।<br> भित्त्वा द्वैतमहामोहं योगी योगफलं लभेत्॥</p></blockquote><p>इति। तुशब्दो वक्ष्यमाणापेक्षया विशेषद्योतक एवमुत्तरसूत्रगतोऽप्येतत्सूत्रापेक्षया॥३४॥ ;;;</p><hr><p>;;; trans</p><p>Yataścottīrṇapuryaṣṭakapramātṛbhāvasya yogino nāntaḥ sukhaduḥkhasaṁsparśo&#39;ta evāsau—</p><blockquote><p><strong>Tadvimuktastu kevalī||34||</strong></p></blockquote><p>Tābhyāṁ sukhaduḥkhābhyāṁ viśeṣeṇa muktaḥ saṁskāramātreṇāpyantarasaṁspṛṣṭaḥ kevalī kevalaṁ cinmātrapramātṛtārūpaṁ yasya| Taduktaṁ śrīkālikākrame</p><blockquote><p>Sukhaduḥkhādivijñānavikalpānalpakalpitam|<br> Bhittvā dvaitamahāmohaṁ yogī yogaphalaṁ labhet||</p></blockquote><p>iti| Tuśabdo vakṣyamāṇāpekṣayā viśeṣadyotaka evamuttarasūtragato&#39;pyetatsūtrāpekṣayā||34||</p><p>;;;</p><hr><p>И <em>(ca)</em>, поскольку <em>(yatas)</em> для Yogī <em>(yoginaḥ)</em>, который вышел за пределы состояния <em>(uttīrṇa... bhāvasya)</em>, где тонкое тело --интеллект, эго, ум и тонкие элементы-- <em>(puryaṣṭaka)</em> [рассматривается как][объективно существующий] Воспринимающий <em>(pramātṛ)</em>, восприятие <em>(saṁsparśaḥ)</em> наслаждения и боли <em>(sukha-duḥkha)</em> не является <em>(na)</em> внутренним <em>(antar)</em> --оно не затрагивает его истинное &quot;Я&quot;, как отмечалось в комментарии к предыдущему афоризму--<sup>1</sup>, следовательно <em>(atas eva)</em> он <em>(asau)</em>—</p><blockquote><p><strong>[Поскольку этот благородный Yogī] абсолютно свободен <em>(vimuktaḥ)</em> от того --наслаждения и боли-- <em>(tad)</em>, только [он есть] один <em>(kevalī)</em> тогда <em>(tu)</em>, [короче говоря, он достиг своей собственной Самости, являющейся одновременно &quot;единственным&quot; Познающим и &quot;единственной&quot; Массой чистого Сознания]||34||</strong></p></blockquote><p>Будучи полностью и особым образом <em>(viśeṣeṇa)</em> свободным <em>(muktaḥ)</em> от них обоих <em>(tābhyām)</em> —от наслаждения и боли <em>(sukha-duḥkhābhyām)</em>— [и] не будучи затронутым <em>(asaṁspṛṣṭaḥ)</em> внутренне <em>(antar)</em> даже <em>(api)</em> простыми <em>(mātreṇa)</em> остаточными впечатлениями <em>(saṁskāra)</em><sup>2</sup>, только [он есть] один <em>(kevalī)</em>, [а именно, он является] тем, чья <em>(yasya)</em> природа <em>(rūpam)</em> заключается только <em>(kevalam)</em> в сущности воспринимающего, или познающего <em>(pramātṛtā)</em>, кто есть чистое Сознание <em>(cit-mātra)</em> --другими словами, он является чистым Сознанием, единственным и истинным Познающим всего--|</p><p>Так <em>(tad)</em> было сказано <em>(uktam)</em> в досточтимом Kālikākrama <em>(śrī-kālikākrame)</em> [также]:</p><blockquote><p>&quot;Yogī <em>(yogī)</em> следует обрести <em>(labhet)</em> плод <em>(phalam)</em> в Йоге <em>(yoga)</em> посредством рассечения на части <em>(bhittvā)</em> великого <em>(mahā)</em> заблуждения <em>(moham)</em>, [именуемого] двойственностью <em>(dvaita)</em>, которое --заблуждение-- сфабриковано <em>(kalpitam)</em> многочисленными <em>(analpa)</em> мыслями <em>(vikalpa)</em>, [основанными на] когнициях <em>(vijñāna)</em>, [таких как] наслаждение <em>(sukha)</em>, боль <em>(duḥkha)</em>, и т.д. <em>(ādi... iti)</em>&quot;||</p></blockquote><p>Термин <em>(śabdaḥ)</em> &quot;tu&quot; --т.е. тогда-- <em>(tu)</em> [имеющий место в настоящем афоризме] ясно показывает <em>(dyotakaḥ)</em> разницу <em>(viśeṣa)</em> в отношении того <em>(apekṣayā)</em>, что должно быть упомянуто <em>(vakṣyamāṇa)</em> [в следующем афоризме]; поэтому также <em>(evam... api)</em> [слово &quot;tu&quot;], встречающееся <em>(gataḥ)</em> в следующем <em>(uttara)</em> афоризме <em>(sūtra)</em>, [ясно показывает различие] в отношении того <em>(apekṣayā)</em>, [что упомянуто] в этом <em>(etad)</em> афоризме <em>(sūtra)</em><sup>3</sup>||34||</p><hr><ol><li><p>Существует другой возможный перевод для &quot;saṁsparśa&quot;: контакт. Таким образом, фраза &quot;восприятие <em>(saṁsparśaḥ)</em> удовольствия и боли <em>(sukha-duḥkha)</em> не является <em>(na)</em> внутренним <em>(antar)</em>&quot; превращается в &quot;контакт <em>(saṁsparśaḥ)</em> с удовольствием и болью <em>(sukha-duḥkha)</em> не является <em>(na)</em> внутренним <em>(antar)</em>&quot;. В любом случае, конечное учение одно и то же: удовольствие и боль совсем не затрагивают его сущностную природу.</p></li><li><p>Он не отождествляется со своим тонким телом (интеллектом, эго, умом и тонкими элементами), не говоря уже о его физическом теле. Кроме того, не будучи даже внутренне затронутым никакими остаточными впечатлениями от действий, он также не подвержен влиянию своего причинного (каузального) тела. Он пребывает в своем духовном теле, называемом супракаузальным, и даже за его пределами. Следовательно, он есть тот, чья природа — чистое Сознание. На самом деле, &quot;каждый&quot; является чистым Сознанием, но из-за отождествления с каузальным, тонким и физическим телами, большинство людей не осознаёт свою сущностную природу и, следовательно, живёт в рабстве.</p></li><li><p>С одной стороны, термин &quot;tu&quot; используется в смысле &quot;тогда&quot; в текущем афоризме, чтобы завершить описание просветленного состояния великого Yogī. С другой стороны, то же самое слово используется в значении &quot;однако&quot; в последующем афоризме, перед описанием состояния ограниченного существа. Так или иначе, ограниченное существо также может стать освобожденной душой. Это будет показано будущими афоризмами. На самом деле, если бы ограниченный индивид &quot;уже не был бы&quot; Высшим Существом, он не смог бы осознать Его. Невозможно достичь того, чего уже здесь нет. Это очень трудно понять большинству людей, я знаю, но это абсолютная правда. Я знаю это, благодаря непосредственному опыту, а не просто благодаря логике и доказательствам.</p><p>Когда вы ощутите хотя бы единожды свое собственное Высшее Я, вы осознаете, что воспринимали Его всегда. Другая трудная для понимания истина для большинства читателей, но это совершенно верно. Если вы не понимаете это сейчас, не беспокойтесь, потому что рано или поздно вы поймете это, если будете упорны в чтении, практике и т.п. Продолжайте пилить ветку с &quot;правой&quot; стороны, и в конце концов вы внезапно упадете. Когда вы пилите в этом положении, ничего не происходит, пока вы не услышите хруст, ха-ха. Я говорю серьезно, несмотря на мой смех. Все это Писание также учило той же истине. Если вы будете читать его снова и снова, ветка будет подпилена и вы осознаете, что вы — Высшая Самость. Это всегда одна и та же история, &quot;очевидно&quot;, что только тела/личности меняются. Короче говоря, это всегда история о том, когда &quot;Ты&quot; становишься &quot;собой&quot;, а затем опять &quot;Собой&quot;, поскольку здесь больше никого нет. Вот почему афоризм гласит, что великий Yogī — kevalī, или один.</p></li></ol></div><div class="stanza"><a href="#s3.35" id="s3.35" class="num">3.35</a><p>;;; sans</p><p>यदाह—</p><blockquote><p><strong>मोहप्रतिसंहतस्तु कर्मात्मा॥३५॥</strong></p></blockquote><p>मोहेनाज्ञानेन प्रतिसंहतस्तदेकघनस्तत एव सुखदुःखाश्रयो यः स पुनः कर्मात्मा नित्यं शुभाशुभकलङ्कितः। तदुक्तं तत्रैव</p><blockquote><p>यदविद्यावृततया विकल्पविधियोगतः।<br> शिवादीन्नैव झटिति समुद्भावयतेऽखिलान्॥<br> ततः शुभाशुभा भावा लक्ष्यन्ते तद्वशत्वतः।<br> अशुभेभ्यश्च भावेभ्यः परं दुःखं प्रजायते॥</p></blockquote><p>इति॥३५॥ ;;;</p><hr><p>;;; trans</p><p>Yadāha—</p><blockquote><p><strong>Mohapratisaṁhatastu karmātmā||35||</strong></p></blockquote><p>Mohenājñānena pratisaṁhatastadekaghanastata eva sukhaduḥkhāśrayo yaḥ sa punaḥ karmātmā nityaṁ śubhāśubhakalaṅkitaḥ| Taduktaṁ tatraiva</p><blockquote><p>Yadavidyāvṛtatayā vikalpavidhiyogataḥ|<br> Śivādīnnaiva jhaṭiti samudbhāvayate&#39;khilān||<br> Tataḥ śubhāśubhā bhāvā lakṣyante tadvaśatvataḥ|<br> Aśubhebhyaśca bhāvebhyaḥ paraṁ duḥkhaṁ prajāyate||</p></blockquote><p>iti||35||</p><p>;;;</p><hr><p>[Śiva, автор Śivasūtra-s,] сказал <em>(āha)</em> так --т.е. следующее-- <em>(yad)</em>—</p><blockquote><p><strong>Однако <em>(tu)</em>, тот, кто является плотной массой <em>(pratisaṁhataḥ)</em> заблуждения <em>(moha)</em> [всего лишь] вовлечен <em>(ātmā)</em> в действия <em>(karma)</em>||35||</strong></p></blockquote><p>С заблуждением [или] неведением <em>(mohena ajñānena)</em>. [Термин] pratisaṁhata <em>(pratisaṁhataḥ)</em> [означает], что он --т.е. ограниченный индивид-- является плотной массой <em>(eka-ghanaḥ)</em>, [заполненной] тем --т.е. заблуждением, или неведением-- <em>(tad)</em>. По этой самой причине <em>(tatas eva)</em>, он является получателем <em>(āśrayaḥ yaḥ)</em> наслаждения и боли <em>(sukha-duḥkha)</em>. С другой стороны <em>(punar)</em>, из-за своей вовлеченности <em>(ātmā)</em> в действия <em>(karma)</em>, он <em>(saḥ)</em> всегда запятнан <em>(nityam kalaṅkitaḥ)</em> хорошими и плохими поступками <em>(śubha-aśubha)</em>|</p><p>Это <em>(tad)</em> было провозглашено <em>(uktam)</em> в этом самом [Писании] --в Kālikākrama-- <em>(tatra eva)</em>:</p><blockquote><p>&quot;[В случае невежественного человека,] будучи охваченным, или покрытым <em>(āvṛtatayā)</em> неведением <em>(avidyā)</em> о Том --о Самости-- <em>(yad)</em> [и] из-за <em>(yogatas)</em> использования, или применения <em>(vidhi)</em> мыслей <em>(vikalpa)</em>, он не сразу осознает <em>(na eva jhaṭiti samudbhāvayate)</em> все [таттвы, или категории] <em>(akhilān)</em>, начинающиеся с <em>(ādīn)</em> Śiva <em>(śiva)</em> [как свою собственную сущностную природу, которая есть плотная масса Сознания]<sup>1</sup></p></blockquote><p>. Тогда <em>(tatas)</em>, появляются <em>(lakṣyante)</em> хорошие и плохие <em>(śubha-aśubhāḥ)</em> состояния <em>(bhāvāḥ)</em> ; и <em>(ca)</em> поскольку [такой человек] находится под контролем <em>(vaśatvataḥ)</em> этого [неведения о своем собственном Я] <em>(tad)</em>, великая <em>(param)</em> боль <em>(duḥkham)</em> приходит к [нему] <em>(prajāyate)</em> из-за [вышеупомянутых] плохих состояний <em>(aśubhebhyaḥ... bhāvebhyaḥ... iti)</em><sup>2</sup>&quot;||</p><p>||35||</p><hr><ol><li><p>Очевидно, требования размера/ритма заставили автора опустить несколько слов. Тогда что-то вроде этого должно было бы быть написано: &quot; शिवादितत्त्वांश्चिद्घनस्वभावतया नैव झटिति समुद्भावयतेऽखिलान्॥ &quot; - &quot;Śivāditattvāṁścidghanasvabhāvatayā naiva jhaṭiti samudbhāvayate&#39;khilān||&quot; он не сразу осознает <em>(na eva jhaṭiti samudbhāvayate)</em> все <em>(akhilān)</em> таттвы, или категории <em>(tattvān)</em>, начиная с <em>(ādi)</em> Śiva <em>(śiva)</em> как [свою собственную] сущностную природу <em>(sva-bhāvatayā)</em>, которая является плотной массой <em>(ghana)</em> Сознания <em>(cit)</em>&quot;.</p><p>Спряжение &quot;samudbhāvayate&quot; (т.е. он полагает, думает и т.д.) следует интерпретировать здесь как &quot;parāmṛśati&quot; (он мысленно ухватывает, осознает и т.п.). Слово &quot;jhaṭiti&quot; означает &quot;тотчас/немедленно&quot;. Полученное в результате значение всего утверждения состоит в том, что невежественный человек становится жертвой Māyīyamala (Майического загрязнения) и, следовательно, испытывает двойственность. Другими словами, он воспринимает все таттвы, как отличные от себя.</p></li><li><p>Эти плохие состояния коренятся в Kārmamala (Кармическом загрязнении). Это загрязнение является полным замешательством в отношении реального Делателя действий. Поскольку этот человек игнорирует то, что Господь является реальным Исполнителем всего, он думает, что он делает. Таким образом, он вовлекается в хорошие и плохие действия, и из-за неизбежных плодов его плохих поступков к нему приходит ужасная боль.</p></li></ol></div><div class="stanza"><a href="#s3.36" id="s3.36" class="num">3.36</a><p>;;; sans</p><p>एवमीदृशस्यापि कर्मात्मनो यदानर्गलमाहेशशक्तिपातवशोन्मिषितसहजस्वातन्त्र्ययोगो भवति तदास्य—</p><blockquote><p><strong>भेदतिरस्कारे सर्गान्तरकर्मत्वम्॥३६॥</strong></p></blockquote><p>शरीरप्राणाद्यहन्ताभिमानात्मनः सकलप्रलयाकलादिप्रमात्रुचितस्य भेदस्य तिरस्कारे स्थितस्यापि चिद्घनस्वभावोन्मज्जनादपहस्तने सति क्रमेण मन्त्रमन्त्रेश्वरमन्त्रमहेश्वरात्मकस्वमाहात्म्यावाप्तौ सर्गान्तरकर्मत्वं यथाभिलषितनिर्मेयनिर्मातृत्वं भवति। तथा च श्रीस्वच्छन्दे</p><blockquote><p>त्रिगुणेन तु जप्येन स्वच्छन्दसदृशो भवेत्।</p></blockquote><p>इति स्वच्छन्दसादृश्येन भेदतिरस्कारमस्योक्त्वा</p><blockquote><p>ब्रह्मविष्ण्विन्द्रदेवानां सिद्धदैत्योरगेशिनाम्॥<br> भयदाता च हर्ता च शापानुग्रहकृद्भवेत्।<br> दर्पं हरति कालस्य पातयेद्भूधरानपि॥</p></blockquote><p>इत्युक्तम्॥३६॥ ;;;</p><hr><p>;;; trans</p><p>Evamīdṛśasyāpi karmātmano yadānargalamāheśaśaktipātavaśonmiṣitasahajasvātantryayogo bhavati tadāsya—</p><blockquote><p><strong>Bhedatiraskāre sargāntarakarmatvam||36||</strong></p></blockquote><p>Śarīraprāṇādyahantābhimānātmanaḥ sakalapralayākalādipramātrucitasya bhedasya tiraskāre sthitasyāpi cidghanasvabhāvonmajjanādapahastane sati krameṇa mantramantreśvaramantramaheśvarātmakasvamāhātmyāvāptau sargāntarakarmatvaṁ yathābhilaṣitanirmeyanirmātṛtvaṁ bhavati| Tathā ca śrīsvacchande</p><blockquote><p>Triguṇena tu japyena svacchandasadṛśo bhavet|</p></blockquote><p>iti svacchandasādṛśyena bhedatiraskāramasyoktvā</p><blockquote><p>Brahmaviṣṇvindradevānāṁ siddhadaityorageśinām||<br> Bhayadātā ca hartā ca śāpānugrahakṛdbhavet|<br> Darpaṁ harati kālasya pātayedbhūdharānapi||</p></blockquote><p>ityuktam||36||</p><p>;;;</p><hr><p>Так <em>(evam)</em>, даже <em>(api)</em> для таких [невежественных людей] <em>(īdṛśasya)</em>, кто вовлечен <em>(ātmanaḥ)</em> в действия <em>(karma)</em>, когда <em>(yadā)</em> происходит <em>(bhavati)</em> единение <em>(yogaḥ)</em> с естественной <em>(sahaja)</em> Абсолютной Свободой [Высшей Самости] <em>(svātantrya)</em>, которое возникает <em>(unmiṣita)</em> из-за <em>(vaśa)</em> беспрепятственного <em>(anargala)</em> дарования Милости --т.е. нисхождения Силы-- <em>(śakti-pāta)</em>, приходящей от Maheśa --т.е. &quot;Великого Господа&quot;, эпитет Śiva-- <em>(māheśa)</em>, тогда <em>(tadā)</em> в его случае <em>(asya)</em>—</p><blockquote><p><strong>Когда различие <em>(bheda)</em> исчезает <em>(tiraskāre)</em>, [у просветленного Yogī появляется] способность совершить <em>(karmatvam)</em> другое <em>(antara)</em> Творение <em>(sarga)</em>||36||</strong></p></blockquote><p>[С одной стороны, в составном слове &quot;Bhedatiraskāre&quot;, слово &quot;bheda&quot; означает] &quot;различие или двойственность&quot; <em>(bhedasya)</em>, [состоящее из разных областей опыта] соответствующих <em>(ucitasya)</em> воспринимающих <em>(pramātṛ)</em>, [таких как] Sakala <em>(sakala)</em>, Pralayākala <em>(pralaya-akala)</em>, и т.д. <em>(ādi)</em>, чья природа <em>(ātmanaḥ)</em> [имеет дело с][ошибочной] концепцией, или идеей <em>(abhimāna)</em>, что [их] &quot;Я&quot; или &quot;реальная Самость&quot; <em>(ahantā)</em> является телом <em>(śarīra)</em>, жизненной энергией <em>(prāṇa)</em>, и т.п. <em>(ādi)</em><sup>1</sup>. [С другой стороны, в том же сложном слове &quot;Bhedatiraskāre&quot;, термин &quot;tiraskāre&quot; означает] &quot;при исчезновении&quot; <em>(tiraskāre)</em>, т.е. когда имеет место отбрасывание, или устранение <em>(apahastane sati)</em> имеющегося [различия, или двойственности] <em>(sthitasya api)</em> из-за появления <em>(unmajjanāt)</em> своей сущностной пироды <em>(sva-bhāva)</em>, являющейся плотной массой <em>(ghana)</em> Сознания <em>(cit)</em>. [В общем, все сложное слово &quot;Bhedatiraskāre&quot;означает &quot;При исчезновении различия, или двойственности&quot;,] т.е. в постепенном достижении <em>(krameṇa avāptau)</em> его <em>(sva)</em> возвышенного состояния <em>(māhātmya)</em>, проявляющегося в форме <em>(ātmaka)</em> [высших воспринимающих, называемых] Mantra <em>(mantra)</em>, Mantreśvara <em>(mantra-īśvara)</em> [и] Mantramaheśvara <em>(mantra-mahā-īśvara)</em>. [Когда все это случается,][у просветленного yogī] появляется <em>(bhavati)</em> способность совершить <em>(karmatvam)</em> другое <em>(antara)</em> Творение <em>(sarga)</em>, [т.е.] состояние творца <em>(nirmātṛtvam)</em> [для созидания] вещей <em>(nirmeya)</em> в соответствии со своими желаниями <em>(yathā-abhilaṣita)</em>|</p><p>Так <em>(tathā ca)</em>, в досточтимом Svacchandatantra <em>(śrī-svacchande)</em>, исчезновение <em>(tiraskāram)</em> различия, или двойственности <em>(bheda)</em> в его случае <em>(asya)</em> из-за подобия <em>(sādṛśyena)</em> со Svacchanda --Независимого Господа, кто абсолютно Свободен-- <em>(svacchanda)</em> (имеем) через [молчаливое] упоминание <em>(uktvā)</em> [в следующей строке]:</p><blockquote><p>&quot;Посредством тройной <em>(triguṇena tu)</em> джапы <em>(japyena)</em><sup>2</sup> он становится <em>(bhavet)</em> подобным <em>(sadṛśaḥ)</em> Svacchanda --Свободному Господу-- <em>(svacchanda... iti)</em>&quot;||<br> [См. первую строку в VI, 54 в Svacchandatantra]</p></blockquote><p>[сразу после этого,] было сказано <em>(uktam)</em>:</p><blockquote><p>&quot;Среди богов --божественных существ-- <em>(devānām)</em> Brahmā <em>(brahma)</em>, Viṣṇu <em>(viṣṇu)</em> [и] Indra <em>(indra)</em>, [и также] среди [полубогов, известных как] siddha-s --не совершенные существа, а вид сущностей, превосходящих простых призраков-- <em>(siddha)</em>, daitya-s --демонов-- <em>(daitya)</em> [и] urageśī-s --змеиных королей-- <em>(uraga-īśinām)</em>, он становится <em>(bhavet)</em> дающим <em>(dātā)</em> и <em>(ca... ca)</em> рассеивающим <em>(hartā)</em> страх <em>(bhaya)</em> посредством проклятия <em>(śāpa...kṛt)</em> [и] дарования Милости <em>(anugraha-kṛt)</em>, [соответственно]. Он разрушает <em>(harati)</em> гордыню <em>(darpam)</em> Kāla --как смерти, так и Ямы, повелителя смерти-- <em>(kālasya)</em> [и] вызывает падение <em>(pātayet)</em> даже <em>(api)</em> гор <em>(bhūdharān... iti)</em>&quot;||<br> [См. заключительную часть в VI, 54 вместе со всем VI, 55 in Svacchandatantra]</p></blockquote><p>||36||</p><hr><ol><li><p>Эта тема уже кратко объяснялась мной в пояснении 4 в III, 19. Но я должен повторить свое объяснение здесь, потому что я скажу кое-что еще по этому поводу. Имеется семь типов воспринимающих, или скорее семь этапов для одного воспринимающего:</p><ol><li><p>Śiva (его сфера — это Śiva и Śakti таттвы, первые две категории) - Он — Высший Воспринимающий, в своей собственной сущностной природе. Вселенная здесь отсутствует.</p></li><li><p>Mantramaheśvara (его сфера — это Sadāśiva-таттва, третья категория) - Хотя вселенная возникла, она все еще не четкая. Итак, этот воспринимающий еще полностью поглощен своей собственной Самостью, или Aham (&quot;Я&quot;).</p></li><li><p>Mantreśvara (его сфера — это Īśvara-таттва, четвертая категория) - Вселенная возникает как отчетливая реальность, и в результате этот воспринимающий совершенно поглощается этим дивом, но он все еще сохраняет свою Осознанность незатронутой.</p></li><li><p>Mantra (его сфера — это Sadvidyā, или Śuddhavidyā таттва, пятая категория) - Здесь имеет место баланс между Aham (&quot;Я&quot;) и Idam (&quot;Это&quot;, т.е. вселенная). Этот воспринимающий в равной степени осознает оба аспекта одновременно.</p><p>Предыдущие четыре воспринимающие являются божественными, или высшими. Они полностью лишены mala, или загрязнения. На самом деле, первый воспринимающий — это даже не тип воспринимающего, но Сам Господь! У Него отсутствует вселенная для восприятия, потому что Он укоренен в Себе Самом, как Aham, или &quot;Я&quot;, а именно у Него нет &quot;Idam&quot; (&quot;Это&quot;), или вселенной как Его сферы восприятия.</p><p>Теперь, следующие оставшиеся воспринимающие, погруженные в страдания, т.е. в bheda, или двойственность:</p></li><li><p>Vijñānākala (его сфера находится между Sadvidyā и Māyā таттвами, т.е. между пятой и шестой категориями). Он осознает, что Śiva — его настоящее &quot;Я&quot;, но испытывает недостаток силы (отсюда, он является &quot;akala&quot;) из-за Āṇavamala.</p></li><li><p>Pralayākala (его сфера — это Māyā и ее пять Kañcuka-s, с шестой по одиннадцатую категории). Этот воспринимающий также лишен силы (akala) и полностью отождествлен со своей жизненной энергией. Следовательно, вселенная является ему в растворенном виде. Другими словами, он воспринимает ее как пустоту. Он сохраняет три загрязнения, а именно, Āṇavamala, Māyīyamala и Kārmamala, но последние две не действуют, поскольку вселенная претерпела &quot;pralaya&quot;, или &quot;растворение&quot;.</p></li><li><p>Sakala (его сфера — начиная с Puruṣa --двенадцатой категории-- и заканчивая последней). С одной стороны, этот воспринимающий не лишен силы (вот почему он — &quot;sakala&quot;, или &quot;с силой&quot;), но она ограничена во всех отношениях, особенно в отношении его концепции &quot;Я&quot;. Он убежден, что физическое тело (содержащее пять грубых элементов) и тонкое тело (состоящее из интеллекта, эго, ума и тонких элементов) является его настоящим &quot;Я&quot;. Это ошибочное понятие заставляет его использовать и без того уже ограниченные силы воли, знания и действия многими бесполезными способами. Вместо того, чтобы использовать их для достижения Осознания своего Истинного Я, он, как сумасшедший, бежит за удовольствиями и любой ценой убегает от боли, все время ищет Высшее Блаженство, но при этом игнорируя как свое состояние искателя Высшего Блаженства, так и реальное место, где это Блаженство пребывает (т.е. в его настоящем Я).</p></li></ol><p>OK, &quot;bheda&quot; или &quot;различие/двойственность&quot; является &quot;подходящим для&quot; каждого воспринимающего в следующем смысле: &quot;प्रमातॄणां यथास्वस्वभावं भिन्नप्रमेयात्मकभेदाभासः&quot; - &quot;Pramātṝṇāṁ yathāsvasvabhāvaṁ bhinnaprameyātmakabhedābhāsaḥ&quot; - &quot;Проявление bheda, или двойственности, состоящей из различных prameya-s, [происходит] в соответствии с сущностной природой воспринимающих&quot;. Prameya — это все, что воспринимающий ощущает/различает. Каждый воспринимающий имеет prameya, или область восприятия, соответствующую, или подходящую его собственной природе. Например, Sakala имеет вселенную, образованную разными реалиями, от самого бога Brahmā до последнего червя, в то время как у Pralayākala просто отсутствует область восприятия, или prameya. .</p></li><li><p>Термин &quot;japya&quot; буквально означает &quot;повторенная мантра, или молитва&quot;, в то время как хорошо известное слово &quot;japa&quot; буквально означает &quot;повторение/начитывание мантры, или молитвы&quot;. Так, первое указывает на то, что прочитано, а последнее является актом начитывания. В любом случае, в этом контексте в трехкратном japya ни одно из этих буквальных значений не указывается. Существует три типа japya: (1) Śāmbhava, где вы фиксируете свое внимание на точке соединения Śiva и Śakti (Господа и Его Силы); (2) Śākta, где вы фиксируете свое внимание на переходной точке pramāṇa и prameya (средства познания плюс самого знания, и познаваемого --т.е. объекта--) (3) Āṇava, где вы фиксируете свое внимание на переходной точке prāṇa и apāna (жизненных энергий, выходящих и входящих на выдохе и вдохе, соответственно).</p><p>Конечно, большинство стремящихся начнут с простейшей japya, т.е. с хорошо знакомой концентрации на промежутке между выдохом и вдохом (или наоборот). Затем они продвинутся на следующий уровень, осознавая точку перехода от ума/чувств/познания к (познаваемому) объекту. Каждый раз, когда вы что-то воспринимаете своими умом/чувствами, имеет место познание, или знание как результат (например, вы видите дерево и думаете, &quot;О, это дерево&quot;). Обычные люди (т.е. просто Sakala-s) осознают либо сам объект, либо их ум/чувства/познания, но yogī-s отличаются тем, что осознают точку соединения этих двух вещей (pramāṇa and prameya). В заключение, очень продвинутые yogī-s достигают высшей формы japya, т.е. Śāmbhavajapya, через осознание переходной точки от Господа к Его Силе. Короче говоря, он сосредоточивает свое внимание на союзе между &quot;Я&quot;, или &quot;Самостью&quot; и Его &quot;Я-сознанием&quot;. Ну, если вам удастся достичь такого уровня духовности, мои поздравления, возвышенные Yogī, или Yoginī, потому что сделать это так сложно, что я не могу не смеяться каждый раз, думая об этом! Только Милость Господа, обильно дарованная человеку, способна сделать его пригодным для Śāmbhavajapya.</p></li></ol></div><div class="stanza"><a href="#s3.37" id="s3.37" class="num">3.37</a><p>;;; sans</p><p>न चैतदस्यासम्भाव्यं यतः—</p><blockquote><p><strong>करणशक्तिः स्वतोऽनुभवात्॥३७॥</strong></p></blockquote><p>स्वतः स्वस्मादेवानुभवात्सङ्कल्पस्वप्नादौ करणशक्तिः तत्तदसाधारणार्थनिर्मातृत्वमात्मनः सिद्धमेव। अनेनैवाशयेन श्रीप्रत्यभिज्ञायाम्</p><blockquote><p>अत एव यथाभीष्टसमुल्लेखावभासनात्।<br> ज्ञानक्रिये स्फुट एव सिद्धे सर्वस्य जीवतः॥</p></blockquote><p>इत्युक्तम्। तथासम्भवाद्यदि चैतद्गाढाभिनिवेशेन विमृशति तदा सर्वसाधारणाभीष्टार्थनिर्मातृतापि भवति। तदुक्तं तत्त्वगर्भे</p><blockquote><p>यदा तु तेऽपि सुव्यक्तस्वसामर्थ्यगुणोज्ज्वलाः।<br> भवेद्दृढतरादूरदारिता दार्ढ्यदीनता।<br> तदा च तेषां सङ्कल्पः कल्पपादपतां व्रजेत्॥</p></blockquote><p>इति॥३७॥ ;;;</p><hr><p>;;; trans</p><p>Na caitadasyāsambhāvyaṁ yataḥ—</p><blockquote><p><strong>Karaṇaśaktiḥ svato&#39;nubhavāt||37||</strong></p></blockquote><p>Svataḥ svasmādevānubhavātsaṅkalpasvapnādau karaṇaśaktiḥ tattadasādhāraṇārthanirmātṛtvamātmanaḥ siddhameva| Anenaivāśayena śrīpratyabhijñāyām</p><blockquote><p>Ata eva yathābhīṣṭasamullekhāvabhāsanāt|<br> Jñānakriye sphuṭa eva siddhe sarvasya jīvataḥ||</p></blockquote><p>ityuktam| Tathāsambhavādyadi caitadgāḍhābhiniveśena vimṛśati tadā sarvasādhāraṇābhīṣṭārthanirmātṛtāpi bhavati| Taduktaṁ tattvagarbhe</p><blockquote><p>Yadā tu te&#39;pi suvyaktasvasāmarthyaguṇojjvalāḥ|<br> Bhaveddṛḍhatarādūradāritā dārḍhyadīnatā|<br> Tadā ca teṣāṁ saṅkalpaḥ kalpapādapatāṁ vrajet||</p></blockquote><p>iti||37||</p><p>;;;</p><hr><p>Эта [созидательная способность] <em>(etad)</em> не является <em>(na ca)</em> невозможной <em>(asambhāvyam)</em> для него <em>(asya)</em>, потому что <em>(yatas)</em>—</p><blockquote><p><strong>[Любой может осознать свою] созидательную <em>(karaṇa)</em> силу <em>(śaktiḥ)</em> на своем собственном <em>(svataḥ)</em> опыте <em>(anubhavāt)</em>||37||</strong></p></blockquote><p>Его <em>(ātmanaḥ)</em> созидательная <em>(karaṇa)</em> сила <em>(śaktiḥ)</em>, [или] способность к творению <em>(nirmātṛtvam)</em> различных <em>(tad-tad)</em> необычайных <em>(asādhāraṇa)</em> вещей <em>(artha)</em> доказана, или установлена <em>(siddham)</em> &quot;svato&#39;nubhavāt&quot; <em>(svataḥ... anubhavāt)</em>, т.е. исходя из его собственного <em>(svasmāt eva)</em> опыта --anubhava-- <em>(anubhavāt)</em> в процессе воображения, сна и т.п. <em>(saṅkalpa-svapna-ādau)</em>|</p><p>Та же идея <em>(anena eva āśayena)</em>, была высказана <em>(uktam)</em> в уважаемой Īśvarapratyabhijñā <em>(śrī-pratyabhijñāyām)</em>:</p><blockquote><p>&quot;По этой самой причине <em>(atas eva)</em>, исходя из сил концептуализации и воплощения <em>(samullekha-avabhāsanāt)</em> в соответствии с его желанием <em>(yathā-abhīṣṭa)</em>, [силы] знания и действия <em>(jñāna-kriye)</em> всех живых существ <em>(sarvasya jīvataḥ)</em> [являются] ясно <em>(sphuṭe eva)</em> доказанными, или установленными <em>(siddhe... iti)</em><sup>1</sup>&quot;||<br> [См. I, VI, 11 в Īśvarapratyabhijñā]</p></blockquote><p>Благодаря такой возможности <em>(tathā-sambhavāt)</em>, если <em>(yadi ca)</em> этот [Yogī] <em>(etad)</em>, с твердой и сильной решимостью <em>(gāḍha-abhiniveśena)</em> желает проявить посредством [своей] Vimarśa [или Я-Сознания] <em>(vimṛśati)</em>, тогда <em>(tadā)</em> также <em>(api)</em> возникает <em>(bhavati)</em> состояние творца <em>(nirmātṛtā)</em> вещей <em>(artha)</em> в соответствии с его желанием <em>(abhīṣṭa)</em>, которое --т.е. состояние творца-- является общим <em>(sādhāraṇā)</em> для всех <em>(sarva)</em><sup>2</sup>|</p><p>Так <em>(tad)</em> было констатировано <em>(uktam)</em> в Tattvagarbha <em>(tattvagarbhe)</em>:</p><blockquote><p>&quot;Когда <em>(yadā tu)</em> они --великие Yogī-s-- <em>(te api)</em> сияют --букв. светящиеся-- <em>(ujjvalāḥ)</em> с их <em>(sva)</em> свойством <em>(guṇa)</em> действенности <em>(sāmarthya)</em> ясного проявления <em>(su-vyakta)</em>, [все] отсутствие <em>(dīnatā)</em> стабильности и силы <em>(dārḍhya)</em> быстро <em>(adūra)</em> [и] очень решительно <em>(dṛḍhatara)</em> становится <em>(bhavet)</em> расколотым на куски --фигурально выражаясь-- <em>(dāritā)</em>, и <em>(ca)</em> тогда <em>(tadā)</em> их <em>(teṣām)</em> воля --сила воли-- <em>(saṅkalpaḥ)</em> становится <em>(vrajet)</em> Kalpapādapa, или Kalpataru --деревом, исполняющим желания-- <em>(kalpapādapatām... iti)</em><sup>3</sup>&quot;||</p></blockquote><p>||37||</p><hr><ol><li><p>Согласно Abhinavagupta, как он пишет в своем комментарии на Īśvarapratyabhijñā, известном как Īśvarapratyabhijñāvimarśinī, &quot;Avabhāsana&quot; (сила концептуализации) означает &quot;Jñānaśakti&quot; (Силу Знания), и &quot;Samullekha&quot; (сила воплощения) указывает на &quot;Kriyāśakti&quot; (Силу Действия). Первая предполагает, в то время как последняя осуществляет/воплощает, а именно, выполняет то, что было задумано первым. Итак, Сила Действия реализует все, что было ранее задумано Силой Знания. Просто!</p><p>Я переведу для вас весь комментарий на эту строфу, не только для того, чтобы доказать мою точку зрения, но также и для того, чтобы вы могли глубже понять ее. Я сделаю все возможное, потому что Abhinavagupta является таким ученым и массивным учителем, что часто очень сложно понять его сочинения должным образом. И поскольку способ написания этого мастера является изысканным и сложным, Я буду также объяснять каждую часть этого текста между двойными дефисами (--...--). Итак, начали:</p><p>&quot;यदिदं यथाभीष्टस्य बहिरसत्त्वादननुभूतस्यापि सम्यगुल्लेखनमवभासनं च विकल्पस्य प्रसङ्गाद्दर्शितमस्मादेव हेतोरिदमपि सिद्ध्यति— यः कश्चित्कीटो वा ब्रह्मा वा जीवनक्रियाविष्टस्तस्यावभासनरूपा ज्ञानशक्तिरुल्लेखनरूपा च क्रियाशक्तिर्नैसर्गिकी ततस्तस्यां भूमौ व्यतिरिक्तेश्वरोपकल्पितपूर्वसिद्धसृष्ट्युपजीवनसम्भावनापि नास्तीति स्वमेवैश्वर्यं स्फुटं प्रत्यभिज्ञेयं जानाति करोति च— इति ज्ञानक्रियास्वातन्त्र्यलक्षणमेकवचनेन सर्वस्य जीवजातस्य वस्तुत एकेश्वररूपतां सूचयति। इति शिवम्॥११॥&quot; -</p><p>&quot;Yadidaṁ yathābhīṣṭasya bahirasattvādananubhūtasyāpi samyagullekhanamavabhāsanaṁ ca vikalpasya prasaṅgāddarśitamasmādeva hetoridamapi siddhyati— Yaḥ kaścitkīṭo vā brahmā vā jīvanakriyāviṣṭastasyāvabhāsanarūpā jñānaśaktirullekhanarūpā ca kriyāśaktirnaisargikī tatastasyāṁ bhūmau vyatirikteśvaropakalpitapūrvasiddhasṛṣṭyupajīvanasambhāvanāpi nāstīti svamevaiśvaryaṁ sphuṭaṁ pratyabhijñeyaṁ jānāti karoti ca— Iti jñānakriyāsvātantryalakṣaṇamekavacanena sarvasya jīvajātasya vastuta ekeśvararūpatāṁ sūcayati| Iti śivam||11||&quot; -</p><p>&quot;[Термин &#39;samullekha&#39; в строфе означает] то, которое [является] полным &#39;ullekha&#39; [или] исполнением, или приданием формы. [Это означает формировать] что-либо по желанию, что еще не было пережито на опыте, так как не существовало внешне. И [термин &#39;avabhāsana&#39;, или] сила концептуализации показана в случае с vikalpa, или мыслью --т.е. это очевидно из простого созерцания, как чьи-то мысли представляют себе новые вещи--. Последняя --сила концептуализации-- доказана посредством [наличия] первой --силы воплощения; то есть поскольку если что-то было выполнено, или фактически создано, это является обоснованным доказательством того, что вначале оно было задумано-- — Всё, что наделено деятельностью и жизнью, будь то червь или [сам] Brahmā --создатель материальной вселенной--, сила знания и естественная сила действия этих [живых существ] проявляются по своей природе как силы концептуализации и воплощения, [соответственно]. Следовательно, на этой основе --т.е. на основе воображения, имеющего места в сфере фантазии этих живых существ и т.д.--, нет даже предположения, что существование творения, которое ранее было признано истинным, было создано другим владыкой --короче говоря, исходя из опыта любого живого существа, все, что задумано и создано в его/ее уме, не требует присутствия там другого человека кроме самого себя/самой себя--. Таким образом, [такое живое существо] ясно знает и осуществляет его/ее собственное господство, которое должно быть осознано--т.е. он/она знает и создает вещи как владыка, но еще должен осознать/признать его/ее собственное господство-- — В этом случае, [это господство] характеризуется знанием, действием и свободой, в единственном числе --а именно, имеет место одно господство/владычество, не два и более--, в случае всех тех, кто одержим жизнью --т.е. живых существ--, фактически указывает на их состояние, как существ, созданных единственным Господом --вкратце, господство/владычество, проявляемое живыми существами, будучи только одним в каждом из них, на самом деле указывает на то, что их сущностной природой является &#39;один&#39; Господь --.</p><p>Да будет благоденствие для всех существ!&quot;.</p><p>Надеюсь, что я правильно все понял. Abhinavagupta — такой великий мастер и мыслитель, что необходимо присутствие ученика калибра самого Kṣemarāja (его главного ученика) для того, чтобы расшифровать его учения на санкрите. Господи, будь милосерден, и помни, что я — один на моей маленькой лодке посреди океана подлинных Писаний, комментариев и объяснений ученых с мудростью ведических провидцев, большинство из которых написаны на чистейшем санскрите (даже не в транслитерации). К сожалению, у меня нет никого, физически говоря, кто бы нашептывал перевод мне в уши. Было бы здорово иметь кого-нибудь. Ок, тем временем я должен продолжать грести.</p></li><li><p>Очевидно, что состояние творца, которое является обычным для всех, указывает на то, что оно универсально. Это правильно с точки зрения, что &quot;все живые существа&quot; имеют такое же состояние творца, как и просветленный Yogī, потому что единственный Господь пребывает во всех одинаково. Так или иначе, согласно мудрецам, в этом конкретном контексте, интерпретация фразы такова: &quot; विमर्शातिशयवशात्सर्वजनैस्तन्निर्मितिर्द्रष्टुं शक्यते &quot; - &quot;Vimarśātiśayavaśātsarvajanaistannirmitirdraṣṭuṁ śakyate&quot; - &quot;Из-за преобладания Vimarśa, или Я-сознания --т.е. Śakti--, его творение может быть видно всем людям&quot;. Теперь все совершенно ясно!</p></li><li><p>Это относится к одному из пяти деревьев рая бога Индры. Говорят, что этот Kalpapādapa, или Kalpataru исполняет все желания. Точно так же, когда сила (а именно, созидательная сила) Yogī-s находится на пике, их сила воли может выполнить любое желание. Кратко: Они могут проявлять все, что угодно по своему желанию. Это потому, что они подобны Господу в это время. Что в этом случае я могу еще сказать о них?</p></li></ol></div><div class="stanza"><a href="#s3.38" id="s3.38" class="num">3.38</a><p>;;; sans</p><p>यतश्च करणशक्तिशब्दोक्त्या तुर्यात्मा स्वातन्त्र्यशक्तिरेव बोधरूपस्य प्रमातुः सारमतो मायाशक्त्यपहस्तिततत्स्वरूपोत्तेजनाय—</p><blockquote><p><strong>त्रिपदाद्यनुप्राणनम्॥३८॥</strong></p></blockquote><p>त्रयाणां भावौन्मुख्यतदभिष्वङ्गतदन्तर्मुखीभावनामयानां सृष्टिस्थितिलयशब्दोक्तानां पदानामवस्थानां यदादि प्रधानं त्रिचमत्कृतित्वेनानन्दघनं तुर्याख्यं पदं मायाशक्त्याच्छादितमपि तत्तद्विषयोपभोगाद्यवसरेषु विद्युद्वदाभासमानं तेन तत्तदवसरेषु क्षणमात्रोदितेनाप्यनुप्राणनमन्तर्मुखतद्विमर्शावस्थितितारतम्येनानुगततया प्राणनमात्मनस्तेनैव जीवितेनापि जीवितस्योत्तेजनं कुर्यात्। तदुक्तं श्रीविज्ञानभैरवे</p><blockquote><p>अन्तः स्वानुभवानन्दा विकल्पोन्मुक्तगोचरा।<br> यावस्था भरिताकारा भैरवी भैरवात्मनः॥<br> तद्वपुस्तत्त्वतो ज्ञेयं विमलं विश्वपूरणम्।</p></blockquote><p>इत्याद्युपक्रम्य</p><blockquote><p>शक्तिसङ्गमसङ्क्षुब्धशक्त्यावेशावसानिकम्।<br> यत्सुखं ब्रह्मतत्त्वस्य तत्सुखं स्वाक्यमुच्यते॥<br> लेहनामन्थनाकोटैः स्त्रीसुखस्य भरात्स्मृतेः।<br> शक्त्यभावेऽपि देवेशि भवेदानन्दसम्प्लवः॥<br> आनन्दे महति प्राप्ते दृष्टे वा बान्धवे चिरात्।<br> आनन्दमुद्गतं ध्यात्वा तन्मयस्तल्लयीभवेत्॥<br> जग्धिपानकृतोल्लासरसानन्दविजृम्भणात्।<br> भावयेद्भरितावस्थां महानन्दस्ततो भवेत्॥<br> गीतादिविषयास्वादासमसौख्यैकतात्मनः।<br> योगिनस्तन्मयत्वेन मनोरूढेस्तदात्मता॥</p></blockquote><p>इत्यादिनोपायप्रदर्शनेन प्रपञ्चितम्। एतच्च</p><blockquote><p>अतिक्रुद्धः प्रहृष्टो वा किं करोमीति वा मृशन्।<br> धावन्वा यत्पदं गच्छेत्तत्र स्पन्दः प्रतिष्ठितः॥</p></blockquote><p>इत्यादिना</p><blockquote><p>... प्रबुद्धः स्यादनावृतः॥</p></blockquote><p>इत्यन्तेन प्रदर्शितम्। एतत्स्पन्दनिर्णये निराकाङ्क्षं मयैव निर्णीतम्। त्रिषु चतुर्थम्... (३-२०) इति सूत्रेण जागरादौ तुर्यानुप्राणनमुक्तम्। अनेन सर्वदशानुगतादिमध्यान्तेषु सृष्तिस्थितिसंहारनिरूपितया भङ्ग्या निरूपितेष्विति विशेषः॥३८॥ ;;;</p><hr><p>;;; trans</p><p>Yataśca karaṇaśaktiśabdoktyā turyātmā svātantryaśaktireva bodharūpasya pramātuḥ sāramato māyāśaktyapahastitatatsvarūpottejanāya—</p><blockquote><p><strong>Tripadādyanuprāṇanam||38||</strong></p></blockquote><p>Trayāṇāṁ bhāvaunmukhyatadabhiṣvaṅgatadantarmukhībhāvanāmayānāṁ sṛṣṭisthitilayaśabdoktānāṁ padānāmavasthānāṁ yadādi pradhānaṁ tricamatkṛtitvenānandaghanaṁ turyākhyaṁ padaṁ māyāśaktyācchāditamapi tattadviṣayopabhogādyavasareṣu vidyudvadābhāsamānaṁ tena tattadavasareṣu kṣaṇamātroditenāpyanuprāṇanamantarmukhatadvimarśāvasthititāratamyenānugatatayā prāṇanamātmanastenaiva jīvitenāpi jīvitasyottejanaṁ kuryāt| Taduktaṁ śrīvijñānabhairave</p><blockquote><p>Antaḥ svānubhavānandā vikalponmuktagocarā|<br> Yāvasthā bharitākārā bhairavī bhairavātmanaḥ||<br> Tadvapustattvato jñeyaṁ vimalaṁ viśvapūraṇam|</p></blockquote><p>ityādyupakramya</p><blockquote><p>Śaktisaṅgamasaṅkṣubdhaśaktyāveśāvasānikam|<br> Yatsukhaṁ brahmatattvasya tatsukhaṁ svākyamucyate||<br> Lehanāmanthanākoṭaiḥ strīsukhasya bharātsmṛteḥ|<br> Śaktyabhāve&#39;pi deveśi bhavedānandasamplavaḥ||<br> Ānande mahati prāpte dṛṣṭe vā bāndhave cirāt|<br> Ānandamudgataṁ dhyātvā tanmayastallayībhavet||<br> Jagdhipānakṛtollāsarasānandavijṛmbhaṇāt|<br> Bhāvayedbharitāvasthāṁ mahānandastato bhavet||<br> Gītādiviṣayāsvādāsamasaukhyaikatātmanaḥ|<br> Yoginastanmayatvena manorūḍhestadātmatā||</p></blockquote><p>ityādinopāyapradarśanena prapañcitam| Etacca</p><blockquote><p>Atikruddhaḥ prahṛṣṭo vā kiṁ karomīti vā mṛśan|<br> Dhāvanvā yatpadaṁ gacchettatra spandaḥ pratiṣṭhitaḥ||</p></blockquote><p>ityādinā</p><blockquote><p>... prabuddhaḥ syādanāvṛtaḥ||</p></blockquote><p>ityantena pradarśitam| Etatspandanirṇaye nirākāṅkṣaṁ mayaiva nirṇītam| Triṣu caturtham... (3-20) iti sūtreṇa jāgarādau turyānuprāṇanamuktam| Anena sarvadaśānugatādimadhyānteṣu sṛṣtisthitisaṁhāranirūpitayā bhaṅgyā nirūpiteṣviti viśeṣaḥ||38||</p><p>;;;</p><hr><p>И <em>(ca)</em> поскольку <em>(yatas)</em> через выражение <em>(uktyā)</em> слова <em>(śabda)</em> &quot;karaṇaśakti&quot; --созидательная сила-- <em>(karaṇa-śakti)</em>, [неопровержимо показывается][его] Сила <em>(śaktiḥ)</em> Абсолютной Свободы <em>(svātantrya... eva)</em>, чьей природой <em>(ātmā)</em> является Turya --Четвертое Состояние-- <em>(turya)</em>, [будучи] сутью <em>(sāram)</em> воспринимающего <em>(pramātuḥ)</em>, который есть ничто иное как Bodha, или Сознание <em>(bodha-rūpasya)</em>, поэтому <em>(atas)</em>, для стимуляции <em>(uttejanāya)</em> сущностной природы <em>(sva-rūpa)</em> этой [Turya] <em>(tad)</em>, которая была отвергнута <em>(apahastita)</em> māyāśakti <em>(māyā-śakti)</em><sup>1</sup>—</p><blockquote><p>[Следует выполнять] оживление <em>(anuprāṇanam)</em> трех состояний --проявления, поддержания и растворения-- <em>(tri-pada)</em> главным <em>(ādi)</em>, [другими словами, &quot;четвертым состоянием Сознания, Свидетелем трех других, полным трансцендентного Блаженства&quot;]||38||</p></blockquote><p>Из трех <em>(trayāṇām)</em> pada-s, или состояний <em>(padānām avasthānām)</em>, упомянутых <em>(uktānām)</em> словами <em>(śabda)</em> проявление <em>(sṛṣṭi)</em>, поддержание <em>(sthiti)</em> [и] растворение <em>(laya)</em> —характеризуемых <em>(mayānām)</em> ориентацией <em>(aunmukhya)</em> на объекты <em>(bhāva)</em>, интенсивной привязанностью <em>(abhiṣvaṅga)</em> к ним <em>(tad)</em> [и] внутренней <em>(antarmukhī)</em> ассимиляцией <em>(bhāvanā)</em> этих [самых объектов] <em>(tad)</em>, [соответственно]—, то, которое <em>(yad)</em> [есть] ādi <em>(ādi)</em> [или] главное <em>(pradhānam)</em> [— это] состояние <em>(padam)</em>, называемое <em>(ākhyam)</em> Турья (Turya) --Четвертое-- <em>(turya)</em>, компактная масса <em>(ghana)</em> Блаженства <em>(ānanda)</em>, поскольку она пронизывает, или наполняет [другие] три --проявление, поддержание и растворение-- Высшим Наслаждением <em>(tri-camatkṛtitvena)</em>. [Turya,] хотя <em>(api)</em> сокрыта, или замаскирована <em>(ācchāditam)</em> māyāśakti <em>(māyā-śakti)</em>, возникает <em>(abhāsamānam)</em> [на мгновение] подобно <em>(vat)</em> молнии <em>(vidyut)</em> в случае <em>(avasareṣu)</em> наслаждения <em>(upabhoga)</em> различными <em>(tad-tad)</em> объектами <em>(viṣaya)</em> и т.д. <em>(ādi)</em>. Из-за этого <em>(tena)</em>, в различных случаях <em>(tad-tad-avasareṣu)</em>, [в которых проявляется Turya], даже если <em>(api)</em> это возникло <em>(uditena)</em> только на мгновение <em>(kṣaṇa-mātra)</em>, следует выполнять <em>(kuryāt)</em> anuprāṇana --оживление-- <em>(anuprāṇanam)</em>, [именуемое, &quot;anugatatayā prāṇana&quot;, или] восстановление <em>(prāṇanam)</em>, следуя <em>(anugatatayā)</em> в разной степени <em>(tāratamyena)</em> неизменности, или постоянству <em>(avasthiti)</em> в осознании <em>(vimarśa)</em> этой [компактной массы Блаженства] <em>(tad)</em>, находящейся внутри <em>(antarmukha)</em>. [Короче говоря, следует выполнять] стимуляцию <em>(uttejanam)</em> [такой] жизненной силы <em>(jīvitasya)</em> через оживление <em>(jīvitena api)</em> себя <em>(ātmanaḥ)</em> этой жизнеспособностью <em>(tena eva jīvitena api)</em>|</p><p>То же <em>(tad)</em> было сказано <em>(uktam)</em> в уважаемом Vijñānabhairava <em>(śrī-vijñānabhairave)</em>, начиная с <em>(upakramya)</em> --сейчас Писание будет объяснять Parā (термин, женского рода, который буквально означает &quot;Высшая&quot;, т.е. Высшее Состояние, или Высшая Богиня), состояние, также известное как Turyātīta (Состояние за пределами Четвертого)--:</p><blockquote><p>&quot;[Parā, Высшее Состояние, или Высшая Богиня — это] Блаженство <em>(ānandā)</em>, которое можно испытать --т.е. на собственном опыте-- <em>(sva-anubhava)</em> внутри --в Я-Сознании-- <em>(antar)</em>, [Её] поле действия <em>(gocarā)</em> свободно от <em>(unmukta)</em> мыслей <em>(vikalpa)</em> --т.е. человек должен освободиться от мыслей, чтобы испытать такое Состояние, или Богиню--, это состояние <em>(yā avasthā)</em> своей собственной Самости <em>(ātmanaḥ)</em>, которая есть Bhairava --Śiva, или Господь-- <em>(bhairava)</em>, [является] Bhairavī --Śakti, или божественной Силой Bhairava-- <em>(bhairavī)</em>, чья форма <em>(ākārā)</em> является полной <em>(bharita)</em> --т.е. Она наполнена Высшим Наслаждением, полученным от опыта единства со вселенной--.</p></blockquote><blockquote><p>Это [Высшее Состояние, или Богиня] <em>(tad)</em> должно быть в действительности известно как <em>(tattvatas jñeyam)</em> чистая <em>(vimalam)</em> форма --сущностная природа-- <em>(vapus)</em>, которая наполняет <em>(pūraṇam)</em> вселенную <em>(viśva)</em>— и т.д. <em>(iti ādi)</em>&quot;||</p></blockquote><p>[И впоследствии этот предмет] рассматривается подробно [в Vijñānabhairava] <em>(prapañcitam)</em> посредством [серии строф], показывающих <em>(pradarśanena)</em> способы [приближения к этому Высшему Состоянию, или Богине] <em>(upāya)</em>:</p><blockquote><p>&quot;Это наслаждение, которое <em>(yad sukham)</em> [имеет место] в конце <em>(āvasānikam)</em> погружения <em>(āveśa)</em> в Силу Блаженства <em>(śakti)</em>, которое --т.е. погружение-- вызывается <em>(saṅkṣubdha)</em> через половой акт <em>(saṅgama)</em> с женщиной <em>(śakti)</em> --кратко, &#39;это наслаждение, случающееся во время оргазма&#39;--, это, как считается <em>(ucyate)</em> — наслаждение <em>(tad sukham)</em> Высшего Брахмы --Абсолюта-- <em>(brahmatattvasya)</em>, т.е. [это наслаждение], связанное со своей собственной Самостью <em>(svākyam)</em><sup>2</sup>.</p></blockquote><blockquote><p>О Богиня <em>(deva-īśi)</em>!, даже <em>(api)</em> при отсутствии <em>(abhāve)</em> женщины <em>(śakti)</em> происходит <em>(bhavet)</em> изобилие, или поток <em>(samplavaḥ)</em> блаженства <em>(ānanda)</em> от воспоминания <em>(smṛteḥ)</em>, в полной мере <em>(bharāt)</em>, о наслаждении <em>(sukhasya)</em>, испытываемом с женщиной <em>(strī)</em> в форме крепких поцелуев, объятий и прижиманий <em>(lehana-āmanthana-ākoṭaiḥ)</em><sup>3</sup>.</p></blockquote><blockquote><p>Когда приходит великая радость <em>(ānande mahati prāpte)</em>, или <em>(vā)</em> когда видите родственника, друга и т.п. <em>(dṛṣṭe... bāndhave)</em> после долгой разлуки <em>(cirāt)</em>, (или) после созерцания <em>(dhyātvā)</em> радости <em>(ānandam)</em>, которая возникла <em>(udgatam)</em>, вы становитесь тождественным <em>(mayaḥ)</em> с ней --с этой радостью-- <em>(tad)</em> [и] растворяетесь <em>(layībhavet)</em> в ней <em>(tad)</em><sup>4</sup>.</p></blockquote><blockquote><p>При распространении <em>(vijṛmbhaṇāt)</em> удовольствия <em>(ānanda)</em>, относящегося ко вкусу, или аромату <em>(rasa)</em>, возникающего <em>(ullāsa)</em> при употреблении <em>(kṛta)</em> еды <em>(jagdhi)</em> [и] питья <em>(pāna)</em>, следует сконцентрироваться --в смысле фиксации своего внимания-- <em>(bhāvayet)</em> на состоянии <em>(avasthā)</em> полноты [этого удовольствия] <em>(bharita)</em>; [и] тогда <em>(tatas)</em> появляется <em>(bhavet)</em> великое <em>(mahā)</em> блаженство <em>(ānandaḥ)</em> <sup>5</sup>.</p></blockquote><blockquote><p>В случае Yogī <em>(yoginaḥ)</em>, который пребывает <em>(ātmanaḥ)</em> в единстве <em>(ekatā)</em> с несравнимым <em>(asama)</em> восторгом <em>(saukhya)</em>, [порожденным] наслаждением <em>(āsvāda)</em> объектами <em>(viṣaya)</em>, [такими как] пение <em>(gītā)</em>, и т.п. <em>(ādi)</em>, из-за возвышения <em>(rūḍheḥ)</em> [его] ума <em>(manas)</em> [происходит] отождествление <em>(mayatvena)</em> с ним --с несравнимым восторгом-- <em>(tad)</em>, [и из-за этого,][возникает] отождествление с Тем --с Высшим Брахмой, Абсолютом-- <em>(tad-ātmatā)</em> <sup>6</sup>— и т.д. <em>(iti ādinā)</em>&quot;||</p></blockquote><p>Эта же [самая истина] <em>(etad ca)</em> была показана [в Spandakārikā-s] <em>(pradarśitam)</em>, начиная с <em>(ādinā)</em>:</p><blockquote><p>&quot;Spanda <em>(spandaḥ)</em> прочно заключена <em>(pratiṣṭhitaḥ)</em> в этом <em>(tatra)</em> состоянии <em>(padam)</em>, в которое <em>(yad)</em> [человек] входит <em>(gacchet)</em>, [когда он] чрезвычайно разгневан <em>(atikruddhaḥ)</em>, невероятно доволен, или восхищен <em>(prahṛṣṭaḥ)</em>, размышляет <em>(mṛśan)</em> &quot;что <em>(kim)</em> я делаю <em>(karomi iti)</em>?&quot;, или <em>(vā.. vā... vā)</em> убегает [ради спасения своей жизни] <em>(dhāvan... iti)</em>&quot;||<br> [См. Spandakārikā-s I, 22]</p></blockquote><p>и заканчивая с <em>(antena)</em>:</p><blockquote><p>&quot;... [Однако Yogī, который] не охвачен [тьмой неведения] <em>(anāvṛtaḥ)</em>, остается <em>(syāt)</em> пробужденным и просветленным <em>(prabuddhaḥ... iti)</em> [в этих самых условиях]&quot;||<br> [См. Spandakārikā-s I, 25 - последняя часть]</p></blockquote><p>Этот [предмет] <em>(etad)</em> был исследован <em>(nirṇītam)</em> мною <em>(mayā eva)</em> в Spandanirṇaya <em>(spandanirṇaye)</em> без необходимости добавлять туда [ещё] хоть одно слово для завершения [такого исследования] <em>(nis-ākāṅkṣam)</em> --в том смысле, что предмет был &quot;тщательно&quot; исследован там, в Spandanirṇaya, его научном комментарии на Spandakārikā-s--|</p><p>Посредством афоризма <em>(sūtreṇa)</em> &quot;Четвертое состояние сознания, [которое является Свидетелем] <em>(caturtham)</em>, [следует вливать подобно [непрерывному потоку] масла] в [другие] три <em>(triṣu)</em>, [то есть, в бодрствование, сон со сновидениями и глубокий сон]. (III, 20) [в этом Писании] <em>(3-20 iti)</em>&quot; --остальная часть цитируемого афоризма была добавлена в скобки--, было упомянуто <em>(uktam)</em> оживление <em>(anuprāṇanam)</em> посредством Turya --Четвертого состояния-- <em>(turya)</em> в отношении бодрствования и т.д. <em>(jāgarā-ādau)</em> --т.е. бодрствования, сна со сновидениями и глубокого сна-- |</p><p>В этом афоризме --который сейчас изучается-- <em>(anena)</em>, [оживление посредством Turya является] ссылкой на начальные, промежуточные и конечные точки <em>(ādi-madhya-anteṣu)</em>, присутствующие <em>(anugata)</em> во всех <em>(sarva)</em> состояниях <em>(daśā)</em> --при бодрствовании, сне и глубоком сне--, [но] определяющиеся и удостоверяющиеся <em>(nirūpiteṣu)</em> косвенно, как <em>(nirūpitayā bhaṅgyā)</em> проявление <em>(sṛṣti)</em>, поддержание <em>(sthiti)</em> [и] растворение <em>(saṁhāra)</em>. Таково различие <em>(iti viśeṣaḥ)</em>||38||</p><hr><ol><li><p>Слово &quot;māyāśakti&quot; не является синонимом &quot;māyātattva&quot; (хорошо известной 6<sup> ой </sup> категории в процессе универсального проявления), поскольку первая — это &quot;сила&quot;, а вторая — &quot;проявление&quot;. Поэтому, māyāśakti есть сила, посредством которой Господь способен пророждать различие, или двойственность. Он использует Свою собственную māyāśakti, чтобы скрыть Его сущностную природу от Самого Себя. Какой парадокс! Конечно, это все про тебя, читатель! Это происходит прямо сейчас (а не в далекой галактике!)... посмотри, как твоя собственная māyāśakti скрывает твоё истинное &quot;Я&quot; и заменяет его тем, что известно как эго, или ложное &quot;Я&quot;. Ты — это не тонкое тело, состоящее из интеллекта, эго, ума и тонких элементов, даже не каузальное, произведенное из жизненной энергии. Ты есть Он! Когда ты осознаёшь это, &quot;ты находишься в правильном Сосотоянии&quot;, согласно Трике, где бы ни находилось твое тело, куда бы не направился твой ум, и каков бы ни был твой жизненный статус.</p><p>Закрой глаза на мгновение. Теперь открой. И держи свои глаза открытыми и созерцай объекты перед собой. Это является Его проявлением объектов. Наконец, закрой глаза снова. Это Его растворение объектов. Затем, повтори процесс, фиксируя своё внимание на &quot;Я&quot; (Свидетеле, или Познающем), а не на объектах. Ну, это есть Turya. Со временем, если вы делаете это правильно, вы испытаете Высшее Наслаждение, потому что такова природа Turya. Все это Наслаждение существует в одном месте, а именно, в Тебе, Свидетеле, или Познающем. Ты испытываешь удовольствие в трех других состояниях, поскольку крошечная часть этого Высшего Наслаждения фильтруется через сеть, сотканную māyāśakti. Когда эта сеть удаляется, ты испытываешь всю существующую Радость одновременно. Огромное Наслаждение затопляет тебя в каждый момент времени. Конечно, процесс получения этого колоссального Блаженства будет постепенным и в соответствии с твоими собственными возможностями. Ты предпочтешь это или жалкое состояние Сакалы, все время просящего о каком-то счастье?</p><p>Но, даже после получения всего существующего, или возможного Наслаждения (т.е. Turya), ты можешь идти еще дальше, к Turyātīta (состоянию за пределами Turya, или Четвертого). В этом состоянии ты испытываешь только чистое Сознание, и ты полностью неуязвим. Тем не менее, речь идет не о том, что ты неуязвим в смысле своего рода сверхчеловека, который останавливает пули голыми руками... нет... это не о том же жалком состоянии Сакалы, а о &quot;перезаряженном&quot;, о новом виде существования. Ты неуязвим, потому что не зависишь от чего-либо еще в &quot;бытии&quot;. Ты всегда таков, но в Turyātīta у тебя есть реальное осознание того, что это абсолютно верно. Пока ты удерживаешь это осознание, ты &quot;без всякого сомнения&quot; ощущаешь, что являешься Высшим Существом, независимо от того, живо или мертво твое тело, работает твой ум или остановлен, живешь ли ты в Млечном Пути или в самой дальней галактике вселенной и т.д. Это не просто безразличие или претенциозность (поскольку они все еще находятся в царстве ума), но истинная абсолютная непривязанность к чему бы то ни было (индивидуальности, уму, телу, жизни, смерти, времени, страху... ко всему!). Ты остаешься чистым Сознанием в Абсолютной Свободе!</p><p>Поначалу, этот опыт длится только мгновение и случается благодаря Его Милости, но по мере стабилизации в Turya, шансы войти в контакт с Turyātīta соответственно увеличиваются. Этот опыт абсолютно ошеломляющий и не поддается описанию. Интересен момент, когда ты впервые достигаешь Turyātīta. Это длится мгновение, но невозможно сказать, &quot;как долго это было&quot;, поскольку время и пространство отсутствуют в Turyātīta, несмотря на то, что ты можешь сохранять восприятие своего тела, внешних объектов и т.д. На самом деле, ты можешь продолжать свою обычную деятельность, пребывая в Turyātīta. Тем не менее, хотя ты воспринимаешь пространство, время, движение и т.п., ничего этого не существует по-прежнему (т.е. до такой реализации). Очевидно, я не могу объяснить это должным образом, используя слова. Я мог бы написать целую книгу, и все же мои объяснения были бы не точными, поскольку слова рождаются после Turyātīta, а не до Нее. Итак, сначала Она проявляется внезапно и тут же уходит, и это все, что ты обнаруживаешь. После такого достижения, ты задаёшься вопросом, а что ты делал до этого. По сравнению с этим возвышенным Состоянием, все, что ты испытывал ранее, похоже на &quot;пыль&quot;. Ну, это просто была игра Сознания, ты знаешь. Так или иначе, в процессе Turyātīta все кажется пронизанным абсолютным спокойствием, то есть никаких мнений за и против чего-либо. Несмотря на то, что действия продолжают выполняться, мысли продолжают появляться, полные мнений и тому подобных вещей, ты остаёшься полностью беспристрастным к этому. Это просто &quot;Бытие&quot; за пределами всех &quot;желаний&quot;, &quot;знаний&quot; и &quot;деяний&quot;. Невероятно! И, конечно, ты желаешь этого больше. Это твое Сокровище, и как только ты обнаруживаешь его, невозможно оставить и забыть о нем. Нет, ты хочешь во что бы то ни стало обрести Turyātīta снова, потому что это самое важное в твоей жизни (&quot;ничего более важного, чем достичь это снова&quot;, это так, как ты себя чувствуешь, и да!, что может быть более важным, чем твоя Свобода?)... но... ты осознаешь, что не можешь получить это &quot;по желанию&quot;. Это всегда о Его Милости, хотя, как это ни парадоксально, но Ты есть Он.</p><p>В этот момент, ты должен быть терпелив и продолжать наслаждаться Turya и ее компактной массой Блаженства все больше и больше, пока Она не заполнит всю твою жизнь. Постепенно, Turyātīta будет посещить тебя все чаще и чаще, пока ты не станешь освобожденным полностью, т.е. полностью Свободным, как Господь. Мой собственный Гуру передал мне эту истину: &quot;Бог обитает в тебе, как ты&quot;, и это то, что ты обнаруживаешь рано или поздно, если будешь настойчив. Будь уверен, что полная встреча с Богом — это самый удивительный опыт, который может иметь человек! Кем бы ни был читатель, Господь сияет во всем Своем Великолепии всегда и везде. Автор говорит что-то подобное, но завуалировано, т.е. без моих подробных объяснений. OK, хватит пока раскрывать эти секреты о Turyātīta.</p><p>Возвращаясь к процессам проявления, поддержания и растворения объектов, во всем этом нет ничего сложного, поскольку это связано с собственным опытом каждого. И даже в случае, когда у тебя будут отключены все чувства (например, во время сна), ментальные объекты продолжат проявляться, сохраняться некоторое время, и наконец, вновь растворяться. Даже пустота глубокого сна проявляется, поддерживается и растворяется. Однако, Turya присутствует всегда, пронизывая все другие три состояния. Она не проявляется, не поддерживается и не растворяется, поскольку Она — не объект (то, что может быть воспринято, т.е. твоё тело, стол, мысль и т.д.), а Высший Субъект, или Господь (Ты!).</p><p>На самом деле, осознаёшь ли ты или нет, что являешься Свидетелем, или Познающим, ты — всё равно Он. Здесь нет никого другого, кроме Господа. Все, что кажется существующим отличным и отдельным от Него, обусловлено игрой Его māyāśakti.</p></li><li><p>Эта строфа из Vijñānabhairava — очень каверзная, и если ты являешься санскритским студентом, пытающимся перевести ее, то без дополнительной помощи ты скорее всего допустишь некоторые ошибки. Чтобы предотвратить эти ошибки, и показать другим людям, читающим это примечание, почему я перевел эту строфу таким образом, мне придется процитировать некоторые объяснения из комментария, написанного Śivopādhyāya... да, мне придется продолжать переводить. Начнем: &quot; शक्तिसङ्गमः स्त्रीसङ्गस्तेन सङ्क्षुब्धः सम्प्रवृत्तः शक्त्यावेश आनन्दशक्तिसमावेशस्तदावसानिकं तत्पार्यन्तिकम्। &quot; - &quot;Śaktisaṅgamaḥ strīsaṅgastena saṅkṣubdhaḥ sampravṛttaḥ śaktyāveśa ānandaśaktisamāveśastadāvasānikaṁ tatpāryantikam|&quot; - &quot;&#39;Śaktisaṅgama&#39; [означает] &#39;половой акт с женщиной&#39;; &#39;saṅkṣubdha&#39; [— это] &#39;то, что порождается этим [половым актом]&#39;; &#39;śaktyāveśa&#39; [относится к] &#39;растворению в Силе Блаженства --т.е. Śakti--&#39;; [и] &#39;tadāvasānika&#39; [указывает] &#39;на конец этого [растворения]&#39;&quot;. После этого, святой объясняет следующее: &quot; ... ब्रह्मतत्त्वस्य सुखं परब्रह्मानन्दस्... &quot; - &quot;... brahmatattvasya sukhaṁ parabrahmānandas...&quot; - &quot;... восторг Brahmatattva, т.е. Наслаждение Высшим Брахмой --Абсолютом--&quot;, и в заключении &quot; ... तत्सुखं स्वकमेव स्वाक्यम्... आत्मन एव सम्बन्धि... &quot; - &quot;... tatsukhaṁ svakameva svākyam... ātmana eva sambandhi...&quot; - &quot;... наслаждение [есть] &#39;svākya&#39;, или &#39;свое собственное&#39;, [короче говоря,] относящееся исключительно к своей собственной Самости...&quot;, а именно, только ты сам &quot;всегда&quot; являешься источником этого восторга.</p><p>Однако, мудрец цитирует строфу на всякий случай, если кто-то може неправильно понять учение:</p><blockquote><p>&quot;जायया सम्परिष्वक्तो न बाह्यं वेद नान्तरम्।<br> निदर्शनं श्रुतिः प्राह मूर्खस्तं मन्यते विधिम्॥&quot; -</p></blockquote><blockquote><p>&quot;Jāyayā sampariṣvakto na bāhyaṁ veda nāntaram|<br> Nidarśanaṁ śrutiḥ prāha mūrkhastaṁ manyate vidhim||&quot; -</p></blockquote><blockquote><p>&quot;Когда человек тесно обнимается с женщиной, он знает/ощущает ни снаружи и ни внутри --это полное единство--. Ведическое Писание упоминает [это как] пример или иллюстрацию. [Только] глупец расценивает это как запрет [для простого плотского удовольствия]&quot;.</p></blockquote><p>Тогда не будь глупым и рассматривай эту аналогию в качестве примера того, как во время полового акта с женщиной Блаженство Господа просачивается сквозь сеть, созданную Его собственной māyāśakti. Обычное соитие с женщиной не оживотворяет тебя, а делает твое тело/ум уставшим и тупым. Чтобы испытать оживотворение через половой акт с женщиной, необходимо быть мощным Yogī, наделенным большим количеством нужных знаний о том, чем, на самом деле, является женщина по отношению к мужчине. Но большинство мужчин вообще не обладают подобным типом знания и силы, им не рекомендуется искать Блаженство Турьи через простой половой акт с женщиной. Безусловно, это же относится и к женщинам. Любой человек, являющийся Сакалой, или тем, кто полностью отождествлен со своим физическим телом и умом/эго/интеллектом, не может получить &quot;полное&quot; Наслаждение Брахмы, занимаясь сексом, а &quot;только&quot; его каплю, которая просачивается через него.</p><p>Больше аналогий будет дано через другие строфы, извлеченные из Vijñānabhairava. OK, теперь все должно быть совершенно ясно.</p></li><li><p>Догадайся что? Другая хитрая строфа из Vijñānabhairava. Я вообще не рекомендую эту строфу для новичков в санскрите. Это только для продвинутых переводчиков, которые способны читать соответствующие комментарии, написанные частично Kṣemarāja и частично Śivopādhyāya. Имееется еще другой комментарий, называемый Vijñānakaumudī, написанный Ānandabhaṭṭa. Если вы получите их оба, надо быть осторожным с номерами строф, поскольку они все время не совпадают друг с другом (например, строфа 70, которую я сейчас объясняю, согласно первому комментарию, в Vijñānakaumudī находится под номером 69!). Ну хорошо, я буду использовать комментарий, написанный частично Kṣemarāja и частично Śivopādhyāya, чтобы рассеять все сомнения и недопонимание относительно значения определенных терминов:</p><p>&quot;लेहनं वक्त्रासवास्वादनं परिचुम्बनमिति यावत्। आमन्थनं प्रधानाङ्गविलोडनमालिङ्गनं वा। आकोटः पुनःपुनर्मर्दनं नखक्षतादिर्वा।&quot; - &quot;Lehanaṁ vaktrāsavāsvādanaṁ paricumbanamiti yāvat| Āmanthanaṁ pradhānāṅgaviloḍanamāliṅganaṁ vā| Ākoṭaḥ punaḥpunarmardanaṁ nakhakṣatādirvā|&quot; - &quot;[Термин] &#39;lehana&#39; [буквально означает] &#39;вкушение нектара, или сока губ женщины [своим собственным] ртом&#39;, т.е. &#39;крепкий поцелуй&#39; --приставка &#39;pari&#39; усиливает корень &#39;cumb&#39;, &#39;целовать&#39;, следовательно это &#39;крепко&#39; добавляется к выражению--; таково значение. [С другой стороны,] &#39;āmanthana&#39; [— это] &#39;смешивание с основными частями тела&#39;, или &#39;обнимание&#39;. [И] &#39;ākoṭa&#39; [указывает на] &#39;надавливание снова и снова&#39;, или &#39;царапание ногтями, и т.д.&#39;&quot;.</p><p>Ах, это было круто! Теперь вам полностью понятно, что означают термины. Как видно, хорошего словаря санскрита не достаточно для расшифровки строфы. Это довольно распространено в случае подавляющего большинства санскритских Писаний, имеющих дело с глубокой философией, но некоторые из них особенно трудно переводить правильно без специальной помощи, такие как Vijñānabhairava, Svacchandatantra и Mālinīvijayatantra. Без помощи сосответствующих комментариев, написанных превосходными учеными и мастерами, невозможно даже правильно начать их перевод. И очень часто для понимания комментария требуется больше времени, чем для самого Писания (кроме шуток). В целом, ваши санскритские знания, также как и философские, должны быть достаточно серьезными, или вы потерпите крах, если возьметесь за перевод монументальных Писаний, подобных тем, что я примел в качестве примера.</p></li><li><p>Ну, мне необходимо сделать два пояснения: (1) Согласно Ānandabhaṭṭa в его Vijñānakaumudī: &quot; ... बान्धवे वा सुहृदादौ चिरकालानन्तरं... दृष्टे... &quot; - &quot;... bāndhave vā suhṛdādau cirakālānantaraṁ... dṛṣṭe...&quot; - &quot;... или когда &#39;bāndhava&#39;, а именно &#39;suhṛd&#39;, и т.д. видеться после долгого времени...&quot;. Слово &quot;suhṛd&quot; (букв. добросердечный) подразумевает человека, которого любишь. Очень часто это переводится как &quot;друг&quot;, но также означает &quot;союзник&quot;. И согласно комментарию Kṣemarāja и Śivopādhyāya: &quot;... बान्धवे पुत्रमित्रादौ...&quot; - &quot;... bāndhave putramitrādau...&quot;</p></li></ol><ul><li><p>&quot;... &#39;bāndhava&#39;, букв. сын, друг и т.п...&quot;. Теперь вам понятно, почему я перевел термин &quot;bāndhava&quot; как &quot;родственник, друг и т.п.&quot;.</p><p>(2) В комментарии, написанном этими двумя мудрецами (Kṣemarāja и Śivopādhyāya) и также в Vijñānakaumudī, имеется другое прочтение для последней строки в строфе: &quot;आनन्दमुद्गतं ध्यात्वा तल्लयस्तन्मना भवेत्&quot; - &quot;Ānandamudgataṁ dhyātvā tallayastanmanā bhavet&quot; - &quot;Размышляя о возникшей радости, человек растворяется в ней --в этой радости-- [и] его ум погружается в нее --буквально, человек становится тем, чей ум поглощён этим&#39;--&quot;.</p><p>Это все!</p></li></ul><ol start="5"><li>Часть &quot;Bhāvayedbharitāvasthāṁ...&quot; строфы трудна для понимания, но, к счастью, мудрец Ānandabhaṭṭa проливает много света на это в своей Vijñānakaumudī: &quot; ... तदनुभवेनानन्दपूर्णत्वावस्थायां भावयत एकाग्रचित्तस्य परमानन्दप्राप्तिर्... &quot; - &quot;... tadanubhavenānandapūrṇatvāvasthāyāṁ bhāvayata ekāgracittasya paramānandaprāptir...&quot;</li></ol><ul><li><p>&quot;Благодаря переживанию этого удовольствия [связанного со вкусом], можно достичь высшего блаженства в случае того, кто созерцает —а именно, того, чей ум сконцентрирован— в состоянии полноты [такого] наслаждения&quot;.</p><p>Теперь вам должен быть совершенно понятен мой подход к переводу.</p></li></ul><ol start="6"><li>Поскольку последняя часть строфы очень сложна, мне пришлось прибегнуть к комментариям, составленным Kṣemarāja и Śivopādhyāya, чтобы полностью понять смысл: &quot; ... तस्य मनोरूढेर्हेतोर्यत्तन्मयत्वं शाक्तस्पर्शावेशस्तेन हेतुना तदात्मता— परब्रह्ममयत्वापत्तिः।... &quot; - &quot;... tasya manorūḍherhetoryattanmayatvaṁ śāktasparśāveśastena hetunā tadātmatā— parabrahmamayatvāpattiḥ|...&quot; - &quot;Из-за возвышения ума этого Yogī, [возникает] отождествление с ним --с таким несравнимым восторгом--, что является погружением в прикосновение, или контакт с Śakti --божественной Силой в форме Я-сознания--. Из-за этого [отождествления или поглощения, происходит] отождествление с Тем, т.е. имеет место отождествление с Высшим Брахмой --Абсолютом--&quot;.</li></ol><p>Ну вот, готово!</p></div><div class="stanza"><a href="#s3.39" id="s3.39" class="num">3.39</a><p>;;; sans</p><p>एतच्च त्रिपदाद्यनुप्राणनमन्तर्मुखत्वावष्टम्भत्वदशायामेवासाद्य न सन्तुष्येदपि तु—</p><blockquote><p><strong>चित्तस्थितिवच्छरीरकरणबाह्येषु॥३९॥</strong></p></blockquote><p>त्रिपदाद्यनुप्राणनमित्येव। यथान्तर्मुखरूपायां चित्तस्थितौ तुर्येणानुप्राणनं कुर्यात्तथा शरीरकरणबाह्याभासात्मिकायां बहिर्मुखतायामप्यान्तरविमर्शावष्टम्भबलात्क्रमात्क्रमं तारतम्यभाजा तेनानुप्राणनं कुर्यात्। यदुक्तं श्रीविज्ञानभैरवे</p><blockquote><p>सर्वं जगत्स्वदेहं वा स्वानन्दभरितं स्मरेत्।<br> युगपत्स्वामृतेनैव परानन्दमयो भवेत्॥</p></blockquote><p>इति। एवं ह्यानन्दात्मा स्वातन्त्र्यशक्तिः सर्वदशासु स्फुटीभूता सती यथेष्टनिर्माणकारिणी भवति॥३९॥ ;;;</p><hr><p>;;; trans</p><p>Etacca tripadādyanuprāṇanamantarmukhatvāvaṣṭambhatvadaśāyāmevāsādya na santuṣyedapi tu—</p><blockquote><p><strong>Cittasthitivaccharīrakaraṇabāhyeṣu||39||</strong></p></blockquote><p>Tripadādyanuprāṇanamityeva| Yathāntarmukharūpāyāṁ cittasthitau turyeṇānuprāṇanaṁ kuryāttathā śarīrakaraṇabāhyābhāsātmikāyāṁ bahirmukhatāyāmapyāntaravimarśāvaṣṭambhabalātkramātkramaṁ tāratamyabhājā tenānuprāṇanaṁ kuryāt| Yaduktaṁ śrīvijñānabhairave</p><blockquote><p>Sarvaṁ jagatsvadehaṁ vā svānandabharitaṁ smaret|<br> Yugapatsvāmṛtenaiva parānandamayo bhavet||</p></blockquote><p>iti| Evaṁ hyānandātmā svātantryaśaktiḥ sarvadaśāsu sphuṭībhūtā satī yatheṣṭanirmāṇakāriṇī bhavati||39||</p><p>;;;</p><hr><p>И <em>(ca)</em> этот [Yogī] <em>(etad)</em>, достигнув <em>(āsādya)</em> оживления <em>(anuprāṇanam)</em> трех состояний --проявления, поддержания и растворения-- <em>(tri-pada)</em> посредством главного --через Turya-- <em>(ādi)</em> только <em>(eva)</em> при условии <em>(daśāyām)</em> внутреннего <em>(antarmukhatva)</em> крепкого удерживания <em>(avaṣṭambhatva)</em>, не должен довольствоваться <em>(na santuṣyet)</em> [только этим], но также <em>(api tu)</em>—</p><blockquote><p><strong>Как [в случае] <em>(vat)</em> ментальных <em>(citta)</em> состояний <em>(sthiti)</em>, [так и касательно] тела <em>(śarīra)</em>, органов чувств <em>(karaṇa)</em> и внешних объектов --bāhya-- <em>(bāhyeṣu)</em>, [должно быть &quot;оживление, или оживотворение&quot; через наполнение их Блаженством четвертого состояния сознания]||39||</strong></p></blockquote><p>[Должен быть добавлен предыдущий афоризм для завершения смысла текущего:] &quot;[Должно быть] оживление <em>(anuprāṇanam)</em> трех состояний --проявления, поддержания и растворения-- <em>(tri-pada)</em> посредством главного <em>(ādi... iti eva)</em> [другими словами, &#39;четвертым состоянием сознания, которое является Свидетелем трех других и наполнено трансцендентным Блаженством&#39;]&quot;|</p><p>Так же, как <em>(yathā)</em> ему следует выполнять <em>(kuryāt)</em> оживление <em>(anuprāṇanam)</em> посредством Turya --четвертым состоянием, которое является компактной массой Блаженства-- <em>(turyeṇa)</em> в отношении внутреннего ментального состояния <em>(antarmukha-rūpāyām citta-sthitau)</em>, также <em>(tathā)</em> и <em>(api)</em> в отношении внешнего состояния <em>(bahis-mukhatāyām)</em>, чьей природой <em>(ātmikāyām)</em> является проявление <em>(ābhāsa)</em> тела <em>(śarīra)</em>, органов чувств <em>(karaṇa)</em> [и] внешних объектов <em>(bāhya)</em>, он должен выполнять <em>(kuryāt)</em> оживление <em>(anuprāṇanam)</em> посредством этого --через Turya-- <em>(tena)</em>, очень постепенно <em>(kramāt-kramam)</em>, т.е. понемногу <em>(tāratamya-bhājā)</em>, с помощью силы, [получаемой из] <em>(balāt)</em> крепкого удерживания <em>(avaṣṭambha)</em> внутренней <em>(āntara)</em> осознанности <em>(vimarśa)</em>|</p><p>Это <em>(yad)</em> было описано <em>(uktam)</em> в уважаемом Vijñānabhairava <em>(śrī-vijñānabhairave)</em>:</p><blockquote><p>&quot;Следует помнить <em>(smaret)</em> весь <em>(sarvam)</em> мир <em>(jagat)</em> или <em>(vā)</em> свое собственное <em>(sva)</em> тело <em>(deham)</em>, как наполненные <em>(bharitam)</em> своим собственным <em>(sva)</em> блаженством <em>(ānanda)</em>. В то же время <em>(yugapad)</em> --т.е. когда он способен помнить таким образом--, он наполнен <em>(mayaḥ bhavet)</em> Высшим <em>(para)</em> Наслаждением <em>(ānanda)</em> из-за своего собственного Нектара <em>(sva-amṛtena eva... iti)</em>&quot;||</p></blockquote><p>Таким образом <em>(evam hi)</em>, Сила <em>(śaktiḥ)</em> Абсолютной Свободы <em>(svātantrya)</em>, чья природа <em>(ātmā)</em> — Блаженство <em>(ānanda)</em>, становится проявленной <em>(sphuṭī-bhūtā satī)</em> во всех состояниях <em>(sarva-daśāsu)</em>, [и в результате,] превращается в <em>(bhavati)</em> Творца <em>(nirmāṇakāriṇī)</em>, [который способен проявлять все, что угодно] по желанию <em>(yathā-iṣṭa)</em><sup>1</sup>||39||</p><hr><ol><li>Абсолютная Свобода Господа всегда проявляется во всех состояниях всех существ, но в случае великого Yogī, разница заключается в том, что он полностью осознает эту Абсолютную Свободу. Другими словами, он имеет постоянное осознание Этого, в то время как остальные существа, пребывающие в рабстве, не имеют такого осознавания. В этом отличие! И конечно, именно в этом смысле мудрец Kṣemarāja утверждал, что в этом превосходном Yogī Сила Абсолютной Свободы становится Творцом чего угодно по (его) желанию. Хорошо!</li></ol></div>',6)]))}const d=e(p,[["render",o]]);export{k as __pageData,d as default};
