import{_ as a,v as t,b as e,R as m}from"./chunks/framework.5eef4778.js";const c=JSON.parse('{"title":"Афоризм 7","description":"Шива сутра вимаршини. Глава 3.","frontmatter":{"title":"Афоризм 7","description":"Шива сутра вимаршини. Глава 3.","tags":"vim-3","date":"2021-02-06T00:00:00.000Z"},"headers":[],"relativePath":"texts/shiva-sutra-vimarshini/3/7/index.md","filePath":"texts/shiva-sutra-vimarshini/3/7/index.md"}'),p={name:"texts/shiva-sutra-vimarshini/3/7/index.md"},o=m('<div class="stanza"><a href="#s3.7" id="s3.7" class="num">3.7</a><p>;;; sans</p><p>तदाह—</p><blockquote><p><strong>मोहजयादनन्ताभोगात्सहजविद्याजयः॥७॥</strong></p></blockquote><p>मोहस्याख्यात्यात्मकसमनान्तपाशात्मनो मायाया जयादभिभवात्। कीदृशाद् अनन्तः संस्कारप्रशमपर्यन्त आभोगो विस्तारो यस्य तादृशात्</p><blockquote><p>वेदनानादिधर्मस्य...।</p></blockquote><p>इत्यादिना निरूपितरूपायाः सहजविद्याया जयो लाभो भवति। आणवोपायस्यापि शाक्तोपायपर्यवसानादित्युक्तत्वात्। तथा च श्रीस्वच्छन्दे</p><blockquote><p>समनान्तं वरारोहे पाशजालमनन्तकम्।</p></blockquote><p>इत्युपक्रम्य</p><blockquote><p>पाशावलोकनं त्यक्त्वा स्वरूपालोकनं हि यत्।<br> आत्मव्याप्तिर्भवत्येषा शिवव्याप्तिस्ततोऽन्यथा॥<br> सार्वज्ञ्यादिगुणा येऽर्था व्यापकान्भावयेद्यदा।<br> शिवव्याप्तिर्भवत्येषा चैतन्ये हेतुरूपिणी॥</p></blockquote><p>इति ग्रन्थेनात्मव्याप्त्यन्तस्य मोहस्य जयादुन्मनाशिवव्याप्त्यात्मनः सहजविद्यायाः प्राप्तिरुक्ता। यदुक्तं तत्रैव</p><blockquote><p>आत्मतत्त्वं ततस्त्यक्त्वा विद्यातत्त्वे नियोजयेत्।<br> उन्मना सा तु विज्ञेया मनः सङ्कल्प उच्यते॥<br> सङ्कल्पः क्रमतो ज्ञानमुन्मनं युगपत्स्थितम्।<br> तस्मात्सा तु परा विद्या यस्मादन्या न विद्यते।<br> विन्दते ह्यत्र युगपत्सार्वज्ञ्यादिगुणान्परान्॥<br> वेदनानादिधर्मस्य परमात्मत्वबोधना।<br> वर्जनापरमात्मत्वे तस्माद्विद्येति सोच्यते॥<br> तत्रस्थो व्यञ्जयेत्तेजः परं परमकारणम्।</p></blockquote><p>इति॥७॥ ;;;</p><hr><p>;;; trans</p><p>Tadāha—</p><blockquote><p><strong>Mohajayādanantābhogātsahajavidyājayaḥ||7||</strong></p></blockquote><p>Mohasyākhyātyātmakasamanāntapāśātmano māyāyā jayādabhibhavāt| Kīdṛśād anantaḥ saṁskārapraśamaparyanta ābhogo vistāro yasya tādṛśāt</p><blockquote><p>Vedanānādidharmasya...|</p></blockquote><p>ityādinā nirūpitarūpāyāḥ sahajavidyāyā jayo lābho bhavati| Āṇavopāyasyāpi śāktopāyaparyavasānādityuktatvāt| Tathā ca śrīsvacchande</p><blockquote><p>Samanāntaṁ varārohe pāśajālamanantakam|</p></blockquote><p>ityupakramya</p><blockquote><p>Pāśāvalokanaṁ tyaktvā svarūpālokanaṁ hi yat|<br> Ātmavyāptirbhavatyeṣā śivavyāptistato&#39;nyathā||<br> Sārvajñyādiguṇā ye&#39;rthā vyāpakānbhāvayedyadā|<br> Śivavyāptirbhavatyeṣā caitanye heturūpiṇī||</p></blockquote><p>iti granthenātmavyāptyantasya mohasya jayādunmanāśivavyāptyātmanaḥ sahajavidyāyāḥ prāptiruktā| Yaduktaṁ tatraiva</p><blockquote><p>Ātmatattvaṁ tatastyaktvā vidyātattve niyojayet|<br> Unmanā sā tu vijñeyā manaḥ saṅkalpa ucyate||<br> Saṅkalpaḥ kramato jñānamunmanaṁ yugapatsthitam||<br> Tasmātsā tu parā vidyā yasmādanyā na vidyate|<br> Vindate hyatra yugapatsārvajñyādiguṇānparān||<br> Vedanānādidharmasya paramātmatvabodhanā|<br> Varjanāparamātmatve tasmādvidyeti socyate||<br> Tatrastho vyañjayettejaḥ paraṁ paramakāraṇam|</p></blockquote><p>iti||7||</p><p>;;;</p><hr><p>[Śiva] сказал <em>(āha)</em> так --т.е. что-- <em>(tad)</em> [когда Moha, или Заблуждение повержено]—</p><blockquote><p><strong>[Yogī достигает] совершенного владения <em>(jayaḥ)</em> Естественным <em>(sahaja)</em> Знанием <em>(vidyā)</em> через всепроникающее покорение <em>(jayāt anantābhogāt)</em> Māyā, или неведения <em>(moha)</em>||7||</strong></p></blockquote><p>Mohajayāt <em>(mohasya... jayāt)</em> [означает] посредством покорения <em>(abhibhavāt)</em> Māyā <em>(māyāyāḥ)</em>, чьей природой --т.е. Māyā-- является <em>(ātmanaḥ)</em> [быть] петлей <em>(pāśa)</em>, которая заканчивается <em>(anta)</em> в Samanā <em>(samanā)</em> [и] состоит из <em>(ātmaka)</em> изначального неведения <em>(akhyāti)</em><sup>1</sup>|</p><p>Посредством какого вида [покорения] <em>(kīdṛśāt)</em>? Через тот вид [завоевания] <em>(tādṛśāt)</em>, чье <em>(yasya)</em> ābhoga <em>(ābhogaḥ)</em> , [или] расширение <em>(vistāraḥ)</em> [является] безграничным (ananta) --т.е. беспредельным-- <em>(anantaḥ)</em>, а именно, простирается до <em>(antaḥ)</em> прекращения <em>(praśama)</em> латентных впечатлений/отпечатков [прошлых действий] <em>(saṁskāra)</em><sup>2</sup>|</p><p>[Итак,] имеет место <em>(bhavati)</em> совершенное владение, или покорение <em>(jayaḥ)</em>, [другими словами,] овладение <em>(lābhaḥ)</em> Естественным Знанием <em>(sahaja-vidyāyāḥ)</em>, чья природа <em>(rūpāyāḥ)</em> охарактеризована <em>(nirūpita)</em> [в следующем фрагменте Svacchandatantra:]</p><blockquote><p>&quot;Она производит исследование <em>(vedanā)</em> безначальных характеристик [Господа] --т.е. Его абсолютной Свободы, или Svātantrya-- <em>(anādi-dharmasya)</em>, и т.д. <em>(iti ādinā)</em><sup>3</sup>&quot;||<br> [См. IV, 396 в Svacchandatantra]</p></blockquote><p>[О приобретении Естественных Знаний было сказано] даже <em>(api)</em> [в контексте] Āṇavopāya <em>(āṇava-upāyasya)</em> поскольку она --т.е. Āṇavopāya-- как говорится <em>(iti uktatvāt)</em>, заканчивается <em>(paryavasānāt)</em> в Śāktopāya <em>(śākta-upāya)</em>|</p><p>Так <em>(tathā ca)</em>, в уважаемом Svacchandatantra <em>(śrī-svacchande)</em>, начиная с <em>(upakramya)</em>:</p><blockquote><p>&quot;О Прекрасная <em>(vara-ārohe)</em>!, [имеется] нескончаемая <em>(anantakam)</em> сеть <em>(jālam)</em> петель <em>(pāśa)</em>, доходящая до <em>(antam)</em> Samanā <em>(samanā... iti)</em>&quot;||<br> [См. IV, 432 в Svacchandatantra]</p></blockquote><p>[и заканчивая с:]</p><blockquote><p>&quot;После отбрасывания <em>(tyaktvā)</em> отождествления себя <em>(avalokanam)</em> с петлей --т.е. оков-- <em>(pāśa)</em>, [имеет место] этот <em>(hi yad)</em> акт видения <em>(ālokanam)</em> своей собственной сущностной природы <em>(sva-rūpa)</em>. Это <em>(eṣā)</em> есть <em>(bhavati)</em> Ātmavyāpti --а именно, проникновение в Самость-- <em>(ātma-vyāptiḥ)</em><sup>4</sup></p></blockquote><p>. Śivavyāpti --т.е. слияние с Śiva-- <em>(śiva-vyāptiḥ)</em> [происходит] отличным образом <em>(anyathā)</em> от этого --т.е. от Ātmavyāpti-- <em>(tatas)</em>. Аrtha-s --желаемые объекты-- <em>(ye arthāḥ)</em> [— это] те, характеристиками которых <em>(guṇāḥ)</em> является всеведение <em>(sārvajñya)</em>, и т.п. <em>(ādi)</em>. Когда <em>(yadā)</em> [Yogī] созерцает, [внутри себя эти artha-s] <em>(bhāvayet)</em> как пронизывающие [все сущее] <em>(vyāpakān)</em>, это <em>(eṣā)</em> есть <em>(bhavati)</em> Śivavyāpti <em>(śiva-vyāptiḥ)</em>, которое является причиной, или средством <em>(hetu-rūpiṇī)</em> для [стимулирования, или развития] Caitanya <em>(caitanye... iti)</em> [в Svātantrya, или Абсолютную Свободу Высшего Господа]<sup>5</sup>&quot;|| [См. IV, 434-435 в Svacchandatantra]</p><p>[Таким образом,] согласно книге --т.е. Svacchandatantra-- <em>(granthena)</em>, говорится <em>(uktā)</em>, что [происходит] овладение <em>(prāptiḥ)</em> Sahajavidyā --Естественным Знанием-- <em>(sahaja-vidyāyāḥ)</em>, природой которого <em>(ātmanaḥ)</em> является Śivavyāpti --слияние с Śiva-- <em>(śiva-vyāpti)</em>, [ведущего к] Unmanā --состоянию Śiva, полному Его Абсолютной Свободы-- <em>(unmanā)</em> благодаря покорению <em>(jayāt)</em> Moha, или Māyā <em>(mohasya)</em>, которая распространяется до <em>(antasya)</em> Ātmavyāpti, или проникновения в Самость <em>(ātma-vyāpti)</em> --другими словами, Māyā заканчивается, когда проявляется Ātmavyāpti--|</p><p>Так <em>(yad)</em> было сказано <em>(uktam)</em> в том самом [Писании] <em>(tatra eva)</em> --а именно, в Svacchandatantra--:</p><blockquote><p>&quot;По этой причине <em>(tatas)</em>, через отказ от <em>(tyaktvā)</em> Ātmatattva --букв. принципа Самости, т.е. Ātmavyāpti, или проникновения в Самость-- <em>(ātma-tattva)</em>, [Yogī] должен связать [себя] <em>(niyojayet)</em> с Vidyātattva --букв. с принципом божественного Знания-- <em>(vidyā-tattve)</em>. Это [божественное Знание, или Vidyā] <em>(sā)</em> должно быть известно <em>(vijñeyā)</em> как Unmanā --состояние Śiva-- <em>(unmanā)</em>, несомненно <em>(tu)</em>. Manas --т.е. ум, или состояние, в котором работает ум-- <em>(manas)</em> как считается, — это <em>(ucyate)</em> намерение, или желание <em>(saṅkalpaḥ)</em>. [Такое] намерение, или желание <em>(saṅkalpaḥ)</em> [есть] знание <em>(jñānam)</em>, [характеризуемое] постепенностью <em>(kramatas)</em>, тогда как Unmanā --Высшее Состояние, свободное от ума-- <em>(unmanam)</em> [характеризуется] одновременностью <em>(yugapad)</em> [и является] устойчивым --т.е. вечным-- <em>(sthitam)</em>. Поэтому <em>(tasmāt)</em>, поскольку <em>(yasmāt)</em> нет <em>(na vidyate)</em> другого <em>(anyā)</em>, Она <em>(sā)</em> несомненно [является] <em>(tu)</em> Высшей <em>(parā)</em> Vidyā --т.е. божественным Знанием-- <em>(vidyā)</em>. Тогда --когда Высшая Vidyā появляется-- <em>(atra)</em>, действительно <em>(ca)</em>, [Yogī] приобретает <em>(vindate)</em> высшие <em>(parān)</em> качества <em>(guṇān)</em> всеведения <em>(sārvajñya)</em>, и т.п. <em>(ādi)</em>, одномоментно <em>(yugapad)</em>. Так как [1] Она вызывает исследование <em>(vedanā)</em> безначальных характеристик [Господа] --т.е. Его абсолютной Свободы, или Svātantrya-- <em>(anādi-dharmasya)</em>; [2] побуждает и становится причиной восприятия/понимания <em>(bodhanā)</em> состояния Высшего Я <em>(parama-ātmatva)</em>; [и][3] устраняет то <em>(varjanā)</em>, что не является состоянием Высшего Я <em>(a-parama-ātmatve)</em>; в силу [всего] этого <em>(tasmāt)</em> Она <em>(sā)</em> называется <em>(ucyate)</em> &#39;Vidyā&#39; <em>(vidyā iti)</em>. Укоренившись <em>(sthaḥ)</em> там --в состоянии, свободном от ума (unmanā), т.е. в Высшем Я-- <em>(tatra)</em>, он проявляет <em>(vyañjayet)</em> Высший <em>(param)</em> Свет <em>(tejas)</em> --букв. Свет Господа--, Высшую <em>(parama)</em> Причину <em>(kāraṇam... iti)</em>&quot;||<br> [См. IV, 393-397 в Svacchandatantra]</p></blockquote><p>||7||</p><hr><ol><li><p>Samanā — это Śakti, или Сила Господа, проявляющая себя как мысль от śūnya, или состояния пустоты, до таттвы 36.</p></li><li><p>Безграничное расширение, т.е. всепроникающее. Отсюда мой перевод в афоризме: jayāt anantābhogāt - через всепроникающее покорение. Saṁskāra-s — это латентные впечатления от прошлых karma-s, или действий. Совокупность всех латентных впечатлений есть эго. Поэтому, когда такая совокупность удаляется, вашей индивидуальности приходит конец, и Господь остается один в форме вашей собственной Самости (Вас Самих!).</p></li><li><p>Мудрец Kṣemarāja процитировал всю строфу ранее, в афоризме 21 Первой Главы. Тем не менее, он полностью процитирует ее опять в комментарии к этому афоризму.</p></li><li><p>Это первая стадия процесса Окончательного Освобождения, согласно Трике. Оковы — это все ограничения, такие как Kañcuka-s, эго, ум, тело и т.д. Когда человек воспринимает свое внутреннее Я, или сущностную природу как только Jñāna, или Знание, лишенное всякой Kriyā, или деятельности, это есть Ātmavyāpti, или присущее Самости (букв. неотъемлемое и неотделимое присутствие Самости в Yogī). В этом случае, Самость воспринимается как Свидетель, изолированный от всего остального. Так например, мудрец Kṣemarāja выражает в своем комментарии на эту строфу в Svacchandatantra: &quot;... स्वरूपं पासोत्तीर्णचिन्मात्रत्वं यद्... &quot; - &quot;... svarūpaṁ pāsottīrṇacinmātratvaṁ yad...&quot; - &quot;... [Термин] svarūpa [в этой строфе— это то,] что состоит только из Сознания, которое превосходит петли, или оковы...&quot;.</p></li></ol><p>В свою очередь, термин &quot;avalokana&quot;, хотя буквально означает &quot;созерцание, видение и т.п..&quot;, согласно Kṣemarāja есть &quot; आत्मत्वेनाभिमनन &quot; - &quot;ātmatvenābhimanana&quot; - &quot;думать, что [что-то] есть вы&quot;. Отсюда мой перевод &quot;avalokana&quot; как &quot;отождествление&quot;. Это разъяснение поможет людям понять, почему я перевел таким образом, особенно тем, кто изучает санскрит глубоко. Текст Svacchandatantra полон &quot;кажущихся&quot; противоречий, как будто автор временами не следовал строгим грамматическим правилам. Поэтому, переводчикам необходимо прочитать научный комментарий Kṣemarāja (т.е. Svacchandoddyota), поскольку он принадлежит к традиции и, следовательно, знает тайные значения многих неясных терминов и выражений, встречающихся в почтенном Svacchandatantra. Кстати, Svacchandoddyota —это единственный комментарий на Svacchandatantra. Был другой комментатор, но он потерялся на протяжении веков.</p><p>Кроме того, обращаясь сейчас к санскритским ученикам: Svacchandatantra — это яркий пример теории против практики, я имею в виду, что вы можете выучить все грамматические правила, но когда вы сталкиваетесь с определенными &quot;реальными&quot; текстами, вы не можете их перевести. Почему? Потому что они не следуют грамматической теории, будь то из-за ошибок в транскрипции, или потому, что автор зашифровал тайные значения странным образом, или потому, что стиль очень старый, или просто потому, что потребность ритма требовала твиста в манере написания. Вот почему я не люблю обучать санскриту через примеры, подобные этим: &quot;Это — коробка, а это — мой кот&quot; и т.п., как если бы санскрит был английским или испанским языком. Это излишне, хотя, по-видимому, совершенно необходимо, когда так обучают новичков санскриту, что заставляет их думать, будто настоящие тексты всегда будут следовать логичным и приятным шаблонам. Это основная причина, по которой многие продвинутые ученики санскрита не способны переводить поначалу реальные тексты &quot;своими силами&quot;, несмотря на все их массивные грамматические знания.</p><ol start="5"><li>Для санскритских учеников: текст &quot;Sārvajñyādiguṇā ye&#39;rthā vyāpakānbhāvayedyadā&quot; выглядит странно, потому что там отсутствует слово &quot;tān&quot; [те - Аккузатив множественного числа от tad (м.р.)]. Поэтому, отсутствует очевидная связь между &quot;Sārvajñyādiguṇā ye&#39;rthāḥ&quot; и &quot;vyāpakān&quot;, поскольку в первом случае используется Номинатив множественного числа, а в последнем — Аккузатив множественного числа. Теперь, для остальных смертных (ха-ха!): мне надо четко указать некоторые моменты, чтобы вы поняли, почему именно я перевел строфу таким образом. Чтобы выполнить все это, я переведу весь комментарий Kṣemarāja на две строки &quot;Sārvajñyādiguṇā ye&#39;rthā vyāpakānbhāvayedyadā| Śivavyāptirbhavatyeṣā caitanye heturūpiṇī||&quot; в его Svacchandoddyota:</li></ol><blockquote><p>&quot; ये वक्ष्यमाणाः सर्वज्ञत्वसर्वकर्तृत्वादिगुणा धर्माः परमोपादेयत्वेनार्थ्यमानत्वादर्थास्तान्व्यापकानिति अशेषमन्तरभेदेन क्रोडीकुर्वतो यदा स्वात्मनि भावयेत्तदा तद्भावनापरिनिष्पत्तिरात्मनः शिवव्याप्तिः सा च भावके चैतन्ये हेतुरूपिणी प्रयोजिका तत्प्रसादादेव शुद्धात्मनस्तद्भावनाप्ररूढेः॥४३५॥ &quot; - &quot;Ye vakṣyamāṇāḥ sarvajñatvasarvakartṛtvādiguṇā dharmāḥ paramopādeyatvenārthyamānatvādarthāstānvyāpakāniti aśeṣamantarabhedena kroḍīkurvato yadā svātmani bhāvayettadā tadbhāvanāpariniṣpattirātmanaḥ śivavyāptiḥ sā ca bhāvake caitanye heturūpiṇī prayojikā tatprasādādeva śuddhātmanastadbhāvanāprarūḍheḥ||435||&quot; - &quot;artha-s --желаемые объекты--, которые будут изучаться позже, — это те, чьими guṇa, или характеристиками являются всеведение, всемогущество и т.п., поскольку они указаны как превосходнейшие. Когда [великий Yogī] созерцает, в своем собственном существе --т.е. внутри себя--, те [artha-s], как &#39;пронизывающие [все сущее], т.е. как охватывающие все через внутреннюю недуальность, тогда совершенство его созерцания [есть] его Śivavyāpti [или слияние с Śiva, Высшим Господом]. Это [Śivavyāpti] является причиной, или средством для стимулирования, или развития Caitanya --Сознания-- в чистом уме того, кто достиг этого созерцания только лишь благодаря Его Милости&quot;.</p></blockquote><p>Теперь короткое пояснение, что сказал мудрец. Хотя вначале, в Ātmavyāpti, великий Yogī испытывал свою сущностную природу, как просто Jñāna, или Знание, полностью лишенное Kriyā, или действия, после этого, artha-s, такие как всеведение, всемогущество и пр., созерцаются как всепроникающие, или включающие в себя все сущее через внутреннюю недвойственность. Короче, силы всеведения, всемогущества и т.п. проецируются на все, и весь процесс завершается у Yogī видением единства всех вещей. Самость больше не рассматривается как нечто изолированное от остального, а наоборот, Yogī осознает, что его Самость является Высшим Господом (Śiva) и, следовательно, всей вселенной.</p><p>Тем не менее, в процессе имеются этапы. Первым признаком Śivavyāpti является Kramamudrā. В этой Mudrā (Печати) есть порядок следования, или последовательность (krama). Как (это происходит)? Yogī не может остановить процесс последовательного открывания и закрывания глаз. Когда глаза закрыты, он созерцает свою внутреннюю Самость, а когда они открыты, он видит всю вселенную, пронизанную этой Самостью. Эта последовательность продолжается некоторое время и полностью контролируется Господом (т.е. никакое эго совершенно не способно осуществить такое). Тем не менее, появление Kramamudrā и вытекающее из этого состояния осознание, что все есть Высшее Я, не является Окончательным Освобождением, согласно Трике. Окончательное Освобождение достигается только тогда, когда Yogī приобретает Абсолютную Свободу (Svātantrya, или Svācchandya) Господа. И в этом смысле Śivavyāpti есть &quot; भावके चैतन्ये हेतुरूपिणी प्रयोजिका &quot; - &quot;bhāvake caitanye heturūpiṇī prayojikā&quot;, т.е. &quot;причина, или средство для стимулирования, или развития Caitanya --Сознания--&quot; . Развитие, или трансформация во что? В Абсолютную Свободу! В общем, Śivavyāpti приводит к Окончательному Освобождению, действительно. И это происходит только в чистом уме того, кто достиг такого созерцания. И конечно, это имеет место только благодаря Его Милости, и никак иначе (например, через усилия). Здесь имеется больше тайн, но Господь говорит мне, что этого было больше, чем достаточно.</p></div>',1),n=[o];function i(y,r,v,u,s,d){return t(),e("div",null,n)}const q=a(p,[["render",i]]);export{c as __pageData,q as default};
